'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  AlertTriangle,
  TrendingUp,
  TrendingDown,
  Users,
  Search,
  Filter,
  Download,
  RefreshCw,
  Calendar,
  BarChart3,
  AlertCircle,
} from 'lucide-react';
import { format, subDays, subMonths } from 'date-fns';
import { WarningSeverity, DuplicateType, UserDecision } from '@prisma/client';
import { useSession } from 'next-auth/react';
import { redirect } from 'next/navigation';
import { hasPermission } from '@/lib/permissions';
import { PERMISSIONS } from '@/lib/permissions';

interface DuplicateStatistics {
  totalWarnings: number;
  proceedCount: number;
  cancelledCount: number;
  proceedRate: number;
  severityBreakdown: Record<WarningSeverity, number>;
}

interface DuplicateWarning {
  id: string;
  createdAt: string;
  severity: WarningSeverity;
  warningType: DuplicateType;
  triggerAction: string;
  decisionMade: boolean;
  userDecision?: UserDecision;
  decisionAt?: string;
  triggeredBy: {
    id: string;
    name: string;
    email: string;
    role: string;
  };
  potentialDuplicates: Array<{
    id: string;
    matchType: DuplicateType;
    confidence: number;
    existingCompany?: string;
    ownedBy?: {
      id: string;
      name: string;
      role: string;
    };
    lastContactDate?: string;
    recordStatus?: string;
  }>;
}

export default function DuplicatesAdminPage() {
  const { data: session } = useSession();
  const [statistics, setStatistics] = useState<DuplicateStatistics | null>(null);
  const [warnings, setWarnings] = useState<DuplicateWarning[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [severityFilter, setSeverityFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [dateRange, setDateRange] = useState<string>('30');

  // Check admin permissions
  useEffect(() => {
    if (session && !hasPermission(session.user as any, PERMISSIONS.DUPLICATES.MANAGE.resource, PERMISSIONS.DUPLICATES.MANAGE.action) &&
        !hasPermission(session.user as any, PERMISSIONS.DUPLICATES.VIEW_ALL.resource, PERMISSIONS.DUPLICATES.VIEW_ALL.action)) {
      redirect('/');
    }
  }, [session]);

  // Fetch statistics and warnings
  useEffect(() => {
    if (session) {
      fetchData();
    }
  }, [session, dateRange]);

  const fetchData = async () => {
    setLoading(true);
    try {
      const dateFrom = dateRange === 'all' ? undefined : 
        format(subDays(new Date(), parseInt(dateRange)), 'yyyy-MM-dd\'T\'HH:mm:ss\'Z\'');
      const dateTo = format(new Date(), 'yyyy-MM-dd\'T\'HH:mm:ss\'Z\'');

      const [statsResponse, warningsResponse] = await Promise.all([
        fetch(`/api/admin/duplicates?action=statistics${dateFrom ? `&dateFrom=${dateFrom}&dateTo=${dateTo}` : ''}`),
        fetch('/api/admin/duplicates?action=recent-warnings&limit=100&includeResolved=true')
      ]);

      if (statsResponse.ok) {
        const stats = await statsResponse.json();
        setStatistics(stats);
      }

      if (warningsResponse.ok) {
        const warningsData = await warningsResponse.json();
        setWarnings(warningsData);
      }
    } catch (error) {
      console.error('Error fetching duplicate data:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredWarnings = warnings.filter(warning => {
    const matchesSearch = !searchTerm || 
      warning.triggeredBy.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      warning.triggeredBy.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      warning.potentialDuplicates.some(dup => 
        dup.existingCompany?.toLowerCase().includes(searchTerm.toLowerCase())
      );

    const matchesSeverity = severityFilter === 'all' || warning.severity === severityFilter;

    const matchesStatus = statusFilter === 'all' || 
      (statusFilter === 'pending' && !warning.decisionMade) ||
      (statusFilter === 'resolved' && warning.decisionMade) ||
      (statusFilter === 'proceeded' && warning.userDecision === UserDecision.PROCEEDED) ||
      (statusFilter === 'cancelled' && warning.userDecision === UserDecision.CANCELLED);

    return matchesSearch && matchesSeverity && matchesStatus;
  });

  const getSeverityColor = (severity: WarningSeverity) => {
    switch (severity) {
      case WarningSeverity.CRITICAL:
        return 'destructive';
      case WarningSeverity.HIGH:
        return 'destructive';
      case WarningSeverity.MEDIUM:
        return 'secondary';
      default:
        return 'outline';
    }
  };

  const getDecisionColor = (decision?: UserDecision) => {
    switch (decision) {
      case UserDecision.PROCEEDED:
        return 'default';
      case UserDecision.CANCELLED:
        return 'secondary';
      default:
        return 'outline';
    }
  };

  const formatMatchType = (matchType: DuplicateType) => {
    return matchType.toLowerCase().replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Less than 1 hour ago';
    if (diffInHours < 24) return `${diffInHours} hours ago`;
    if (diffInHours < 168) return `${Math.floor(diffInHours / 24)} days ago`;
    return format(date, 'MMM dd, yyyy');
  };

  if (!session) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Duplicate Management</h1>
          <p className="text-muted-foreground">
            Monitor and analyze duplicate detection system performance
          </p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={fetchData} disabled={loading}>
            <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
          <Button variant="outline">
            <Download className="h-4 w-4 mr-2" />
            Export
          </Button>
        </div>
      </div>

      <Tabs defaultValue="overview" className="space-y-4">
        <TabsList>
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="warnings">Recent Warnings</TabsTrigger>
          <TabsTrigger value="analytics">Analytics</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-4">
          {/* Date Range Selector */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-5 w-5" />
                Time Period
              </CardTitle>
            </CardHeader>
            <CardContent>
              <Select value={dateRange} onValueChange={setDateRange}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Select time period" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="7">Last 7 days</SelectItem>
                  <SelectItem value="30">Last 30 days</SelectItem>
                  <SelectItem value="90">Last 90 days</SelectItem>
                  <SelectItem value="365">Last year</SelectItem>
                  <SelectItem value="all">All time</SelectItem>
                </SelectContent>
              </Select>
            </CardContent>
          </Card>

          {/* Statistics Cards */}
          {statistics && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <Card>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">Total Warnings</CardTitle>
                  <AlertTriangle className="h-4 w-4 text-muted-foreground" />
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{statistics.totalWarnings}</div>
                  <p className="text-xs text-muted-foreground">
                    Duplicate warnings shown
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">Proceed Rate</CardTitle>
                  <TrendingUp className="h-4 w-4 text-muted-foreground" />
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{statistics.proceedRate.toFixed(1)}%</div>
                  <p className="text-xs text-muted-foreground">
                    BDRs proceeding despite warnings
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">Proceeded</CardTitle>
                  <Users className="h-4 w-4 text-muted-foreground" />
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{statistics.proceedCount}</div>
                  <p className="text-xs text-muted-foreground">
                    Warnings overridden
                  </p>
                </CardContent>
              </Card>

              <Card>
                <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                  <CardTitle className="text-sm font-medium">Cancelled</CardTitle>
                  <TrendingDown className="h-4 w-4 text-muted-foreground" />
                </CardHeader>
                <CardContent>
                  <div className="text-2xl font-bold">{statistics.cancelledCount}</div>
                  <p className="text-xs text-muted-foreground">
                    Actions cancelled
                  </p>
                </CardContent>
              </Card>
            </div>
          )}

          {/* Severity Breakdown */}
          {statistics && (
            <Card>
              <CardHeader>
                <CardTitle>Warning Severity Breakdown</CardTitle>
                <CardDescription>Distribution of warning severities</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                  {Object.entries(statistics.severityBreakdown).map(([severity, count]) => (
                    <div key={severity} className="text-center">
                      <div className="text-2xl font-bold">{count}</div>
                      <Badge variant={getSeverityColor(severity as WarningSeverity)}>
                        {severity}
                      </Badge>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="warnings" className="space-y-4">
          {/* Filters */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Filter className="h-5 w-5" />
                Filters
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex flex-col md:flex-row gap-4">
                <div className="flex-1">
                  <Label htmlFor="search">Search</Label>
                  <div className="relative">
                    <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
                    <Input
                      id="search"
                      placeholder="Search by user name, email, or company..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      className="pl-8"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="severity">Severity</Label>
                  <Select value={severityFilter} onValueChange={setSeverityFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All severities" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Severities</SelectItem>
                      <SelectItem value="CRITICAL">Critical</SelectItem>
                      <SelectItem value="HIGH">High</SelectItem>
                      <SelectItem value="MEDIUM">Medium</SelectItem>
                      <SelectItem value="LOW">Low</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <Label htmlFor="status">Status</Label>
                  <Select value={statusFilter} onValueChange={setStatusFilter}>
                    <SelectTrigger>
                      <SelectValue placeholder="All statuses" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Statuses</SelectItem>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="resolved">Resolved</SelectItem>
                      <SelectItem value="proceeded">Proceeded</SelectItem>
                      <SelectItem value="cancelled">Cancelled</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Warnings Table */}
          <Card>
            <CardHeader>
              <CardTitle>Recent Duplicate Warnings ({filteredWarnings.length})</CardTitle>
              <CardDescription>
                Latest duplicate detection warnings and their outcomes
              </CardDescription>
            </CardHeader>
            <CardContent>
              {loading ? (
                <div className="text-center py-8">
                  <RefreshCw className="h-8 w-8 animate-spin mx-auto mb-2" />
                  <p>Loading warnings...</p>
                </div>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>User</TableHead>
                      <TableHead>Type</TableHead>
                      <TableHead>Severity</TableHead>
                      <TableHead>Matches</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredWarnings.map((warning) => (
                      <TableRow key={warning.id}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{warning.triggeredBy.name}</div>
                            <div className="text-sm text-muted-foreground">{warning.triggeredBy.email}</div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <Badge variant="outline">
                            {formatMatchType(warning.warningType)}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          <Badge variant={getSeverityColor(warning.severity)}>
                            {warning.severity}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          <div className="text-sm">
                            {warning.potentialDuplicates.length} duplicate(s)
                            {warning.potentialDuplicates.length > 0 && (
                              <div className="text-xs text-muted-foreground">
                                {warning.potentialDuplicates[0].existingCompany || 'Unknown Company'}
                                {warning.potentialDuplicates[0].ownedBy && 
                                  ` â€¢ ${warning.potentialDuplicates[0].ownedBy.name}`
                                }
                              </div>
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          {warning.decisionMade ? (
                            <Badge variant={getDecisionColor(warning.userDecision)}>
                              {warning.userDecision}
                            </Badge>
                          ) : (
                            <Badge variant="outline">Pending</Badge>
                          )}
                        </TableCell>
                        <TableCell>
                          <div className="text-sm">{formatTimeAgo(warning.createdAt)}</div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="analytics" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                System Performance Analytics
              </CardTitle>
              <CardDescription>
                Detailed insights into duplicate detection system effectiveness
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <p className="text-sm text-muted-foreground">
                  Advanced analytics and reporting features will be available in future releases.
                  Current basic statistics are shown in the Overview tab.
                </p>
                
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="p-4 border rounded-lg">
                    <h4 className="font-semibold mb-2">Most Common Duplicate Types</h4>
                    <p className="text-sm text-muted-foreground">
                      Analysis of which types of duplicates are most frequently detected
                    </p>
                  </div>
                  
                  <div className="p-4 border rounded-lg">
                    <h4 className="font-semibold mb-2">User Behavior Patterns</h4>
                    <p className="text-sm text-muted-foreground">
                      Insights into how different users respond to duplicate warnings
                    </p>
                  </div>
                  
                  <div className="p-4 border rounded-lg">
                    <h4 className="font-semibold mb-2">Time-based Trends</h4>
                    <p className="text-sm text-muted-foreground">
                      Duplicate detection patterns over time and seasonal variations
                    </p>
                  </div>
                  
                  <div className="p-4 border rounded-lg">
                    <h4 className="font-semibold mb-2">False Positive Analysis</h4>
                    <p className="text-sm text-muted-foreground">
                      Track system accuracy and identify areas for improvement
                    </p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

'use client';

import React from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Separator } from '@/components/ui/separator';
import { useState, useEffect } from 'react';
import { Shield, User, Users, Crown, Settings, Save, RotateCcw, Info } from 'lucide-react';
import { toast } from 'sonner';
import { Alert, AlertDescription } from '@/components/ui/alert';

interface Permission {
  id: string;
  name: string;
  resource: string;
  action: string;
  description?: string;
}

interface RolePermissionsData {
  rolePermissions: Record<string, Permission[]>;
  availablePermissions: Permission[];
}

const ROLES = [
  { key: 'BDR', name: 'BDR', icon: User, description: 'Business Development Representatives - front-line sales staff' },
  { key: 'TEAM_LEAD', name: 'Team Lead', icon: Users, description: 'Team leaders who manage small groups of BDRs' },
  { key: 'MANAGER', name: 'Manager', icon: Shield, description: 'Managers who oversee territories and multiple teams' },
  { key: 'DIRECTOR', name: 'Director', icon: Crown, description: 'Directors with broad organizational oversight' },
];

const RESOURCE_GROUPS = {
  'Core Operations': ['LEADS', 'PIPELINE', 'FINANCE'],
  'Administration': ['USERS', 'SETTINGS', 'ACTIVITY_LOGS'],
  'Reporting & Analytics': ['REPORTS', 'DUPLICATES'],
  'Communication': ['MESSAGING', 'TEMPLATES'],
};

const ACTION_DESCRIPTIONS = {
  CREATE: 'Create new records',
  READ: 'View and access records',
  UPDATE: 'Edit and modify records', 
  DELETE: 'Remove records',
  VIEW_ALL: 'See all data across organization',
  VIEW_TEAM: 'See team/territory data',
  EXPORT: 'Export data to external formats',
  MANAGE: 'Full administrative control',
};

async function fetchRolePermissions(): Promise<RolePermissionsData> {
  const res = await fetch('/api/admin/roles/permissions');
  if (!res.ok) throw new Error('Failed to fetch role permissions');
  return res.json();
}

async function updateRolePermissions(role: string, permissionIds: string[]): Promise<void> {
  const res = await fetch('/api/admin/roles/permissions', {
    method: 'PATCH',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ role, permissionIds }),
  });
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.error || 'Failed to update role permissions');
  }
}

export default function RolePermissionsAdmin() {
  const queryClient = useQueryClient();
  const [selectedRole, setSelectedRole] = useState('BDR');
  const [localPermissions, setLocalPermissions] = useState<Record<string, string[]>>({});
  const [hasChanges, setHasChanges] = useState(false);

  const { data, isLoading } = useQuery({
    queryKey: ['rolePermissions'],
    queryFn: fetchRolePermissions,
  });

  const updateMutation = useMutation({
    mutationFn: ({ role, permissionIds }: { role: string; permissionIds: string[] }) => 
      updateRolePermissions(role, permissionIds),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['rolePermissions'] });
      setHasChanges(false);
      toast.success('Role permissions updated successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  // Initialize local permissions when data loads
  useEffect(() => {
    if (data) {
      const initial: Record<string, string[]> = {};
      ROLES.forEach(role => {
        initial[role.key] = (data.rolePermissions[role.key] || []).map((p: Permission) => p.id);
      });
      setLocalPermissions(initial);
    }
  }, [data]);

  // Check for changes
  useEffect(() => {
    if (data && Object.keys(localPermissions).length > 0) {
      const hasAnyChanges = ROLES.some(role => {
        const current = (data.rolePermissions[role.key] || []).map((p: Permission) => p.id).sort();
        const local = (localPermissions[role.key] || []).sort();
        return JSON.stringify(current) !== JSON.stringify(local);
      });
      setHasChanges(hasAnyChanges);
    }
  }, [data, localPermissions]);

  const handlePermissionToggle = (role: string, permissionId: string, checked: boolean) => {
    setLocalPermissions(prev => {
      const current = prev[role] || [];
      const updated = checked 
        ? [...current, permissionId]
        : current.filter(id => id !== permissionId);
      
      return { ...prev, [role]: updated };
    });
  };

  const handleSaveRole = (role: string) => {
    const permissionIds = localPermissions[role] || [];
    updateMutation.mutate({ role, permissionIds });
  };

  const handleResetRole = (role: string) => {
    if (data) {
      setLocalPermissions(prev => ({
        ...prev,
        [role]: (data.rolePermissions[role] || []).map((p: Permission) => p.id)
      }));
    }
  };

  const getPermissionsByGroup = (permissions: Permission[]) => {
    const grouped: Record<string, Permission[]> = {};
    
    Object.entries(RESOURCE_GROUPS).forEach(([groupName, resources]) => {
      grouped[groupName] = permissions.filter(p => resources.includes(p.resource));
    });

    return grouped;
  };

  const getRoleIcon = (roleKey: string) => {
    const role = ROLES.find(r => r.key === roleKey);
    return role?.icon || User;
  };

  if (isLoading) {
    return <div className="p-6">Loading role permissions...</div>;
  }

  if (!data) {
    return <div className="p-6">Failed to load role permissions.</div>;
  }

  const currentRole = ROLES.find(r => r.key === selectedRole);
  const rolePermissionIds = localPermissions[selectedRole] || [];
  const groupedPermissions = getPermissionsByGroup(data.availablePermissions);

  return (
    <div className="p-6 space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Settings className="h-5 w-5" />
            Role-Based Permissions Management
          </CardTitle>
          <p className="text-sm text-gray-600">
            Configure default permissions for each role. These apply globally to all users with that role.
          </p>
        </CardHeader>
        <CardContent>
          <div className="flex flex-col lg:flex-row gap-6">
            {/* Role Selection Sidebar */}
            <div className="lg:w-64 space-y-2">
              <h3 className="font-medium text-sm text-gray-700 mb-3">Select Role</h3>
              {ROLES.map((role) => {
                const Icon = role.icon;
                const hasLocalChanges = data && localPermissions[role.key] && 
                  JSON.stringify((data.rolePermissions[role.key] || []).map((p: Permission) => p.id).sort()) !== 
                  JSON.stringify((localPermissions[role.key] || []).sort());
                
                return (
                  <div key={role.key} className="relative">
                    <Button
                      variant={selectedRole === role.key ? 'default' : 'outline'}
                      className="w-full justify-start gap-2 h-auto p-3"
                      onClick={() => setSelectedRole(role.key)}
                    >
                      <Icon className="h-4 w-4" />
                      <div className="text-left">
                        <div className="font-medium">{role.name}</div>
                        <div className="text-xs opacity-70 line-clamp-1">
                          {role.description}
                        </div>
                      </div>
                    </Button>
                    {hasLocalChanges && (
                      <div className="absolute -top-1 -right-1 w-3 h-3 bg-orange-500 rounded-full"></div>
                    )}
                  </div>
                );
              })}
            </div>

            {/* Permissions Configuration */}
            <div className="flex-1">
              {currentRole && (
                <div className="space-y-6">
                  {/* Role Header */}
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      {React.createElement(currentRole.icon, { className: "h-6 w-6" })}
                      <div>
                        <h2 className="text-xl font-semibold">{currentRole.name} Permissions</h2>
                        <p className="text-sm text-gray-600">{currentRole.description}</p>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleResetRole(selectedRole)}
                        disabled={!hasChanges}
                      >
                        <RotateCcw className="h-4 w-4 mr-1" />
                        Reset
                      </Button>
                      <Button
                        size="sm"
                        onClick={() => handleSaveRole(selectedRole)}
                        disabled={!hasChanges || updateMutation.isPending}
                      >
                        <Save className="h-4 w-4 mr-1" />
                        {updateMutation.isPending ? 'Saving...' : 'Save Changes'}
                      </Button>
                    </div>
                  </div>

                  {hasChanges && (
                    <Alert>
                      <Info className="h-4 w-4" />
                      <AlertDescription>
                        You have unsaved changes. Click "Save Changes" to apply them.
                      </AlertDescription>
                    </Alert>
                  )}

                  {/* Permission Groups */}
                  <div className="space-y-6">
                    {Object.entries(groupedPermissions).map(([groupName, permissions]) => (
                      <Card key={groupName}>
                        <CardHeader className="pb-3">
                          <CardTitle className="text-lg">{groupName}</CardTitle>
                        </CardHeader>
                        <CardContent>
                          <div className="grid gap-4">
                            {permissions.length === 0 ? (
                              <p className="text-sm text-gray-500 italic">No permissions in this category</p>
                            ) : (
                              Object.entries(
                                permissions
                                  .sort((a, b) => a.resource.localeCompare(b.resource) || a.action.localeCompare(b.action))
                                  .reduce((acc, permission) => {
                                    if (!acc[permission.resource]) {
                                      acc[permission.resource] = [];
                                    }
                                    acc[permission.resource].push(permission);
                                    return acc;
                                  }, {} as Record<string, Permission[]>)
                              ).map(([resource, resourcePermissions]) => (
                                <div key={resource}>
                                  <h4 className="font-medium text-sm text-gray-700 mb-2">{resource}</h4>
                                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2 ml-4">
                                    {resourcePermissions.map((permission) => (
                                      <div key={permission.id} className="flex items-center space-x-2">
                                        <Checkbox
                                          id={permission.id}
                                          checked={rolePermissionIds.includes(permission.id)}
                                          onCheckedChange={(checked) =>
                                            handlePermissionToggle(selectedRole, permission.id, !!checked)
                                          }
                                        />
                                        <label
                                          htmlFor={permission.id}
                                          className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 cursor-pointer"
                                        >
                                          <div className="flex items-center gap-2">
                                            <Badge variant="outline" className="text-xs">
                                              {permission.action}
                                            </Badge>
                                            <span className="text-xs text-gray-600">
                                              {ACTION_DESCRIPTIONS[permission.action as keyof typeof ACTION_DESCRIPTIONS] || permission.action}
                                            </span>
                                          </div>
                                        </label>
                                      </div>
                                    ))}
                                  </div>
                                  {resourcePermissions.length > 0 && <Separator className="mt-3" />}
                                </div>
                              ))
                            )}
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';
import { useState } from 'react';
import { format } from 'date-fns';
import { UserPlus, Edit, Trash2, Shield, User as UserIcon, KeyRound, Settings } from 'lucide-react';
import { toast } from 'sonner';
async function fetchTerritories(): Promise<{ id: string; name: string }[]> {
  const res = await fetch('/api/territories');
  if (!res.ok) return [];
  const data = await res.json();
  return data?.territories ?? [];
}

interface User {
  id: string;
  name: string | null;
  email: string;
  role: 'ADMIN' | 'DIRECTOR' | 'MANAGER' | 'TEAM_LEAD' | 'BDR';
  isActive: boolean;
  lastLoginAt: string | null;
  createdAt: string;
  updatedAt: string;
  territory?: { id: string; name: string } | null;
  _count: {
    pipelineItems: number;
    leads: number;
    activityLogs: number;
  };
}

interface CreateUserForm {
  name: string;
  email: string;
  password: string;
  role: 'ADMIN' | 'DIRECTOR' | 'MANAGER' | 'TEAM_LEAD' | 'BDR';
  territoryId?: string | null;
}

interface UpdateUserForm {
  name?: string;
  email?: string;
  password?: string;
  role?: 'ADMIN' | 'DIRECTOR' | 'MANAGER' | 'TEAM_LEAD' | 'BDR';
  territoryId?: string | null;
}

interface UserPermission {
  id: string;
  resource: string;
  action: string;
  expiresAt: string | null;
  createdAt: string;
}

interface AvailablePermission {
  id: string;
  resource: string;
  action: string;
}

async function fetchUsers(): Promise<User[]> {
  const res = await fetch('/api/admin/users');
  if (!res.ok) throw new Error('Failed to fetch users');
  return res.json();
}

async function createUser(data: CreateUserForm): Promise<User> {
  const res = await fetch('/api/admin/users', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data),
  });
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.error || 'Failed to create user');
  }
  return res.json();
}

async function updateUser(id: string, data: UpdateUserForm): Promise<User> {
  const res = await fetch(`/api/admin/users/${id}`, {
    method: 'PATCH',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data),
  });
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.error || 'Failed to update user');
  }
  return res.json();
}

async function deleteUser(id: string): Promise<void> {
  const res = await fetch(`/api/admin/users/${id}`, {
    method: 'DELETE',
  });
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.error || 'Failed to delete user');
  }
}

async function resetUserPassword(id: string, newPassword: string): Promise<void> {
  const res = await fetch(`/api/admin/users/${id}/reset-password`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ newPassword }),
  });
  if (!res.ok) {
    const error = await res.json();
    throw new Error(error.error || 'Failed to reset password');
  }
}

export default function UsersAdmin() {
  const queryClient = useQueryClient();
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [resetPasswordDialogOpen, setResetPasswordDialogOpen] = useState(false);
  const [permissionsDialogOpen, setPermissionsDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [newPassword, setNewPassword] = useState('');
  
  const [createForm, setCreateForm] = useState<CreateUserForm>({
    name: '',
    email: '',
    password: '',
    role: 'BDR',
    territoryId: null,
  });
  
  const [editForm, setEditForm] = useState<UpdateUserForm>({});
  const [selectedUserForPermissions, setSelectedUserForPermissions] = useState<User | null>(null);
  const [newPermissionResource, setNewPermissionResource] = useState('');
  const [newPermissionAction, setNewPermissionAction] = useState('');
  const [newPermissionExpiry, setNewPermissionExpiry] = useState('');

  const { data: userPermissions = [], refetch: refetchPermissions } = useQuery({
    queryKey: ['userPermissions', selectedUserForPermissions?.id],
    queryFn: async () => {
      if (!selectedUserForPermissions) return { userPermissions: [], availablePermissions: [] };
      const res = await fetch(`/api/admin/users/${selectedUserForPermissions.id}/permissions`);
      if (!res.ok) throw new Error('Failed to fetch permissions');
      return res.json();
    },
    enabled: !!selectedUserForPermissions,
  });

  const { data: users = [], isLoading } = useQuery({
    queryKey: ['users'],
    queryFn: fetchUsers,
  });
  const { data: territories = [] } = useQuery({
    queryKey: ['territories'],
    queryFn: fetchTerritories,
  });

  const createMutation = useMutation({
    mutationFn: createUser,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['users'] });
      setCreateDialogOpen(false);
      setCreateForm({ name: '', email: '', password: '', role: 'BDR' });
      toast.success('User created successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateUserForm }) => updateUser(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['users'] });
      setEditDialogOpen(false);
      setSelectedUser(null);
      setEditForm({});
      toast.success('User updated successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const deleteMutation = useMutation({
    mutationFn: deleteUser,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['users'] });
      toast.success('User deleted successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const resetPasswordMutation = useMutation({
    mutationFn: ({ id, newPassword }: { id: string; newPassword: string }) => resetUserPassword(id, newPassword),
    onSuccess: () => {
      setResetPasswordDialogOpen(false);
      setSelectedUser(null);
      setNewPassword('');
      toast.success('Password reset successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const addPermissionMutation = useMutation({
    mutationFn: async ({ userId, resource, action, expiresAt }: { userId: string; resource: string; action: string; expiresAt?: string }) => {
      const res = await fetch(`/api/admin/users/${userId}/permissions`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ resource, action, expiresAt }),
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to add permission');
      }
      return res.json();
    },
    onSuccess: () => {
      refetchPermissions();
      setNewPermissionResource('');
      setNewPermissionAction('');
      setNewPermissionExpiry('');
      toast.success('Permission added successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const removePermissionMutation = useMutation({
    mutationFn: async ({ userId, permissionId }: { userId: string; permissionId: string }) => {
      const res = await fetch(`/api/admin/users/${userId}/permissions?permissionId=${permissionId}`, {
        method: 'DELETE',
      });
      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to remove permission');
      }
      return res.json();
    },
    onSuccess: () => {
      refetchPermissions();
      toast.success('Permission removed successfully');
    },
    onError: (error: Error) => {
      toast.error(error.message);
    },
  });

  const handleCreate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!createForm.name || !createForm.email || !createForm.password) {
      toast.error('Please fill in all required fields');
      return;
    }
    createMutation.mutate(createForm);
  };

  const handleEdit = (user: User) => {
    setSelectedUser(user);
    setEditForm({
      name: user.name || '',
      email: user.email,
      role: user.role,
      territoryId: user.territory?.id ?? null,
    });
    setEditDialogOpen(true);
  };

  const handleUpdate = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedUser) return;
    
    // Only send fields that have changed
    const updates: UpdateUserForm = {};
    if (editForm.name && editForm.name !== selectedUser.name) updates.name = editForm.name;
    if (editForm.email && editForm.email !== selectedUser.email) updates.email = editForm.email;
    if (editForm.password) updates.password = editForm.password;
    if (editForm.role && editForm.role !== selectedUser.role) updates.role = editForm.role;
    if (editForm.territoryId !== selectedUser.territory?.id) updates.territoryId = editForm.territoryId ?? null;
    
    if (Object.keys(updates).length === 0) {
      toast.info('No changes to save');
      return;
    }
    
    updateMutation.mutate({ id: selectedUser.id, data: updates });
  };

  const handleDelete = (user: User) => {
    const hasData = user._count.pipelineItems + user._count.leads + user._count.activityLogs > 0;
    
    if (hasData) {
      toast.error(`Cannot delete ${user.name}. They have associated data that must be reassigned first.`);
      return;
    }
    
    if (confirm(`Are you sure you want to delete ${user.name}? This action cannot be undone.`)) {
      deleteMutation.mutate(user.id);
    }
  };

  const handleResetPassword = (user: User) => {
    setSelectedUser(user);
    setNewPassword('');
    setResetPasswordDialogOpen(true);
  };

  const handlePasswordReset = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedUser || !newPassword) {
      toast.error('Please enter a new password');
      return;
    }
    
    if (newPassword.length < 6) {
      toast.error('Password must be at least 6 characters');
      return;
    }
    
    resetPasswordMutation.mutate({ id: selectedUser.id, newPassword });
  };

  const handleManagePermissions = (user: User) => {
    setSelectedUserForPermissions(user);
    setPermissionsDialogOpen(true);
  };

  const handleAddPermission = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedUserForPermissions || !newPermissionResource || !newPermissionAction) {
      toast.error('Please select resource and action');
      return;
    }
    addPermissionMutation.mutate({
      userId: selectedUserForPermissions.id,
      resource: newPermissionResource,
      action: newPermissionAction,
      expiresAt: newPermissionExpiry || undefined,
    });
  };

  const handleRemovePermission = (permissionId: string) => {
    if (!selectedUserForPermissions) return;
    if (confirm('Are you sure you want to remove this permission?')) {
      removePermissionMutation.mutate({
        userId: selectedUserForPermissions.id,
        permissionId,
      });
    }
  };

  if (isLoading) {
    return <div className="p-6">Loading users...</div>;
  }

  return (
    <div className="p-6 space-y-6">
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <UserIcon className="h-5 w-5" />
            User Management
          </CardTitle>
          <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <UserPlus className="h-4 w-4 mr-2" />
                Add User
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create New User</DialogTitle>
              </DialogHeader>
              <form onSubmit={handleCreate} className="space-y-4">
                <div>
                  <Label htmlFor="create-name">Name</Label>
                  <Input
                    id="create-name"
                    value={createForm.name}
                    onChange={(e) => setCreateForm({ ...createForm, name: e.target.value })}
                    placeholder="Full name"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="create-email">Email</Label>
                  <Input
                    id="create-email"
                    type="email"
                    value={createForm.email}
                    onChange={(e) => setCreateForm({ ...createForm, email: e.target.value })}
                    placeholder="user@emg.com"
                    required
                  />
                </div>
                <div>
                  <Label htmlFor="create-password">Password</Label>
                  <Input
                    id="create-password"
                    type="password"
                    value={createForm.password}
                    onChange={(e) => setCreateForm({ ...createForm, password: e.target.value })}
                    placeholder="Minimum 6 characters"
                    required
                  />
                </div>
                 <div>
                  <Label htmlFor="create-role">Role</Label>
                  <Select
                    value={createForm.role}
                     onValueChange={(value: any) => setCreateForm({ ...createForm, role: value })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="BDR">BDR</SelectItem>
                       <SelectItem value="TEAM_LEAD">Team Lead</SelectItem>
                       <SelectItem value="MANAGER">Manager</SelectItem>
                       <SelectItem value="DIRECTOR">Director</SelectItem>
                      <SelectItem value="ADMIN">Admin</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                 <div>
                   <Label htmlFor="create-territory">Territory (optional)</Label>
                   <Select
                     value={createForm.territoryId || 'none'}
                     onValueChange={(value) => setCreateForm({ ...createForm, territoryId: value === 'none' ? null : value })}
                   >
                     <SelectTrigger>
                       <SelectValue placeholder="Select a territory" />
                     </SelectTrigger>
                     <SelectContent>
                       <SelectItem value="none">None</SelectItem>
                       {territories.map(t => (
                         <SelectItem key={t.id} value={t.id}>{t.name}</SelectItem>
                       ))}
                     </SelectContent>
                   </Select>
                 </div>
                <Button type="submit" disabled={createMutation.isPending} className="w-full">
                  {createMutation.isPending ? 'Creating...' : 'Create User'}
                </Button>
              </form>
            </DialogContent>
          </Dialog>
        </CardHeader>
        <CardContent>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Name</TableHead>
                <TableHead>Email</TableHead>
                <TableHead>Role</TableHead>
                <TableHead>Status</TableHead>
                <TableHead>Last Login</TableHead>
                <TableHead>Data Count</TableHead>
                <TableHead>Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {users.map((user) => (
                <TableRow key={user.id}>
                  <TableCell className="font-medium">{user.name || 'No name'}</TableCell>
                  <TableCell>{user.email}</TableCell>
                  <TableCell>
                    <Badge variant={user.role === 'ADMIN' ? 'default' : 'secondary'} className="gap-1">
                      {user.role === 'ADMIN' ? <Shield className="h-3 w-3" /> : <UserIcon className="h-3 w-3" />}
                      {user.role}
                    </Badge>
                  </TableCell>
                  <TableCell>
                    <Badge variant={user.isActive ? 'default' : 'destructive'}>
                      {user.isActive ? 'Active' : 'Inactive'}
                    </Badge>
                  </TableCell>
                  <TableCell className="text-sm text-gray-600">
                    {user.lastLoginAt 
                      ? format(new Date(user.lastLoginAt), 'MMM dd, yyyy HH:mm')
                      : 'Never logged in'
                    }
                  </TableCell>
                  <TableCell>
                    <div className="text-sm text-gray-600">
                      {user._count.pipelineItems} pipeline, {user._count.leads} leads, {user._count.activityLogs} logs
                    </div>
                  </TableCell>
                  <TableCell>
                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleEdit(user)}
                      >
                        <Edit className="h-3 w-3" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleResetPassword(user)}
                      >
                        <KeyRound className="h-3 w-3" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleManagePermissions(user)}
                      >
                        <Settings className="h-3 w-3" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleDelete(user)}
                        disabled={user._count.pipelineItems + user._count.leads + user._count.activityLogs > 0}
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </CardContent>
      </Card>

      {/* Edit User Dialog */}
      <Dialog open={editDialogOpen} onOpenChange={setEditDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Edit User: {selectedUser?.name}</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleUpdate} className="space-y-4">
            <div>
              <Label htmlFor="edit-name">Name</Label>
              <Input
                id="edit-name"
                value={editForm.name || ''}
                onChange={(e) => setEditForm({ ...editForm, name: e.target.value })}
                placeholder="Full name"
              />
            </div>
            <div>
              <Label htmlFor="edit-email">Email</Label>
              <Input
                id="edit-email"
                type="email"
                value={editForm.email || ''}
                onChange={(e) => setEditForm({ ...editForm, email: e.target.value })}
                placeholder="user@emg.com"
              />
            </div>
            <div>
              <Label htmlFor="edit-password">New Password (leave blank to keep current)</Label>
              <Input
                id="edit-password"
                type="password"
                value={editForm.password || ''}
                onChange={(e) => setEditForm({ ...editForm, password: e.target.value })}
                placeholder="New password"
              />
            </div>
              <div>
              <Label htmlFor="edit-role">Role</Label>
              <Select
                value={editForm.role || 'BDR'}
                onValueChange={(value: any) => setEditForm({ ...editForm, role: value })}
              >
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="BDR">BDR</SelectItem>
                    <SelectItem value="TEAM_LEAD">Team Lead</SelectItem>
                    <SelectItem value="MANAGER">Manager</SelectItem>
                    <SelectItem value="DIRECTOR">Director</SelectItem>
                  <SelectItem value="ADMIN">Admin</SelectItem>
                </SelectContent>
              </Select>
            </div>
              <div>
                <Label htmlFor="edit-territory">Territory (optional)</Label>
                <Select
                  value={editForm.territoryId || 'none'}
                  onValueChange={(value) => setEditForm({ ...editForm, territoryId: value === 'none' ? null : value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select a territory" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="none">None</SelectItem>
                    {territories.map(t => (
                      <SelectItem key={t.id} value={t.id}>{t.name}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            <Button type="submit" disabled={updateMutation.isPending} className="w-full">
              {updateMutation.isPending ? 'Updating...' : 'Update User'}
            </Button>
          </form>
        </DialogContent>
      </Dialog>

      {/* Reset Password Dialog */}
      <Dialog open={resetPasswordDialogOpen} onOpenChange={setResetPasswordDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reset Password: {selectedUser?.name}</DialogTitle>
          </DialogHeader>
          <form onSubmit={handlePasswordReset} className="space-y-4">
            <div>
              <Label htmlFor="reset-password">New Password</Label>
              <Input
                id="reset-password"
                type="password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                placeholder="Enter new password (min 6 characters)"
                required
              />
              <p className="text-sm text-muted-foreground mt-1">
                The user will need to use this new password to log in.
              </p>
            </div>
            <Button type="submit" disabled={resetPasswordMutation.isPending} className="w-full">
              <KeyRound className="h-4 w-4 mr-2" />
              {resetPasswordMutation.isPending ? 'Resetting...' : 'Reset Password'}
            </Button>
          </form>
        </DialogContent>
      </Dialog>

      {/* Permissions Management Dialog */}
      <Dialog open={permissionsDialogOpen} onOpenChange={setPermissionsDialogOpen}>
        <DialogContent className="max-w-4xl">
          <DialogHeader>
