                        <SelectItem value="DUPLICATES">Duplicates</SelectItem>
                        <SelectItem value="MESSAGING">Messaging</SelectItem>
                        <SelectItem value="TEMPLATES">Templates</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="permission-action">Action</Label>
                    <Select value={newPermissionAction} onValueChange={setNewPermissionAction}>
                      <SelectTrigger>
                        <SelectValue placeholder="Select action" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="CREATE">Create</SelectItem>
                        <SelectItem value="READ">Read</SelectItem>
                        <SelectItem value="UPDATE">Update</SelectItem>
                        <SelectItem value="DELETE">Delete</SelectItem>
                        <SelectItem value="VIEW_ALL">View All</SelectItem>
                        <SelectItem value="VIEW_TEAM">View Team</SelectItem>
                        <SelectItem value="EXPORT">Export</SelectItem>
                        <SelectItem value="MANAGE">Manage</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                  <div>
                    <Label htmlFor="permission-expiry">Expiry Date (optional)</Label>
                    <Input
                      id="permission-expiry"
                      type="date"
                      value={newPermissionExpiry}
                      onChange={(e) => setNewPermissionExpiry(e.target.value)}
                    />
                  </div>
                </div>
                <Button type="submit" disabled={addPermissionMutation.isPending}>
                  {addPermissionMutation.isPending ? 'Adding...' : 'Add Permission Override'}
                </Button>
              </form>
            </div>

            {/* Role-based Permissions Info */}
            <div className="p-4 bg-gray-50 rounded">
              <h4 className="font-medium text-sm mb-2">Role-based Permissions ({selectedUserForPermissions?.role})</h4>
              <p className="text-sm text-gray-600">
                {selectedUserForPermissions?.role === 'BDR' && 'BDRs can manage their own leads/pipeline, read finance data, and access messaging/templates.'}
                {selectedUserForPermissions?.role === 'TEAM_LEAD' && 'Team Leads can manage team data, read finance, and access messaging/templates.'}
                {selectedUserForPermissions?.role === 'MANAGER' && 'Managers can manage territory data, view team finance, and access most features.'}
                {selectedUserForPermissions?.role === 'DIRECTOR' && 'Directors can view/manage most data across all territories.'}
                {selectedUserForPermissions?.role === 'ADMIN' && 'Admins have full access to all features and data.'}
              </p>
              <p className="text-xs text-gray-500 mt-2">
                Permission overrides add to (not replace) role-based permissions.
              </p>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import { NextRequest, NextResponse } from "next/server";
import { getServerSession } from "next-auth/next";
import { authOptions } from "@/lib/auth";
import { prisma } from "@/lib/db";
import { createActivityLogSchema } from "@/lib/validations";
import { Role } from "@prisma/client";

export async function GET(req: NextRequest) {
  try {
    // 1. Get Session securely on the server
    const session = await getServerSession(authOptions);

    // 2. Check Authentication
    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { role, id: userId } = session.user;

    const { searchParams } = new URL(req.url);
    const bdr = searchParams.get('bdr') || undefined;
    const activityType = searchParams.get('activityType') || undefined;
    const fromDate = searchParams.get('fromDate') ? new Date(searchParams.get('fromDate')!) : undefined;
    const toDate = searchParams.get('toDate') ? new Date(searchParams.get('toDate')!) : undefined;
    const leadId = searchParams.get('leadId') ? Number(searchParams.get('leadId')) : undefined;
    const pipelineItemId = searchParams.get('pipelineItemId') ? Number(searchParams.get('pipelineItemId')) : undefined;
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = parseInt(searchParams.get('pageSize') || '20');

    const skip = (page - 1) * pageSize;

    // 3. Enforce Authorization (RBAC) - Build where clause with role-based filtering
    const where: Record<string, unknown> = {};

    // Role-based data filtering
    if (role === Role.BDR) {
      // BDRs can only see their own activity logs
      where.bdrId = userId;
    } else if (role === Role.ADMIN) {
      // Admins can see all activity logs - no additional filtering
    } else {
      // Unknown role - deny access
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }

    // Note: For BDRs, we ignore the 'bdr' filter param since they can only see their own data
    // For Admins, we can still apply the bdr filter if provided
    if (bdr && role === Role.ADMIN) {
      where.bdr = { name: bdr };
    }
    if (activityType) where.activityType = activityType;
    if (leadId) where.leadId = leadId;
    if (pipelineItemId) where.pipelineItemId = pipelineItemId;

    // Date range query
    if (fromDate || toDate) {
      where.timestamp = {};
      if (fromDate) where.timestamp.gte = fromDate;
      if (toDate) where.timestamp.lte = toDate;
    }

    // Get total count for pagination
    const total = await prisma.activityLog.count({ where });

    // Get activity logs
    const logs = await prisma.activityLog.findMany({
      where,
      skip,
      take: pageSize,
      orderBy: { timestamp: 'desc' },
      include: {
        bdr: {
          select: {
            id: true,
            name: true,
            email: true,
          }
        },
        lead: {
          select: {
            id: true,
            name: true,
            company: true
          }
        },
        pipelineItem: {
          select: {
            id: true,
            name: true,
            company: true,
            category: true,
            status: true
          }
        }
      }
    });

    // Normalize response: return bdr as a string (name) to match frontend expectations
    const normalizedLogs = logs.map((log: any) => ({
      ...log,
      bdr: log.bdr?.name || '',
    }));

    // Calculate total pages
    const totalPages = Math.ceil(total / pageSize);

    return NextResponse.json({
      logs: normalizedLogs,
      total,
      page,
      pageSize,
      totalPages,
    });
  } catch (error) {
    console.error("Error fetching activity logs:", error);
    const errorMessage = error instanceof Error ? error.message : "Failed to fetch activity logs";
    return NextResponse.json(
      { error: errorMessage },
      { status: 500 }
    );
  }
}

export async function POST(req: NextRequest) {
  try {
    // 1. Get Session securely on the server
    const session = await getServerSession(authOptions);

    // 2. Check Authentication
    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { role, id: userId } = session.user;
    const data = await req.json();

    // Validate the request body
    const validatedData = createActivityLogSchema.parse(data);

    // 3. Enforce Authorization (RBAC) for activity log creation
    let activityData = { ...validatedData };

    if (role === Role.BDR) {
      // BDRs can only create activity logs assigned to themselves
      activityData.bdrId = userId;
    } else if (role === Role.ADMIN) {
      // Admins can assign activity logs to any user
      // Use the provided bdrId or assign to themselves if not provided
      if (!activityData.bdrId) {
        activityData.bdrId = userId;
      }
    } else {
      // Unknown role - deny access
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }

    // Create the activity log and update the pipeline item's lastUpdated
    const [activityLog] = await prisma.$transaction([
      prisma.activityLog.create({
        data: {
          bdrId: activityData.bdrId,
          activityType: activityData.activityType,
          description: activityData.description,
          scheduledDate: activityData.scheduledDate,
          completedDate: activityData.completedDate,
          notes: activityData.notes,
          leadId: activityData.leadId,
          pipelineItemId: activityData.pipelineItemId,
          previousStatus: activityData.previousStatus,
          newStatus: activityData.newStatus,
          previousCategory: activityData.previousCategory,
          newCategory: activityData.newCategory,
        },
        include: {
          bdr: {
            select: {
              id: true,
              name: true,
              email: true,
            }
          }
        }
      }),
      // Update lastUpdated on the pipeline item if it exists
      ...(activityData.pipelineItemId ? [
        prisma.pipelineItem.update({
          where: { id: activityData.pipelineItemId },
          data: { lastUpdated: new Date() },
        })
      ] : [])
    ]);

    return NextResponse.json(activityLog, { status: 201 });
  } catch (error) {
    console.error("Error creating activity log:", error);
    const errorMessage = error instanceof Error ? error.message : "Failed to create activity log";
    return NextResponse.json(
      { error: errorMessage },
      { status: 400 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth-options';
import { duplicateDetectionService } from '@/lib/duplicate-detection';
import { hasPermission } from '@/lib/permissions';
import { PERMISSIONS } from '@/lib/permissions';
import { z } from 'zod';

// Request schema for statistics
const statisticsSchema = z.object({
  dateFrom: z.string().datetime().optional(),
  dateTo: z.string().datetime().optional(),
});

// GET endpoint for duplicate statistics and management data
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions);
    
    if (!session?.user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Check admin permissions
    if (!hasPermission(session.user as any, PERMISSIONS.DUPLICATES.MANAGE.resource, PERMISSIONS.DUPLICATES.MANAGE.action) &&
        !hasPermission(session.user as any, PERMISSIONS.DUPLICATES.VIEW_ALL.resource, PERMISSIONS.DUPLICATES.VIEW_ALL.action)) {
      return NextResponse.json({ error: 'Admin access required' }, { status: 403 });
    }

    const { searchParams } = new URL(request.url);
    const action = searchParams.get('action') || 'statistics';
    
    switch (action) {
      case 'statistics': {
        const dateFrom = searchParams.get('dateFrom');
        const dateTo = searchParams.get('dateTo');
        
        const dateRange = (dateFrom && dateTo) ? {
          from: new Date(dateFrom),
          to: new Date(dateTo),
        } : undefined;
        
        const statistics = await duplicateDetectionService.getDuplicateStatistics(dateRange);
        
        return NextResponse.json(statistics);
      }
      
      case 'recent-warnings': {
        const limit = parseInt(searchParams.get('limit') || '50');
        const includeResolved = searchParams.get('includeResolved') === 'true';
        
        const warnings = await duplicateDetectionService.getRecentWarnings(limit, includeResolved);
        
        return NextResponse.json(warnings);
      }
      
      default:
        return NextResponse.json({ error: 'Invalid action' }, { status: 400 });
    }
    
  } catch (error) {
    console.error('Error in admin duplicates endpoint:', error);
    
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: 'Invalid parameters', details: error.errors },
        { status: 400 }
      );
    }
    
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity } from '@/lib/security';
import { Resource, Action, Role } from '@prisma/client';

export async function GET(request: NextRequest) {
  return withSecurity(Resource.SETTINGS, Action.MANAGE, async (context) => {
    // Get all current role permissions
    const rolePermissions = await prisma.rolePermission.findMany({
      include: {
        permission: true
      }
    });

    // Get all available permissions
    const allPermissions = await prisma.permission.findMany({
      orderBy: [
        { resource: 'asc' },
        { action: 'asc' }
      ]
    });

    // Group permissions by role
    const permissionsByRole = rolePermissions.reduce((acc, rp) => {
      const role = rp.role;
      if (!acc[role]) {
        acc[role] = [];
      }
      acc[role].push({
        id: rp.permission.id,
        name: rp.permission.name,
        resource: rp.permission.resource,
        action: rp.permission.action,
        description: rp.permission.description
      });
      return acc;
    }, {} as Record<Role, any[]>);

    return NextResponse.json({
      rolePermissions: permissionsByRole,
      availablePermissions: allPermissions.map(p => ({
        id: p.id,
        name: p.name,
        resource: p.resource,
        action: p.action,
        description: p.description
      }))
    });
  }, request);
}

export async function POST(request: NextRequest) {
  return withSecurity(Resource.SETTINGS, Action.MANAGE, async (context) => {
    const body = await request.json();
    const { role, permissionId } = body;

    if (!role || !permissionId) {
      return NextResponse.json(
        { error: 'Role and permission ID are required' },
        { status: 400 }
      );
    }

    // Check if permission exists
    const permission = await prisma.permission.findUnique({
      where: { id: permissionId }
    });

    if (!permission) {
      return NextResponse.json(
        { error: 'Permission not found' },
        { status: 404 }
      );
    }

    // Check if role permission already exists
    const existingRolePermission = await prisma.rolePermission.findFirst({
      where: {
        role: role as Role,
        permissionId
      }
    });

    if (existingRolePermission) {
      return NextResponse.json(
        { error: 'Role already has this permission' },
        { status: 400 }
      );
    }

    // Create the role permission
    const rolePermission = await prisma.rolePermission.create({
      data: {
        role: role as Role,
        permissionId
      },
      include: {
        permission: true
      }
    });

    return NextResponse.json({
      id: rolePermission.permission.id,
      name: rolePermission.permission.name,
      resource: rolePermission.permission.resource,
      action: rolePermission.permission.action,
      description: rolePermission.permission.description
    });
  }, request);
}

export async function DELETE(request: NextRequest) {
  return withSecurity(Resource.SETTINGS, Action.MANAGE, async (context) => {
    const { searchParams } = new URL(request.url);
    const role = searchParams.get('role');
    const permissionId = searchParams.get('permissionId');

    if (!role || !permissionId) {
      return NextResponse.json(
        { error: 'Role and permission ID are required' },
        { status: 400 }
      );
    }

    // Delete the role permission
    await prisma.rolePermission.deleteMany({
      where: {
        role: role as Role,
        permissionId
      }
    });

    return NextResponse.json({ success: true });
  }, request);
}

// Bulk update endpoint for better UX
export async function PATCH(request: NextRequest) {
  return withSecurity(Resource.SETTINGS, Action.MANAGE, async (context) => {
    const body = await request.json();
    const { role, permissionIds } = body;

    if (!role || !Array.isArray(permissionIds)) {
      return NextResponse.json(
        { error: 'Role and permission IDs array are required' },
        { status: 400 }
      );
    }

    // Use transaction to ensure atomicity
    await prisma.$transaction(async (tx) => {
      // Remove all existing permissions for this role
      await tx.rolePermission.deleteMany({
        where: { role: role as Role }
      });

      // Add new permissions
      if (permissionIds.length > 0) {
        await tx.rolePermission.createMany({
          data: permissionIds.map((permissionId: string) => ({
            role: role as Role,
            permissionId
          }))
        });
      }
    });

    return NextResponse.json({ success: true });
  }, request);
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity } from '@/lib/security';
import { Resource, Action } from '@prisma/client';

interface RouteParams {
  params: { id: string };
}

export async function GET(request: NextRequest, { params }: RouteParams) {
  return withSecurity(Resource.USERS, Action.READ, async (context) => {
    const resolvedParams = await params;
    const userId = resolvedParams.id;
    
    // Get user's explicit permissions
    const userPermissions = await prisma.userPermission.findMany({
      where: {
        userId,
        OR: [
          { expiresAt: null },
          { expiresAt: { gt: new Date() } }
        ]
      },
      include: {
        permission: true
      }
    });

    // Get all available permissions
    const allPermissions = await prisma.permission.findMany({
      orderBy: [
        { resource: 'asc' },
        { action: 'asc' }
      ]
    });

    return NextResponse.json({
      userPermissions: userPermissions.map(up => ({
        id: up.id,
        resource: up.permission.resource,
        action: up.permission.action,
        expiresAt: up.expiresAt,
        createdAt: up.createdAt
      })),
      availablePermissions: allPermissions
    });
  }, request);
}

export async function POST(request: NextRequest, { params }: RouteParams) {
  return withSecurity(Resource.USERS, Action.MANAGE, async (context) => {
    const resolvedParams = await params;
    const userId = resolvedParams.id;
    const body = await request.json();
    const { resource, action, expiresAt } = body;

    // Find the permission
    const permission = await prisma.permission.findFirst({
      where: { resource, action }
    });

    if (!permission) {
      return NextResponse.json(
        { error: 'Permission not found' },
        { status: 400 }
      );
    }

    // Check if permission already exists
    const existingPermission = await prisma.userPermission.findFirst({
      where: {
        userId,
        permissionId: permission.id,
        OR: [
          { expiresAt: null },
          { expiresAt: { gt: new Date() } }
        ]
      }
    });

    if (existingPermission) {
      return NextResponse.json(
        { error: 'User already has this permission' },
        { status: 400 }
      );
    }

    // Create the permission override
    const userPermission = await prisma.userPermission.create({
      data: {
        userId,
        permissionId: permission.id,
        expiresAt: expiresAt ? new Date(expiresAt) : null,
        grantedBy: context.userId
      },
      include: {
        permission: true
      }
    });

    return NextResponse.json({
      id: userPermission.id,
      resource: userPermission.permission.resource,
      action: userPermission.permission.action,
      expiresAt: userPermission.expiresAt,
      createdAt: userPermission.createdAt
    });
  }, request);
}

export async function DELETE(request: NextRequest, { params }: RouteParams) {
  return withSecurity(Resource.USERS, Action.MANAGE, async (context) => {
    const resolvedParams = await params;
    const userId = resolvedParams.id;
    const { searchParams } = new URL(request.url);
    const permissionId = searchParams.get('permissionId');

    if (!permissionId) {
      return NextResponse.json(
        { error: 'Permission ID is required' },
        { status: 400 }
      );
    }

    // Delete the user permission
    await prisma.userPermission.deleteMany({
      where: {
        userId,
        id: permissionId
      }
    });

    return NextResponse.json({ success: true });
  }, request);
}

import { NextRequest, NextResponse } from "next/server";
import { getServerSession } from "next-auth/next";
import { authOptions } from "@/lib/auth";
import { prisma } from "@/lib/db";
import { hashPassword } from "@/lib/auth-utils";
import { Role } from "@prisma/client";
import { z } from "zod";

const updateUserSchema = z.object({
  name: z.string().min(1, "Name is required").optional(),
  email: z.string().email("Invalid email address").optional(),
  password: z.string().min(6, "Password must be at least 6 characters").optional(),
  role: z.enum(["ADMIN", "DIRECTOR", "MANAGER", "TEAM_LEAD", "BDR"]).optional(),
  isActive: z.boolean().optional(),
  territoryId: z.string().nullable().optional(),
});

export async function GET(req: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const session = await getServerSession(authOptions);

    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { id } = await params;

    // Users can only view their own profile unless they're admin
    if (session.user.role !== "ADMIN" && session.user.id !== id) {
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }

    const user = await prisma.user.findUnique({
      where: { id },
      select: {
        id: true,
        name: true,
        email: true,
        role: true,
        isActive: true,
        lastLoginAt: true,
        createdAt: true,
        updatedAt: true,
        _count: {
          select: {
            pipelineItems: true,
            leads: true,
            activityLogs: true,
          }
        }
      },
    });

    if (!user) {
      return NextResponse.json({ error: "User not found" }, { status: 404 });
    }

    return NextResponse.json(user);
  } catch (error) {
    console.error("Error fetching user:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function PATCH(req: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const session = await getServerSession(authOptions);

    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { id } = await params;

    // Users can only edit their own profile unless they're admin
    // Admins can edit anyone, but regular users can only edit themselves
    if (session.user.role !== "ADMIN" && session.user.id !== id) {
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }

    const body = await req.json();
    const validatedData = updateUserSchema.parse(body);

    // Check if user exists
    const existingUser = await prisma.user.findUnique({
      where: { id },
    });

    if (!existingUser) {
      return NextResponse.json({ error: "User not found" }, { status: 404 });
    }

    // If email is being updated, check for conflicts
    if (validatedData.email && validatedData.email !== existingUser.email) {
      const emailConflict = await prisma.user.findUnique({
        where: { email: validatedData.email },
      });

      if (emailConflict) {
        return NextResponse.json({ error: "Email already exists" }, { status: 400 });
      }
    }

    // If non-admin tries to change role, prevent it
    if (validatedData.role && session.user.role !== "ADMIN") {
      return NextResponse.json({ error: "Only admins can change user roles" }, { status: 403 });
    }

    // Prepare update data
    const updateData: any = {};
    if (validatedData.name !== undefined) updateData.name = validatedData.name;
    if (validatedData.email !== undefined) updateData.email = validatedData.email;
    if (validatedData.role !== undefined) updateData.role = validatedData.role as Role;
    if (validatedData.isActive !== undefined) updateData.isActive = validatedData.isActive;
    if (validatedData.territoryId !== undefined) updateData.territoryId = validatedData.territoryId;
    
    // Hash password if provided
    if (validatedData.password) {
      updateData.hashedPassword = await hashPassword(validatedData.password);
    }

    const updatedUser = await prisma.user.update({
      where: { id },
      data: updateData,
      select: {
        id: true,
        name: true,
        email: true,
        role: true,
        createdAt: true,
        updatedAt: true,
      },
    });

    return NextResponse.json(updatedUser);
  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: error.errors }, { status: 400 });
    }
    
    console.error("Error updating user:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

export async function DELETE(req: NextRequest, { params }: { params: Promise<{ id: string }> }) {
  try {
    const session = await getServerSession(authOptions);

    if (!session || !session.user || session.user.role !== "ADMIN") {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { id } = await params;

    // Prevent admin from deleting themselves
    if (session.user.id === id) {
      return NextResponse.json({ error: "Cannot delete your own account" }, { status: 400 });
    }

    // Check if user exists
    const user = await prisma.user.findUnique({
      where: { id },
      include: {
        _count: {
          select: {
            pipelineItems: true,
            leads: true,
            activityLogs: true,
          }
        }
      }
    });

    if (!user) {
      return NextResponse.json({ error: "User not found" }, { status: 404 });
    }

    // Check if user has associated data
    if (user._count.pipelineItems > 0 || user._count.leads > 0 || user._count.activityLogs > 0) {
      return NextResponse.json({ 
        error: "Cannot delete user with associated data. Please reassign their data first.",
        details: {
          pipelineItems: user._count.pipelineItems,
          leads: user._count.leads,
          activityLogs: user._count.activityLogs,
        }
      }, { status: 400 });
    }

    await prisma.user.delete({
      where: { id },
    });

    return NextResponse.json({ message: "User deleted successfully" });
  } catch (error) {
    console.error("Error deleting user:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from "next/server";
import { getServerSession } from "next-auth/next";
