export async function POST(req: NextRequest) {
  try {
    const session = await getServerSession(authOptions);

    if (!session || !session.user || session.user.role !== "ADMIN") {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const body = await req.json();
    const validatedData = createUserSchema.parse(body);

    // Check if email already exists
    const existingUser = await prisma.user.findUnique({
      where: { email: validatedData.email },
    });

    if (existingUser) {
      return NextResponse.json({ error: "Email already exists" }, { status: 400 });
    }

    // Hash the password
    const hashedPassword = await hashPassword(validatedData.password);

    // Create the user
    const user = await prisma.user.create({
      data: {
        name: validatedData.name,
        email: validatedData.email,
        hashedPassword,
        role: validatedData.role as Role,
        territoryId: validatedData.territoryId ?? null,
      },
      select: {
        id: true,
        name: true,
        email: true,
        role: true,
        createdAt: true,
        updatedAt: true,
      },
    });

    return NextResponse.json(user, { status: 201 });
  } catch (error) {
    if (error instanceof z.ZodError) {
      return NextResponse.json({ error: error.errors }, { status: 400 });
    }
    
    console.error("Error creating user:", error);
    return NextResponse.json({ error: "Internal server error" }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { SecurityService, withSecurity } from '@/lib/security';
import { prisma } from '@/lib/db';
import { Resource, Action } from '@prisma/client';

export async function GET(req: NextRequest) {
  return withSecurity(Resource.ACTIVITY_LOGS, Action.READ, async (context) => {
    const { searchParams } = new URL(req.url);
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = Math.min(parseInt(searchParams.get('pageSize') || '50'), 100); // Limit max page size
    const skip = (page - 1) * pageSize;
    
    const userId = searchParams.get('userId');
    const action = searchParams.get('action');
    const resource = searchParams.get('resource');
    const success = searchParams.get('success');
    const startDate = searchParams.get('startDate');
    const endDate = searchParams.get('endDate');

    // Build where clause based on user permissions and filters
    let where: any = {};

    // Apply row-level security for audit logs
    if (context.role === 'BDR' || context.role === 'TEAM_LEAD') {
      // BDRs and team leads can only see their own audit logs
      where.userId = context.userId;
    } else if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      // Managers can see logs from users in territories they manage
      where.user = {
        territoryId: {
          in: context.managedTerritoryIds
        }
      };
    }
    // Directors and Admins can see all logs (no additional restrictions)

    // Apply filters
    if (userId && (context.role === 'ADMIN' || context.role === 'DIRECTOR' || 
        (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0))) {
      where.userId = userId;
    }
    
    if (action) where.action = action;
    if (resource) where.resource = resource;
    if (success !== null && success !== undefined) {
      where.success = success === 'true';
    }
    
    if (startDate || endDate) {
      where.timestamp = {};
      if (startDate) where.timestamp.gte = new Date(startDate);
      if (endDate) where.timestamp.lte = new Date(endDate);
    }

    // Get total count
    const total = await prisma.auditLog.count({ where });

    // Get audit logs
    const auditLogs = await prisma.auditLog.findMany({
      where,
      skip,
      take: pageSize,
      orderBy: {
        timestamp: 'desc'
      },
      include: {
        user: {
          select: {
            id: true,
            name: true,
            email: true,
            role: true,
            territory: {
              select: {
                name: true
              }
            }
          }
        }
      }
    });

    // Sanitize sensitive data based on user role
    const sanitizedLogs = auditLogs.map(log => {
      const sanitized: any = {
        id: log.id,
        action: log.action,
        resource: log.resource,
        resourceId: log.resourceId,
        success: log.success,
        timestamp: log.timestamp,
        user: log.user,
        ipAddress: log.ipAddress
      };

      // Only include sensitive details for admins and directors
      if (context.role === 'ADMIN' || context.role === 'DIRECTOR') {
        sanitized.details = log.details;
        sanitized.errorMsg = log.errorMsg;
        sanitized.userAgent = log.userAgent;
        sanitized.sessionId = log.sessionId;
      } else {
        // Provide limited details for other roles
        sanitized.details = log.details ? { action: log.details.action || 'N/A' } : null;
        if (log.errorMsg && log.success === false) {
          sanitized.errorMsg = 'Error occurred'; // Generic error message
        }
      }

      return sanitized;
    });

    const totalPages = Math.ceil(total / pageSize);

    return NextResponse.json({
      auditLogs: sanitizedLogs,
      total,
      page,
      pageSize,
      totalPages,
    });
  }, req);
}

// Get audit log statistics
export async function POST(req: NextRequest) {
  return withSecurity(Resource.ACTIVITY_LOGS, Action.READ, async (context) => {
    const body = await req.json();
    const { action: requestAction, startDate, endDate } = body;

    if (requestAction !== 'stats') {
      return NextResponse.json({ error: 'Invalid action' }, { status: 400 });
    }

    // Build base where clause for time range
    let where: any = {};
    if (startDate || endDate) {
      where.timestamp = {};
      if (startDate) where.timestamp.gte = new Date(startDate);
      if (endDate) where.timestamp.lte = new Date(endDate);
    }

    // Apply row-level security
    if (context.role === 'BDR' || context.role === 'TEAM_LEAD') {
      where.userId = context.userId;
    } else if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      where.user = {
        territoryId: {
          in: context.managedTerritoryIds
        }
      };
    }

    // Get statistics
    const [
      totalActions,
      successfulActions,
      failedActions,
      actionsByType,
      actionsByResource,
      topUsers
    ] = await Promise.all([
      // Total actions
      prisma.auditLog.count({ where }),
      
      // Successful actions
      prisma.auditLog.count({ where: { ...where, success: true } }),
      
      // Failed actions
      prisma.auditLog.count({ where: { ...where, success: false } }),
      
      // Actions by type
      prisma.auditLog.groupBy({
        by: ['action'],
        where,
        _count: { action: true },
        orderBy: { _count: { action: 'desc' } }
      }),
      
      // Actions by resource
      prisma.auditLog.groupBy({
        by: ['resource'],
        where,
        _count: { resource: true },
        orderBy: { _count: { resource: 'desc' } }
      }),
      
      // Top users by activity (only for managers and above)
      (context.role === 'MANAGER' || context.role === 'DIRECTOR' || context.role === 'ADMIN') 
        ? prisma.auditLog.groupBy({
            by: ['userId'],
            where,
            _count: { userId: true },
            orderBy: { _count: { userId: 'desc' } },
            take: 10
          }).then(async (results) => {
            const userIds = results.map(r => r.userId);
            const users = await prisma.user.findMany({
              where: { id: { in: userIds } },
              select: { id: true, name: true, email: true }
            });
            return results.map(result => ({
              user: users.find(u => u.id === result.userId),
              count: result._count.userId
            }));
          })
        : Promise.resolve([])
    ]);

    return NextResponse.json({
      summary: {
        totalActions,
        successfulActions,
        failedActions,
        successRate: totalActions > 0 ? (successfulActions / totalActions * 100).toFixed(2) : '0.00'
      },
      actionsByType: actionsByType.map(item => ({
        action: item.action,
        count: item._count.action
      })),
      actionsByResource: actionsByResource.map(item => ({
        resource: item.resource,
        count: item._count.resource
      })),
      topUsers: topUsers
    });
  }, req);
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';
import { triggerConversationEvent } from '@/lib/realtime';
import sanitizeHtml from 'sanitize-html';
import { canSendMessage } from '@/lib/rate-limit';

interface Params { params: Promise<{ id: string }> }

const sanitizeOptions: sanitizeHtml.IOptions = {
  allowedTags: ['b','i','em','strong','a','code','pre','p','ul','ol','li','br','span'],
  allowedAttributes: { a: ['href','rel','target'], span: ['data-user-id','class'] },
  transformTags: {
    a: sanitizeHtml.simpleTransform('a', { rel: 'noopener noreferrer', target: '_blank' })
  }
};

async function resolveMentions(content: string) {
  // Find patterns like @{userId}
  const ids = Array.from(new Set(Array.from(content.matchAll(/@\{([a-zA-Z0-9_-]+)\}/g)).map((m) => m[1])));
  if (ids.length === 0) return content;
  const users = await prisma.user.findMany({ where: { id: { in: ids } }, select: { id: true, name: true, email: true } });
  let out = content;
  for (const u of users) {
    const name = u.name || u.email || 'User';
    const re = new RegExp(`@\\{${u.id}\\}`, 'g');
    out = out.replace(re, `<span class="mention" data-user-id="${u.id}">@${name}</span>`);
  }
  return out;
}

// GET messages with cursor pagination
export async function GET(req: NextRequest, { params }: Params) {
  const { id } = await params;
  try {
    const url = new URL(req.url);
    const cursor = url.searchParams.get('cursor');
    const take = Math.min(parseInt(url.searchParams.get('take') || '50', 10), 100);

    const data = await withSecurity(Resource.MESSAGING, Action.READ, async (context) => {
      const membership = await prisma.conversationMember.findUnique({
        where: { conversationId_userId: { conversationId: id, userId: context.userId } },
      });
      if (!membership) throw new Error('Forbidden');
      const conv = await prisma.conversation.findUnique({ where: { id }, select: { isLocked: true } });
      if (conv?.isLocked) return NextResponse.json({ error: 'Conversation locked' }, { status: 423 });

      const messages = await prisma.message.findMany({
        where: { conversationId: id },
        orderBy: { createdAt: 'desc' },
        take,
        ...(cursor ? { cursor: { id: cursor }, skip: 1 } : {}),
        include: {
          attachments: true,
          reactions: true,
          reads: { include: { user: { select: { id: true, name: true } } } },
          sender: { select: { id: true, name: true } },
        },
      });

      const nextCursor = messages.length === take ? messages[messages.length - 1]?.id : null;
      return { messages, nextCursor };
    }, req);

    return NextResponse.json(data);
  } catch (err: any) {
    await SecurityService.logAction({ action: 'READ', resource: 'MESSAGING', resourceId: id, success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to fetch messages' }, { status: 400 });
  }
}

// POST send message
export async function POST(req: NextRequest, { params }: Params) {
  const { id } = await params;
  try {
    const body = await req.json();
    const data = await withSecurity(Resource.MESSAGING, Action.CREATE, async (context) => {
      const membership = await prisma.conversationMember.findUnique({
        where: { conversationId_userId: { conversationId: id, userId: context.userId } },
      });
      if (!membership) throw new Error('Forbidden');
      const allowed = await canSendMessage(context.userId, id);
      if (!allowed) {
        return NextResponse.json({ error: 'Rate limit' }, { status: 429 });
      }

      let { content, attachments, parentId } = body as { content?: string; attachments?: Array<{ url: string; fileName: string; mimeType: string; size: number; width?: number; height?: number }>; parentId?: string };
      content = content ? await resolveMentions(content) : null as any;
      content = content ? sanitizeHtml(content, sanitizeOptions) : null as any;

      const message = await prisma.message.create({
        data: {
          conversationId: id,
          senderId: context.userId,
          content,
          parentId: parentId || null,
          attachments: attachments && attachments.length ? {
            create: attachments.map(a => ({ url: a.url, fileName: a.fileName, mimeType: a.mimeType, size: a.size, width: a.width, height: a.height }))
          } : undefined,
        },
      });

      await prisma.conversation.update({ where: { id }, data: { lastMessageAt: new Date() } });
      // Do not fail request if realtime triggers error
      Promise.resolve().then(() => triggerConversationEvent(id, 'message:new', { messageId: message.id })).catch(()=>{});
      // Notify members on their private channels for desktop notifications
      try {
        const { triggerUserEvent } = await import('@/lib/realtime');
        const members = await prisma.conversationMember.findMany({ where: { conversationId: id }, select: { userId: true } });
        for (const m of members) {
          if (m.userId === context.userId) continue;
          Promise.resolve().then(() => triggerUserEvent(m.userId, 'message:new', { conversationId: id, messageId: message.id })).catch(()=>{});
        }
      } catch {}
      return message;
    }, req);

    if (data instanceof NextResponse) return data;
    return NextResponse.json({ message: data }, { status: 201 });
  } catch (err: any) {
    await SecurityService.logAction({ action: 'CREATE', resource: 'MESSAGING', resourceId: id, success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to send message' }, { status: 400 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';
import { triggerConversationEvent } from '@/lib/realtime';

interface Params { params: Promise<{ id: string }> }

// Mark conversation as read by creating a read receipt on the latest message
export async function POST(req: NextRequest, { params }: Params) {
  const conversationId = (await params).id;
  try {
    const result = await withSecurity(Resource.MESSAGING, Action.READ, async (context) => {
      const membership = await prisma.conversationMember.findUnique({
        where: { conversationId_userId: { conversationId, userId: context.userId } },
      });
      if (!membership) throw new Error('Forbidden');

      const latest = await prisma.message.findFirst({
        where: { conversationId },
        orderBy: { createdAt: 'desc' },
        select: { id: true },
      });
      if (!latest) return { ok: true };
      await prisma.messageRead.upsert({
        where: { messageId_userId: { messageId: latest.id, userId: context.userId } },
        create: { messageId: latest.id, userId: context.userId },
        update: { readAt: new Date() },
      });
      // Do not fail request if realtime fails
      Promise.resolve().then(() =>
        triggerConversationEvent(conversationId, 'message:read', { messageId: latest.id, userId: context.userId })
      ).catch(()=>{});
      return { ok: true };
    }, req);
    return NextResponse.json(result);
  } catch (err: any) {
    await SecurityService.logAction({ action: 'READ', resource: 'MESSAGING', resourceId: conversationId, success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to mark read' }, { status: 400 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

interface Params { params: Promise<{ id: string }> }

// PATCH: rename/add/remove members (admin in conversation)
export async function PATCH(req: NextRequest, { params }: Params) {
  const resolvedParams = await params;
  const { id } = resolvedParams;
  try {
    const body = await req.json();
    const result = await withSecurity(Resource.MESSAGING, Action.UPDATE, async (context) => {
      const membership = await prisma.conversationMember.findUnique({
        where: { conversationId_userId: { conversationId: id, userId: context.userId } },
      });
      if (!membership) throw new Error('Not a member');
      const isAdmin = membership.role === 'admin';
      const { name, addMemberIds, removeMemberIds, lock } = body as {
        name?: string;
        addMemberIds?: string[];
        removeMemberIds?: string[];
        lock?: boolean;
      };

      // Only admin can rename, add/remove, or lock
      if (!isAdmin) throw new Error('Forbidden');

      const updates: any = {};
      if (typeof name === 'string') updates.name = name || null;
      if (typeof lock === 'boolean') updates.isLocked = lock;

      const tx = await prisma.$transaction(async (tx) => {
        const conv = await tx.conversation.update({ where: { id }, data: updates });
        if (addMemberIds?.length) {
          const toAdd = Array.from(new Set(addMemberIds));
          await tx.conversationMember.createMany({
            data: toAdd.map((uid) => ({ conversationId: id, userId: uid, role: 'member' })),
            skipDuplicates: true,
          });
        }
        if (removeMemberIds?.length) {
          await tx.conversationMember.deleteMany({
            where: { conversationId: id, userId: { in: removeMemberIds } },
          });
        }
        return conv;
      });

      return tx;
    }, req);
    return NextResponse.json({ conversation: result });
  } catch (err: any) {
    await SecurityService.logAction({ action: 'UPDATE', resource: 'MESSAGING', resourceId: params.id, success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to update conversation' }, { status: 400 });
  }
}

// DELETE: admin-only
export async function DELETE(req: NextRequest, { params }: Params) {
  const { id } = params;
  try {
    await withSecurity(Resource.MESSAGING, Action.MANAGE, async (context) => {
      const membership = await prisma.conversationMember.findUnique({
        where: { conversationId_userId: { conversationId: id, userId: context.userId } },
      });
      if (!membership || membership.role !== 'admin') throw new Error('Forbidden');
      // Soft-delete by removing members and clearing name
      await prisma.conversation.update({ where: { id }, data: { name: null } });
      await prisma.conversationMember.deleteMany({ where: { conversationId: id } });
    }, req);
    return NextResponse.json({ ok: true });
  } catch (err: any) {
    await SecurityService.logAction({ action: 'DELETE', resource: 'MESSAGING', resourceId: id, success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to delete conversation' }, { status: 400 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

// GET /api/chat/conversations
export async function GET(req: NextRequest) {
  try {
    const data = await withSecurity(Resource.MESSAGING, Action.READ, async (context) => {
      const conversations = await prisma.conversation.findMany({
        where: {
          members: { some: { userId: context.userId } },
        },
        orderBy: [{ lastMessageAt: 'desc' }, { updatedAt: 'desc' }],
        include: {
          members: { select: { userId: true, role: true, user: { select: { id: true, name: true, role: true } } } },
          messages: {
            take: 1,
            orderBy: { createdAt: 'desc' },
            include: { attachments: true, reactions: true, reads: true, sender: { select: { id: true, name: true } } },
          },
        },
      });

      // Compute unread counts per conversation
      const result = await Promise.all(
        conversations.map(async (c) => {
          const lastReadAt = await prisma.messageRead.findFirst({
            where: { userId: context.userId, message: { conversationId: c.id } },
            orderBy: { readAt: 'desc' },
            select: { readAt: true },
          });

          const where: any = {
            conversationId: c.id,
            isDeleted: false,
          };
          if (lastReadAt?.readAt) {
            where.createdAt = { gt: lastReadAt.readAt };
          }

          const unreadCount = await prisma.message.count({ where });

          return { ...c, unreadCount };
        })
      );

      return result;
    }, req);

    return NextResponse.json({ conversations: data });
  } catch (err: any) {
    console.error('Conversations GET failed:', err);
    await SecurityService.logAction({ action: 'LIST', resource: 'MESSAGING', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to load conversations', detail: err?.message }, { status: 500 });
  }
}

// POST /api/chat/conversations
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();
    const data = await withSecurity(Resource.MESSAGING, Action.CREATE, async (context) => {
      const { name, memberIds } = body as { name?: string; memberIds: string[] };
      const uniqueMembers = Array.from(new Set([context.userId, ...(memberIds || [])]));

      const conversation = await prisma.conversation.create({
        data: {
          name: name || null,
          isGroup: !!name || uniqueMembers.length > 2,
          createdById: context.userId,
          members: {
            create: uniqueMembers.map((uid) => ({ userId: uid, role: uid === context.userId ? 'admin' : 'member' })),
          },
        },
        include: { members: { include: { user: { select: { id: true, name: true } } } } },
      });

      // Notify invited users
      // Note: this is fire-and-forget; do not block
      Promise.resolve().then(async () => {
        const { triggerUserEvent } = await import('@/lib/realtime');
        for (const m of conversation.members) {
          await triggerUserEvent(m.userId, 'conversation:updated', { conversationId: conversation.id });
        }
      });

      return conversation;
    }, req);

    return NextResponse.json({ conversation: data }, { status: 201 });
  } catch (err: any) {
    console.error('Conversations POST failed:', err);
    await SecurityService.logAction({ action: 'CREATE', resource: 'MESSAGING', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to create conversation', detail: err?.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

const ALLOWLIST = ['github.com','vercel.com','docs.google.com','www.npmjs.com'];

async function fetchMetadata(url: string) {
  const controller = new AbortController();
  const id = setTimeout(() => controller.abort(), 3500);
  try {
    const res = await fetch(url, { signal: controller.signal, redirect: 'follow' });
    const html = await res.text();
    const titleMatch = html.match(/<title>([^<]+)<\/title>/i);
    const title = titleMatch ? titleMatch[1].trim() : url;
    return { title };
  } finally {
    clearTimeout(id);
  }
}

export async function POST(req: NextRequest) {
  try {
    const { url } = await req.json();
    const hostname = new URL(url).hostname.toLowerCase();
    if (!ALLOWLIST.some((d) => hostname.endsWith(d))) {
      return NextResponse.json({ error: 'Domain not allowed' }, { status: 400 });
    }
    const data = await withSecurity(Resource.MESSAGING, Action.READ, async () => {
      return await fetchMetadata(url);
    }, req);
    return NextResponse.json(data);
  } catch (err: any) {
    await SecurityService.logAction({ action: 'LINK_PREVIEW', resource: 'MESSAGING', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed' }, { status: 400 });
  }
}

