import { PERMISSIONS } from '@/lib/permissions';
import { Resource } from '@prisma/client';
import { normalizeCompanyName, normalizePersonName, normalizeEmail, calculateStringSimilarity } from '@/lib/duplicate-detection';

// Request schema validation
const searchSchema = z.object({
  query: z.string().min(2).max(100),
  type: z.enum(['company', 'contact', 'email', 'phone', 'all']).optional().default('all'),
  limit: z.number().min(1).max(100).optional().default(20),
  includeInactive: z.boolean().optional().default(false),
});

export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions);
    
    if (!session?.user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Check permissions for database search
    if (!hasPermission(session.user as any, PERMISSIONS.LEADS.READ.resource, PERMISSIONS.LEADS.READ.action) &&
        !hasPermission(session.user as any, PERMISSIONS.PIPELINE.READ.resource, PERMISSIONS.PIPELINE.READ.action)) {
      return NextResponse.json({ error: 'Insufficient permissions' }, { status: 403 });
    }

    const { searchParams } = new URL(request.url);
    const validatedParams = searchSchema.parse({
      query: searchParams.get('query'),
      type: searchParams.get('type'),
      limit: searchParams.get('limit') ? parseInt(searchParams.get('limit')!) : undefined,
      includeInactive: searchParams.get('includeInactive') === 'true',
    });
    
    const { query, type, limit, includeInactive } = validatedParams;
    const results: any[] = [];
    
    // Get data access filter based on user permissions
    const leadAccessFilter = getDataAccessFilter(session.user as any, Resource.LEADS);
    const pipelineAccessFilter = getDataAccessFilter(session.user as any, Resource.PIPELINE);
    
    // Search leads
    if (type === 'all' || type === 'company' || type === 'contact' || type === 'email') {
      const leadResults = await prisma.lead.findMany({
        where: {
          ...leadAccessFilter,
          AND: [
            includeInactive ? {} : { status: { not: 'Closed' } },
            {
              OR: [
                { name: { contains: query, mode: 'insensitive' } },
                { company: { contains: query, mode: 'insensitive' } },
                { email: { contains: query, mode: 'insensitive' } },
                { phone: { contains: query } },
              ]
            }
          ]
        },
        include: {
          bdr: {
            select: { id: true, name: true, role: true }
          },
          activityLogs: {
            orderBy: { timestamp: 'desc' },
            take: 1,
          }
        },
        take: Math.min(limit, 50),
        orderBy: { addedDate: 'desc' }
      });
      
      results.push(...leadResults.map(lead => ({
        id: `lead-${lead.id}`,
        type: 'lead',
        name: lead.name,
        company: lead.company,
        email: lead.email,
        phone: lead.phone,
        status: lead.status,
        addedDate: lead.addedDate,
        lastActivity: lead.activityLogs[0]?.timestamp,
        owner: lead.bdr,
        relevanceScore: calculateSearchRelevance(query, {
          name: lead.name,
          company: lead.company,
          email: lead.email,
        })
      })));
    }
    
    // Search pipeline items
    if (type === 'all' || type === 'company' || type === 'contact' || type === 'email') {
      const pipelineResults = await prisma.pipelineItem.findMany({
        where: {
          ...pipelineAccessFilter,
          AND: [
            includeInactive ? {} : { 
              status: { 
                notIn: ['Closed - Won', 'Closed - Lost', 'Dead'] 
              } 
            },
            {
              OR: [
                { name: { contains: query, mode: 'insensitive' } },
                { company: { contains: query, mode: 'insensitive' } },
                { email: { contains: query, mode: 'insensitive' } },
                { phone: { contains: query } },
              ]
            }
          ]
        },
        include: {
          bdr: {
            select: { id: true, name: true, role: true }
          },
          activityLogs: {
            orderBy: { timestamp: 'desc' },
            take: 1,
          }
        },
        take: Math.min(limit, 50),
        orderBy: { lastUpdated: 'desc' }
      });
      
      results.push(...pipelineResults.map(item => ({
        id: `pipeline-${item.id}`,
        type: 'pipeline',
        name: item.name,
        company: item.company,
        email: item.email,
        phone: item.phone,
        status: item.status,
        category: item.category,
        value: item.value,
        addedDate: item.addedDate,
        lastActivity: item.activityLogs[0]?.timestamp || item.lastUpdated,
        owner: item.bdr,
        relevanceScore: calculateSearchRelevance(query, {
          name: item.name,
          company: item.company,
          email: item.email,
        })
      })));
    }
    
    // Sort by relevance and limit results
    const sortedResults = results
      .sort((a, b) => b.relevanceScore - a.relevanceScore)
      .slice(0, limit);
    
    return NextResponse.json({
      results: sortedResults,
      totalFound: results.length,
      query,
      searchType: type,
    });
    
  } catch (error) {
    console.error('Error performing duplicate search:', error);
    
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: 'Invalid search parameters', details: error.errors },
        { status: 400 }
      );
    }
    
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

// Calculate search relevance score
function calculateSearchRelevance(query: string, fields: { name?: string | null; company?: string | null; email?: string | null }): number {
  const normalizedQuery = query.toLowerCase().trim();
  let maxScore = 0;
  
  // Check exact matches (highest score)
  if (fields.name && normalizePersonName(fields.name).includes(normalizedQuery)) {
    maxScore = Math.max(maxScore, 1.0);
  }
  if (fields.company && normalizeCompanyName(fields.company).includes(normalizedQuery)) {
    maxScore = Math.max(maxScore, 1.0);
  }
  if (fields.email && normalizeEmail(fields.email).includes(normalizedQuery)) {
    maxScore = Math.max(maxScore, 1.0);
  }
  
  // Check similarity matches
  if (fields.name) {
    const nameSimilarity = calculateStringSimilarity(normalizedQuery, normalizePersonName(fields.name));
    maxScore = Math.max(maxScore, nameSimilarity * 0.9);
  }
  
  if (fields.company) {
    const companySimilarity = calculateStringSimilarity(normalizedQuery, normalizeCompanyName(fields.company));
    maxScore = Math.max(maxScore, companySimilarity * 0.9);
  }
  
  // Partial matches (lower score)
  const searchTerms = normalizedQuery.split(' ');
  for (const term of searchTerms) {
    if (term.length < 2) continue;
    
    if (fields.name && normalizePersonName(fields.name).includes(term)) {
      maxScore = Math.max(maxScore, 0.6);
    }
    if (fields.company && normalizeCompanyName(fields.company).includes(term)) {
      maxScore = Math.max(maxScore, 0.6);
    }
  }
  
  return maxScore;
}

import { NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth-options';
import { prisma } from '@/lib/db';
import { z } from 'zod';

const editorialItemSchema = z.object({
  name: z.string().min(1),
  title: z.string().optional(),
  company: z.string().optional(),
  email: z.string().email().optional().or(z.literal('')),
  phone: z.string().optional(),
  bdrId: z.string(),
  status: z.enum(['LIST_OUT_QA_INTERVIEW_PROPOSED', 'INTERVIEW_SCHEDULED', 'INTERVIEW_COMPLETED', 'QA_SUBMITTED', 'QA_APPROVED', 'PUBLISHED', 'DECLINED']),
  notes: z.string().optional(),
  link: z.string().optional(),
  interviewDate: z.string().optional(),
  qaSubmissionDate: z.string().optional(),
  qaApprovedDate: z.string().optional(),
  publicationDate: z.string().optional(),
  publicationLink: z.string().optional(),
  leadId: z.number().optional(),
  pipelineItemId: z.number().optional(),
});

// GET /api/editorial - Get all editorial board items
export async function GET(request: Request) {
  try {
    const session = await getServerSession(authOptions);
    if (!session?.user?.email) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const bdr = searchParams.get('bdr') || undefined;
    const status = searchParams.get('status') || undefined;
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = parseInt(searchParams.get('pageSize') || '50');
    const skip = (page - 1) * pageSize;

    const where: any = {};
    if (bdr) where.bdrId = bdr;
    if (status) where.status = status;

    const [items, totalCount] = await Promise.all([
      prisma.editorialBoardItem.findMany({
        where,
        include: {
          bdr: {
            select: { name: true, email: true }
          },
          lead: {
            select: { name: true, company: true }
          },
          pipelineItem: {
            select: { name: true, company: true }
          }
        },
        orderBy: [
          { status: 'asc' },
          { lastUpdated: 'desc' }
        ],
        skip,
        take: pageSize,
      }),
      prisma.editorialBoardItem.count({ where })
    ]);

    return NextResponse.json({
      items,
      pagination: {
        page,
        pageSize,
        totalCount,
        totalPages: Math.ceil(totalCount / pageSize),
      }
    });

  } catch (error) {
    console.error('Error fetching editorial items:', error);
    return NextResponse.json(
      { error: 'Failed to fetch editorial items' },
      { status: 500 }
    );
  }
}

// POST /api/editorial - Create new editorial board item
export async function POST(request: Request) {
  try {
    const session = await getServerSession(authOptions);
    if (!session?.user?.email) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const validatedData = editorialItemSchema.parse(body);

    // Convert date strings to Date objects if provided
    const dateFields = ['interviewDate', 'qaSubmissionDate', 'qaApprovedDate', 'publicationDate'];
    const processedData = { ...validatedData };
    
    dateFields.forEach(field => {
      if (processedData[field as keyof typeof processedData]) {
        processedData[field as keyof typeof processedData] = new Date(processedData[field as keyof typeof processedData] as string);
      }
    });

    const editorialItem = await prisma.editorialBoardItem.create({
      data: {
        ...processedData,
        lastUpdated: new Date(),
      },
      include: {
        bdr: {
          select: { name: true, email: true }
        },
        lead: {
          select: { name: true, company: true }
        },
        pipelineItem: {
          select: { name: true, company: true }
        }
      }
    });

    return NextResponse.json(editorialItem, { status: 201 });

  } catch (error) {
    console.error('Error creating editorial item:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: 'Validation failed', details: error.errors },
        { status: 400 }
      );
    }
    return NextResponse.json(
      { error: 'Failed to create editorial item' },
      { status: 500 }
    );
  }
}

import { NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth-options';
import { prisma } from '@/lib/db';
import { z } from 'zod';

const syncFromPipelineSchema = z.object({
  pipelineItemId: z.number(),
  force: z.boolean().optional().default(false), // Force create even if already exists
});

// POST /api/editorial/sync - Sync pipeline item to editorial board
export async function POST(request: Request) {
  try {
    const session = await getServerSession(authOptions);
    if (!session?.user?.email) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    const { pipelineItemId, force } = syncFromPipelineSchema.parse(body);

    // Get the pipeline item
    const pipelineItem = await prisma.pipelineItem.findUnique({
      where: { id: pipelineItemId },
      include: {
        bdr: {
          select: { id: true, name: true, email: true }
        },
        lead: {
          select: { id: true, name: true, company: true, email: true, phone: true }
        }
      }
    });

    if (!pipelineItem) {
      return NextResponse.json({ error: 'Pipeline item not found' }, { status: 404 });
    }

    // Check if already exists in editorial board (unless force is true)
    if (!force) {
      const existingEditorialItem = await prisma.editorialBoardItem.findFirst({
        where: { pipelineItemId }
      });

      if (existingEditorialItem) {
        return NextResponse.json(
          { error: 'Pipeline item already exists in editorial board', editorialItem: existingEditorialItem },
          { status: 409 }
        );
      }
    }

    // Determine initial status based on pipeline status
    let editorialStatus: string;
    switch (pipelineItem.status) {
      case 'List Out':
      case 'Partner List Sent':
        editorialStatus = 'LIST_OUT_QA_INTERVIEW_PROPOSED';
        break;
      case 'Q&A':
      case 'Free Q&A Offered':
        editorialStatus = 'QA_SUBMITTED';
        break;
      case 'Sold':
        editorialStatus = 'QA_APPROVED';
        break;
      default:
        editorialStatus = 'LIST_OUT_QA_INTERVIEW_PROPOSED';
    }

    // Create editorial board item with duplicated data
    const editorialItem = await prisma.editorialBoardItem.create({
      data: {
        name: pipelineItem.name,
        title: pipelineItem.title || undefined,
        company: pipelineItem.company || undefined,
        email: pipelineItem.email || pipelineItem.lead?.email || undefined,
        phone: pipelineItem.phone || pipelineItem.lead?.phone || undefined,
        bdrId: pipelineItem.bdrId,
        status: editorialStatus as any,
        notes: `Synced from pipeline: ${pipelineItem.notes || 'No notes'}`,
        link: pipelineItem.link || undefined,
        leadId: pipelineItem.leadId || undefined,
        pipelineItemId: pipelineItem.id,
        lastUpdated: new Date(),
      },
      include: {
        bdr: {
          select: { name: true, email: true }
        },
        lead: {
          select: { name: true, company: true }
        },
        pipelineItem: {
          select: { name: true, company: true }
        }
      }
    });

    // Log the sync activity
    await prisma.activityLog.create({
      data: {
        bdrId: pipelineItem.bdrId,
        activityType: 'Editorial_Sync',
        description: `Pipeline item synced to editorial board`,
        notes: `Status: ${pipelineItem.status} â†’ ${editorialStatus}`,
        pipelineItemId: pipelineItem.id,
        editorialItemId: editorialItem.id,
      }
    });

    return NextResponse.json({
      success: true,
      editorialItem,
      message: `Pipeline item "${pipelineItem.name}" synced to editorial board`
    }, { status: 201 });

  } catch (error) {
    console.error('Error syncing pipeline item to editorial board:', error);
    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { error: 'Validation failed', details: error.errors },
        { status: 400 }
      );
    }
    return NextResponse.json(
      { error: 'Failed to sync pipeline item to editorial board' },
      { status: 500 }
    );
  }
}

// POST /api/editorial/sync/auto - Auto-sync eligible pipeline items
export async function PUT(request: Request) {
  try {
    const session = await getServerSession(authOptions);
    if (!session?.user?.email) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    // Find pipeline items in eligible statuses that aren't already in editorial board
    const eligibleStatuses = ['List Out', 'Partner List Sent', 'Q&A', 'Free Q&A Offered', 'Sold'];
    
    const eligibleItems = await prisma.pipelineItem.findMany({
      where: {
        status: { in: eligibleStatuses },
        editorialItems: {
          none: {} // Not already in editorial board
        }
      },
      include: {
        bdr: {
          select: { id: true, name: true, email: true }
        },
        lead: {
          select: { id: true, name: true, company: true, email: true, phone: true }
        }
      },
      take: 50 // Limit to avoid overwhelming the system
    });

    const syncedItems = [];
    
    for (const pipelineItem of eligibleItems) {
      // Determine initial status based on pipeline status
      let editorialStatus: string;
      switch (pipelineItem.status) {
        case 'List Out':
        case 'Partner List Sent':
          editorialStatus = 'LIST_OUT_QA_INTERVIEW_PROPOSED';
          break;
        case 'Q&A':
        case 'Free Q&A Offered':
          editorialStatus = 'QA_SUBMITTED';
          break;
        case 'Sold':
          editorialStatus = 'QA_APPROVED';
          break;
        default:
          editorialStatus = 'LIST_OUT_QA_INTERVIEW_PROPOSED';
      }

      try {
        // Create editorial board item
        const editorialItem = await prisma.editorialBoardItem.create({
          data: {
            name: pipelineItem.name,
            title: pipelineItem.title || undefined,
            company: pipelineItem.company || undefined,
            email: pipelineItem.email || pipelineItem.lead?.email || undefined,
            phone: pipelineItem.phone || pipelineItem.lead?.phone || undefined,
            bdrId: pipelineItem.bdrId,
            status: editorialStatus as any,
            notes: `Auto-synced from pipeline: ${pipelineItem.notes || 'No notes'}`,
            link: pipelineItem.link || undefined,
            leadId: pipelineItem.leadId || undefined,
            pipelineItemId: pipelineItem.id,
            lastUpdated: new Date(),
          }
        });

        // Log the sync activity
        await prisma.activityLog.create({
          data: {
            bdrId: pipelineItem.bdrId,
            activityType: 'Editorial_Auto_Sync',
            description: `Pipeline item auto-synced to editorial board`,
            notes: `Status: ${pipelineItem.status} â†’ ${editorialStatus}`,
            pipelineItemId: pipelineItem.id,
            editorialItemId: editorialItem.id,
          }
        });

        syncedItems.push({
          pipelineItem: { id: pipelineItem.id, name: pipelineItem.name },
          editorialItem: { id: editorialItem.id, status: editorialStatus }
        });
      } catch (error) {
        console.error(`Failed to sync pipeline item ${pipelineItem.id}:`, error);
        // Continue with other items even if one fails
      }
    }

    return NextResponse.json({
      success: true,
      syncedCount: syncedItems.length,
      syncedItems,
      message: `Auto-synced ${syncedItems.length} pipeline items to editorial board`
    });

  } catch (error) {
    console.error('Error auto-syncing pipeline items:', error);
    return NextResponse.json(
      { error: 'Failed to auto-sync pipeline items to editorial board' },
      { status: 500 }
    );
  }
}

