// GET - List finance entries with filters and monthly grouping
export async function GET(req: NextRequest) {
  try {
    // 1. Get Session securely on the server
    const session = await getServerSession(authOptions);

    // 2. Check Authentication
    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { role, id: userId } = session.user;

    const { searchParams } = new URL(req.url);
    const search = searchParams.get('search') || '';
    const status = searchParams.get('status') || undefined;
    const bdr = searchParams.get('bdr') || undefined;
    const month = searchParams.get('month') || undefined;
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = parseInt(searchParams.get('pageSize') || '10');
    const groupByMonth = searchParams.get('groupByMonth') === 'true';
    const analytics = searchParams.get('analytics') === 'true';
    const skip = (page - 1) * pageSize;
    
    // 3. Enforce Authorization (RBAC) - Build where clause with role-based filtering
    const where: {
      OR?: Array<{ company: { contains: string; mode: 'insensitive' } } | 
                  { notes: { contains: string; mode: 'insensitive' } }>;
      status?: string;
      bdr?: string; // Changed from { name: string } to string
      bdrId?: string;
      month?: string;
    } = {};

    // Role-based data filtering
    if (role === Role.BDR) {
      // BDRs can only see their own finance entries
      where.bdrId = userId;
    } else if (role === Role.ADMIN) {
      // Admins can see all finance entries - no additional filtering
    } else {
      // Unknown role - deny access
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }
    
    if (search) {
      where.OR = [
        { company: { contains: search, mode: 'insensitive' } },
        { notes: { contains: search, mode: 'insensitive' } },
      ];
    }
    if (status) where.status = status;
    // Note: For BDRs, we ignore the 'bdr' filter param since they can only see their own data
    // For Admins, we can still apply the bdr filter if provided
    if (bdr) {
      // Accept BDR by name in UI; map to userId for query
      const targetUser = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
      if (targetUser) {
        where.bdrId = targetUser.id;
      } else {
        where.bdrId = '___NO_MATCH___';
      }
    }
    if (month) where.month = month;
    
    if (analytics) {
      // Get all finance entries for analytics
      const allEntries = await prisma.financeEntry.findMany({
        where,
        orderBy: { createdAt: 'desc' }
      });
      
      // Calculate analytics
      const totalRevenue = allEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      
      // Year-to-date calculations
      const currentYear = new Date().getFullYear();
      const ytdEntries = allEntries.filter(entry => {
        const entryYear = entry.createdAt ? new Date(entry.createdAt).getFullYear() : currentYear;
        return entryYear === currentYear;
      });
      const ytdRevenue = ytdEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      const ytdDeals = ytdEntries.length;
      
      // Monthly calculations
      const currentMonth = new Date().toISOString().slice(0, 7);
      const currentMonthEntries = allEntries.filter(entry => entry.month === currentMonth);
      const monthlyRevenue = currentMonthEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      const monthlyDeals = currentMonthEntries.length;
      
      // Quarterly calculations
      const currentQuarter = Math.floor((new Date().getMonth() + 3) / 3);
      const quarterlyEntries = allEntries.filter(entry => {
        if (!entry.createdAt) return false;
        const entryDate = new Date(entry.createdAt);
        const entryQuarter = Math.floor((entryDate.getMonth() + 3) / 3);
        return entryDate.getFullYear() === currentYear && entryQuarter === currentQuarter;
      });
      const quarterlyRevenue = quarterlyEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      
      // Growth calculations
      const lastMonth = new Date(new Date().setMonth(new Date().getMonth() - 1)).toISOString().slice(0, 7);
      const lastMonthEntries = allEntries.filter(entry => entry.month === lastMonth);
      const lastMonthRevenue = lastMonthEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      const monthlyGrowth = lastMonthRevenue > 0 ? ((monthlyRevenue - lastMonthRevenue) / lastMonthRevenue) * 100 : 0;
      
      // Deal calculations
      const totalDeals = allEntries.length;
      const averageDealSize = totalDeals > 0 ? totalRevenue / totalDeals : 0;
      const conversionRate = totalDeals > 0 ? (allEntries.filter(entry => entry.status === 'Paid').length / totalDeals) * 100 : 0;
      
      // Calculate overdue days
      const overdueEntries = allEntries.filter(entry => 
        entry.status === 'Overdue' && entry.dueDate
      );
      const overdueDays = overdueEntries.length > 0 ? 
        overdueEntries.reduce((sum, entry) => {
          const daysDiff = Math.floor((new Date().getTime() - new Date(entry.dueDate!).getTime()) / (1000 * 60 * 60 * 24));
          return sum + daysDiff;
        }, 0) / overdueEntries.length : 0;
      
      // Calculate average payment time
      const paidEntries = allEntries.filter(entry => 
        entry.status === 'Paid' && entry.invoiceDate && entry.dueDate
      );
      const averagePaymentTime = paidEntries.length > 0 ? 
        paidEntries.reduce((sum, entry) => {
          const invoiceDate = new Date(entry.invoiceDate!);
          const dueDate = new Date(entry.dueDate!);
          const daysDiff = Math.floor((dueDate.getTime() - invoiceDate.getTime()) / (1000 * 60 * 60 * 24));
          return sum + Math.abs(daysDiff);
        }, 0) / paidEntries.length : 0;
      
      // Status breakdown
      const statusBreakdown = allEntries.reduce((acc: {[key: string]: number}, entry) => {
        acc[entry.status] = (acc[entry.status] || 0) + 1;
        return acc;
      }, {});
      
      // Payment status
      const paymentStatus = {
        paid: allEntries.filter(entry => entry.status === 'Paid').length,
        pending: allEntries.filter(entry => entry.status === 'Pending').length,
        overdue: allEntries.filter(entry => entry.status === 'Overdue').length,
      };
      
      // Monthly trends (last 6 months)
      const monthlyTrends = [];
      for (let i = 5; i >= 0; i--) {
        const date = new Date();
        date.setMonth(date.getMonth() - i);
        const monthStr = date.toISOString().slice(0, 7);
        const monthEntries = allEntries.filter(entry => entry.month === monthStr);
        monthlyTrends.push({
          month: date.toLocaleDateString('en-US', { year: 'numeric', month: 'short' }),
          revenue: monthEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0),
          deals: monthEntries.length
        });
      }
      
      // BDR performance
      const bdrPerformance = Object.entries(
        allEntries.reduce((acc: {[key: string]: {revenue: number, deals: number}}, entry) => {
          const bdrName = entry.bdr || 'Unassigned';
          if (!acc[bdrName]) {
            acc[bdrName] = { revenue: 0, deals: 0 };
          }
          acc[bdrName].revenue += entry.gbpAmount || 0;
          acc[bdrName].deals += 1;
          return acc;
        }, {})
      ).map(([bdr, data]) => ({
        bdr,
        revenue: data.revenue,
        deals: data.deals,
        avgDealSize: data.deals > 0 ? data.revenue / data.deals : 0
      })).sort((a, b) => b.revenue - a.revenue);
      
      const analytics = {
        totalRevenue,
        ytdRevenue,
        monthlyRevenue,
        quarterlyRevenue,
        averageDealSize,
        conversionRate,
        overdueDays,
        monthlyGrowth,
        totalDeals,
        ytdDeals,
        monthlyDeals,
        averagePaymentTime,
        statusBreakdown,
        monthlyTrends,
        bdrPerformance,
        paymentStatus
      };
      
      return NextResponse.json({ analytics });
    }
    
    if (groupByMonth) {
      // Get all entries grouped by month with BDR relation for name resolution
      const financeEntries = await prisma.financeEntry.findMany({
        where,
        orderBy: [
          { month: 'asc' },
          { createdAt: 'desc' }
        ],
        include: {
          bdr: { select: { name: true } }
        }
      });

      // Map BDR relation to string name for UI
      const normalized = financeEntries.map((e: any) => ({
        ...e,
        bdr: e.bdr?.name || '',
      }));

      // Group entries by month
      const groupedEntries = normalized.reduce((acc: { [key: string]: typeof normalized }, entry) => {
        if (!acc[entry.month]) {
          acc[entry.month] = [];
        }
        acc[entry.month].push(entry);
        return acc;
      }, {} as { [key: string]: typeof normalized });

      return NextResponse.json({
        groupedEntries,
        total: normalized.length,
      });
    }
    
    // Get total count
    const total = await prisma.financeEntry.count({ where });
    
    // Get finance entries
    const financeEntries = await prisma.financeEntry.findMany({
      where,
      skip,
      take: pageSize,
      include: {
        bdr: {
          select: { id: true, name: true }
        }
      },
      orderBy: [
        { month: 'desc' },
        { createdAt: 'desc' }
      ]
    });
    
    return NextResponse.json({
      financeEntries: financeEntries.map((e: any) => ({
        ...e,
        bdr: e.bdr?.name || '',
      })),
      total,
      page,
      pageSize,
      totalPages: Math.ceil(total / pageSize)
    });
    
  } catch (error) {
    console.error("Error fetching finance entries:", error);
    return NextResponse.json(
      { error: "Failed to fetch finance entries" },
      { status: 500 }
    );
  }
}

// POST - Create a new finance entry
export async function POST(req: NextRequest) {
  try {
    // 1. Get Session securely on the server
    const session = await getServerSession(authOptions);

    // 2. Check Authentication
    if (!session || !session.user) {
      return NextResponse.json({ error: "Unauthorized" }, { status: 401 });
    }

    const { role, id: userId, name: userName } = session.user;
    const data = await req.json();
    
    // Validate the finance entry data
    const validatedData = createFinanceEntrySchema.parse(data);
    
    // 3. Enforce Authorization (RBAC) for finance entry creation
    let financeData = { ...validatedData };
    
    if (role === Role.BDR) {
      // BDRs can only create finance entries assigned to themselves
      financeData.bdr = userName; // Use the user's name as the BDR
    } else if (role === Role.ADMIN) {
      // Admins can assign finance entries to any BDR
      // Use the provided bdr or assign to themselves if not provided
      if (!financeData.bdr) {
        financeData.bdr = userName;
      }
    } else {
      // Unknown role - deny access
      return NextResponse.json({ error: "Forbidden" }, { status: 403 });
    }
    
    // Convert date strings to Date objects
    const processedData = {
      ...financeData,
      invoiceDate: financeData.invoiceDate ? new Date(financeData.invoiceDate) : null,
      dueDate: financeData.dueDate ? new Date(financeData.dueDate) : null,
      exchangeRateDate: financeData.exchangeRateDate ? new Date(financeData.exchangeRateDate) : null,
    };
    
    // Handle automatic USD to GBP conversion if needed
    if (processedData.soldAmount && processedData.soldAmount > 0 && !processedData.gbpAmount) {
      try {
        const rate = await exchangeRateService.getCurrentRate();
        processedData.gbpAmount = exchangeRateService.convertUSDToGBP(processedData.soldAmount, rate);
        processedData.exchangeRate = rate;
        processedData.exchangeRateDate = new Date();
      } catch (error) {
        console.error('Error converting currency:', error);
        // Continue without conversion if API fails
      }
    }
    
    // Create the finance entry
    const financeEntry = await prisma.financeEntry.create({
      data: processedData
    });

    // Notify finance viewers about new deal
    try {
      const viewers = await prisma.user.findMany({ where: { isActive: true }, select: { id: true, role: true } });
      const eligible = viewers.filter(u => u.role === Role.ADMIN || u.role === Role.MANAGER || u.role === Role.DIRECTOR || u.role === Role.TEAM_LEAD || u.role === Role.BDR);
      const title = 'New Finance Deal Added';
      const message = `${financeEntry.company} â€¢ ${financeEntry.status}${financeEntry.gbpAmount ? ` â€¢ Â£${financeEntry.gbpAmount}` : ''}`;
      await Promise.all(
        eligible.map(u => fetch(`${process.env.NEXT_PUBLIC_BASE_URL || ''}/api/reporting/advanced/notifications`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ userId: u.id, type: 'info', title, message, priority: 'high' })
        }).catch(() => null))
      );
    } catch {}

    return NextResponse.json(financeEntry, { status: 201 });
    
  } catch (error) {
    console.error("Error creating finance entry:", error);
    
    // Handle validation errors
    if (error && typeof error === 'object' && 'name' in error && error.name === "ZodError") {
      return NextResponse.json(
        { error: "Validation error", details: 'errors' in error ? error.errors : [] },
        { status: 400 }
      );
    }
    
    return NextResponse.json(
      { error: "Failed to create finance entry" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { requireRole } from '@/lib/authorize';

export const GET = requireRole(['admin','manager'], async function (req: NextRequest) {
  const targets = await prisma.kpiTarget.findMany();
  return NextResponse.json(targets);
});

export const PUT = requireRole(['admin'], async function (req: NextRequest) {
  const body = await req.json(); // expects [{name,value}]
  if (!Array.isArray(body)) {
    return NextResponse.json({ error: 'Invalid payload' }, { status: 400 });
  }
  for (const t of body) {
    await prisma.kpiTarget.upsert({
      where: { name: t.name },
      update: { value: t.value },
      create: { name: t.name, value: t.value },
    });
  }
  const targets = await prisma.kpiTarget.findMany();
  return NextResponse.json(targets);
});

import { prisma } from "@/lib/db";
import { updateLeadSchema } from "@/lib/validations";
import { NextRequest, NextResponse } from "next/server";
import { PERMISSIONS, hasPermission } from "@/lib/permissions";
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth-options';

interface RouteParams {
  params: {
    id: string;
  };
}

// GET - Fetch a specific lead
export async function GET(req: NextRequest, { params }: RouteParams) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const leadId = Number(id);
    
    if (isNaN(leadId)) {
      return NextResponse.json(
        { error: "Invalid lead ID" },
        { status: 400 }
      );
    }
    
    const lead = await prisma.lead.findUnique({
      where: { id: leadId },
    });
    
    if (!lead) {
      return NextResponse.json(
        { error: "Lead not found" },
        { status: 404 }
      );
    }
    
    return NextResponse.json(lead);
    
  } catch (error) {
    console.error("Error fetching lead:", error);
    return NextResponse.json(
      { error: "Failed to fetch lead" },
      { status: 500 }
    );
  }
}

// PUT - Update a lead
export async function PUT(req: NextRequest, { params }: RouteParams) {
  try {
    const { id } = params;
    const leadId = Number(id);
    
    if (isNaN(leadId)) {
      return NextResponse.json(
        { error: "Invalid lead ID" },
        { status: 400 }
      );
    }
    
    const data = await req.json();
    
    // Get the existing lead data first
    const existingLead = await prisma.lead.findUnique({
      where: { id: leadId },
    });
    
    if (!existingLead) {
      return NextResponse.json(
        { error: "Lead not found" },
        { status: 404 }
      );
    }
    
    // Check if status is changing
    const statusChanged = existingLead.status !== data.status;
    const previousStatus = existingLead.status;
    const newStatus = data.status;
    
    // Merge the existing data with the partial update
    const mergedData = {
      ...existingLead,
      ...data,
      id: leadId,
    };
    
    // Validate the merged data
    const validatedData = updateLeadSchema.parse(mergedData);
    
    // Update the lead
    const lead = await prisma.lead.update({
      where: { id: leadId },
      data: {
        name: validatedData.name,
        title: validatedData.title,
        bdr: validatedData.bdr,
        company: validatedData.company,
        source: validatedData.source,
        status: validatedData.status,
        link: validatedData.link,
        phone: validatedData.phone,
        notes: validatedData.notes,
        email: validatedData.email,
      },
    });
    
    // If status changed, create an activity log for the status transition
    if (statusChanged) {
      await prisma.activityLog.create({
        data: {
          bdr: lead.bdr || 'Unknown',
          activityType: 'Status_Change',
          description: `Lead status changed from ${previousStatus} to ${newStatus}`,
          previousStatus: previousStatus,
          newStatus: newStatus,
          leadId: leadId,
          timestamp: new Date(),
        },
      });
      
      // If this is a transition from "Call Booked" to a completion status (excluding no show/rescheduled),
      // also create a Call_Completed activity log
      if (previousStatus === 'Call Booked' && 
          newStatus !== 'Call Booked' && 
          !['no show', 'rescheduled', 'No Show', 'Rescheduled'].includes(newStatus.toLowerCase())) {
        await prisma.activityLog.create({
          data: {
            bdr: lead.bdr || 'Unknown',
            activityType: 'Call_Completed',
            description: `Call completed automatically: ${previousStatus} â†’ ${newStatus}`,
            previousStatus: previousStatus,
            newStatus: newStatus,
            leadId: leadId,
            timestamp: new Date(),
          },
        });
      }
    }

    // Notify finance viewers if notes indicate a deal was added
    try {
      const session = await getServerSession(authOptions);
      const updatedNotes: string = (data?.notes ?? '').toString();
      const previousNotes: string = (existingLead?.notes ?? '').toString();
      const notesChanged = updatedNotes !== previousNotes;
      const dealKeywords = [
        /\bdeal\b/i,
        /\bsold\b/i,
        /\bagreement\b/i,
        /\binvoice\b/i,
        /\bgbp\b/i,
        /\bamount\b/i,
      ];
      const hasDealSignal = notesChanged && dealKeywords.some((re) => re.test(updatedNotes));
      if (hasDealSignal) {
        // Find all users with finance board access (READ on FINANCE)
        const financeUsers = await prisma.user.findMany({
          where: { isActive: true },
          select: { id: true, role: true },
        });
        const recipients = financeUsers
          .filter((u) => hasPermission(u as any, 2 /* Resource.FINANCE */ as any, 1 /* Action.READ */ as any))
          .map((u) => u.id);
        // Emit ephemeral notifications via existing notifications endpoint contract (no DB persistence)
        const title = 'Deal activity on lead notes';
        const msg = `Lead ${lead.name} (${lead.company || 'Unknown'}) notes indicate a deal update.`;
        // Best-effort fire-and-forget post per recipient
        await Promise.all(
          recipients.map((uid) =>
            fetch(`${process.env.NEXT_PUBLIC_BASE_URL || ''}/api/reporting/advanced/notifications`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ userId: uid, type: 'info', title, message: msg, priority: 'high' }),
            }).catch(() => null)
          )
        );
      }
    } catch (e) {
      // Do not fail lead update on notification error
    }
    
    return NextResponse.json(lead);
    
  } catch (error: any) {
    console.error("Error updating lead:", error);
    
    // Handle validation errors
    if (error.name === "ZodError") {
      return NextResponse.json(
        { error: "Validation error", details: error.errors },
        { status: 400 }
      );
    }
    
    // Handle Prisma errors
    if (error.code === "P2025") {
      return NextResponse.json(
        { error: "Lead not found" },
        { status: 404 }
      );
    }
    
    return NextResponse.json(
      { error: "Failed to update lead" },
      { status: 500 }
    );
  }
}

// DELETE - Delete a lead
export async function DELETE(req: NextRequest, { params }: RouteParams) {
  try {
    const { id } = params;
    const leadId = Number(id);
    
    if (isNaN(leadId)) {
      return NextResponse.json(
        { error: "Invalid lead ID" },
        { status: 400 }
      );
    }
    
    await prisma.lead.delete({
      where: { id: leadId },
    });
    
    return NextResponse.json({ message: "Lead deleted" }, { status: 200 });
    
  } catch (error: any) {
    console.error("Error deleting lead:", error);
    
    // Handle Prisma errors
    if (error.code === "P2025") {
      return NextResponse.json(
        { error: "Lead not found" },
        { status: 404 }
      );
    }
    
    return NextResponse.json(
      { error: "Failed to delete lead" },
      { status: 500 }
    );
  }
}

import { prisma } from "@/lib/db";
import { NextRequest, NextResponse } from "next/server";
import { z } from "zod";

const bulkDeleteSchema = z.object({
  ids: z.array(z.number()).min(1, "No IDs provided"),
});

// DELETE - Bulk delete leads
export async function DELETE(req: NextRequest) {
  try {
    // Some environments strip bodies from DELETE; try to parse, otherwise read from query string
    let data: unknown = undefined;
    try {
      data = await req.json();
    } catch {
      // fallback to query param ids=1,2,3
      const { searchParams } = new URL(req.url);
      const idsParam = searchParams.get("ids");
      if (idsParam) {
        data = { ids: idsParam.split(",").map((s) => Number(s)).filter((n) => !Number.isNaN(n)) };
      }
    }

    const { ids } = bulkDeleteSchema.parse(data);

    const result = await prisma.lead.deleteMany({
      where: { id: { in: ids } },
    });

    return NextResponse.json(
      { message: `Deleted ${result.count} leads`, count: result.count },
      { status: 200 }
    );
  } catch (error: any) {
    if (error.name === "ZodError") {
      return NextResponse.json(
        { error: "Validation error", details: error.errors },
        { status: 400 }
      );
    }

    console.error("Error bulk deleting leads:", error);
    return NextResponse.json(
      { error: "Failed to bulk delete leads" },
      { status: 500 }
    );
  }
}

import { prisma } from "@/lib/db";
import { leadStatusEnum } from "@/lib/validations";
import { NextRequest, NextResponse } from "next/server";
import { z } from "zod";

const bulkUpdateSchema = z.object({
  ids: z.array(z.number()),
  status: z.enum(leadStatusEnum),
});

// PATCH - Bulk update leads status
export async function PATCH(req: NextRequest) {
  try {
    const data = await req.json();
    
    // Validate the data
    const { ids, status } = bulkUpdateSchema.parse(data);
    
    // No IDs provided
    if (ids.length === 0) {
      return NextResponse.json(
        { error: "No lead IDs provided" },
        { status: 400 }
      );
    }
    
    // Get existing leads to track status changes
    const existingLeads = await prisma.lead.findMany({
      where: { id: { in: ids } },
      select: { id: true, status: true, bdr: true }
    });
    
    // Update all leads with the provided IDs
    const result = await prisma.lead.updateMany({
      where: {
        id: { in: ids },
      },
      data: { status },
    });
    
    // Create activity logs for status transitions
    const activityLogs = [];
    for (const lead of existingLeads) {
      if (lead.status !== status) {
        // Create status change activity log
        activityLogs.push({
          bdr: lead.bdr || 'Unknown',
          activityType: 'Status_Change',
          description: `Lead status changed from ${lead.status} to ${status}`,
          previousStatus: lead.status,
          newStatus: status,
          leadId: lead.id,
          timestamp: new Date(),
        });
        
        // If this is a transition from "Call Booked" to a completion status (excluding no show/rescheduled),
        // also create a Call_Completed activity log
        if (lead.status === 'Call Booked' && 
            status !== 'Call Booked' && 
            !['no show', 'rescheduled', 'No Show', 'Rescheduled'].includes(status.toLowerCase())) {
          activityLogs.push({
            bdr: lead.bdr || 'Unknown',
            activityType: 'Call_Completed',
            description: `Call completed automatically: ${lead.status} â†’ ${status}`,
            previousStatus: lead.status,
            newStatus: status,
            leadId: lead.id,
            timestamp: new Date(),
          });
        }
      }
    }
    
    // Create all activity logs in a transaction
    if (activityLogs.length > 0) {
      await prisma.activityLog.createMany({
        data: activityLogs,
      });
    }
    
    return NextResponse.json({
      message: `Updated ${result.count} leads`,
      count: result.count,
      activityLogsCreated: activityLogs.length,
    });
    
  } catch (error: any) {
    console.error("Error bulk updating leads:", error);
    
    // Handle validation errors
    if (error.name === "ZodError") {
      return NextResponse.json(
        { error: "Validation error", details: error.errors },
        { status: 400 }
      );
    }
    
    return NextResponse.json(
      { error: "Failed to update leads" },
      { status: 500 }
    );
  }
}

