import { NextResponse } from "next/server";
import { getServerSession } from "next-auth/next";
import { authOptions } from "@/lib/auth-options";
import { prisma } from "@/lib/db";
import { createLeadSchema } from "@/lib/validations";
import { NextRequest } from "next/server";
import { Resource, Action, DuplicateAction } from "@prisma/client";
import { getAuthenticatedUser, createErrorResponse, createSuccessResponse } from "@/lib/auth-api";
import { hasPermission, getDataAccessFilter } from "@/lib/permissions";
import { SecurityService, withSecurity } from "@/lib/security";
import { duplicateDetectionService } from "@/lib/duplicate-detection";

// GET - List leads with filters
export async function GET(req: NextRequest) {
  return withSecurity(Resource.LEADS, Action.READ, async (context) => {
    const { searchParams } = new URL(req.url);
    const search = searchParams.get('search') || '';
    const status = searchParams.get('status') || undefined;
    const source = searchParams.get('source') || undefined;
    const bdr = searchParams.get('bdr') || undefined;
    const page = parseInt(searchParams.get('page') || '1');
    const pageSize = parseInt(searchParams.get('pageSize') || '10');
    const skip = (page - 1) * pageSize;
    
    // Build base query
    let baseQuery: any = {
      skip,
      take: pageSize,
      orderBy: { addedDate: 'desc' }
    };

    // Build where clause with filters
    let where: any = {};
    
    if (search) {
      where.OR = [
        { name: { contains: search, mode: 'insensitive' } },
        { company: { contains: search, mode: 'insensitive' } },
        { email: { contains: search, mode: 'insensitive' } },
      ];
    }
    if (status) where.status = status;
    if (source) where.source = source;
    
    // Apply BDR filter for users with appropriate permissions
    if (bdr && (context.permissions.includes(`${Resource.LEADS}:${Action.VIEW_ALL}`) || context.permissions.includes(`${Resource.LEADS}:${Action.VIEW_TEAM}`))) {
      const targetUser = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
      if (targetUser) {
        where.bdrId = targetUser.id;
      } else {
        where.bdrId = '___NO_MATCH___';
      }
    }

    baseQuery.where = where;
    
    // Apply row-level security
    const secureQuery = SecurityService.buildSecureQuery(baseQuery, context, Resource.LEADS);
    
    // Get total count with same security restrictions
    const countQuery = SecurityService.buildSecureQuery({ where }, context, Resource.LEADS);
    const total = await prisma.lead.count(countQuery);
    
    // Get leads with pipeline information
    const leads = await prisma.lead.findMany({
      ...secureQuery,
      select: {
        id: true,
        name: true,
        title: true,
        addedDate: true,
        company: true,
        source: true,
        status: true,
        link: true,
        phone: true,
        notes: true,
        email: true,
        bdr: { select: { name: true } },
        pipelineItems: {
          select: {
            id: true,
            category: true,
            status: true,
            lastUpdated: true,
          },
          take: 1,
          orderBy: { lastUpdated: 'desc' }
        }
      }
    });
    
    // Transform the response
    const transformedLeads = leads.map(lead => {
      const { pipelineItems, bdr, ...rest } = lead as any;
      const latestItem = pipelineItems[0];
      return {
        ...rest,
        // Derive lastUpdated from latest pipeline item if available, otherwise fall back to addedDate
        lastUpdated: latestItem?.lastUpdated || rest.addedDate,
        bdr: bdr?.name || bdr || null,
        inPipeline: pipelineItems.length > 0,
        pipelineStatus: latestItem?.status || null,
        pipelineCategory: latestItem?.category || null,
      };
    });

    const totalPages = Math.ceil(total / pageSize);
    
    return new NextResponse(JSON.stringify({
      leads: transformedLeads,
      total,
      page,
      pageSize,
      totalPages,
    }), {
      status: 200,
      headers: {
        'Content-Type': 'application/json',
        'Cache-Control': 'private, max-age=30, stale-while-revalidate=60',
      },
    });
  }, req);
}

export async function POST(req: NextRequest) {
  return withSecurity(Resource.LEADS, Action.CREATE, async (context) => {
    const data = await req.json();
    
    // Validate the request body
    const validatedData = createLeadSchema.parse(data);
    
    // Determine BDR assignment based on permissions
    let bdrId = context.userId; // Default to current user
    
    if (validatedData.bdrId && context.permissions.includes(`${Resource.LEADS}:${Action.VIEW_ALL}`)) {
      // Users with VIEW_ALL permission can assign to any BDR
      bdrId = validatedData.bdrId;
    }
    
    // Create the lead
    const lead = await prisma.lead.create({
      data: {
        name: validatedData.name,
        title: validatedData.title,
        bdrId: bdrId,
        company: validatedData.company,
        source: validatedData.source,
        status: validatedData.status,
        link: validatedData.link,
        phone: validatedData.phone,
        notes: validatedData.notes,
        email: validatedData.email,
      },
      include: {
        bdr: {
          select: {
            id: true,
            name: true,
            email: true,
            territory: {
              select: { name: true }
            }
          }
        }
      }
    });

    // Log the creation
    await SecurityService.logAction({
      action: 'CREATE',
      resource: 'LEADS',
      resourceId: lead.id.toString(),
      details: {
        leadName: lead.name,
        company: lead.company,
        assignedTo: bdrId
      }
    }, req);
    
    // Fire-and-forget: record a duplicate awareness warning for reporting if this company/email was recently active in other owners
    if (lead.company || lead.email) {
      duplicateDetectionService
        .checkForDuplicates(
          { name: lead.name, company: lead.company || undefined, email: lead.email || undefined, phone: lead.phone || undefined, title: lead.title || undefined, linkedinUrl: lead.link || undefined },
          bdrId,
          DuplicateAction.LEAD_CREATE
        )
        .catch(() => {});
    }
    return NextResponse.json(lead, { status: 201 });
  }, req);
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/db";
import { pipelineCategoryEnum } from "@/lib/validations";
import { z } from "zod";

interface RouteParams {
  params: {
    id: string;
  };
}

// Schema for validation
const movePipelineItemSchema = z.object({
  newCategory: z.enum(pipelineCategoryEnum),
  newStatus: z.string(),
});

export async function PATCH(req: NextRequest, { params }: RouteParams) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const pipelineId = Number(id);

    if (isNaN(pipelineId)) {
      return NextResponse.json({ error: "Invalid pipeline item ID" }, { status: 400 });
    }

    // Find the pipeline item first to get current values
    const pipelineItem = await prisma.pipelineItem.findUnique({
      where: { id: pipelineId },
    });

    if (!pipelineItem) {
      return NextResponse.json({ error: "Pipeline item not found" }, { status: 404 });
    }

    // Extract and validate request data
    const data = await req.json();
    const validatedData = movePipelineItemSchema.parse(data);

    // Store previous values for logging
    const previousCategory = pipelineItem.category;
    const previousStatus = pipelineItem.status;

    // Update the pipeline item
    const updatedPipelineItem = await prisma.pipelineItem.update({
      where: { id: pipelineId },
      data: {
        category: validatedData.newCategory,
        status: validatedData.newStatus,
        lastUpdated: new Date(),
      },
    });

    // Create activity log entry for pipeline move
    await prisma.activityLog.create({
      data: {
        bdr: pipelineItem.bdr,
        activityType: "Pipeline_Move",
        description: `Moved from ${previousCategory} (${previousStatus}) to ${validatedData.newCategory} (${validatedData.newStatus})`,
        pipelineItemId: pipelineId,
        previousCategory,
        newCategory: validatedData.newCategory,
        previousStatus,
        newStatus: validatedData.newStatus,
      },
    });

    // If this is a transition from "Call Booked" to a completion status (excluding no show/rescheduled),
    // also create a Call_Completed activity log
    if (previousStatus === 'Call Booked' && 
        validatedData.newStatus !== 'Call Booked' && 
        !['no show', 'rescheduled', 'No Show', 'Rescheduled'].includes(validatedData.newStatus.toLowerCase())) {
      await prisma.activityLog.create({
        data: {
          bdr: pipelineItem.bdr,
          activityType: 'Call_Completed',
          description: `Call completed automatically: ${previousStatus} â†’ ${validatedData.newStatus}`,
          previousStatus: previousStatus,
          newStatus: validatedData.newStatus,
          previousCategory: previousCategory,
          newCategory: validatedData.newCategory,
          pipelineItemId: pipelineId,
          leadId: pipelineItem.leadId,
          timestamp: new Date(),
        },
      });
    }

    return NextResponse.json(updatedPipelineItem);
  } catch (error: any) {
    console.error("Error moving pipeline item:", error);
    return NextResponse.json(
      { error: error.message || "Failed to move pipeline item" },
      { status: 400 }
    );
  }
}

import { prisma } from "@/lib/db";
import { NextRequest, NextResponse } from "next/server";

interface RouteParams {
  params: {
    id: string;
  };
}

// GET - Fetch a specific pipeline item
export async function GET(req: NextRequest, { params }: RouteParams) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const pipelineItemId = Number(id);
    
    if (isNaN(pipelineItemId)) {
      return NextResponse.json(
        { error: "Invalid pipeline item ID" },
        { status: 400 }
      );
    }
    
    const pipelineItem = await prisma.pipelineItem.findUnique({
      where: { id: pipelineItemId },
      include: {
        children: true,
      },
    });
    
    if (!pipelineItem) {
      return NextResponse.json(
        { error: "Pipeline item not found" },
        { status: 404 }
      );
    }
    
    return NextResponse.json(pipelineItem);
    
  } catch (error) {
    console.error("Error fetching pipeline item:", error);
    return NextResponse.json(
      { error: "Failed to fetch pipeline item" },
      { status: 500 }
    );
  }
}

// PUT - Update a pipeline item
export async function PUT(req: NextRequest, { params }: RouteParams) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const pipelineItemId = Number(id);
    
    if (isNaN(pipelineItemId)) {
      return NextResponse.json(
        { error: "Invalid pipeline item ID" },
        { status: 400 }
      );
    }
    
    const data = await req.json();
    
    // Get the existing pipeline item first
    const existingItem = await prisma.pipelineItem.findUnique({
      where: { id: pipelineItemId },
    });
    
    if (!existingItem) {
      return NextResponse.json(
        { error: "Pipeline item not found" },
        { status: 404 }
      );
    }
    
    // Check if status is changing
    const statusChanged = existingItem.status !== data.status;
    const previousStatus = existingItem.status;
    const newStatus = data.status;
    
    // Update the pipeline item with all possible fields
    const pipelineItem = await prisma.pipelineItem.update({
      where: { id: pipelineItemId },
      data: {
        name: data.name,
        company: data.company,
        title: data.title,
        category: data.category,
        status: data.status,
        value: data.value,
        probability: data.probability,
        expectedCloseDate: data.expectedCloseDate,
        callDate: data.callDate,
        link: data.link,
        phone: data.phone,
        notes: data.notes,
        email: data.email,
        lastUpdated: new Date(),
      },
      include: {
        lead: true,
        activityLogs: {
          orderBy: {
            timestamp: 'desc'
          }
        }
      }
    });
    
    // If status changed, create an activity log for the status transition
    if (statusChanged) {
      await prisma.activityLog.create({
        data: {
          bdr: pipelineItem.bdr,
          activityType: 'Status_Change',
          description: `Status changed from ${previousStatus} to ${newStatus}`,
          previousStatus: previousStatus,
          newStatus: newStatus,
          previousCategory: existingItem.category,
          newCategory: data.category,
          pipelineItemId: pipelineItemId,
          leadId: pipelineItem.leadId,
          timestamp: new Date(),
        },
      });
      
      // If this is a transition from "Call Booked" to a completion status (excluding no show/rescheduled),
      // also create a Call_Completed activity log
      if (previousStatus === 'Call Booked' && 
          newStatus !== 'Call Booked' && 
          !['no show', 'rescheduled', 'No Show', 'Rescheduled'].includes(newStatus.toLowerCase())) {
        await prisma.activityLog.create({
          data: {
            bdr: pipelineItem.bdr,
            activityType: 'Call_Completed',
            description: `Call completed automatically: ${previousStatus} â†’ ${newStatus}`,
            previousStatus: previousStatus,
            newStatus: newStatus,
            previousCategory: existingItem.category,
            newCategory: data.category,
            pipelineItemId: pipelineItemId,
            leadId: pipelineItem.leadId,
            timestamp: new Date(),
          },
        });
      }

    }
    
    return NextResponse.json(pipelineItem);
    
  } catch (error) {
    console.error("Error updating pipeline item:", error);
    return NextResponse.json(
      { error: "Failed to update pipeline item" },
      { status: 500 }
    );
  }
}

export async function DELETE(req: NextRequest, { params }: RouteParams) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const pipelineId = Number(id);
    
    if (isNaN(pipelineId)) {
      return NextResponse.json({ error: "Invalid pipeline item ID" }, { status: 400 });
    }
    
    await prisma.pipelineItem.delete({
      where: { id: pipelineId },
    });
    
    return NextResponse.json({ success: true });
  } catch (error: any) {
    console.error("Error deleting pipeline item:", error);
    return NextResponse.json(
      { error: error.message || "Failed to delete pipeline item" },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/db";

// Function to determine pipeline category and status based on lead status
function determinePipelineDetails(leadStatus: string): { category: string; status: string } {
  switch (leadStatus) {
    case "Proposal - Profile":
      return {
        category: "Pipeline",
        status: "Proposal - Profile"
      };
    case "Proposal - Media Sales":
      return {
        category: "Pipeline",
        status: "Proposal - Media"
      };
    case "Agreement - Profile":
      return {
        category: "Pipeline",
        status: "Agreement - Profile"
      };
    case "List Out":
      return {
        category: "Lists_Media_QA",
        status: "List Out"
      };
    case "Sold":
      return {
        category: "Lists_Media_QA",
        status: "Sold"
      };
    case "Call Booked":
      return {
        category: "Calls",
        status: "Call Booked"
      };
    case "DECLINED":
      return {
        category: "Declined_Rescheduled",
        status: "Declined_Rescheduled"
      };
    default:
      // For "BDR Followed Up" and "Passed Over", start with a call
      return {
        category: "Calls",
        status: "Call Proposed"
      };
  }
}

export async function POST(req: NextRequest) {
  try {
    // Get all leads that have already been converted to pipeline
    const convertedLeadIds = await prisma.$queryRaw`
      SELECT "leadId" FROM "PipelineItem" WHERE "leadId" IS NOT NULL
    `;
    
    const leadIdsToExclude = (convertedLeadIds as any[])
      .map((item: any) => item.leadId)
      .filter(Boolean);
    
    // Find all leads that have BDRs assigned and are not in pipeline
    const leads = await prisma.lead.findMany({
      where: {
        bdr: { not: null },
        NOT: {
          id: { in: leadIdsToExclude }
        }
      },
    });
    
    if (leads.length === 0) {
      return NextResponse.json({ 
        message: "No leads found to convert" 
      }, { status: 200 });
    }

    // Create pipeline items for each lead
    const createdItems = [];
    
    for (const lead of leads) {
      try {
        // Determine pipeline category and status based on lead status
        const { category, status } = determinePipelineDetails(lead.status);

        // Create pipeline item
        const pipelineItemData = {
          name: lead.name,
          title: lead.title,
          bdr: lead.bdr!, // We know it's not null from the query
          company: lead.company,
          category: category,
          status: status,
          link: lead.link,
          phone: lead.phone,
          notes: lead.notes,
          email: lead.email,
          leadId: lead.id,
          lastUpdated: new Date(),
        };

        // Use raw SQL to create pipeline item
        const result = await prisma.$queryRaw`
          INSERT INTO "PipelineItem" (
            "name", "title", "bdr", "company", "category", "status", 
            "link", "phone", "notes", "email", "leadId", "lastUpdated", "addedDate"
          ) VALUES (
            ${pipelineItemData.name},
            ${pipelineItemData.title},
            ${pipelineItemData.bdr},
            ${pipelineItemData.company},
            ${pipelineItemData.category},
            ${pipelineItemData.status},
            ${pipelineItemData.link},
            ${pipelineItemData.phone},
            ${pipelineItemData.notes},
            ${pipelineItemData.email},
            ${pipelineItemData.leadId},
            ${pipelineItemData.lastUpdated},
            ${new Date()}
          )
          RETURNING *
        `;
        
        const pipelineItem = Array.isArray(result) ? result[0] : result;

        // Transfer activity logs (updates) from lead to pipeline item
        const activityLogs = await prisma.activityLog.findMany({
          where: {
            leadId: lead.id,
            activityType: 'BDR_Update'
          },
          orderBy: { timestamp: 'asc' }
        });
        
        // Create new activity logs for the pipeline item with the same content
        if (activityLogs.length > 0) {
          await Promise.all(
            activityLogs.map(log =>
              prisma.activityLog.create({
                data: {
                  bdr: log.bdr,
                  activityType: log.activityType,
                  description: log.description,
                  notes: log.notes,
                  pipelineItemId: (pipelineItem as any).id,
                  timestamp: log.timestamp, // Preserve original timestamp
                },
              })
            )
          );
        }

        // Use raw SQL to create activity log
        await prisma.$queryRaw`
          INSERT INTO "ActivityLog" (
            "bdr", "activityType", "description", "leadId", "pipelineItemId",
            "newCategory", "newStatus", "notes", "timestamp"
          ) VALUES (
            ${lead.bdr!},
            ${'Lead_Converted'},
            ${`Converted lead to ${category} (${status})`},
            ${lead.id},
            ${(pipelineItem as any).id},
            ${category},
            ${status},
            ${`Lead converted to pipeline item with ID: ${(pipelineItem as any).id}`},
            ${new Date()}
          )
        `;

        createdItems.push(pipelineItem);
      } catch (error) {
        console.error(`Error converting lead ${lead.id}:`, error);
        // Continue with next lead
      }
    }
    
    return NextResponse.json({
      message: `Converted ${createdItems.length} leads to pipeline items`,
      items: createdItems
    }, { status: 201 });
  } catch (error: any) {
    console.error("Error converting leads to pipeline items:", error);
    return NextResponse.json(
      { error: error.message || "Failed to convert leads" },
      { status: 400 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/db";
import { z } from "zod";
import { allPipelineStatusValues, pipelineCategoryEnum } from "@/lib/validations";

// Schema for validation
const convertLeadSchema = z.object({
  leadId: z.number(),
  category: z.enum(pipelineCategoryEnum),
  status: z.string().refine(value => allPipelineStatusValues.includes(value as any), {
    message: "Invalid status for pipeline item",
  }),
});

export async function POST(req: NextRequest) {
  try {
    const data = await req.json();
    
    // Validate the request body
    const validatedData = convertLeadSchema.parse(data);
    
    // Find the lead
    const lead = await prisma.lead.findUnique({
      where: { id: validatedData.leadId },
    });
    
    if (!lead) {
      return NextResponse.json({ error: "Lead not found" }, { status: 404 });
    }

    // Check if BDR is assigned
    if (!lead.bdr) {
      return NextResponse.json({ error: "A BDR must be assigned to this lead before moving to pipeline" }, { status: 400 });
    }
    
    // Create a pipeline item from the lead
    const pipelineItem = await prisma.pipelineItem.create({
      data: {
        name: lead.name,
        title: lead.title,
        bdr: lead.bdr, // BDR is required in pipeline
        company: lead.company,
        category: validatedData.category,
        status: validatedData.status,
        link: lead.link,
        phone: lead.phone,
        notes: lead.notes,
        email: lead.email,
        leadId: lead.id,
        lastUpdated: new Date(),
      },
    });
    
    // Transfer activity logs (updates) from lead to pipeline item
    const activityLogs = await prisma.activityLog.findMany({
      where: {
        leadId: lead.id,
        activityType: 'BDR_Update'
      },
      orderBy: { timestamp: 'asc' }
    });
    
    // Create new activity logs for the pipeline item with the same content
    if (activityLogs.length > 0) {
      await Promise.all(
        activityLogs.map(log =>
          prisma.activityLog.create({
            data: {
              bdr: log.bdr,
              activityType: log.activityType,
              description: log.description,
              notes: log.notes,
              pipelineItemId: pipelineItem.id,
              timestamp: log.timestamp, // Preserve original timestamp
            },
          })
        )
      );
    }
    
    // Log the activity
    await prisma.activityLog.create({
      data: {
        bdr: lead.bdr,
        activityType: "Lead_Converted",
        description: `Converted lead to ${validatedData.category} (${validatedData.status})`,
        leadId: lead.id,
        pipelineItemId: pipelineItem.id,
        newCategory: validatedData.category,
        newStatus: validatedData.status,
        notes: `Lead converted to pipeline item with ID: ${pipelineItem.id}`
      },
    });
    
    return NextResponse.json(pipelineItem, { status: 201 });
  } catch (error: any) {
    console.error("Error converting lead to pipeline item:", error);
    return NextResponse.json(
      { error: error.message || "Failed to convert lead" },
      { status: 400 }
    );
  }
}

import { NextRequest, NextResponse } from "next/server";
import { prisma } from "@/lib/db";
import { z } from "zod";
import { leadStatusEnum, pipelineCategoryEnum, pipelineStatusEnum } from "@/lib/validations";

// Schema for validation
const bulkConvertLeadSchema = z.object({
  leadIds: z.array(z.number()),
});

// Function to determine pipeline category and status based on lead status
function determinePipelineDetails(leadStatus: string): { category: string; status: string } {
  switch (leadStatus) {
    case "Proposal - Profile":
      return {
        category: "Pipeline",
        status: "Proposal - Profile"
      };
    case "Proposal - Media Sales":
      return {
        category: "Pipeline",
        status: "Proposal - Media"
      };
    case "Agreement - Profile":
      return {
        category: "Pipeline",
        status: "Agreement - Profile"
      };
    case "List Out":
      return {
        category: "Lists_Media_QA",
        status: "List Out"
      };
    case "Sold":
      return {
        category: "Lists_Media_QA",
        status: "Sold"
      };
    case "Call Booked":
      return {
        category: "Calls",
        status: "Call Booked"
      };
    case "DECLINED":
      return {
        category: "Declined_Rescheduled",
        status: "Declined_Rescheduled"
      };
    default:
      // For "BDR Followed Up" and "Passed Over", start with a call
      return {
        category: "Calls",
        status: "Call Proposed"
      };
  }
}

export async function POST(req: NextRequest) {
  try {
    const data = await req.json();
    
    // Validate the request body
    const validatedData = bulkConvertLeadSchema.parse(data);
    
    // Find all leads
    const leads = await prisma.lead.findMany({
      where: { 
        id: { in: validatedData.leadIds },
        bdr: { not: null } // Ensure all leads have BDRs assigned
      },
    });
    
    if (leads.length === 0) {
      return NextResponse.json({ error: "No valid leads found" }, { status: 404 });
    }

    if (leads.length !== validatedData.leadIds.length) {
      return NextResponse.json({ 
        error: "Some leads were not found or don't have BDRs assigned" 
      }, { status: 400 });
    }
    
    // Create pipeline items in a transaction
    const result = await prisma.$transaction(async (tx) => {
      const pipelineItems = await Promise.all(
        leads.map(async (lead) => {
          // Determine pipeline category and status based on lead status
          const { category, status } = determinePipelineDetails(lead.status);

          // Create pipeline item
          const pipelineItem = await tx.pipelineItem.create({
            data: {
              name: lead.name,
              title: lead.title,
              bdr: lead.bdr!, // We know it's not null from the query
              company: lead.company,
              category: category as typeof pipelineCategoryEnum[number],
              status: status,
              link: lead.link,
              phone: lead.phone,
              notes: lead.notes,
              email: lead.email,
              leadId: lead.id,
              lastUpdated: new Date(),
            },
          });

          // Transfer activity logs (updates) from lead to pipeline item
          const activityLogs = await tx.activityLog.findMany({
            where: {
              leadId: lead.id,
              activityType: 'BDR_Update'
            },
            orderBy: { timestamp: 'asc' }
          });
          
          // Create new activity logs for the pipeline item with the same content
          if (activityLogs.length > 0) {
            await Promise.all(
              activityLogs.map(log =>
                tx.activityLog.create({
                  data: {
                    bdr: log.bdr,
                    activityType: log.activityType,
                    description: log.description,
                    notes: log.notes,
                    pipelineItemId: pipelineItem.id,
                    timestamp: log.timestamp, // Preserve original timestamp
                  },
                })
              )
            );
          }

          // Log the activity
          await tx.activityLog.create({
            data: {
              bdr: lead.bdr!,
              activityType: "Lead_Converted",
              description: `Converted lead to ${category} (${status})`,
              leadId: lead.id,
              pipelineItemId: pipelineItem.id,
              newCategory: category,
              newStatus: status,
              notes: `Lead converted to pipeline item with ID: ${pipelineItem.id}`
            },
          });

          return pipelineItem;
        })
      );

      return pipelineItems;
    });
    
    return NextResponse.json({
      message: `Converted ${result.length} leads to pipeline items`,
      items: result
    }, { status: 201 });
  } catch (error: any) {
    console.error("Error converting leads to pipeline items:", error);
    return NextResponse.json(
      { error: error.message || "Failed to convert leads" },
      { status: 400 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { PrismaClient, Resource, Action } from '@prisma/client';
import { createSublistSchema } from '@/lib/validations';
import { SecurityService, withSecurity } from '@/lib/security';

const prisma = new PrismaClient();

// GET - List pipeline items with filters (read-only)
export async function GET(req: NextRequest) {
  return withSecurity(Resource.PIPELINE, Action.READ, async (context) => {
    const { searchParams } = new URL(req.url);
    const search = (searchParams.get('search') || '').trim();
    const category = searchParams.get('category') || undefined;
    const status = searchParams.get('status') || undefined;
    const bdr = searchParams.get('bdr') || undefined;
    const page = parseInt(searchParams.get('page') || '1', 10);
    const pageSize = parseInt(searchParams.get('pageSize') || '50', 10);
    const skip = (page - 1) * pageSize;

    const where: any = {};
    if (search) {
      where.OR = [
        { name: { contains: search, mode: 'insensitive' } },
        { company: { contains: search, mode: 'insensitive' } },
        { email: { contains: search, mode: 'insensitive' } },
      ];
    }
    if (category) where.category = category;
    if (status) where.status = status;

    if (bdr) {
      const targetUser = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
      where.bdrId = targetUser ? targetUser.id : '___NO_MATCH___';
    }

    const baseQuery: any = {
      where,
      skip,
      take: pageSize,
      orderBy: { lastUpdated: 'desc' },
      include: {
        bdr: { select: { name: true, territoryId: true } },
        children: {
          orderBy: { sortOrder: 'asc' },
          include: { bdr: { select: { name: true, territoryId: true } } },
        },
      },
    };

    const secureQuery = SecurityService.buildSecureQuery(baseQuery, context, Resource.PIPELINE);
    const countQuery = SecurityService.buildSecureQuery({ where }, context, Resource.PIPELINE);

    const [itemsRaw, total] = await Promise.all([
      prisma.pipelineItem.findMany(secureQuery),
      prisma.pipelineItem.count(countQuery),
    ]);

    const mapItem = (item: any): any => ({
      id: item.id,
      name: item.name,
      title: item.title,
      addedDate: item.addedDate,
      lastUpdated: item.lastUpdated,
      bdr: item.bdr?.name || null,
      company: item.company,
      category: item.category,
      status: item.status,
      value: item.value,
      probability: item.probability,
      expectedCloseDate: item.expectedCloseDate,
      callDate: item.callDate,
      link: item.link,
      phone: item.phone,
      notes: item.notes,
      email: item.email,
      leadId: item.leadId,
      parentId: item.parentId,
      isSublist: item.isSublist,
      sublistName: item.sublistName,
      sortOrder: item.sortOrder,
      partnerListSize: item.partnerListSize,
      partnerListSentDate: item.partnerListSentDate,
      firstSaleDate: item.firstSaleDate,
      totalSalesFromList: item.totalSalesFromList,
      children: Array.isArray(item.children) ? item.children.map(mapItem) : [],
    });

    const items = itemsRaw.map(mapItem);
    const totalPages = Math.ceil(total / pageSize);

    return new NextResponse(
      JSON.stringify({ items, total, page, pageSize, totalPages }),
      {
        status: 200,
        headers: {
          'Content-Type': 'application/json',
          'Cache-Control': 'private, max-age=15, stale-while-revalidate=30',
        },
      }
    );
  }, req);
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    
    // Validate the request body
    const validatedData = createSublistSchema.parse(body);
    
    // Create the sublist
    const sublist = await prisma.pipelineItem.create({
      data: {
        name: validatedData.name,
        bdr: validatedData.bdr,
        category: validatedData.category,
        status: validatedData.status,
        parentId: validatedData.parentId,
        isSublist: true,
        sublistName: validatedData.name,
        sortOrder: validatedData.sortOrder,
        // Set default values for required fields
        title: null,
        company: null,
        value: null,
        probability: null,
        expectedCloseDate: null,
        callDate: null,
        link: null,
        phone: null,
        notes: `Sublist: ${validatedData.name}`,
        email: null,
        leadId: null,
      },
      include: {
        children: {
          orderBy: {
            sortOrder: 'asc',
          },
        },
        parent: true,
      },
    });

    // Create activity log for sublist creation
    await prisma.activityLog.create({
      data: {
        bdr: validatedData.bdr,
        activityType: 'Note_Added',
        description: `Created sublist: ${validatedData.name}`,
        pipelineItemId: sublist.id,
        notes: `Sublist created in ${validatedData.category} - ${validatedData.status}`,
      },
    });

    return NextResponse.json(sublist);
  } catch (error) {
    console.error('Error creating sublist:', error);
    
    if (error instanceof Error) {
      return NextResponse.json(
        { error: error.message },
        { status: 400 }
      );
    }
    
    return NextResponse.json(
      { error: 'Failed to create sublist' },
      { status: 500 }
    );
  } finally {
    await prisma.$disconnect();
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfDay, endOfDay, parseISO, subDays, startOfWeek, endOfWeek, startOfMonth, endOfMonth } from 'date-fns';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const dateRange = searchParams.get('dateRange') || 'week';
    const territory = searchParams.get('territory');
    const experience = searchParams.get('experience');
    const status = searchParams.get('status');
    const minCalls = parseInt(searchParams.get('minCalls') || '0');
    const maxCalls = parseInt(searchParams.get('maxCalls') || '1000');
    const minConversion = parseFloat(searchParams.get('minConversion') || '0');
    const maxConversion = parseFloat(searchParams.get('maxConversion') || '100');

    // Calculate date range
    let startDate: Date;
    let endDate: Date = new Date();

    switch (dateRange) {
      case 'today':
        startDate = startOfDay(new Date());
        endDate = endOfDay(new Date());
        break;
      case 'week':
        startDate = startOfWeek(new Date(), { weekStartsOn: 1 });
        endDate = endOfWeek(new Date(), { weekStartsOn: 1 });
        break;
      case 'month':
        startDate = startOfMonth(new Date());
        endDate = endOfMonth(new Date());
        break;
      case 'quarter':
        const now = new Date();
        const quarter = Math.floor(now.getMonth() / 3);
        startDate = new Date(now.getFullYear(), quarter * 3, 1);
        endDate = new Date(now.getFullYear(), (quarter + 1) * 3, 0);
        break;
      default:
        startDate = startOfWeek(new Date(), { weekStartsOn: 1 });
        endDate = endOfWeek(new Date(), { weekStartsOn: 1 });
    }

    // Build where clause for users
    const userWhere: any = {
      role: 'BDR',
      isActive: true
    };

    if (status && status !== 'all') {
      userWhere.isActive = status === 'active';
    }

    // Get all BDRs with their basic info
    const bdrs = await prisma.user.findMany({
      where: userWhere,
      select: {
        id: true,
        name: true,
        email: true,
        territoryId: true,
        territory: {
          select: {
            name: true
          }
        },
        createdAt: true
      }
    });

    // Filter by territory if specified
    let filteredBdrs = bdrs;
    if (territory && territory !== 'all') {
      filteredBdrs = bdrs.filter(bdr => bdr.territory?.name?.toLowerCase().includes(territory.toLowerCase()));
    }

    // Filter by experience level
    if (experience && experience !== 'all') {
      const now = new Date();
      filteredBdrs = filteredBdrs.filter(bdr => {
        const experienceYears = (now.getTime() - bdr.createdAt.getTime()) / (1000 * 60 * 60 * 24 * 365);
        switch (experience) {
          case 'junior':
            return experienceYears < 1;
          case 'mid':
            return experienceYears >= 1 && experienceYears < 3;
          case 'senior':
            return experienceYears >= 3;
          default:
            return true;
        }
      });
    }

    // Get performance data for each BDR
    const performanceData = await Promise.all(
      filteredBdrs.map(async (bdr) => {
        // Get activity logs for the date range
        const activityLogs = await prisma.activityLog.findMany({
          where: {
            bdrId: bdr.id,
            timestamp: {
              gte: startDate,
              lte: endDate
            }
          }
        });

        // Calculate metrics
        const callsToday = await prisma.activityLog.count({
          where: {
            bdrId: bdr.id,
            activityType: 'call',
            timestamp: {
              gte: startOfDay(new Date()),
              lte: endOfDay(new Date())
            }
          }
        });

        const callsWeek = await prisma.activityLog.count({
          where: {
            bdrId: bdr.id,
            activityType: 'call',
            timestamp: {
              gte: startOfWeek(new Date(), { weekStartsOn: 1 }),
              lte: endOfWeek(new Date(), { weekStartsOn: 1 })
            }
          }
        });

        const callsMonth = await prisma.activityLog.count({
          where: {
            bdrId: bdr.id,
            activityType: 'call',
            timestamp: {
              gte: startOfMonth(new Date()),
              lte: endOfMonth(new Date())
            }
          }
        });

        const agreementsToday = await prisma.pipelineItem.count({
          where: {
            bdrId: bdr.id,
            agreementDate: {
              gte: startOfDay(new Date()),
              lte: endOfDay(new Date())
            }
          }
        });

        const agreementsWeek = await prisma.pipelineItem.count({
          where: {
            bdrId: bdr.id,
            agreementDate: {
              gte: startOfWeek(new Date(), { weekStartsOn: 1 }),
              lte: endOfWeek(new Date(), { weekStartsOn: 1 })
            }
          }
        });

        const agreementsMonth = await prisma.pipelineItem.count({
          where: {
            bdrId: bdr.id,
            agreementDate: {
              gte: startOfMonth(new Date()),
              lte: endOfMonth(new Date())
            }
          }
        });

        // Calculate conversion rate
        const conversionRate = callsWeek > 0 ? (agreementsWeek / callsWeek) * 100 : 0;

        // Get average call duration (mock data for now)
        const avgCallDuration = Math.random() * 5 + 2; // 2-7 minutes

        // Get lead metrics
        const leadsAssigned = await prisma.lead.count({
          where: {
            bdrId: bdr.id
          }
        });

        const leadsContacted = await prisma.lead.count({
          where: {
            bdrId: bdr.id,
            status: 'contacted'
          }
        });

        // Get follow-ups scheduled
        const followUpsScheduled = await prisma.pipelineItem.count({
          where: {
            bdrId: bdr.id,
            status: 'follow_up'
          }
        });

        // Calculate weekly goal progress (mock data)
        const weeklyGoalProgress = Math.min(100, Math.random() * 120);

        // Calculate monthly rank (mock data)
        const monthlyRank = Math.floor(Math.random() * 20) + 1;

        // Calculate streak (mock data)
        const streak = Math.floor(Math.random() * 10) + 1;

        // Calculate last week performance (mock data)
        const lastWeekPerformance = Math.random() * 100;

        // Determine experience level
        const now = new Date();
        const experienceYears = (now.getTime() - bdr.createdAt.getTime()) / (1000 * 60 * 60 * 24 * 365);
        let experienceLevel = 'senior';
        if (experienceYears < 1) experienceLevel = 'junior';
        else if (experienceYears < 3) experienceLevel = 'mid';

        return {
          id: bdr.id,
          name: bdr.name || 'Unknown BDR',
          callsToday,
          callsWeek,
          callsMonth,
          agreementsToday,
          agreementsWeek,
          agreementsMonth,
          conversionRate: Math.round(conversionRate * 10) / 10,
          avgCallDuration: Math.round(avgCallDuration * 10) / 10,
          leadsAssigned,
          leadsContacted,
          followUpsScheduled,
          weeklyGoalProgress: Math.round(weeklyGoalProgress),
          monthlyRank,
          streak,
          lastWeekPerformance: Math.round(lastWeekPerformance),
          territory: bdr.territory?.name || 'Unassigned',
          experience: experienceLevel,
          status: bdr.isActive ? 'active' : 'inactive'
        };
      })
    );

    // Apply filters
    let filteredData = performanceData;

    // Filter by call count
    filteredData = filteredData.filter(bdr => 
      bdr.callsWeek >= minCalls && bdr.callsWeek <= maxCalls
    );

    // Filter by conversion rate
    filteredData = filteredData.filter(bdr => 
      bdr.conversionRate >= minConversion && bdr.conversionRate <= maxConversion
    );

    // Sort by weekly goal progress (descending)
    filteredData.sort((a, b) => b.weeklyGoalProgress - a.weeklyGoalProgress);

    return NextResponse.json(filteredData);
  } catch (error) {
    console.error('Error fetching BDR performance data:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      data: null
    }, { status: 500 });
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfDay, endOfDay, subDays, startOfWeek, endOfWeek } from 'date-fns';
import { WarningSeverity, DuplicateAction } from '@prisma/client';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const userId = searchParams.get('userId');
    const type = searchParams.get('type');
    const priority = searchParams.get('priority');
    const channel = searchParams.get('channel'); // optional logical channel e.g., 'finance'

    // Get current date ranges
    const today = new Date();
    const yesterday = subDays(today, 1);
    const startOfThisWeek = startOfWeek(today, { weekStartsOn: 1 });
    const endOfThisWeek = endOfWeek(today, { weekStartsOn: 1 });

    // Generate notifications based on system events, duplicate warnings, and performance
    const notifications: Array<{ id: string; type: 'success'|'warning'|'error'|'info'; title: string; message: string; timestamp: string; read: boolean; priority: 'low'|'medium'|'high'; userId?: string; }> = [];

    // Recent company conflict alerts (duplicate warnings)
    const recentDuplicateWarnings = await prisma.duplicateWarning.findMany({
      where: {
        createdAt: { gte: subDays(new Date(), 14) },
        warningType: { in: ['COMPANY_NAME', 'COMPANY_DOMAIN'] as any },
      },
      include: {
        triggeredBy: { select: { id: true, name: true } },
        potentialDuplicates: {
          include: { ownedBy: { select: { id: true, name: true } } }
        }
      },
      orderBy: { createdAt: 'desc' },
      take: 100
    });

    for (const warn of recentDuplicateWarnings) {
      const owners = Array.from(new Set(warn.potentialDuplicates.map(m => m.ownedBy?.name).filter(Boolean))) as string[];
      const ownerIds = Array.from(new Set(warn.potentialDuplicates.map(m => m.ownedBy?.id).filter(Boolean))) as string[];
      const priority: 'high'|'medium'|'low' = warn.severity === WarningSeverity.CRITICAL ? 'high' : warn.severity === WarningSeverity.HIGH ? 'high' : warn.severity === WarningSeverity.MEDIUM ? 'medium' : 'low';
      const title = 'Company conflict detected';
      const message = owners.length > 0
        ? `Similar company recently contacted by ${owners.join(', ')}`
        : `Similar company recently contacted by another BDR`;
      const notif = {
        id: `dup-${warn.id}`,
        type: 'warning' as const,
        title,
        message,
        timestamp: warn.createdAt.toISOString(),
        read: false,
        priority,
      };
      // If we have specific owners, create targeted notifications for them too
      notifications.push(notif);
      for (const oid of ownerIds) {
        notifications.push({ ...notif, id: `dup-${warn.id}-${oid}`, userId: oid });
      }
      // Also notify the user who triggered (so they see context)
      if (warn.triggeredBy?.id) notifications.push({ ...notif, id: `dup-${warn.id}-trigger`, userId: warn.triggeredBy.id });
    }

    // Finance channel: surface recent finance entries as notifications if requested
    if (!type || type === 'info' || channel === 'finance') {
      const recentFinance = await prisma.financeEntry.findMany({
        orderBy: { createdAt: 'desc' },
        take: 10,
      });
      for (const e of recentFinance) {
        notifications.push({
          id: `finance-${e.id}`,
          type: 'info' as const,
          title: 'New/Updated Finance Deal',
          message: `${e.company} â€¢ ${e.status}${e.gbpAmount ? ` â€¢ Â£${e.gbpAmount}` : ''}`,
          timestamp: (e.createdAt || new Date()).toISOString(),
          read: false,
          priority: 'medium' as const,
        });
      }
    }

    // Performance alerts
    const lowPerformingBdrs = await prisma.user.findMany({
      where: {
        role: 'BDR',
        isActive: true
      },
      include: {
        activityLogs: {
          where: {
            timestamp: {
              gte: startOfThisWeek,
              lte: endOfThisWeek
            },
            activityType: 'call'
          }
        }
      }
    });

    // Check for BDRs with low call volume
    lowPerformingBdrs.forEach(bdr => {
      const weeklyCalls = bdr.activityLogs.length;
      if (weeklyCalls < 20) {
        notifications.push({
          id: `low-calls-${bdr.id}`,
          type: 'warning' as const,
          title: 'Low Call Volume Alert',
          message: `${bdr.name} has only made ${weeklyCalls} calls this week (target: 50+)`,
          timestamp: new Date().toISOString(),
          read: false,
          priority: 'medium' as const,
          userId: bdr.id
        });
      }
    });

    // Check for missed daily targets
    const todayCalls = await prisma.activityLog.count({
      where: {
        activityType: 'call',
        timestamp: {
          gte: startOfDay(today),
          lte: endOfDay(today)
        }
      }
    });

    if (todayCalls < 100) {
      notifications.push({
        id: 'low-daily-calls',
        type: 'warning' as const,
        title: 'Daily Call Target Alert',
        message: `Only ${todayCalls} calls made today (target: 100+)`,
        timestamp: new Date().toISOString(),
        read: false,
        priority: 'high' as const
      });
    }

    // Check for high conversion rates (positive notification)
    const highConversionBdrs = await prisma.user.findMany({
      where: {
        role: 'BDR',
        isActive: true
      },
      include: {
        activityLogs: {
          where: {
            timestamp: {
              gte: startOfThisWeek,
              lte: endOfThisWeek
            },
            activityType: 'call'
          }
        },
        pipelineItems: {
          where: {
            agreementDate: {
              gte: startOfThisWeek,
              lte: endOfThisWeek
            }
          }
        }
      }
    });

    highConversionBdrs.forEach(bdr => {
      const calls = bdr.activityLogs.length;
      const agreements = bdr.pipelineItems.length;
      if (calls > 0 && agreements > 0) {
        const conversionRate = (agreements / calls) * 100;
        if (conversionRate > 20) {
          notifications.push({
            id: `high-conversion-${bdr.id}`,
            type: 'success' as const,
            title: 'High Conversion Rate',
            message: `${bdr.name} achieved ${conversionRate.toFixed(1)}% conversion rate this week!`,
            timestamp: new Date().toISOString(),
            read: false,
            priority: 'medium' as const,
            userId: bdr.id
          });
        }
      }
    });

    // System notifications
    notifications.push({
      id: 'system-maintenance',
      type: 'info' as const,
      title: 'System Maintenance',
      message: 'Scheduled maintenance on Sunday 2-4 AM EST',
      timestamp: new Date().toISOString(),
      read: false,
      priority: 'low' as const
    });

    // New feature notification
    notifications.push({
      id: 'new-feature',
      type: 'info' as const,
      title: 'New Advanced Reporting Features',
      message: 'Enhanced BDR performance comparison tools are now available',
      timestamp: new Date().toISOString(),
      read: false,
      priority: 'medium' as const
    });

    // Check for leads that need follow-up
    const leadsNeedingFollowUp = await prisma.lead.count({
      where: {
        status: 'contacted',
        addedDate: {
          lt: subDays(today, 3)
        }
      }
    });

    if (leadsNeedingFollowUp > 10) {
      notifications.push({
        id: 'follow-up-needed',
        type: 'warning' as const,
        title: 'Follow-up Required',
        message: `${leadsNeedingFollowUp} leads need follow-up contact`,
        timestamp: new Date().toISOString(),
        read: false,
        priority: 'high' as const
      });
    }

    // Check for pipeline items expiring soon
    const expiringPipelineItems = await prisma.pipelineItem.count({
      where: {
        expectedCloseDate: {
          gte: today,
          lte: new Date(today.getTime() + 7 * 24 * 60 * 60 * 1000) // Next 7 days
        },
        status: {
          not: 'closed'
        }
      }
    });

    if (expiringPipelineItems > 5) {
      notifications.push({
        id: 'expiring-pipeline',
        type: 'warning' as const,
        title: 'Pipeline Items Expiring',
        message: `${expiringPipelineItems} pipeline items are expected to close this week`,
        timestamp: new Date().toISOString(),
        read: false,
        priority: 'medium' as const
      });
    }

    // Filter notifications based on query parameters
    let filteredNotifications = notifications;

    if (userId) {
      filteredNotifications = filteredNotifications.filter(n => !n.userId || n.userId === userId);
    }

    if (type) {
      filteredNotifications = filteredNotifications.filter(n => n.type === type);
    }

    if (priority) {
      filteredNotifications = filteredNotifications.filter(n => n.priority === priority);
    }

    // Sort by priority and timestamp
    filteredNotifications.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 };
      const aPriority = priorityOrder[a.priority as keyof typeof priorityOrder] || 1;
      const bPriority = priorityOrder[b.priority as keyof typeof priorityOrder] || 1;
      
      if (aPriority !== bPriority) {
        return bPriority - aPriority;
      }
      
      return new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime();
    });

    return NextResponse.json(filteredNotifications);
  } catch (error) {
    console.error('Error fetching notifications:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      notifications: []
    }, { status: 500 });
  }
}

