import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfMonth, endOfMonth, subMonths } from 'date-fns';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const bdr = searchParams.get('bdr');
    const monthsParam = parseInt(searchParams.get('months') || '4', 10);

    const now = new Date();

    // Build where clause for activity logs
    const where: any = {
      activityType: 'Agreement_Sent',
    };
    if (bdr) {
      where.bdr = bdr;
    }

    // Fetch logs for last N months
    const oldestMonthStart = startOfMonth(subMonths(now, monthsParam - 1));
    where.timestamp = {
      gte: oldestMonthStart,
      lte: endOfMonth(now),
    };

    const logs = await prisma.activityLog.findMany({
      select: { timestamp: true, bdr: true },
      where,
    });

    // Monthly aggregation and trend
    const monthly: Array<{ month: string; count: number }> = [];
    for (let i = monthsParam - 1; i >= 0; i--) {
      const mStart = startOfMonth(subMonths(now, i));
      const mEnd = endOfMonth(subMonths(now, i));
      const count = logs.filter((l) => l.timestamp >= mStart && l.timestamp <= mEnd).length;
      monthly.push({ month: mStart.toISOString().substring(0, 7), count });
    }

    // BDR breakdown
    const bdrCounts: Record<string, number> = {};
    logs.forEach((l) => {
      if (!l.bdr) return;
      bdrCounts[l.bdr] = (bdrCounts[l.bdr] || 0) + 1;
    });

    return NextResponse.json({
      range: { months: monthsParam },
      monthly,
      byBdr: bdrCounts,
      generatedAt: now.toISOString(),
    });
  } catch (error) {
    console.error('Error fetching agreement tracking:', error);
    return NextResponse.json(
      { error: (error as Error).message, agreementTracking: null },
      { status: 500 },
    );
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfWeek, endOfWeek, startOfMonth, endOfMonth, subWeeks, subMonths, addWeeks, addMonths } from 'date-fns';

interface BDRAnalytics {
  bdr: string;
  totalItems: number;
  
  // Funnel Metrics
  callsScheduled: number;
  callsConducted: number;
  proposalsSent: number;
  agreementsSigned: number;
  
  // Conversion Rates
  callToProposalRate: number;
  proposalToAgreementRate: number;
  overallConversionRate: number;
  
  // Time-based Performance
  thisWeek: {
    calls: number;
    proposals: number;
    agreements: number;
  };
  thisMonth: {
    calls: number;
    proposals: number;
    agreements: number;
  };
  
  // Forward-looking Pipeline
  upcomingCalls: number;
  pendingProposals: number;
  activeListsOut: number;
  
  // Category Breakdown
  categoryDistribution: Array<{
    category: string;
    count: number;
    percentage: number;
  }>;
  
  // Performance Trends
  weeklyTrend: Array<{
    week: string;
    calls: number;
    proposals: number;
    agreements: number;
  }>;
}

export async function GET() {
  try {
    const now = new Date();
    
    // Date ranges
    const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
    const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });
    const thisMonthStart = startOfMonth(now);
    const thisMonthEnd = endOfMonth(now);
    const nextWeekEnd = endOfWeek(addWeeks(now, 1), { weekStartsOn: 1 });
    
    // Get all pipeline items with detailed information
    const pipelineItems = await prisma.pipelineItem.findMany({
      select: {
        id: true,
        bdr: true,
        category: true,
        status: true,
        callDate: true,
        lastUpdated: true,
        addedDate: true,
      },
    });

    // Get activity logs for more detailed tracking
    const activityLogs = await prisma.activityLog.findMany({
      select: {
        id: true,
        bdr: true,
        activityType: true,
        timestamp: true,
        pipelineItemId: true,
      },
    });

    // Group data by BDR
    const bdrData: { [key: string]: any[] } = {};
    const bdrActivities: { [key: string]: any[] } = {};
    
    pipelineItems.forEach(item => {
      if (item.bdr) {
        if (!bdrData[item.bdr]) bdrData[item.bdr] = [];
        bdrData[item.bdr].push(item);
      }
    });

    activityLogs.forEach(log => {
      if (log.bdr) {
        if (!bdrActivities[log.bdr]) bdrActivities[log.bdr] = [];
        bdrActivities[log.bdr].push(log);
      }
    });

    // Calculate analytics for each BDR
    const bdrAnalytics: BDRAnalytics[] = [];

    for (const [bdrName, items] of Object.entries(bdrData)) {
      const activities = bdrActivities[bdrName] || [];
      
      // Basic counts
      const totalItems = items.length;
      
      // Funnel analysis
      const callsItems = items.filter(item => item.category === 'Calls');
      const proposalItems = items.filter(item => 
        item.status && (item.status.includes('Proposal') || item.status.includes('proposal'))
      );
      const agreementItems = items.filter(item => 
        item.status && (item.status.includes('Agreement') || item.status.includes('agreement'))
      );
      
      const callsScheduled = callsItems.length;
      const callsConducted = callsItems.filter(item => 
        item.callDate && item.callDate <= now
      ).length;
      const proposalsSent = proposalItems.length;
      const agreementsSigned = agreementItems.length;
      
      // Conversion rates
      const callToProposalRate = callsConducted > 0 ? (proposalsSent / callsConducted) * 100 : 0;
      const proposalToAgreementRate = proposalsSent > 0 ? (agreementsSigned / proposalsSent) * 100 : 0;
      const overallConversionRate = callsConducted > 0 ? (agreementsSigned / callsConducted) * 100 : 0;
      
      // Time-based metrics
      const thisWeekCalls = callsItems.filter(item => 
        item.callDate && item.callDate >= thisWeekStart && item.callDate <= thisWeekEnd
      ).length;
      
      const thisWeekProposals = proposalItems.filter(item => 
        item.lastUpdated >= thisWeekStart && item.lastUpdated <= thisWeekEnd
      ).length;
      
      const thisWeekAgreements = agreementItems.filter(item => 
        item.lastUpdated >= thisWeekStart && item.lastUpdated <= thisWeekEnd
      ).length;
      
      const thisMonthCalls = callsItems.filter(item => 
        item.callDate && item.callDate >= thisMonthStart && item.callDate <= thisMonthEnd
      ).length;
      
      const thisMonthProposals = proposalItems.filter(item => 
        item.lastUpdated >= thisMonthStart && item.lastUpdated <= thisMonthEnd
      ).length;
      
      const thisMonthAgreements = agreementItems.filter(item => 
        item.lastUpdated >= thisMonthStart && item.lastUpdated <= thisMonthEnd
      ).length;
      
      // Forward-looking pipeline
      const upcomingCalls = callsItems.filter(item => 
        item.callDate && item.callDate > now
      ).length;
      
      const pendingProposals = proposalItems.filter(item => 
        !item.status.includes('Agreement') && !item.status.includes('Declined')
      ).length;
      
      const activeListsOut = items.filter(item => 
        item.category === 'Lists_Media_QA'
      ).length;
      
      // Category distribution
      const categoryCount: { [key: string]: number } = {};
      items.forEach(item => {
        if (item.category) {
          categoryCount[item.category] = (categoryCount[item.category] || 0) + 1;
        }
      });
      
      const categoryDistribution = Object.entries(categoryCount).map(([category, count]) => ({
        category,
        count,
        percentage: totalItems > 0 ? (count / totalItems) * 100 : 0
      }));
      
      // Weekly trend (last 4 weeks)
      const weeklyTrend = [];
      for (let i = 3; i >= 0; i--) {
        const weekStart = startOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
        const weekEnd = endOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
        
        const weekCalls = callsItems.filter(item => 
          item.callDate && item.callDate >= weekStart && item.callDate <= weekEnd
        ).length;
        
        const weekProposals = proposalItems.filter(item => 
          item.lastUpdated >= weekStart && item.lastUpdated <= weekEnd
        ).length;
        
        const weekAgreements = agreementItems.filter(item => 
          item.lastUpdated >= weekStart && item.lastUpdated <= weekEnd
        ).length;
        
        weeklyTrend.push({
          week: `Week ${4 - i}`,
          calls: weekCalls,
          proposals: weekProposals,
          agreements: weekAgreements
        });
      }
      
      bdrAnalytics.push({
        bdr: bdrName,
        totalItems,
        callsScheduled,
        callsConducted,
        proposalsSent,
        agreementsSigned,
        callToProposalRate: Math.round(callToProposalRate * 100) / 100,
        proposalToAgreementRate: Math.round(proposalToAgreementRate * 100) / 100,
        overallConversionRate: Math.round(overallConversionRate * 100) / 100,
        thisWeek: {
          calls: thisWeekCalls,
          proposals: thisWeekProposals,
          agreements: thisWeekAgreements
        },
        thisMonth: {
          calls: thisMonthCalls,
          proposals: thisMonthProposals,
          agreements: thisMonthAgreements
        },
        upcomingCalls,
        pendingProposals,
        activeListsOut,
        categoryDistribution,
        weeklyTrend
      });
    }
    
    // Sort by overall performance
    bdrAnalytics.sort((a, b) => b.overallConversionRate - a.overallConversionRate);
    
    // Calculate team averages
    const teamAverages = {
      avgConversionRate: bdrAnalytics.reduce((sum, bdr) => sum + bdr.overallConversionRate, 0) / bdrAnalytics.length,
      avgCallToProposalRate: bdrAnalytics.reduce((sum, bdr) => sum + bdr.callToProposalRate, 0) / bdrAnalytics.length,
      avgProposalToAgreementRate: bdrAnalytics.reduce((sum, bdr) => sum + bdr.proposalToAgreementRate, 0) / bdrAnalytics.length,
      totalUpcomingCalls: bdrAnalytics.reduce((sum, bdr) => sum + bdr.upcomingCalls, 0),
      totalPendingProposals: bdrAnalytics.reduce((sum, bdr) => sum + bdr.pendingProposals, 0),
      totalActiveListsOut: bdrAnalytics.reduce((sum, bdr) => sum + bdr.activeListsOut, 0)
    };

    return NextResponse.json({
      bdrAnalytics,
      teamAverages,
      generatedAt: now.toISOString()
    });
    
  } catch (error) {
    console.error('Error fetching analytics:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      bdrAnalytics: [],
      teamAverages: {}
    }, { status: 500 });
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfDay, endOfDay, parseISO } from 'date-fns';

export async function GET(request: Request, { params }: { params: Promise<{ bdr: string }> }) {
  try {
    const resolvedParams = await params;
    const { bdr } = resolvedParams;
    const { searchParams } = new URL(request.url);
    const startDate = searchParams.get('startDate');
    const endDate = searchParams.get('endDate');

    // Map BDR route param (name) to userId for relational field filtering
    const user = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
    if (!user) {
      return NextResponse.json([], { status: 200 });
    }

    const where: any = {
      bdrId: user.id,
    };

    if (startDate && endDate) {
      where.timestamp = {
        gte: startOfDay(parseISO(startDate)),
        lte: endOfDay(parseISO(endDate)),
      };
    }

    const bdrPerformance = await prisma.activityLog.groupBy({
      by: ['activityType'],
      _count: {
        _all: true,
      },
      where,
    });

    return NextResponse.json(bdrPerformance);
  } catch (error) {
    console.error('Error fetching BDR performance:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      bdrPerformance: null
    }, { status: 500 });
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfDay, endOfDay, parseISO, startOfWeek, endOfWeek } from 'date-fns';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const bdr = searchParams.get('bdr');
    const startDateParam = searchParams.get('startDate');
    const endDateParam = searchParams.get('endDate');

    // Default: current week range
    const now = new Date();
    const defaultStart = startOfDay(startOfWeek(now, { weekStartsOn: 1 }));
    const defaultEnd = endOfDay(endOfWeek(now, { weekStartsOn: 1 }));

    const startDate = startDateParam ? startOfDay(parseISO(startDateParam)) : defaultStart;
    const endDate = endDateParam ? endOfDay(parseISO(endDateParam)) : defaultEnd;

    const where: any = {
      activityType: 'Call_Completed',
      timestamp: {
        gte: startDate,
        lte: endDate,
      },
    };

    if (bdr) {
      where.bdr = bdr;
    }

    // Fetch relevant logs
    const logs = await prisma.activityLog.findMany({
      select: {
        timestamp: true,
        bdr: true,
      },
      where,
    });

    // Aggregate by BDR total
    const bdrTotals: Record<string, number> = {};
    logs.forEach((l) => {
      if (!l.bdr) return;
      bdrTotals[l.bdr] = (bdrTotals[l.bdr] || 0) + 1;
    });

    // Heatmap data â€“ 7 days Ã— 24h matrix (Mon-Sun)
    const heatmap = Array.from({ length: 7 }, () => Array(24).fill(0));
    logs.forEach((l) => {
      const d = new Date(l.timestamp);
      const dayIdx = (d.getDay() + 6) % 7; // Make Monday=0
      const hour = d.getHours();
      heatmap[dayIdx][hour] += 1;
    });

    return NextResponse.json({
      range: { startDate, endDate },
      totals: bdrTotals,
      heatmap,
      generatedAt: now.toISOString(),
    });
  } catch (error) {
    console.error('Error fetching call volume:', error);
    return NextResponse.json(
      { error: (error as Error).message, callVolume: null },
      { status: 500 },
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
// import { requireRole } from '@/lib/authorize';
import { prisma } from '@/lib/db';

export async function GET(req: NextRequest) {
  const { searchParams } = new URL(req.url);
  const bdr = searchParams.get('bdr');

  const pipelineWhere: any = {};
  const activityWhere: any = {};
  const financeWhere: any = {};
  if (bdr && bdr !== 'all') {
    pipelineWhere.bdr = bdr;
    activityWhere.bdr = bdr;
    financeWhere.bdr = bdr;
  }

  const [callsConducted, proposalsSent, agreementsSigned, listsSent, sales] = await Promise.all([
    prisma.activityLog.count({ where: { ...activityWhere, activityType: 'Call_Completed' } }),
    prisma.activityLog.count({ where: { ...activityWhere, activityType: 'Proposal_Sent' } }),
    prisma.activityLog.count({ where: { ...activityWhere, activityType: 'Agreement_Sent' } }),
    prisma.activityLog.count({ where: { ...activityWhere, activityType: 'Partner_List_Sent' } }),
    prisma.financeEntry.count({ where: { ...financeWhere } }), // Count all finance entries as sales
  ]);

  return NextResponse.json({
    callsConducted,
    proposalsSent,
    agreementsSigned,
    listsSent,
    sales,
  });
}

