    // Role-based filtering for activity logs - filter by bdrId (user relation)
    if (role === Role.BDR) {
      // BDRs can only see their own activity logs
      activityLogWhere.bdrId = userId;
    } else if ((role === Role.ADMIN || role === Role.DIRECTOR || role === Role.MANAGER) && bdr) {
      // Filter by specific BDR user
      const targetUser = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
      if (targetUser) {
        activityLogWhere.bdrId = targetUser.id;
      } else {
        activityLogWhere.bdrId = '___NO_MATCH___';
      }
    }

    const leadWhere: any = {};
    // Role-based filtering for leads - filter by bdrId (user relation)
    if (role === Role.BDR) {
      // BDRs can only see their own leads
      leadWhere.bdrId = userId;
    } else if ((role === Role.ADMIN || role === Role.DIRECTOR || role === Role.MANAGER) && bdr) {
      // Filter by specific BDR user (reuse targetUser from above)
      const targetUser = await prisma.user.findFirst({ where: { name: bdr }, select: { id: true } });
      if (targetUser) {
        leadWhere.bdrId = targetUser.id;
      } else {
        leadWhere.bdrId = '___NO_MATCH___';
      }
    }

    // Get comprehensive data with optimized queries
    const [
      pipelineItems,
      activityLogs,
      leads,
      allBDRs,
      kpiTargetsRaw,
      financeEntries
    ] = await Promise.all([
      prisma.pipelineItem.findMany({
        where: pipelineWhere,
        select: {
          id: true,
          name: true,
          bdr: { select: { name: true } },
          category: true,
          status: true,
          addedDate: true,
          lastUpdated: true,
          callDate: true,
          expectedCloseDate: true,
          partnerListSentDate: true,
          partnerListSize: true,
          firstSaleDate: true,
          totalSalesFromList: true,
          value: true,
          isSublist: true,
          parentId: true,
          notes: true,
        },
      }),
      prisma.activityLog.findMany({
        where: activityLogWhere,
        select: {
          id: true,
          bdr: { select: { name: true } },
          activityType: true,
          timestamp: true,
          pipelineItemId: true,
          // Include status transition fields so call completion detection works
          previousStatus: true,
          newStatus: true,
          previousCategory: true,
          newCategory: true,
          notes: true,
        },
        orderBy: { timestamp: 'desc' },
        take: 1000, // Limit to most recent 1000 for performance
      }),
      prisma.lead.findMany({
        where: leadWhere,
        select: {
          id: true,
          bdr: { select: { name: true } },
          status: true,
          addedDate: true,
        },
      }),
      prisma.user.findMany({
        where: { isActive: true },
        select: { name: true }
      }).then(u => u.map(x => x.name || '').filter(Boolean) as string[]),
      prisma.kpiTarget.findMany({
        select: { name: true, value: true }
      }),
      prisma.financeEntry.findMany({
        where: financeWhere,
        select: {
          id: true,
          bdr: { select: { name: true } },
          status: true,
          soldAmount: true,
          gbpAmount: true,
          invoiceDate: true,
          createdAt: true,
          month: true,
        },
        orderBy: { createdAt: 'desc' },
        take: 1000, // Limit to most recent 1000 for performance
      }),
    ]);

    const kpiTargets = kpiTargetsRaw.reduce((acc: { [key: string]: number }, target: { name: string, value: number }) => {
      acc[target.name] = target.value;
      return acc;
    }, {});

    // Calculate KPIs
    const kpis = calculateKPIs(
      pipelineItems.map(i => ({ ...i, bdr: i.bdr?.name || '' })),
      activityLogs.map(l => ({ ...l, bdr: l.bdr?.name || '' })),
      kpiTargets,
      now,
      financeEntries.map(f => ({ ...f, bdr: f.bdr?.name || '' }))
    );
    
    // Team Performance Analysis
    const teamPerformance = calculateTeamPerformance(
      pipelineItems.map(i => ({ ...i, bdr: { name: i.bdr?.name || '' } })),
      activityLogs.map(l => ({ ...l, bdr: { name: l.bdr?.name || '' } })),
      financeEntries.map(f => ({ ...f, bdr: { name: f.bdr?.name || '' } }))
    );
    
    // Pipeline Health Assessment
    const pipelineHealth = assessPipelineHealth(
      pipelineItems.map(i => ({ ...i, bdr: i.bdr?.name || '' })),
      activityLogs.map(l => ({ ...l, bdr: l.bdr?.name || '' })),
      now,
      financeEntries.map(f => ({ ...f, bdr: f.bdr?.name || '' }))
    );
    
    // Trend Analysis
    const trends = calculateTrends(
      pipelineItems.map(i => ({ ...i, bdr: i.bdr?.name || '' })),
      activityLogs.map(l => ({ ...l, bdr: l.bdr?.name || '' })),
      now,
      financeEntries.map(f => ({ ...f, bdr: f.bdr?.name || '' }))
    );
    
    // Critical Actions + add company conflict alert count as high-priority actions
    const criticalActions = identifyCriticalActions(pipelineItems, activityLogs, teamPerformance, now);

    // Count recent duplicate warnings about companies (last 14 days)
    const recentCompanyConflicts = await prisma.duplicateWarning.count({
      where: {
        createdAt: { gte: subDays(now, 14) },
        warningType: { in: ['COMPANY_NAME', 'COMPANY_DOMAIN'] as any },
      }
    });
    if (recentCompanyConflicts > 0) {
      criticalActions.unshift({
        priority: 'high',
        category: 'team',
        action: `${recentCompanyConflicts} recent company conflict alert(s) detected across team`,
        metric: recentCompanyConflicts,
        deadline: 'This week',
      });
    }
    
    // Financial Summary
    const financialSummary = calculateFinancialSummary(pipelineItems, activityLogs, now, financeEntries);
    
    // Predictive Insights
    const predictions = generatePredictiveInsights(pipelineItems, activityLogs, trends, now);

    const dashboard: ExecutiveDashboard = {
      kpis,
      teamPerformance,
      pipelineHealth,
      trends,
      criticalActions,
      financialSummary,
      predictions,
      bdrList: allBDRs
    };

    const respBody = {
      dashboard,
      generatedAt: now.toISOString(),
      period,
    };

    setCache(cacheKey, respBody);

    return new NextResponse(JSON.stringify(respBody), {
      status: 200,
      headers: {
        'Content-Type': 'application/json',
        'Cache-Control': 'private, max-age=60, stale-while-revalidate=120',
      },
    });
    
  } catch (error) {
    console.error('Error generating executive dashboard:', error);
    return new NextResponse(JSON.stringify({ 
      error: (error as Error).message,
      dashboard: null
    }), { status: 500, headers: { 'Content-Type': 'application/json' } });
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { 
  startOfWeek, endOfWeek, startOfMonth, endOfMonth, 
  subWeeks, subMonths, addMonths, format, differenceInDays 
} from 'date-fns';

interface ExecutiveSummary {
  // High-level KPIs
  kpis: {
    totalAgreements: number;
    monthlyGrowthRate: number;
    averageConversionRate: number;
    pipelineHealth: 'Excellent' | 'Good' | 'Fair' | 'Poor';
    forecastAccuracy: number;
  };

  // Trends and Forecasts
  trends: {
    activityProjection: Array<{
      month: string;
      projected: number;
      actual: number;
    }>;
    activityTrends: Array<{
      week: string;
      calls: number;
      proposals: number;
      agreements: number;
      trend: 'up' | 'down' | 'stable';
    }>;
  };

  // Team Performance
  teamInsights: {
    topPerformer: {
      name: string;
      conversionRate: number;
      agreements: number;
    };
    underPerformer: {
      name: string;
      conversionRate: number;
      recommendations: string[];
    };
    teamAverage: {
      callsPerWeek: number;
      proposalsPerWeek: number;
      agreementsPerWeek: number;
      conversionRate: number;
    };
  };

  // Risk Analysis
  riskFactors: Array<{
    type: 'High' | 'Medium' | 'Low';
    description: string;
    impact: string;
    recommendation: string;
  }>;

  // Opportunities
  opportunities: Array<{
    category: string;
    description: string;
    potentialValue: number;
    timeline: string;
  }>;

  // Action Items
  actionItems: Array<{
    priority: 'High' | 'Medium' | 'Low';
    item: string;
    owner: string;
    deadline: string;
  }>;
}

export async function GET() {
  try {
    const now = new Date();
    const thisMonthStart = startOfMonth(now);
    const thisMonthEnd = endOfMonth(now);
    const lastMonthStart = startOfMonth(subMonths(now, 1));
    const lastMonthEnd = endOfMonth(subMonths(now, 1));
    const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
    const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });

    // Get all data
    const allPipelineItems = await prisma.pipelineItem.findMany({
      select: {
        id: true,
        bdr: true,
        category: true,
        status: true,
        callDate: true,
        lastUpdated: true,
        addedDate: true,
      },
    });

    // Agreement calculations
    const thisMonthAgreements = allPipelineItems.filter(item => 
      item.status?.includes('Agreement') && 
      item.lastUpdated >= thisMonthStart && 
      item.lastUpdated <= thisMonthEnd
    ).length;

    const lastMonthAgreements = allPipelineItems.filter(item => 
      item.status?.includes('Agreement') && 
      item.lastUpdated >= lastMonthStart && 
      item.lastUpdated <= lastMonthEnd
    ).length;

    const monthlyGrowthRate = lastMonthAgreements > 0 
      ? ((thisMonthAgreements - lastMonthAgreements) / lastMonthAgreements) * 100 
      : 0;

    // Conversion rate calculations
    const totalCalls = allPipelineItems.filter(item => item.category === 'Calls').length;
    const totalAgreements = allPipelineItems.filter(item => item.status?.includes('Agreement')).length;
    const averageConversionRate = totalCalls > 0 ? (totalAgreements / totalCalls) * 100 : 0;

    // Pipeline health assessment
    const activePipeline = allPipelineItems.filter(item => 
      !item.status?.includes('Agreement') && !item.status?.includes('Declined')
    ).length;

    let pipelineHealth: 'Excellent' | 'Good' | 'Fair' | 'Poor' = 'Poor';
    if (activePipeline > 50) pipelineHealth = 'Excellent';
    else if (activePipeline > 25) pipelineHealth = 'Good';
    else if (activePipeline > 10) pipelineHealth = 'Fair';

    // Team performance analysis
    const bdrPerformance: { [key: string]: any } = {};
    
    allPipelineItems.forEach(item => {
      if (!item.bdr) return;
      
      if (!bdrPerformance[item.bdr]) {
        bdrPerformance[item.bdr] = {
          calls: 0,
          proposals: 0,
          agreements: 0,
        };
      }
      
      if (item.category === 'Calls') bdrPerformance[item.bdr].calls++;
      if (item.status?.includes('Proposal')) bdrPerformance[item.bdr].proposals++;
      if (item.status?.includes('Agreement')) {
        bdrPerformance[item.bdr].agreements++;
      }
    });

    // Calculate conversion rates for each BDR
    Object.keys(bdrPerformance).forEach(bdr => {
      const perf = bdrPerformance[bdr];
      perf.conversionRate = perf.calls > 0 ? (perf.agreements / perf.calls) * 100 : 0;
    });

    // Find top and underperformer
    const bdrList = Object.entries(bdrPerformance).map(([name, data]: [string, any]) => ({
      name,
      ...data,
    }));

    const topPerformer = bdrList.reduce((best, current) => 
      current.conversionRate > best.conversionRate ? current : best
    );

    const underPerformer = bdrList.reduce((worst, current) => 
      current.conversionRate < worst.conversionRate ? current : worst
    );

    // Team averages
    const teamAverage = {
      callsPerWeek: Math.round(totalCalls / Object.keys(bdrPerformance).length / 4), // Assuming 4 weeks
      proposalsPerWeek: Math.round(bdrList.reduce((sum, bdr) => sum + bdr.proposals, 0) / Object.keys(bdrPerformance).length / 4),
      agreementsPerWeek: Math.round(totalAgreements / Object.keys(bdrPerformance).length / 4),
      conversionRate: averageConversionRate,
    };

    // Risk analysis
    const riskFactors = [];
    
    if (averageConversionRate < 10) {
      riskFactors.push({
        type: 'High' as const,
        description: 'Low overall conversion rate',
        impact: 'Reduced agreements and missed targets',
        recommendation: 'Implement sales training and review call scripts',
      });
    }

    if (activePipeline < 10) {
      riskFactors.push({
        type: 'High' as const,
        description: 'Low pipeline activity',
        impact: 'Future agreement shortfall',
        recommendation: 'Increase prospecting activities and lead generation',
      });
    }

    if (monthlyGrowthRate < 0) {
      riskFactors.push({
        type: 'Medium' as const,
        description: 'Negative month-over-month growth',
        impact: 'Agreement decline trend',
        recommendation: 'Analyze lost deals and improve conversion processes',
      });
    }

    // Opportunities
    const opportunities = [];
    
    if (topPerformer.conversionRate > averageConversionRate * 1.5) {
      opportunities.push({
        category: 'Training',
        description: `Replicate ${topPerformer.name}'s successful strategies across the team`,
        potentialValue: (topPerformer.conversionRate - averageConversionRate) * totalCalls * 100, // Estimated value
        timeline: '2-4 weeks',
      });
    }

    const upcomingCalls = allPipelineItems.filter(item => 
      item.category === 'Calls' && item.callDate && item.callDate > now
    ).length;

    if (upcomingCalls > 20) {
      opportunities.push({
        category: 'Agreements',
        description: 'High volume of upcoming calls presents conversion opportunities',
        potentialValue: upcomingCalls * averageConversionRate * 50, // Estimated deal size
        timeline: '1-2 weeks',
      });
    }

    // Action items
    const actionItems = [];
    
    if (underPerformer.conversionRate < averageConversionRate * 0.5) {
      actionItems.push({
        priority: 'High' as const,
        item: `Provide additional coaching to ${underPerformer.name}`,
        owner: 'Sales Manager',
        deadline: format(addMonths(now, 0), 'MMM dd'),
      });
    }

    if (pipelineHealth === 'Poor') {
      actionItems.push({
        priority: 'High' as const,
        item: 'Increase lead generation activities',
        owner: 'Marketing Team',
        deadline: format(addMonths(now, 0), 'MMM dd'),
      });
    }

    actionItems.push({
      priority: 'Medium' as const,
      item: 'Review and optimize call scripts',
      owner: 'Sales Team',
      deadline: format(addMonths(now, 1), 'MMM dd'),
    });

    // Generate trends data (simplified)
    const activityProjection = [];
    for (let i = -2; i <= 3; i++) {
      const month = addMonths(now, i);
      const monthAgreements = i <= 0 ? 
        allPipelineItems
          .filter(item => 
            item.status?.includes('Agreement') && 
            item.lastUpdated >= startOfMonth(month) && 
            item.lastUpdated <= endOfMonth(month)
          ).length :
        thisMonthAgreements * (1 + (monthlyGrowthRate / 100)) ** i; // Projected
      
      activityProjection.push({
        month: format(month, 'MMM yyyy'),
        projected: i > 0 ? monthAgreements : 0,
        actual: i <= 0 ? monthAgreements : 0,
      });
    }

    // Activity trends (last 4 weeks)
    const activityTrends = [];
    for (let i = 3; i >= 0; i--) {
      const weekStart = startOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
      const weekEnd = endOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
      
      const weekCalls = allPipelineItems.filter(item => 
        item.category === 'Calls' && 
        item.callDate && item.callDate >= weekStart && item.callDate <= weekEnd
      ).length;
      
      const weekProposals = allPipelineItems.filter(item => 
        item.status?.includes('Proposal') && 
        item.lastUpdated >= weekStart && item.lastUpdated <= weekEnd
      ).length;
      
      const weekAgreements = allPipelineItems.filter(item => 
        item.status?.includes('Agreement') && 
        item.lastUpdated >= weekStart && item.lastUpdated <= weekEnd
      ).length;
      
      activityTrends.push({
        week: `Week ${4 - i}`,
        calls: weekCalls,
        proposals: weekProposals,
        agreements: weekAgreements,
        trend: 'stable' as const, // Simplified
      });
    }

    const executiveSummary: ExecutiveSummary = {
      kpis: {
        totalAgreements: thisMonthAgreements,
        monthlyGrowthRate: Math.round(monthlyGrowthRate * 100) / 100,
        averageConversionRate: Math.round(averageConversionRate * 100) / 100,
        pipelineHealth,
        forecastAccuracy: 85, // Placeholder
      },
      trends: {
        activityProjection,
        activityTrends,
      },
      teamInsights: {
        topPerformer: {
          name: topPerformer.name,
          conversionRate: Math.round(topPerformer.conversionRate * 100) / 100,
          agreements: topPerformer.agreements,
        },
        underPerformer: {
          name: underPerformer.name,
          conversionRate: Math.round(underPerformer.conversionRate * 100) / 100,
          recommendations: [
            'Schedule 1-on-1 coaching sessions',
            'Shadow top performers on calls',
            'Review and practice objection handling',
          ],
        },
        teamAverage,
      },
      riskFactors,
      opportunities,
      actionItems,
    };

    return NextResponse.json({
      summary: executiveSummary,
      generatedAt: now.toISOString(),
    });

  } catch (error) {
    console.error('Error generating executive summary:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      summary: null,
    }, { status: 500 });
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { startOfMonth, endOfMonth, subMonths } from 'date-fns';

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const bdr = searchParams.get('bdr');
    const monthsParam = parseInt(searchParams.get('months') || '4', 10);

    const now = new Date();

    // Time filter
    const oldestMonthStart = startOfMonth(subMonths(now, monthsParam - 1));

    // Fetch partner list sent logs
    const logWhere: any = {
      activityType: 'Partner_List_Sent',
      timestamp: { gte: oldestMonthStart, lte: endOfMonth(now) },
    };
    if (bdr) logWhere.bdr = bdr;
    const listLogs = await prisma.activityLog.findMany({ select: { timestamp: true, bdr: true, pipelineItemId: true }, where: logWhere });

    // Fetch pipeline items linked to these lists for size & conversion
    const pipelineIds = listLogs.map((l) => l.pipelineItemId).filter(Boolean);
    const items = await prisma.pipelineItem.findMany({
      where: { id: { in: pipelineIds as number[] } },
      select: { id: true, partnerListSize: true, status: true, totalSalesFromList: true, bdr: true, partnerListSentDate: true },
    });

    // Monthly counts
    const monthly: Array<{ month: string; lists: number; avgSize: number }> = [];
    for (let i = monthsParam - 1; i >= 0; i--) {
      const mStart = startOfMonth(subMonths(now, i));
      const mEnd = endOfMonth(subMonths(now, i));
      const monthItems = items.filter((it) => it.partnerListSentDate && it.partnerListSentDate >= mStart && it.partnerListSentDate <= mEnd);
      const lists = monthItems.length;
      const avgSize = lists > 0 ? Math.round(monthItems.reduce((s, it) => s + (it.partnerListSize || 0), 0) / lists) : 0;
      monthly.push({ month: mStart.toISOString().substring(0, 7), lists, avgSize });
    }

    // Overall stats
    const totalLists = items.length;
    const averageSize = totalLists > 0 ? Math.round(items.reduce((s, it) => s + (it.partnerListSize || 0), 0) / totalLists) : 0;
    const soldLists = items.filter((it) => it.status === 'Sold');
    const conversionRate = totalLists > 0 ? Math.round((soldLists.length / totalLists) * 100 * 100) / 100 : 0;

    return NextResponse.json({
      range: { months: monthsParam },
      monthly,
      overall: { totalLists, averageSize, conversionRate },
      generatedAt: now.toISOString(),
    });
  } catch (error) {
    console.error('Error fetching lists out:', error);
    return NextResponse.json(
      { error: (error as Error).message, listsOut: null },
      { status: 500 },
    );
  }
}

