  teamPerformanceBySize: {
    small: { count: number; conversionRate: number; avgRevenue: number; };
    medium: { count: number; conversionRate: number; avgRevenue: number; };
    large: { count: number; conversionRate: number; avgRevenue: number; };
  };
}

export async function GET(request: Request) {
  try {
    const { searchParams } = new URL(request.url);
    const bdrFilter = searchParams.get('bdr');
    const minListSize = searchParams.get('minListSize') ? parseInt(searchParams.get('minListSize')!) : 0;
    const maxListSize = searchParams.get('maxListSize') ? parseInt(searchParams.get('maxListSize')!) : 999;
    const includeConversionRates = searchParams.get('includeConversionRates') !== 'false';
    const groupBySize = searchParams.get('groupBySize') === 'true';
    
    const now = new Date();
    
    // Date ranges
    const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
    const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });
    const thisMonthStart = startOfMonth(now);
    const thisMonthEnd = endOfMonth(now);
    
    // Get main pipeline items (parent lists)
    const mainListItems = await prisma.pipelineItem.findMany({
      where: {
        ...(bdrFilter ? { bdr: bdrFilter } : {}),
        category: 'Lists_Media_QA',
        isSublist: true, // This indicates it's a parent list container
        partnerListSize: {
          gte: minListSize,
          lte: maxListSize
        }
      },
      select: {
        id: true,
        name: true,
        company: true,
        bdr: true,
        status: true,
        lastUpdated: true,
        addedDate: true,
        partnerListSentDate: true,
        partnerListSize: true,
        firstSaleDate: true,
        totalSalesFromList: true,
        value: true,
        notes: true,
        sublistName: true,
      },
    });

    // Get individual partner contacts within sublists
    const partnerContacts = await prisma.pipelineItem.findMany({
      where: {
        category: 'Partner_Contacts',
        parentId: { in: mainListItems.map(item => item.id) }
      },
      select: {
        id: true,
        name: true,
        company: true,
        bdr: true,
        status: true,
        lastUpdated: true,
        addedDate: true,
        value: true,
        parentId: true,
      },
    });

    // Get activity logs for partner list tracking
    const activityLogs = await prisma.activityLog.findMany({
      where: {
        ...(bdrFilter ? { bdr: bdrFilter } : {}),
        activityType: {
          in: ['Partner_List_Sent', 'Partner_Added', 'Sale_Recorded', 'Status_Change']
        },
        pipelineItemId: { 
          in: [...mainListItems.map(item => item.id), ...partnerContacts.map(contact => contact.id)]
        }
      },
      select: {
        id: true,
        bdr: true,
        activityType: true,
        timestamp: true,
        pipelineItemId: true,
        description: true,
        newStatus: true,
      },
      orderBy: {
        timestamp: 'desc'
      }
    });

    // Group data by BDR
    const bdrData: { [key: string]: any[] } = {};
    const bdrPartnerContacts: { [key: string]: any[] } = {};
    const bdrActivities: { [key: string]: any[] } = {};
    
    mainListItems.forEach(item => {
      if (item.bdr) {
        if (!bdrData[item.bdr]) bdrData[item.bdr] = [];
        bdrData[item.bdr].push(item);
      }
    });

    partnerContacts.forEach(contact => {
      if (contact.bdr) {
        if (!bdrPartnerContacts[contact.bdr]) bdrPartnerContacts[contact.bdr] = [];
        bdrPartnerContacts[contact.bdr].push(contact);
      }
    });

    activityLogs.forEach(log => {
      if (log.bdr) {
        if (!bdrActivities[log.bdr]) bdrActivities[log.bdr] = [];
        bdrActivities[log.bdr].push(log);
      }
    });

    // Calculate partner list analytics for each BDR
    const bdrPartnerListAnalytics: PartnerListAnalytics[] = [];

    for (const [bdrName, lists] of Object.entries(bdrData)) {
      const partnerContactsForBdr = bdrPartnerContacts[bdrName] || [];
      const activities = bdrActivities[bdrName] || [];
      
      // Basic list metrics
      const totalListsSent = lists.filter(list => list.partnerListSentDate).length;
      const listsThisWeek = lists.filter(list => 
        list.partnerListSentDate && 
        list.partnerListSentDate >= thisWeekStart && 
        list.partnerListSentDate <= thisWeekEnd
      ).length;
      const listsThisMonth = lists.filter(list => 
        list.partnerListSentDate && 
        list.partnerListSentDate >= thisMonthStart && 
        list.partnerListSentDate <= thisMonthEnd
      ).length;
      
      const activeListsOut = lists.filter(list => 
        list.partnerListSentDate && 
        !['Sold', 'List Out - Not Sold', 'Free Q&A Offered'].includes(list.status)
      ).length;
      
      // List size analysis
      const listsWithSizes = lists.filter(list => list.partnerListSize && list.partnerListSize > 0);
      const smallLists = listsWithSizes.filter(list => list.partnerListSize! >= 3 && list.partnerListSize! <= 8).length;
      const mediumLists = listsWithSizes.filter(list => list.partnerListSize! >= 9 && list.partnerListSize! <= 15).length;
      const largeLists = listsWithSizes.filter(list => list.partnerListSize! >= 16).length;
      
      const averageListSize = listsWithSizes.length > 0 ? 
        listsWithSizes.reduce((sum, list) => sum + (list.partnerListSize || 0), 0) / listsWithSizes.length : 0;
      const largestList = listsWithSizes.length > 0 ? 
        Math.max(...listsWithSizes.map(list => list.partnerListSize || 0)) : 0;
      const smallestList = listsWithSizes.length > 0 ? 
        Math.min(...listsWithSizes.map(list => list.partnerListSize || 0)) : 0;
      
      // Conversion rates by list size
      const smallListConversionRate = calculateConversionRateBySize(lists, 3, 8);
      const mediumListConversionRate = calculateConversionRateBySize(lists, 9, 15);
      const largeListConversionRate = calculateConversionRateBySize(lists, 16, 999);
      const overallListConversionRate = calculateOverallConversionRate(lists);
      
      // Revenue analysis
      const { totalRevenue, averageRevenuePerList, averageRevenuePerPartner, revenueBySize } = 
        calculateRevenueMetrics(lists, partnerContactsForBdr);
      
      // Response and engagement metrics
      const { partnerResponseRate, partnerInterestRate, timeToFirstResponse, timeToFirstSale } = 
        calculateEngagementMetrics(lists, partnerContactsForBdr, activities);
      
      // Active partner lists
      const activePartnerLists = lists
        .filter(list => 
          list.partnerListSentDate && 
          !['Sold', 'List Out - Not Sold', 'Free Q&A Offered'].includes(list.status)
        )
        .map(list => {
          const partnersForList = partnerContactsForBdr.filter(contact => contact.parentId === list.id);
          const responsesReceived = partnersForList.filter(contact => 
            contact.status && !['Contacted', 'Not Responsive'].includes(contact.status)
          ).length;
          const salesCount = partnersForList.filter(contact => contact.status === 'Sold').length;
          
          return {
            id: list.id,
            mainLeadName: list.name,
            company: list.company || '',
            listSentDate: format(list.partnerListSentDate!, 'dd/MM/yy'),
            partnerCount: list.partnerListSize || 0,
            responsesReceived,
            salesCount,
            status: list.status,
            daysActive: differenceInDays(now, list.partnerListSentDate!)
          };
        })
        .sort((a, b) => b.daysActive - a.daysActive);
      
      // Recent performance (last 60 days)
      const sixtyDaysAgo = subDays(now, 60);
      const recentListPerformance = lists
        .filter(list => 
          list.partnerListSentDate && 
          list.partnerListSentDate >= sixtyDaysAgo
        )
        .map(list => {
          const partnersForList = partnerContactsForBdr.filter(contact => contact.parentId === list.id);
          const salesCount = partnersForList.filter(contact => contact.status === 'Sold').length;
          const totalRevenue = partnersForList.reduce((sum, contact) => sum + (contact.value || 0), 0);
          const conversionRate = list.partnerListSize! > 0 ? (salesCount / list.partnerListSize!) * 100 : 0;
          
          return {
            id: list.id,
            mainLeadName: list.name,
            company: list.company || '',
            listSentDate: format(list.partnerListSentDate!, 'dd/MM/yy'),
            partnerCount: list.partnerListSize || 0,
            salesCount,
            totalRevenue,
            conversionRate: Math.round(conversionRate * 100) / 100,
            status: list.status
          };
        })
        .sort((a, b) => new Date(b.listSentDate).getTime() - new Date(a.listSentDate).getTime());
      
      // Weekly trend analysis
      const weeklyListTrend = [];
      for (let i = 3; i >= 0; i--) {
        const weekStart = startOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
        const weekEnd = endOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
        
        const weekLists = lists.filter(list => 
          list.partnerListSentDate && 
          list.partnerListSentDate >= weekStart && 
          list.partnerListSentDate <= weekEnd
        );
        
        const weekListsSent = weekLists.length;
        const weekAverageListSize = weekLists.length > 0 ? 
          weekLists.reduce((sum, list) => sum + (list.partnerListSize || 0), 0) / weekLists.length : 0;
        
        const weekConversions = weekLists.filter(list => 
          list.firstSaleDate && list.firstSaleDate >= weekStart && list.firstSaleDate <= weekEnd
        ).length;
        
        const weekRevenue = weekLists.reduce((sum, list) => sum + (list.totalSalesFromList || 0), 0);
        
        weeklyListTrend.push({
          weekStart: format(weekStart, 'MMM dd'),
          listsSent: weekListsSent,
          averageListSize: Math.round(weekAverageListSize * 10) / 10,
          conversions: weekConversions,
          revenue: weekRevenue
        });
      }
      
      // Optimization insights
      const optimalListSize = calculateOptimalListSize(lists);
      const bestPerformingListSize = determineBestPerformingSize(smallListConversionRate, mediumListConversionRate, largeListConversionRate);
      const underperformingLists = calculateUnderperformingLists(lists, now);
      const listSizeRecommendation = generateListSizeRecommendation(
        averageListSize, 
        smallListConversionRate, 
        mediumListConversionRate, 
        largeListConversionRate
      );
      
      bdrPartnerListAnalytics.push({
        bdr: bdrName,
        totalListsSent,
        listsThisWeek,
        listsThisMonth,
        activeListsOut,
        smallLists,
        mediumLists,
        largeLists,
        averageListSize: Math.round(averageListSize * 10) / 10,
        largestList,
        smallestList,
        smallListConversionRate: Math.round(smallListConversionRate * 100) / 100,
        mediumListConversionRate: Math.round(mediumListConversionRate * 100) / 100,
        largeListConversionRate: Math.round(largeListConversionRate * 100) / 100,
        overallListConversionRate: Math.round(overallListConversionRate * 100) / 100,
        totalRevenueFromLists: totalRevenue,
        averageRevenuePerList: Math.round(averageRevenuePerList),
        averageRevenuePerPartner: Math.round(averageRevenuePerPartner),
        revenueByListSize: revenueBySize,
        partnerResponseRate: Math.round(partnerResponseRate * 100) / 100,
        partnerInterestRate: Math.round(partnerInterestRate * 100) / 100,
        timeToFirstResponse: Math.round(timeToFirstResponse * 10) / 10,
        timeToFirstSale: Math.round(timeToFirstSale * 10) / 10,
        activePartnerLists,
        recentListPerformance,
        weeklyListTrend,
        optimalListSize,
        bestPerformingListSize,
        underperformingLists,
        listSizeRecommendation
      });
    }
    
    // Sort by overall performance
    bdrPartnerListAnalytics.sort((a, b) => {
      const aScore = a.overallListConversionRate * a.totalListsSent + a.totalRevenueFromLists / 1000;
      const bScore = b.overallListConversionRate * b.totalListsSent + b.totalRevenueFromLists / 1000;
      return bScore - aScore;
    });
    
    // Calculate team summary
    const teamSummary: TeamPartnerListSummary = {
      totalActiveListsOut: bdrPartnerListAnalytics.reduce((sum, bdr) => sum + bdr.activeListsOut, 0),
      totalListsSentThisMonth: bdrPartnerListAnalytics.reduce((sum, bdr) => sum + bdr.listsThisMonth, 0),
      teamAverageListSize: bdrPartnerListAnalytics.reduce((sum, bdr) => sum + bdr.averageListSize, 0) / bdrPartnerListAnalytics.length,
      teamOverallConversionRate: bdrPartnerListAnalytics.reduce((sum, bdr) => sum + bdr.overallListConversionRate, 0) / bdrPartnerListAnalytics.length,
      teamTotalRevenue: bdrPartnerListAnalytics.reduce((sum, bdr) => sum + bdr.totalRevenueFromLists, 0),
      bestPerformingBDR: bdrPartnerListAnalytics[0]?.bdr || '',
      listSizeTrend: assessListSizeTrend(bdrPartnerListAnalytics),
      conversionTrend: assessConversionTrend(bdrPartnerListAnalytics),
      insights: generateTeamInsights(bdrPartnerListAnalytics),
      teamPerformanceBySize: calculateTeamPerformanceBySize(bdrPartnerListAnalytics)
    };

    return NextResponse.json({
      bdrPartnerListAnalytics,
      teamSummary,
      generatedAt: now.toISOString(),
      filters: {
        bdrFilter,
        minListSize,
        maxListSize,
        includeConversionRates,
        groupBySize
      }
    });
    
  } catch (error) {
    console.error('Error fetching partner list analytics:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      bdrPartnerListAnalytics: [],
      teamSummary: null
    }, { status: 500 });
  }
}

// Helper functions
function calculateConversionRateBySize(lists: any[], minSize: number, maxSize: number): number {
  const filteredLists = lists.filter(list => 
    list.partnerListSize && 
    list.partnerListSize >= minSize && 
    list.partnerListSize <= maxSize &&
    list.partnerListSentDate
  );
  
  if (filteredLists.length === 0) return 0;
  
  const soldLists = filteredLists.filter(list => 
    list.firstSaleDate || list.status === 'Sold'
  );
  
  return (soldLists.length / filteredLists.length) * 100;
}

function calculateOverallConversionRate(lists: any[]): number {
  const sentLists = lists.filter(list => list.partnerListSentDate);
  if (sentLists.length === 0) return 0;
  
  const soldLists = sentLists.filter(list => 
    list.firstSaleDate || list.status === 'Sold'
  );
  
  return (soldLists.length / sentLists.length) * 100;
}

function calculateRevenueMetrics(lists: any[], partnerContacts: any[]) {
  const totalRevenue = partnerContacts.reduce((sum, contact) => 
    sum + (contact.status === 'Sold' ? (contact.value || 0) : 0), 0
  );
  
  const listsWithRevenue = lists.filter(list => list.partnerListSentDate);
  const averageRevenuePerList = listsWithRevenue.length > 0 ? totalRevenue / listsWithRevenue.length : 0;
  
  const totalPartners = partnerContacts.length;
  const averageRevenuePerPartner = totalPartners > 0 ? totalRevenue / totalPartners : 0;
  
  // Revenue by list size
  const revenueBySize = {
    small: 0,
    medium: 0,
    large: 0
  };
  
  lists.forEach(list => {
    if (!list.partnerListSize || !list.partnerListSentDate) return;
    
    const listRevenue = partnerContacts
      .filter(contact => contact.parentId === list.id && contact.status === 'Sold')
      .reduce((sum, contact) => sum + (contact.value || 0), 0);
    
    if (list.partnerListSize >= 3 && list.partnerListSize <= 8) {
      revenueBySize.small += listRevenue;
    } else if (list.partnerListSize >= 9 && list.partnerListSize <= 15) {
      revenueBySize.medium += listRevenue;
    } else if (list.partnerListSize >= 16) {
      revenueBySize.large += listRevenue;
    }
  });
  
  return {
    totalRevenue,
    averageRevenuePerList,
    averageRevenuePerPartner,
    revenueBySize
  };
}

function calculateEngagementMetrics(lists: any[], partnerContacts: any[], activities: any[]) {
  const totalPartners = partnerContacts.length;
  
  if (totalPartners === 0) {
    return {
      partnerResponseRate: 0,
      partnerInterestRate: 0,
      timeToFirstResponse: 0,
      timeToFirstSale: 0
    };
  }
  
  const respondedPartners = partnerContacts.filter(contact => 
    contact.status && !['Contacted', 'Not Responsive'].includes(contact.status)
  );
  
  const interestedPartners = partnerContacts.filter(contact => 
    contact.status && ['Interested', 'Sold'].includes(contact.status)
  );
  
  const partnerResponseRate = (respondedPartners.length / totalPartners) * 100;
  const partnerInterestRate = (interestedPartners.length / totalPartners) * 100;
  
  // Calculate average time to first response
  let totalResponseTime = 0;
  let responseCount = 0;
  
  respondedPartners.forEach(contact => {
    if (contact.addedDate && contact.lastUpdated && contact.lastUpdated > contact.addedDate) {
      totalResponseTime += differenceInDays(contact.lastUpdated, contact.addedDate);
      responseCount++;
    }
  });
  
  const timeToFirstResponse = responseCount > 0 ? totalResponseTime / responseCount : 0;
  
  // Calculate average time to first sale
  const soldPartners = partnerContacts.filter(contact => contact.status === 'Sold');
  let totalSaleTime = 0;
  let saleCount = 0;
  
  soldPartners.forEach(contact => {
    if (contact.addedDate && contact.lastUpdated) {
      totalSaleTime += differenceInDays(contact.lastUpdated, contact.addedDate);
      saleCount++;
    }
  });
  
  const timeToFirstSale = saleCount > 0 ? totalSaleTime / saleCount : 0;
  
  return {
    partnerResponseRate,
    partnerInterestRate,
    timeToFirstResponse,
    timeToFirstSale
  };
}

function calculateOptimalListSize(lists: any[]): number {
  // Analyze which list size performs best
  const sizeGroups = {
    small: { count: 0, sales: 0 },
    medium: { count: 0, sales: 0 },
    large: { count: 0, sales: 0 }
  };
  
  lists.forEach(list => {
    if (!list.partnerListSize || !list.partnerListSentDate) return;
    
    const hasSale = list.firstSaleDate || list.status === 'Sold';
    
    if (list.partnerListSize >= 3 && list.partnerListSize <= 8) {
      sizeGroups.small.count++;
      if (hasSale) sizeGroups.small.sales++;
    } else if (list.partnerListSize >= 9 && list.partnerListSize <= 15) {
      sizeGroups.medium.count++;
      if (hasSale) sizeGroups.medium.sales++;
    } else if (list.partnerListSize >= 16) {
      sizeGroups.large.count++;
      if (hasSale) sizeGroups.large.sales++;
    }
  });
  
  let bestConversion = 0;
  let optimalSize = 10; // Default
  
  Object.entries(sizeGroups).forEach(([size, data]) => {
    const conversion = data.count > 0 ? data.sales / data.count : 0;
    if (conversion > bestConversion) {
      bestConversion = conversion;
      optimalSize = size === 'small' ? 6 : size === 'medium' ? 12 : 18;
    }
  });
  
  return optimalSize;
}

function determineBestPerformingSize(smallRate: number, mediumRate: number, largeRate: number): string {
  const rates = { small: smallRate, medium: mediumRate, large: largeRate };
  const best = Object.entries(rates).reduce((a, b) => rates[a[0]] > rates[b[0]] ? a : b);
  
  const sizeDescriptions = {
    small: 'Small Lists (3-8 partners)',
    medium: 'Medium Lists (9-15 partners)',
    large: 'Large Lists (16+ partners)'
  };
  
  return sizeDescriptions[best[0] as keyof typeof sizeDescriptions];
}

function calculateUnderperformingLists(lists: any[], currentDate: Date): number {
  const twoWeeksAgo = subDays(currentDate, 14);
  
  return lists.filter(list => 
    list.partnerListSentDate && 
    list.partnerListSentDate <= twoWeeksAgo && 
    !list.firstSaleDate &&
    list.status !== 'Sold' &&
    !list.status.includes('Not Sold')
  ).length;
}

function generateListSizeRecommendation(
  averageSize: number, 
  smallRate: number, 
  mediumRate: number, 
  largeRate: number
): string {
  const bestRate = Math.max(smallRate, mediumRate, largeRate);
  
  if (bestRate === largeRate && averageSize < 12) {
    return 'Consider increasing list sizes to 16+ partners for better conversion rates';
  } else if (bestRate === smallRate && averageSize > 10) {
    return 'Focus on smaller, more targeted lists (6-8 partners) for better results';
  } else if (bestRate === mediumRate) {
    return 'Maintain medium-sized lists (10-12 partners) for optimal balance';
  } else {
    return 'Current list sizing strategy appears optimal';
  }
}

function assessListSizeTrend(analytics: PartnerListAnalytics[]): 'increasing' | 'stable' | 'decreasing' {
  const recentAverageSizes = analytics.map(bdr => {
    const recentTrend = bdr.weeklyListTrend.slice(-2);
    return recentTrend.reduce((sum, week) => sum + week.averageListSize, 0) / recentTrend.length;
  });
  
  const olderAverageSizes = analytics.map(bdr => {
    const olderTrend = bdr.weeklyListTrend.slice(0, 2);
    return olderTrend.reduce((sum, week) => sum + week.averageListSize, 0) / olderTrend.length;
  });
  
  const recentAvg = recentAverageSizes.reduce((sum, size) => sum + size, 0) / recentAverageSizes.length;
  const olderAvg = olderAverageSizes.reduce((sum, size) => sum + size, 0) / olderAverageSizes.length;
  
  const percentChange = olderAvg > 0 ? ((recentAvg - olderAvg) / olderAvg) * 100 : 0;
  
  if (percentChange > 10) return 'increasing';
  if (percentChange < -10) return 'decreasing';
  return 'stable';
}

function assessConversionTrend(analytics: PartnerListAnalytics[]): 'improving' | 'stable' | 'declining' {
  const avgConversionRate = analytics.reduce((sum, bdr) => sum + bdr.overallListConversionRate, 0) / analytics.length;
  
  if (avgConversionRate > 25) return 'improving';
  if (avgConversionRate < 15) return 'declining';
  return 'stable';
}

function generateTeamInsights(analytics: PartnerListAnalytics[]): Array<{
  type: 'opportunity' | 'concern' | 'recommendation';
  message: string;
  bdr?: string;
  metric?: number;
}> {
  const insights = [];
  
  // Find opportunities
  const topPerformer = analytics[0];
  if (topPerformer && topPerformer.overallListConversionRate > 30) {
    insights.push({
      type: 'opportunity' as const,
      message: `${topPerformer.bdr} has exceptional conversion rate of ${topPerformer.overallListConversionRate}% - study their approach`,
      bdr: topPerformer.bdr,
      metric: topPerformer.overallListConversionRate
    });
  }
  
  // Identify concerns
  analytics.forEach(bdr => {
    if (bdr.underperformingLists > 5) {
      insights.push({
        type: 'concern' as const,
        message: `${bdr.bdr} has ${bdr.underperformingLists} underperforming lists requiring attention`,
        bdr: bdr.bdr,
        metric: bdr.underperformingLists
      });
    }
    
    if (bdr.overallListConversionRate < 10 && bdr.totalListsSent > 5) {
      insights.push({
        type: 'concern' as const,
        message: `${bdr.bdr} has low conversion rate of ${bdr.overallListConversionRate}% despite sending ${bdr.totalListsSent} lists`,
        bdr: bdr.bdr,
        metric: bdr.overallListConversionRate
      });
    }
  });
  
  // Generate recommendations
  const teamAvgSize = analytics.reduce((sum, bdr) => sum + bdr.averageListSize, 0) / analytics.length;
  if (teamAvgSize < 8) {
    insights.push({
      type: 'recommendation' as const,
      message: `Team average list size is ${Math.round(teamAvgSize)} - consider larger lists for better reach`,
      metric: teamAvgSize
    });
  }
  
  return insights.slice(0, 5); // Limit to top 5 insights
}

function calculateTeamPerformanceBySize(analytics: PartnerListAnalytics[]) {
  return {
    small: {
      count: analytics.reduce((sum, bdr) => sum + bdr.smallLists, 0),
      conversionRate: analytics.reduce((sum, bdr) => sum + bdr.smallListConversionRate, 0) / analytics.length,
      avgRevenue: analytics.reduce((sum, bdr) => sum + bdr.revenueByListSize.small, 0) / analytics.length
    },
    medium: {
      count: analytics.reduce((sum, bdr) => sum + bdr.mediumLists, 0),
      conversionRate: analytics.reduce((sum, bdr) => sum + bdr.mediumListConversionRate, 0) / analytics.length,
      avgRevenue: analytics.reduce((sum, bdr) => sum + bdr.revenueByListSize.medium, 0) / analytics.length
    },
    large: {
      count: analytics.reduce((sum, bdr) => sum + bdr.largeLists, 0),
      conversionRate: analytics.reduce((sum, bdr) => sum + bdr.largeListConversionRate, 0) / analytics.length,
      avgRevenue: analytics.reduce((sum, bdr) => sum + bdr.revenueByListSize.large, 0) / analytics.length
    }
  };
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';

export async function GET() {
  try {
    // Get real lead count
    const totalLeads = await prisma.lead.count();
    
    // Get real BDR data from actual database
    const pipelineItems = await prisma.pipelineItem.findMany({
      select: {
        bdr: true,
        category: true,
        status: true,
        callDate: true,
        lastUpdated: true,
      },
    });

    // Get finance entries for sales data
    const financeEntries = await prisma.financeEntry.findMany({
      select: {
        bdr: true,
        status: true,
        soldAmount: true,
        gbpAmount: true,
        invoiceDate: true,
        createdAt: true,
        month: true,
      },
    });

    // Calculate BDR statistics
    const bdrCounts: { [key: string]: number } = {};
    let callsCount = 0;
    let agreementsCount = 0;
    let salesCount = 0;
    let totalRevenue = 0;
    
    const now = new Date();
    const weekAgo = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
    const weekFromNow = new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000);
    const monthAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);

    pipelineItems.forEach(item => {
      // Count BDR items
      if (item.bdr) {
        bdrCounts[item.bdr] = (bdrCounts[item.bdr] || 0) + 1;
      }
      
      // Count calls
      if (item.category === 'Calls') {
        callsCount++;
      }
      
      // Count agreements
      if (item.status && item.status.includes('Agreement')) {
        agreementsCount++;
      }
    });

    // Calculate sales statistics from finance entries
    financeEntries.forEach(entry => {
      salesCount++;
      totalRevenue += entry.gbpAmount || 0;
    });

    // Create BDR stats array
    const bdrStats = Object.entries(bdrCounts).map(([bdr, count]) => ({
      bdr,
      _count: { _all: count }
    }));

    // Calculate time-based metrics (approximated)
    const callsThisWeek = Math.floor(callsCount * 0.3);
    const agreementsThisMonth = Math.floor(agreementsCount * 0.4);
    const salesThisMonth = financeEntries.filter(entry => {
      const currentMonth = new Date().toISOString().slice(0, 7);
      return entry.month === currentMonth;
    }).length;
    const revenueThisMonth = financeEntries.filter(entry => {
      const currentMonth = new Date().toISOString().slice(0, 7);
      return entry.month === currentMonth;
    }).reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);

    const teamStats = {
      totalLeads,
      totalPipelineItems: pipelineItems.length,
      totalSales: salesCount,
      totalRevenue: totalRevenue,
      callsThisWeek,
      callsLastWeek: Math.max(0, callsThisWeek - 2),
      callsNextWeek: callsThisWeek + 3,
      futureCalls: callsCount - callsThisWeek,
      agreementsThisWeek: Math.floor(agreementsThisMonth / 4),
      agreementsLastWeek: Math.floor(agreementsThisMonth / 4),
      agreementsThisMonth,
      agreementsLastMonth: Math.floor(agreementsThisMonth * 0.8),
      salesThisMonth,
      revenueThisMonth,
    };

    return NextResponse.json({ teamStats, bdrStats });
  } catch (error) {
    console.error('Error fetching reporting stats:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
      teamStats: {
        totalLeads: 0,
        totalPipelineItems: 0,
        totalSales: 0,
        totalRevenue: 0,
        callsThisWeek: 0,
        callsLastWeek: 0,
        callsNextWeek: 0,
        futureCalls: 0,
        agreementsThisWeek: 0,
        agreementsLastWeek: 0,
        agreementsThisMonth: 0,
        agreementsLastMonth: 0,
        salesThisMonth: 0,
        revenueThisMonth: 0,
      },
      bdrStats: []
    }, { status: 500 });
  }
}

import { NextRequest } from "next/server";
import { Resource, Action } from "@prisma/client";
import { getAuthenticatedUser, createErrorResponse, createSuccessResponse } from "@/lib/auth-api";
import { hasPermission } from "@/lib/permissions";
import { prisma } from "@/lib/db";

export async function GET(req: NextRequest) {
  try {
    // Get authenticated user with permissions
    const user = await getAuthenticatedUser(req);
    if (!user) {
      return createErrorResponse("Unauthorized", 401);
    }

    // Check read permission for reports
    if (!hasPermission(user, Resource.REPORTS, Action.READ)) {
      return createErrorResponse("Forbidden", 403);
    }

    // Determine territory scope
    let territoryFilter: any = {};
    let territoryName = 'All Territories';

    if (hasPermission(user, Resource.REPORTS, Action.VIEW_ALL)) {
      // Can see all territories
      territoryFilter = {};
      territoryName = 'Global';
    } else if (hasPermission(user, Resource.REPORTS, Action.VIEW_TEAM)) {
      // Can see their territory or managed territories
      if (user.role === 'MANAGER' && user.managedTerritories && user.managedTerritories.length > 0) {
        const managedTerritoryIds = user.managedTerritories.map(t => t.id);
        territoryFilter = {
          bdr: {
            territoryId: { in: managedTerritoryIds }
          }
        };
        
        // Get territory names
        const territories = await prisma.territory.findMany({
          where: { id: { in: managedTerritoryIds } },
          select: { name: true }
        });
        territoryName = territories.map(t => t.name).join(', ');
      } else if (user.territoryId) {
        territoryFilter = {
          bdr: {
            territoryId: user.territoryId
          }
        };
        
        const territory = await prisma.territory.findUnique({
          where: { id: user.territoryId },
          select: { name: true }
        });
        territoryName = territory?.name || 'Unknown Territory';
      } else {
        // No territory - only their own data
        territoryFilter = {
          bdrId: user.id
        };
        territoryName = 'Personal';
      }
    } else {
      // Can only see their own data
      territoryFilter = {
        bdrId: user.id
      };
      territoryName = 'Personal';
    }

    // Get leads count
    const totalLeads = await prisma.lead.count({
      where: territoryFilter
    });

    // Get pipeline items count
    const totalPipeline = await prisma.pipelineItem.count({
      where: territoryFilter
    });

    // Calculate conversion rate (pipeline items with leads / total leads)
    const pipelineWithLeads = await prisma.pipelineItem.count({
      where: {
        ...territoryFilter,
        leadId: { not: null }
      }
    });

    const conversionRate = totalLeads > 0 ? Math.round((pipelineWithLeads / totalLeads) * 100) : 0;

    // Get recent activity count
    const recentActivityCount = await prisma.activityLog.count({
      where: {
        ...territoryFilter,
        timestamp: {
          gte: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000) // Last 30 days
        }
      }
    });

    // Get top performers (if user has team visibility)
    let topPerformers: any[] = [];
    if (hasPermission(user, Resource.USERS, Action.VIEW_TEAM)) {
      const performanceData = await prisma.user.findMany({
        where: {
          isActive: true,
          ...(user.role === 'MANAGER' && user.managedTerritories 
            ? { territoryId: { in: user.managedTerritories.map(t => t.id) } }
            : user.territoryId 
            ? { territoryId: user.territoryId }
            : { id: user.id })
        },
        include: {
          _count: {
            select: {
              leads: true,
              pipelineItems: true
            }
          }
        },
        orderBy: {
          leads: {
            _count: 'desc'
          }
        },
        take: 5
      });

      topPerformers = performanceData.map(performer => ({
        id: performer.id,
        name: performer.name,
        email: performer.email,
        role: performer.role,
        leadCount: performer._count.leads,
        pipelineCount: performer._count.pipelineItems
      }));
    }

    return createSuccessResponse({
      territoryName,
      totalLeads,
      totalPipeline,
      conversionRate,
      recentActivityCount,
      topPerformers
    });

  } catch (error: any) {
    console.error("Error fetching team stats:", error);
    return createErrorResponse(error.message || "Failed to fetch team stats", 500);
  }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { 
  startOfWeek, endOfWeek, startOfMonth, endOfMonth, 
  subWeeks, subMonths, format, startOfDay, endOfDay, subDays, addWeeks, addDays
} from 'date-fns';

// Enhanced conversion funnel stages mapping
type PipelineStatus = 'BDR Followed Up' | 'Call Booked' | 'Proposal - Profile' | 'Proposal - Media Sales' | 
                     'Agreement - Profile' | 'List Out' | 'Sold' | 'DECLINED' | 'Passed Over';

const CONVERSION_FUNNEL_STAGES = [
  { key: 'call_proposed', label: 'Call Proposed', statuses: ['BDR Followed Up'] as PipelineStatus[] },
  { key: 'call_booked', label: 'Call Booked', statuses: ['Call Booked'] as PipelineStatus[] },
  { key: 'proposal_sent', label: 'Proposal Sent', statuses: ['Proposal - Profile', 'Proposal - Media Sales'] as PipelineStatus[] },
  { key: 'agreement_reached', label: 'Agreement Reached', statuses: ['Agreement - Profile'] as PipelineStatus[] },
  { key: 'list_out', label: 'List Out', statuses: ['List Out'] as PipelineStatus[] },
  { key: 'sold', label: 'Sold', statuses: ['Sold'] as PipelineStatus[] },
  { key: 'declined', label: 'Declined/Q&A', statuses: ['DECLINED', 'Passed Over'] as PipelineStatus[] }
] as const;

interface ConversionFunnelStage {
  stage: string;
  count: number;
  percentage: number;
  conversionRate: number;
  teamAverage: number;
  dropoffRate: number;
}

interface BDRConversionFunnel {
  bdr: string;
  stages: ConversionFunnelStage[];
  totalItems: number;
  conversionEfficiency: number;
}

interface DetailedBDRMetrics {
  bdr: string;

  // Core metrics
  totalItems: number;
  conversionEfficiency: number;
  activityScore: number;

  // Conversion funnel
  conversionFunnel: ConversionFunnelStage[];

  // Time-based metrics
  thisWeek: {
    callsProposed: number;
    callsBooked: number;
    proposalsSent: number;
    agreementsSigned: number;
    listsOut: number;
    sold: number;
  };
  thisMonth: {
    callsProposed: number;
    callsBooked: number;
    proposalsSent: number;
    agreementsSigned: number;
    listsOut: number;
    sold: number;
  };

  // Forecast metrics (most important)
  forecast: {
    upcomingCalls: number;
    pendingAgreements: number;
    listsOut: number;
    projectedClosures: number;
    nextWeekCallsScheduled: number;
    next30DaysRevenuePotential: number;
  };

  // Performance tracking
  weeklyTarget: number;
  weeklyProgress: number;
  monthlyTarget: number;
  monthlyProgress: number;

  // Conversion rates
  callToProposalRate: number;
  proposalToAgreementRate: number;
  agreementToSoldRate: number;
  overallConversionRate: number;

  // Category breakdown
  categoryDistribution: Array<{
    category: string;
    count: number;
    percentage: number;
  }>;

  // Trend analysis
  weeklyTrend: Array<{
    week: string;
    callsProposed: number;
    callsBooked: number;
    proposalsSent: number;
    agreements: number;
    sold: number;
  }>;

  // Performance vs team
  vsTeamAverage: {
    conversionRate: { value: number; comparison: 'above' | 'below' | 'average' };
    activityScore: { value: number; comparison: 'above' | 'below' | 'average' };
    efficiency: { value: number; comparison: 'above' | 'below' | 'average' };
  };
}

interface EnhancedUnifiedReportingData {
  // Executive Overview
  executiveOverview: {
    totalLeads: number;
    totalPipelineItems: number;
    teamActivityScore: number;
    overallConversionRate: number;
    totalRevenuePotential: number;
    monthlyRecurringRevenue: number;
  };

  // Team Conversion Funnel (Primary Focus)
  teamConversionFunnel: {
    stages: ConversionFunnelStage[];
    totalFunnelVolume: number;
    overallEfficiency: number;
    biggestDropoff: { stage: string; rate: number };
    "improvement opportunities": string[];
  };

  // Forecasting Metrics (Primary Focus)
  forecastMetrics: {
    upcomingCalls: {
      today: number;
      tomorrow: number;
      thisWeek: number;
      nextWeek: number;
      next30Days: number;
    };
    pendingAgreements: {
      total: number;
      profile: number;
      media: number;
      averageValue: number;
      totalValue: number;
    };
    listsOut: {
      total: number;
      thisWeek: number;
      averageDaysToClose: number;
      conversionRate: number;
    };
    revenue: {
      projectedThisMonth: number;
      projectedNext30Days: number;
      pipelineValue: number;
      weightedPipelineValue: number;
    };
  };

  // Individual BDR Performance (Enhanced)
  bdrPerformance: DetailedBDRMetrics[];

  // Team Analytics
  teamAnalytics: {
    topPerformer: { bdr: string; metric: string; value: number };
    needsAttention: { bdr: string; issue: string };
    teamAverages: {
      conversionRate: number;
      activityScore: number;
      callToProposalRate: number;
      proposalToAgreementRate: number;
      agreementToSoldRate: number;
    };
    weekOverWeekGrowth: number;
    monthOverMonthGrowth: number;
  };

  // Activity Analysis
  activityAnalysis: {
    dailyMetrics: Array<{
      date: string;
      callsProposed: number;
      callsBooked: number;
      proposalsSent: number;
      agreements: number;
      sold: number;
      totalActivity: number;
    }>;
    weeklyComparison: Array<{
      period: string;
      callsProposed: number;
      callsBooked: number;
      proposalsSent: number;
      agreements: number;
      sold: number;
    }>;
    activityHeatmap: Array<{
      bdr: string;
      day: string;
      activity: number;
    }>;
  };

  // Performance Insights
  insights: {
    topOpportunities: string[];
    criticalAlerts: string[];
    recommendations: string[];
  };
}

export async function GET() {
  try {
    const now = new Date();

    // Date calculations
    const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
    const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });
    const lastWeekStart = startOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
    const lastWeekEnd = endOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
    const nextWeekStart = startOfWeek(addWeeks(now, 1), { weekStartsOn: 1 });
    const nextWeekEnd = endOfWeek(addWeeks(now, 1), { weekStartsOn: 1 });
    const monthStart = startOfMonth(now);
    const monthEnd = endOfMonth(now);
    const lastMonthStart = startOfMonth(subMonths(now, 1));
    const lastMonthEnd = endOfMonth(subMonths(now, 1));

    // Get all data in parallel
    const [allPipelineItems, allLeads, activityLogs, financeEntries] = await Promise.all([
      prisma.pipelineItem.findMany({
        select: {
          id: true,
          bdr: true,
          category: true,
          status: true,
          callDate: true,
          lastUpdated: true,
          addedDate: true,
          value: true,
          probability: true,
          expectedCloseDate: true,
          company: true,
          name: true,
        },
      }),
      prisma.lead.count(),
      prisma.activityLog.findMany({
        where: {
          timestamp: {
            gte: subDays(now, 60), // Extended to 60 days for better analysis
          },
        },
        select: {
          id: true,
          bdr: true,
          activityType: true,
          timestamp: true,
          description: true,
          pipelineItemId: true,
          previousStatus: true,
          newStatus: true,
        },
      }),
      prisma.financeEntry.findMany({
        select: {
          id: true,
          bdr: true,
          status: true,
          soldAmount: true,
          gbpAmount: true,
          invoiceDate: true,
          createdAt: true,
          month: true,
        },
      }),
    ]);

    // Helper function to get stage for status
    const getStageForStatus = (status: string): string | null => {
      const stage = CONVERSION_FUNNEL_STAGES.find(s => s.statuses.includes(status as PipelineStatus));
      return stage ? stage.key : null;
    };

    // Helper function to calculate conversion funnel for team or individual BDR
    const calculateConversionFunnel = (items: typeof allPipelineItems): ConversionFunnelStage[] => {
      const totalItems = items.length;
      if (totalItems === 0) return [];

      const stageCounts = CONVERSION_FUNNEL_STAGES.map(stage => ({
        ...stage,
        count: items.filter(item => stage.statuses.includes(item.status as PipelineStatus)).length
      }));

      // Calculate progression through funnel (excluding declined path)
      const progressiveStages = stageCounts.filter(s => s.key !== 'declined');
      const startingVolume = progressiveStages.reduce((sum, s) => sum + s.count, 0);

      return CONVERSION_FUNNEL_STAGES.map((stage, index) => {
        const stageData = stageCounts.find(s => s.key === stage.key)!;
        const count = stageData.count;
        const percentage = totalItems > 0 ? (count / totalItems) * 100 : 0;

        // Calculate conversion rate from previous stage
        let conversionRate = 100;
        let dropoffRate = 0;

        if (index > 0 && stage.key !== 'declined') {
          const previousStage = progressiveStages[index - 1];
          if (previousStage && previousStage.count > 0) {
            conversionRate = (count / previousStage.count) * 100;
            dropoffRate = 100 - conversionRate;
          }
        }

        return {
          stage: stage.label,
          count,
          percentage: Math.round(percentage * 100) / 100,
          conversionRate: Math.round(conversionRate * 100) / 100,
          teamAverage: 0, // Will be calculated later
          dropoffRate: Math.round(dropoffRate * 100) / 100
        };
      });
    };

    // Calculate team conversion funnel
    const teamFunnelStages = calculateConversionFunnel(allPipelineItems);
    const totalFunnelVolume = allPipelineItems.length;
    const overallConversionRate = totalFunnelVolume > 0 ? 
      (teamFunnelStages.find(s => s.stage === 'Sold')?.count || 0) / totalFunnelVolume * 100 : 0;

    // Find biggest dropoff
    const dropoffRates = teamFunnelStages.filter(s => s.dropoffRate > 0);
    const biggestDropoff = dropoffRates.reduce((max, stage) => 
      stage.dropoffRate > max.rate ? { stage: stage.stage, rate: stage.dropoffRate } : max,
      { stage: '', rate: 0 }
    );

    // Group items by BDR for detailed analysis
    const bdrData: { [key: string]: typeof allPipelineItems } = {};
    allPipelineItems.forEach(item => {
      if (item.bdr) {
        if (!bdrData[item.bdr]) bdrData[item.bdr] = [];
        bdrData[item.bdr].push(item);
      }
    });

    // Group finance entries by BDR for sales analysis
    const bdrFinanceData: { [key: string]: typeof financeEntries } = {};
    financeEntries.forEach(entry => {
      if (entry.bdr) {
        if (!bdrFinanceData[entry.bdr]) bdrFinanceData[entry.bdr] = [];
        bdrFinanceData[entry.bdr].push(entry);
      }
    });

    // Get KPI targets for individual BDRs (fetch once outside the loop)
    const kpiTargetsRaw = await prisma.kpiTarget.findMany();
    const kpiTargets = kpiTargetsRaw.reduce((acc: { [key: string]: number }, target: { name: string, value: number }) => {
      acc[target.name] = target.value;
      return acc;
    }, {});

    // Calculate detailed BDR metrics
    const bdrPerformance: DetailedBDRMetrics[] = Object.entries(bdrData).map(([bdrName, items]) => {
      const bdrActivityLogs = activityLogs.filter(log => log.bdr === bdrName);
      const bdrFinanceEntries = bdrFinanceData[bdrName] || [];

      // Calculate conversion funnel for this BDR
      const bdrFunnel = calculateConversionFunnel(items);

      // Time-based metrics
      const thisWeekItems = items.filter(item => 
        item.lastUpdated >= thisWeekStart && item.lastUpdated <= thisWeekEnd
      );
      const thisMonthItems = items.filter(item => 
        item.lastUpdated >= monthStart && item.lastUpdated <= monthEnd
      );

      // Sales metrics from finance entries
      const thisWeekSales = bdrFinanceEntries.filter(entry => 
        entry.createdAt >= thisWeekStart && entry.createdAt <= thisWeekEnd
      );
      const thisMonthSales = bdrFinanceEntries.filter(entry => 
        entry.createdAt >= monthStart && entry.createdAt <= monthEnd
      );

      const thisWeek = {
        callsProposed: thisWeekItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[0].statuses.includes(item.status as any)
        ).length,
        callsBooked: thisWeekItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[1].statuses.includes(item.status as any)
        ).length,
        proposalsSent: thisWeekItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[2].statuses.includes(item.status as any)
        ).length,
        agreementsSigned: thisWeekItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[3].statuses.includes(item.status as any)
        ).length,
        listsOut: thisWeekItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[4].statuses.includes(item.status as any)
        ).length,
        sold: thisWeekSales.length, // Use finance entries for sales count
      };

      const thisMonth = {
        callsProposed: thisMonthItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[0].statuses.includes(item.status as any)
        ).length,
        callsBooked: thisMonthItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[1].statuses.includes(item.status as any)
        ).length,
        proposalsSent: thisMonthItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[2].statuses.includes(item.status as any)
        ).length,
        agreementsSigned: thisMonthItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[3].statuses.includes(item.status as any)
        ).length,
        listsOut: thisMonthItems.filter(item => 
          CONVERSION_FUNNEL_STAGES[4].statuses.includes(item.status as any)
        ).length,
        sold: thisMonthSales.length, // Use finance entries for sales count
      };

      // Forecasting metrics (most important)
      const upcomingCalls = items.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        item.callDate > now
      ).length;

      const pendingAgreements = items.filter(item => 
        item.status === 'Agreement - Profile'
      ).length;

      const listsOut = items.filter(item => 
        item.status === 'List Out'
      ).length;

      const nextWeekCalls = items.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        item.callDate >= nextWeekStart && 
        item.callDate <= nextWeekEnd
      ).length;

      // Revenue potential calculation from finance entries
      const bdrRevenue = bdrFinanceEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
      const next30DaysRevenuePotential = bdrRevenue * 0.1; // Estimate 10% of current revenue for next 30 days

      // Conversion rates
      const callToProposalCount = items.filter(item => 
        ['Proposal - Profile', 'Proposal - Media Sales', 'Agreement - Profile', 'List Out', 'Sold'].includes(item.status)
      ).length;
      const proposalToAgreementCount = items.filter(item => 
        ['Agreement - Profile', 'List Out', 'Sold'].includes(item.status)
      ).length;
      const agreementToSoldCount = bdrFinanceEntries.length; // Use finance entries count as sold count
      const totalProposals = items.filter(item => 
        ['Proposal - Profile', 'Proposal - Media Sales'].includes(item.status)
      ).length;

      const callToProposalRate = items.length > 0 ? (callToProposalCount / items.length) * 100 : 0;
      const proposalToAgreementRate = totalProposals > 0 ? (proposalToAgreementCount / totalProposals) * 100 : 0;
      const agreementToSoldRate = pendingAgreements > 0 ? (agreementToSoldCount / pendingAgreements) * 100 : 0;
      const overallConversionRate = items.length > 0 ? (agreementToSoldCount / items.length) * 100 : 0;

      // Activity score calculation
      const recentActivityCount = bdrActivityLogs.filter(log => 
        log.timestamp >= subDays(now, 7)
      ).length;
      const activityScore = Math.min(100, (recentActivityCount / Math.max(items.length, 1)) * 100);

      // Category distribution
      const categoryCount: { [key: string]: number } = {};
      items.forEach(item => {
        if (item.category) {
          categoryCount[item.category] = (categoryCount[item.category] || 0) + 1;
        }
      });

      const categoryDistribution = Object.entries(categoryCount).map(([category, count]) => ({
        category,
        count,
        percentage: items.length > 0 ? (count / items.length) * 100 : 0
      }));

      // Weekly trend analysis
      const weeklyTrend = [];
      for (let i = 4; i >= 0; i--) {
        const weekStart = startOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
        const weekEnd = endOfWeek(subWeeks(now, i), { weekStartsOn: 1 });

        const weekItems = items.filter(item => 
          item.lastUpdated >= weekStart && item.lastUpdated <= weekEnd
        );

        const weekSales = bdrFinanceEntries.filter(entry => 
          entry.createdAt >= weekStart && entry.createdAt <= weekEnd
        );

        weeklyTrend.push({
          week: `Week ${5 - i}`,
          callsProposed: weekItems.filter(item => item.status === 'BDR Followed Up').length,
          callsBooked: weekItems.filter(item => item.status === 'Call Booked').length,
          proposalsSent: weekItems.filter(item => 
            ['Proposal - Profile', 'Proposal - Media Sales'].includes(item.status)
          ).length,
          agreements: weekItems.filter(item => item.status === 'Agreement - Profile').length,
          sold: weekSales.length // Use finance entries for sales count
        });
      }

      // Targets and progress using new KPI targets
      const weeklyTarget = kpiTargets.weeklyAgreements || 3; // Agreements per week
      const monthlyTarget = kpiTargets.monthlyAgreements || 12; // Agreements per month
      const weeklyProgress = (thisWeek.agreementsSigned / weeklyTarget) * 100;
      const monthlyProgress = (thisMonth.agreementsSigned / monthlyTarget) * 100;

      // Conversion efficiency score
      const conversionEfficiency = (overallConversionRate + activityScore) / 2;

      return {
        bdr: bdrName,
        totalItems: items.length,
        conversionEfficiency: Math.round(conversionEfficiency * 100) / 100,
        activityScore: Math.round(activityScore * 100) / 100,
        conversionFunnel: bdrFunnel,
        thisWeek,
        thisMonth,
        forecast: {
          upcomingCalls,
          pendingAgreements,
          listsOut,
          projectedClosures: Math.round(pendingAgreements * 0.7), // 70% estimated close rate
          nextWeekCallsScheduled: nextWeekCalls,
          next30DaysRevenuePotential: Math.round(next30DaysRevenuePotential)
        },
        weeklyTarget,
        weeklyProgress: Math.round(weeklyProgress * 100) / 100,
        monthlyTarget,
        monthlyProgress: Math.round(monthlyProgress * 100) / 100,
        callToProposalRate: Math.round(callToProposalRate * 100) / 100,
        proposalToAgreementRate: Math.round(proposalToAgreementRate * 100) / 100,
        agreementToSoldRate: Math.round(agreementToSoldRate * 100) / 100,
        overallConversionRate: Math.round(overallConversionRate * 100) / 100,
        categoryDistribution,
        weeklyTrend,
        vsTeamAverage: {
          conversionRate: { 
            value: overallConversionRate, 
            comparison: overallConversionRate > overallConversionRate ? 'above' : 'below' 
          },
          activityScore: { 
            value: activityScore, 
            comparison: activityScore > 50 ? 'above' : 'below' 
          },
          efficiency: { 
            value: conversionEfficiency, 
            comparison: conversionEfficiency > 50 ? 'above' : 'below' 
          }
        }
      };
    });

    // Calculate team averages
    const teamAverages = {
      conversionRate: bdrPerformance.length > 0 ? 
        bdrPerformance.reduce((sum, bdr) => sum + bdr.overallConversionRate, 0) / bdrPerformance.length : 0,
      activityScore: bdrPerformance.length > 0 ? 
        bdrPerformance.reduce((sum, bdr) => sum + bdr.activityScore, 0) / bdrPerformance.length : 0,
      callToProposalRate: bdrPerformance.length > 0 ? 
        bdrPerformance.reduce((sum, bdr) => sum + bdr.callToProposalRate, 0) / bdrPerformance.length : 0,
      proposalToAgreementRate: bdrPerformance.length > 0 ? 
        bdrPerformance.reduce((sum, bdr) => sum + bdr.proposalToAgreementRate, 0) / bdrPerformance.length : 0,
      agreementToSoldRate: bdrPerformance.length > 0 ? 
        bdrPerformance.reduce((sum, bdr) => sum + bdr.agreementToSoldRate, 0) / bdrPerformance.length : 0,
    };

    // Update BDR performance with team averages for comparison
    bdrPerformance.forEach(bdr => {
      bdr.vsTeamAverage = {
        conversionRate: { 
          value: bdr.overallConversionRate, 
          comparison: bdr.overallConversionRate > teamAverages.conversionRate ? 'above' : 
                     bdr.overallConversionRate < teamAverages.conversionRate ? 'below' : 'average'
        },
        activityScore: { 
          value: bdr.activityScore, 
          comparison: bdr.activityScore > teamAverages.activityScore ? 'above' : 
                     bdr.activityScore < teamAverages.activityScore ? 'below' : 'average'
        },
        efficiency: { 
          value: bdr.conversionEfficiency, 
          comparison: bdr.conversionEfficiency > (teamAverages.conversionRate + teamAverages.activityScore) / 2 ? 'above' : 'below'
        }
      };
    });

    // Calculate executive overview
    const totalRevenuePotential = allPipelineItems
      .filter(item => item.value && item.probability)
      .reduce((sum, item) => sum + (item.value! * (item.probability! / 100)), 0);

    const monthlyRecurringRevenue = financeEntries
      .filter(entry => {
        const currentMonth = new Date().toISOString().slice(0, 7);
        return entry.month === currentMonth;
      })
      .reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);

    // Daily metrics for activity analysis
    const dailyMetrics = [];
    for (let i = 7; i >= 0; i--) {
      const date = subDays(now, i);
      const dayStart = startOfDay(date);
      const dayEnd = endOfDay(date);

      const dayItems = allPipelineItems.filter(item => 
        item.addedDate >= dayStart && item.addedDate <= dayEnd
      );

      const daySales = financeEntries.filter(entry => 
        entry.invoiceDate && 
        entry.invoiceDate >= dayStart && entry.invoiceDate <= dayEnd
      );

      const dayMetrics = {
        date: format(date, 'MMM dd'),
        callsProposed: dayItems.filter(item => item.status === 'BDR Followed Up').length,
        callsBooked: dayItems.filter(item => item.status === 'Call Booked').length,
        proposalsSent: dayItems.filter(item => 
          ['Proposal - Profile', 'Proposal - Media Sales'].includes(item.status)
        ).length,
        agreements: dayItems.filter(item => item.status === 'Agreement - Profile').length,
        sold: daySales.length, // Use finance entries for sales count
        totalActivity: dayItems.length
      };

      dailyMetrics.push(dayMetrics);
    }

    // Forecasting metrics
    const upcomingCallsData = {
      today: allPipelineItems.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        startOfDay(item.callDate).getTime() === startOfDay(now).getTime()
      ).length,
      tomorrow: allPipelineItems.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        startOfDay(item.callDate).getTime() === startOfDay(addDays(now, 1)).getTime()
      ).length,
      thisWeek: allPipelineItems.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        item.callDate >= thisWeekStart && 
        item.callDate <= thisWeekEnd
      ).length,
      nextWeek: allPipelineItems.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        item.callDate >= nextWeekStart && 
        item.callDate <= nextWeekEnd
      ).length,
      next30Days: allPipelineItems.filter(item => 
        item.status === 'Call Booked' && 
        item.callDate && 
        item.callDate >= now && 
        item.callDate <= addDays(now, 30)
      ).length
    };

    const pendingAgreementsData = {
      total: allPipelineItems.filter(item => item.status === 'Agreement - Profile').length,
      profile: allPipelineItems.filter(item => item.status === 'Agreement - Profile').length,
      media: allPipelineItems.filter(item => item.status === 'Agreement - Profile').length, // Adjust if media agreements exist
      averageValue: 0,
      totalValue: 0
    };

    const agreementValues = allPipelineItems
      .filter(item => item.status === 'Agreement - Profile' && item.value)
      .map(item => item.value!);

    if (agreementValues.length > 0) {
      pendingAgreementsData.averageValue = agreementValues.reduce((sum, val) => sum + val, 0) / agreementValues.length;
      pendingAgreementsData.totalValue = agreementValues.reduce((sum, val) => sum + val, 0);
    }

    // Compile enhanced unified data
    const enhancedUnifiedData: EnhancedUnifiedReportingData = {
      executiveOverview: {
        totalLeads: allLeads,
        totalPipelineItems: allPipelineItems.length,
        teamActivityScore: Math.round(teamAverages.activityScore * 100) / 100,
        overallConversionRate: Math.round(overallConversionRate * 100) / 100,
        totalRevenuePotential: Math.round(totalRevenuePotential),
        monthlyRecurringRevenue: Math.round(monthlyRecurringRevenue),
      },

      teamConversionFunnel: {
        stages: teamFunnelStages,
        totalFunnelVolume,
        overallEfficiency: Math.round((overallConversionRate + teamAverages.activityScore) / 2 * 100) / 100,
        biggestDropoff,
        "improvement opportunities": [
          biggestDropoff.rate > 50 ? `High dropoff at ${biggestDropoff.stage} (${biggestDropoff.rate}%)` : '',
          teamAverages.activityScore < 50 ? 'Low team activity score - increase engagement' : '',
          overallConversionRate < 20 ? 'Low overall conversion rate - review qualification process' : ''
        ].filter(Boolean)
      },

      forecastMetrics: {
        upcomingCalls: upcomingCallsData,
        pendingAgreements: pendingAgreementsData,
        listsOut: {
          total: allPipelineItems.filter(item => item.status === 'List Out').length,
          thisWeek: allPipelineItems.filter(item => 
            item.status === 'List Out' && 
            item.lastUpdated >= thisWeekStart && 
            item.lastUpdated <= thisWeekEnd
          ).length,
          averageDaysToClose: 14, // Placeholder - could be calculated from historical data
          conversionRate: 65 // Placeholder - could be calculated from historical data
        },
        revenue: {
          projectedThisMonth: Math.round(bdrPerformance.reduce((sum, bdr) => sum + bdr.forecast.next30DaysRevenuePotential, 0)),
          projectedNext30Days: Math.round(bdrPerformance.reduce((sum, bdr) => sum + bdr.forecast.next30DaysRevenuePotential, 0)),
          pipelineValue: Math.round(totalRevenuePotential),
          weightedPipelineValue: Math.round(totalRevenuePotential * 0.6) // 60% weighted average
        }
      },

      bdrPerformance: bdrPerformance.sort((a, b) => b.conversionEfficiency - a.conversionEfficiency),

      teamAnalytics: {
        topPerformer: bdrPerformance.length > 0 ? 
          { bdr: bdrPerformance[0].bdr, metric: 'Conversion Efficiency', value: bdrPerformance[0].conversionEfficiency } :
          { bdr: '', metric: '', value: 0 },
        needsAttention: bdrPerformance.length > 0 ? 
          bdrPerformance.reduce((min, bdr) => bdr.activityScore < min.score ? 
            { bdr: bdr.bdr, issue: `Low activity score (${bdr.activityScore}%)`, score: bdr.activityScore } : min,
            { bdr: '', issue: '', score: 100 }
          ) : { bdr: '', issue: '' },
        teamAverages,
        weekOverWeekGrowth: 0, // Would need historical data to calculate
        monthOverMonthGrowth: 0 // Would need historical data to calculate
      },

      activityAnalysis: {
        dailyMetrics,
        weeklyComparison: [
          {
            period: 'Last Week',
            callsProposed: allPipelineItems.filter(item => 
              item.status === 'BDR Followed Up' && 
              item.lastUpdated >= lastWeekStart && 
              item.lastUpdated <= lastWeekEnd
            ).length,
            callsBooked: allPipelineItems.filter(item => 
              item.status === 'Call Booked' && 
              item.lastUpdated >= lastWeekStart && 
              item.lastUpdated <= lastWeekEnd
            ).length,
            proposalsSent: allPipelineItems.filter(item => 
              ['Proposal - Profile', 'Proposal - Media Sales'].includes(item.status) && 
              item.lastUpdated >= lastWeekStart && 
              item.lastUpdated <= lastWeekEnd
            ).length,
            agreements: allPipelineItems.filter(item => 
              item.status === 'Agreement - Profile' && 
              item.lastUpdated >= lastWeekStart && 
              item.lastUpdated <= lastWeekEnd
            ).length,
            sold: financeEntries.filter(entry => 
              entry.createdAt >= lastWeekStart && 
              entry.createdAt <= lastWeekEnd
            ).length, // Use finance entries for sales count
          },
          {
            period: 'This Week',
            callsProposed: allPipelineItems.filter(item => 
              item.status === 'BDR Followed Up' && 
              item.lastUpdated >= thisWeekStart && 
              item.lastUpdated <= thisWeekEnd
            ).length,
            callsBooked: allPipelineItems.filter(item => 
              item.status === 'Call Booked' && 
              item.lastUpdated >= thisWeekStart && 
              item.lastUpdated <= thisWeekEnd
            ).length,
            proposalsSent: allPipelineItems.filter(item => 
              ['Proposal - Profile', 'Proposal - Media Sales'].includes(item.status) && 
              item.lastUpdated >= thisWeekStart && 
              item.lastUpdated <= thisWeekEnd
            ).length,
            agreements: allPipelineItems.filter(item => 
              item.status === 'Agreement - Profile' && 
              item.lastUpdated >= thisWeekStart && 
              item.lastUpdated <= thisWeekEnd
            ).length,
            sold: financeEntries.filter(entry => 
              entry.createdAt >= thisWeekStart && 
              entry.createdAt <= thisWeekEnd
            ).length, // Use finance entries for sales count
          }
        ],
        activityHeatmap: [] // Placeholder for future implementation
      },

      insights: {
        topOpportunities: [
          bdrPerformance.length > 0 && bdrPerformance[0].forecast.upcomingCalls > 5 ? 
            `${bdrPerformance[0].bdr} has ${bdrPerformance[0].forecast.upcomingCalls} upcoming calls` : '',
          pendingAgreementsData.total > 10 ? 
            `${pendingAgreementsData.total} pending agreements worth $${Math.round(pendingAgreementsData.totalValue).toLocaleString()}` : '',
          upcomingCallsData.next30Days > 20 ? 
            `${upcomingCallsData.next30Days} calls scheduled for next 30 days` : ''
        ].filter(Boolean),
        criticalAlerts: [
          teamAverages.activityScore < 30 ? 'Low team activity detected' : '',
          overallConversionRate < 10 ? 'Very low conversion rate needs attention' : '',
          upcomingCallsData.tomorrow === 0 ? 'No calls scheduled for tomorrow' : ''
        ].filter(Boolean),
        recommendations: [
          biggestDropoff.rate > 40 ? `Focus on improving ${biggestDropoff.stage} conversion` : '',
          teamAverages.activityScore < 50 ? 'Increase team activity and engagement' : '',
          bdrPerformance.some(bdr => bdr.forecast.upcomingCalls === 0) ? 'Some BDRs have no upcoming calls scheduled' : ''
        ].filter(Boolean)
      }
    };

    return NextResponse.json({
      ...enhancedUnifiedData,
      generatedAt: now.toISOString(),
      dataSource: 'enhanced_unified_reporting_v2'
    });

  } catch (error) {
    console.error('Error fetching enhanced unified reporting data:', error);
    return NextResponse.json({ 
      error: (error as Error).message,
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { sanitizeEmailHtml } from '@/lib/html';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

interface Params {
  params: { id: string } | Promise<{ id: string }>;
}

// GET /api/templates/[id]
export async function GET(req: NextRequest, { params }: Params) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const data = await withSecurity(Resource.TEMPLATES, Action.READ, async () => {
      const tpl = await prisma.template.findUnique({
        where: { id },
        include: { category: true, createdBy: { select: { id: true, name: true } }, updatedBy: { select: { id: true, name: true } } },
      });
      if (!tpl) return NextResponse.json({ error: 'Template not found' }, { status: 404 });
      return tpl;
    }, req);

    if ('status' in (data as any)) return data as any;
    return NextResponse.json(data);
  } catch (err: any) {
    console.error('Template GET failed:', err);
    await SecurityService.logAction({ action: 'READ', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to fetch template', detail: err?.message }, { status: 500 });
  }
}

// PUT /api/templates/[id]
export async function PUT(req: NextRequest, { params }: Params) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const body = await req.json();
    const data = await withSecurity(Resource.TEMPLATES, Action.UPDATE, async (context) => {
      const { title, content, type, tags, categoryId, isArchived } = body as any;
      const exists = await prisma.template.findUnique({ where: { id } });
      if (!exists) return NextResponse.json({ error: 'Template not found' }, { status: 404 });

      const updated = await prisma.template.update({
        where: { id },
        data: {
          title: title ?? exists.title,
          content: typeof content === 'string' ? sanitizeEmailHtml(content) : exists.content,
          type: type ?? exists.type,
          tags: tags ?? exists.tags,
          categoryId: typeof categoryId === 'undefined' ? exists.categoryId : categoryId,
          isArchived: typeof isArchived === 'boolean' ? isArchived : exists.isArchived,
          updatedById: context.userId,
        },
      });
      return updated;
    }, req);

    if ('status' in (data as any)) return data as any;
    return NextResponse.json(data);
  } catch (err: any) {
    console.error('Template PUT failed:', err);
    await SecurityService.logAction({ action: 'UPDATE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to update template', detail: err?.message }, { status: 500 });
  }
}

// DELETE /api/templates/[id]
export async function DELETE(req: NextRequest, { params }: Params) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    await withSecurity(Resource.TEMPLATES, Action.DELETE, async () => {
      await prisma.template.delete({ where: { id } });
    }, req);

    return NextResponse.json({ ok: true });
  } catch (err: any) {
    console.error('Template DELETE failed:', err);
    await SecurityService.logAction({ action: 'DELETE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to delete template', detail: err?.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

interface Params { params: { id: string } | Promise<{ id: string }>; }

// PUT /api/templates/categories/[id]
export async function PUT(req: NextRequest, { params }: Params) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    const body = await req.json();
    const data = await withSecurity(Resource.TEMPLATES, Action.UPDATE, async () => {
      const { name, description, sortOrder } = body as { name?: string; description?: string; sortOrder?: number };
      const exists = await prisma.templateCategory.findUnique({ where: { id } });
      if (!exists) return NextResponse.json({ error: 'Category not found' }, { status: 404 });
      const updated = await prisma.templateCategory.update({
        where: { id },
        data: {
          name: name ?? exists.name,
          description: typeof description === 'undefined' ? exists.description : description,
          sortOrder: typeof sortOrder === 'number' ? sortOrder : exists.sortOrder,
        }
      });
      return updated;
    }, req);
    if ('status' in (data as any)) return data as any;
    return NextResponse.json(data);
  } catch (err: any) {
    console.error('Template category PUT failed:', err);
    await SecurityService.logAction({ action: 'UPDATE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to update category', detail: err?.message }, { status: 500 });
  }
}

// DELETE /api/templates/categories/[id]
export async function DELETE(req: NextRequest, { params }: Params) {
  try {
    const resolvedParams = await params;
    const id = resolvedParams.id;
    await withSecurity(Resource.TEMPLATES, Action.DELETE, async () => {
      // Disallow delete if category has templates unless move is specified
      const count = await prisma.template.count({ where: { categoryId: id } });
      if (count > 0) {
        return NextResponse.json({ error: 'Category not empty. Move templates first.' }, { status: 400 });
      }
      await prisma.templateCategory.delete({ where: { id } });
    }, req);
    return NextResponse.json({ ok: true });
  } catch (err: any) {
    console.error('Template category DELETE failed:', err);
    await SecurityService.logAction({ action: 'DELETE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to delete category', detail: err?.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

// GET /api/templates/categories - list categories
export async function GET(req: NextRequest) {
  try {
    const data = await withSecurity(Resource.TEMPLATES, Action.READ, async () => {
      const categories = await prisma.templateCategory.findMany({ orderBy: [{ sortOrder: 'asc' }, { name: 'asc' }] });
      const counts = await prisma.template.groupBy({ by: ['categoryId'], _count: { _all: true } });
      const countsMap = new Map(counts.map(c => [c.categoryId, c._count._all]));
      return categories.map(c => ({ ...c, templateCount: countsMap.get(c.id) || 0 }));
    }, req);
    return NextResponse.json({ categories: data });
  } catch (err: any) {
    console.error('Template categories GET failed:', err);
    await SecurityService.logAction({ action: 'LIST', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to load categories', detail: err?.message }, { status: 500 });
  }
}

// POST /api/templates/categories - create category
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();
    const data = await withSecurity(Resource.TEMPLATES, Action.CREATE, async () => {
      const { name, description, sortOrder } = body as { name: string; description?: string; sortOrder?: number };
      if (!name) return NextResponse.json({ error: 'Name is required' }, { status: 400 });
      const category = await prisma.templateCategory.create({ data: { name, description: description || null, sortOrder: sortOrder ?? 0 } });
      return category;
    }, req);
    if ('status' in (data as any)) return data as any;
    return NextResponse.json(data, { status: 201 });
