    console.error('Template categories POST failed:', err);
    await SecurityService.logAction({ action: 'CREATE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to create category', detail: err?.message }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/db';
import { sanitizeEmailHtml } from '@/lib/html';
import { withSecurity, SecurityService } from '@/lib/security';
import { Action, Resource } from '@prisma/client';

// GET /api/templates - list templates with optional filters
export async function GET(req: NextRequest) {
  try {
    const data = await withSecurity(Resource.TEMPLATES, Action.READ, async () => {
      const { searchParams } = new URL(req.url);
      const search = searchParams.get('search') || '';
      const type = searchParams.get('type') || undefined;
      const categoryId = searchParams.get('categoryId') || undefined;
      const includeArchived = searchParams.get('includeArchived') === '1';
      const page = parseInt(searchParams.get('page') || '1');
      const pageSize = parseInt(searchParams.get('pageSize') || '50');
      const skip = (page - 1) * pageSize;

      const where: any = {};
      if (!includeArchived) where.isArchived = false;
      if (categoryId) where.categoryId = categoryId;
      if (type) where.type = type;
      if (search) {
        where.OR = [
          { title: { contains: search, mode: 'insensitive' } },
          { content: { contains: search, mode: 'insensitive' } },
          { tags: { has: search } },
        ];
      }

      const [items, total] = await Promise.all([
        prisma.template.findMany({
          where,
          orderBy: [{ isArchived: 'asc' }, { updatedAt: 'desc' }],
          include: { category: true, createdBy: { select: { id: true, name: true } }, updatedBy: { select: { id: true, name: true } } },
          skip,
          take: pageSize,
        }),
        prisma.template.count({ where }),
      ]);

      return {
        items,
        total,
        page,
        pageSize,
        totalPages: Math.ceil(total / pageSize),
      };
    }, req);

    return NextResponse.json(data);
  } catch (err: any) {
    console.error('Templates GET failed:', err);
    await SecurityService.logAction({ action: 'LIST', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to load templates', detail: err?.message }, { status: 500 });
  }
}

// POST /api/templates - create new template
export async function POST(req: NextRequest) {
  try {
    const body = await req.json();
    const data = await withSecurity(Resource.TEMPLATES, Action.CREATE, async (context) => {
      const { title, content, type, tags, categoryId } = body as {
        title: string; content: string; type?: string; tags?: string[]; categoryId?: string | null;
      };

      if (!title || !content) {
        return NextResponse.json({ error: 'Title and content are required' }, { status: 400 });
      }

      const template = await prisma.template.create({
        data: {
          title,
          content: sanitizeEmailHtml(content),
          type: (type as any) || 'GENERAL',
          tags: tags || [],
          categoryId: categoryId || null,
          createdById: context.userId,
          updatedById: context.userId,
        },
      });

      return template;
    }, req);

    if ('status' in (data as any)) return data as any; // early return if validation response
    return NextResponse.json(data, { status: 201 });
  } catch (err: any) {
    console.error('Templates POST failed:', err);
    await SecurityService.logAction({ action: 'CREATE', resource: 'TEMPLATES', success: false, errorMsg: err?.message }, req);
    return NextResponse.json({ error: 'Failed to create template', detail: err?.message }, { status: 500 });
  }
}

import { NextRequest } from "next/server";
import { getAuthenticatedUser, createErrorResponse, createSuccessResponse } from "@/lib/auth-api";
import { hasPermission } from "@/lib/permissions";
import { Resource, Action } from "@prisma/client";
import { prisma } from "@/lib/db";

export async function GET(req: NextRequest) {
  try {
    const user = await getAuthenticatedUser(req);
    if (!user) return createErrorResponse("Unauthorized", 401);

    // Allow users with USERS:READ or REPORTS:READ to list territories
    if (!hasPermission(user, Resource.USERS, Action.READ) && !hasPermission(user, Resource.REPORTS, Action.READ)) {
      return createErrorResponse("Forbidden", 403);
    }

    const territories = await prisma.territory.findMany({
      where: { isActive: true },
      select: { id: true, name: true },
      orderBy: { name: 'asc' },
    });

    return createSuccessResponse({ territories });
  } catch (error: any) {
    console.error("Error fetching territories:", error);
    return createErrorResponse(error.message || "Failed to fetch territories", 500);
  }
}

import { NextRequest } from "next/server";
import { Resource, Action } from "@prisma/client";
import { getAuthenticatedUser, createErrorResponse, createSuccessResponse } from "@/lib/auth-api";
import { hasPermission } from "@/lib/permissions";
import { prisma } from "@/lib/db";

export async function GET(req: NextRequest) {
  try {
    // Get authenticated user with permissions
    const user = await getAuthenticatedUser(req);
    if (!user) {
      return createErrorResponse("Unauthorized", 401);
    }

    const { searchParams } = new URL(req.url);
    const includeStats = searchParams.get('includeStats') === 'true';
    const q = (searchParams.get('search') || '').trim();
    const forMessaging = q.length > 0;

    // Allow minimal user search when used for messaging, even if the role
    // doesn't have full USERS:READ permission.
    if (!hasPermission(user, Resource.USERS, Action.READ)) {
      if (!(forMessaging && hasPermission(user, Resource.MESSAGING, Action.READ))) {
        return createErrorResponse("Forbidden", 403);
      }
    }

    // Determine what users the current user can see
    let userFilter: any = {};

    if (hasPermission(user, Resource.USERS, Action.READ)) {
      if (hasPermission(user, Resource.USERS, Action.VIEW_ALL)) {
        // Can see all users
        userFilter = {};
      } else if (hasPermission(user, Resource.USERS, Action.VIEW_TEAM)) {
        // Can see users in their territory or managed territories
        if (user.role === 'MANAGER' && user.managedTerritories && user.managedTerritories.length > 0) {
          const managedTerritoryIds = user.managedTerritories.map(t => t.id);
          userFilter = {
            OR: [
              { territoryId: { in: managedTerritoryIds } },
              { id: user.id } // Plus themselves
            ]
          };
        } else if (user.territoryId) {
          userFilter = {
            OR: [
              { territoryId: user.territoryId },
              { id: user.id } // Plus themselves
            ]
          };
        } else {
          // If no territory, can only see themselves
          userFilter = { id: user.id };
        }
      } else {
        // Can only see themselves
        userFilter = { id: user.id };
      }
    } else if (forMessaging && hasPermission(user, Resource.MESSAGING, Action.READ)) {
      // For chat user search, allow searching active users across org
      userFilter = {};
    }

    const users = await prisma.user.findMany({
      where: {
        ...userFilter,
        isActive: true,
        ...(q
          ? {
              OR: [
                { name: { contains: q, mode: 'insensitive' } },
                { email: { contains: q, mode: 'insensitive' } },
              ],
            }
          : {}),
      },
      include: {
        territory: {
          select: {
            id: true,
            name: true
          }
        },
        ...(includeStats && {
          _count: {
            select: {
              leads: true,
              pipelineItems: true,
              activityLogs: true
            }
          }
        })
      },
      orderBy: [
        { role: 'asc' },
        { name: 'asc' }
      ]
    });

    const payload = users.map(user => ({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        territory: user.territory,
        lastLoginAt: user.lastLoginAt,
        createdAt: user.createdAt,
        ...(includeStats && { _count: user._count })
      }));

    // If request is clearly for messaging search (has search query), return a flat users array
    if ((searchParams.get('search') || '').trim()) {
      return createSuccessResponse({ users: payload });
    }
    // Back-compat default shape
    return createSuccessResponse({ data: { users: payload } });

  } catch (error: any) {
    console.error("Error fetching users:", error);
    return createErrorResponse(error.message || "Failed to fetch users", 500);
  }
}

"use client";

import { Suspense, useState } from "react";
import { signIn, getSession } from "next-auth/react";
import { useRouter, useSearchParams } from "next/navigation";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Shield, Eye, EyeOff, Smartphone, Monitor } from "lucide-react";

const loginSchema = z.object({
  email: z.string().email("Please enter a valid email address"),
  password: z.string().min(1, "Password is required"),
});

type LoginFormData = z.infer<typeof loginSchema>;

function LoginFormComponent() {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showPassword, setShowPassword] = useState(false);
  const router = useRouter();
  const searchParams = useSearchParams();
  const callbackUrl = searchParams.get("callbackUrl") || "/";

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      email: "",
      password: "",
    },
  });

  async function onSubmit(values: LoginFormData) {
    setIsLoading(true);
    setError(null);

    try {
      const result = await signIn("credentials", {
        email: values.email,
        password: values.password,
        redirect: false,
      });

      if (result?.error) {
        setError("Invalid email or password. Please try again.");
        setIsLoading(false);
        return;
      }

      // Check if sign in was successful by getting the session
      const session = await getSession();
      if (session) {
        router.push(callbackUrl);
        router.refresh();
      } else {
        setError("Login failed. Please try again.");
        setIsLoading(false);
      }
    } catch (error) {
      console.error("Login error:", error);
      setError("An unexpected error occurred. Please try again.");
      setIsLoading(false);
