'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { LeadsTable } from '@/components/leads-table';
// import { KanbanView } from '@/components/kanban-view'; // Disabled - available for future restoration
import { useLeadColumns } from '@/components/lead-columns';
import { useLeads, useBdrManager } from '@/lib/hooks';
// import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'; // Disabled - available for future restoration
import { ErrorBoundary } from 'react-error-boundary';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
  DropdownMenuCheckboxItem,
} from '@/components/ui/dropdown-menu';
import { Columns, Filter, Plus, Sparkles } from 'lucide-react';
import { leadSourceEnum, leadStatusEnum } from '@/lib/validations';
import { VisibilityState } from '@tanstack/react-table';
import { Lead } from '@/lib/hooks';
import { Navbar } from '@/components/ui/navbar';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { useRouter } from 'next/navigation';
import { LeadsSavedViews, LeadsView } from '@/components/leads-saved-views';

function ErrorFallback({ error, resetErrorBoundary }: { error: Error; resetErrorBoundary: () => void }) {
  return (
    <div className="rounded-lg bg-destructive/10 p-6 text-center">
      <h2 className="mb-2 text-lg font-semibold">Error loading leads</h2>
      <p className="mb-4 text-sm text-muted-foreground">{error.message}</p>
      <Button onClick={resetErrorBoundary}>Try again</Button>
    </div>
  );
}

function LeadsContent() {
  const [pageIndex, setPageIndex] = useState(0);
  const [pageSize, setPageSize] = useState(10);
  const [globalFilter, setGlobalFilter] = useState('');
  const [filters, setFilters] = useState<Record<string, string>>({});
  const [columnVisibility, setColumnVisibility] = useState<VisibilityState>({});
  // Initialize to a fixed value to avoid hydration mismatch; read real value after mount
  const [beginnerMode, setBeginnerMode] = useState<boolean>(false);
  const resizeTimeout = useRef<NodeJS.Timeout | null>(null);
  const isUpdatingVisibility = useRef(false);
  const { bdrs, addBdr } = useBdrManager();
  const router = useRouter();

  const { data, isLoading, error } = useLeads({
    page: pageIndex + 1,
    pageSize,
    search: globalFilter || undefined,
    ...filters
  });

  const leads = data?.leads ?? [];
  const total = data?.total ?? 0;
  const pageCount = Math.ceil(total / pageSize);

  const columns = useLeadColumns();

  // Safe column visibility setter to prevent infinite loops
  const safeSetColumnVisibility = useCallback((newState: VisibilityState) => {
    if (isUpdatingVisibility.current) return;
    isUpdatingVisibility.current = true;
    
    setColumnVisibility(prev => {
      // Replace visibility entirely to ensure predictable layouts
      const updated = { ...newState };
      
      // Schedule a reset of the updating flag after this render cycle
      setTimeout(() => {
        isUpdatingVisibility.current = false;
      }, 0);
      
      return updated;
    });
  }, []);

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the BDR filter if it's currently set
      if (filters.bdr) {
        setFilters(prev => ({ ...prev, bdr: newBdr }));
      }
    }
    return success;
  };

  // Create a memoized function to update visibility based on screen size
  const updateColumnVisibility = useCallback(() => {
    // Get screen width
    const width = window.innerWidth;
    const newVisibility: VisibilityState = {};
    
    if (beginnerMode) {
      // Beginner: show only high priority columns on all screen sizes
      columns.forEach(column => {
        const colId = (column as any).id ?? (column as any).accessorKey;
        if (!colId) return;
        const priority = (column.meta as any)?.priority;
        newVisibility[colId] = priority === 'high';
      });
    } else if (width >= 1280) {
      // Advanced large screens: show all columns
      columns.forEach(column => {
        const colId = (column as any).id ?? (column as any).accessorKey;
        if (!colId) return;
        newVisibility[colId] = true;
      });
    } else if (width >= 768) {
      // Advanced medium screens: show high and medium
      columns.forEach(column => {
        const colId = (column as any).id ?? (column as any).accessorKey;
        if (!colId) return;
        const priority = (column.meta as any)?.priority;
        newVisibility[colId] = priority === 'high' || priority === 'medium';
      });
    } else {
      // Advanced small screens: show only high
      columns.forEach(column => {
        const colId = (column as any).id ?? (column as any).accessorKey;
        if (!colId) return;
        const priority = (column.meta as any)?.priority;
        newVisibility[colId] = priority === 'high';
      });
    }

    safeSetColumnVisibility(newVisibility);
  }, [columns, safeSetColumnVisibility, beginnerMode]);

  // Set default column visibility based on screen size and beginner mode
  useEffect(() => {
    // Initialize column visibility
    updateColumnVisibility();

    // Debounced resize handler to prevent excessive updates
    const handleResize = () => {
      if (resizeTimeout.current) {
        clearTimeout(resizeTimeout.current);
      }
      
      resizeTimeout.current = setTimeout(() => {
        updateColumnVisibility();
        resizeTimeout.current = null;
      }, 200); // 200ms debounce
    };

    // Add event listener
    window.addEventListener('resize', handleResize);
    
    // Cleanup
    return () => {
      window.removeEventListener('resize', handleResize);
      if (resizeTimeout.current) {
        clearTimeout(resizeTimeout.current);
      }
    };
  }, [updateColumnVisibility]);

  // Read beginner mode from localStorage after mount to avoid hydration mismatch
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const stored = window.localStorage.getItem('leads.beginnerMode');
      if (stored !== null) {
        setBeginnerMode(stored === 'true');
      } else {
        // Default to true if not set
        setBeginnerMode(true);
      }
    }
  }, []);

  // Persist beginner mode
  useEffect(() => {
    if (typeof window !== 'undefined') {
      window.localStorage.setItem('leads.beginnerMode', beginnerMode ? 'true' : 'false');
    }
  }, [beginnerMode]);

  // Recompute column visibility when beginner mode toggles
  useEffect(() => {
    updateColumnVisibility();
  }, [beginnerMode, updateColumnVisibility]);

  const handlePaginationChange = (newPageIndex: number, newPageSize: number) => {
    setPageIndex(newPageIndex);
    setPageSize(newPageSize);
  };

  const handleGlobalFilterChange = (value: string) => {
    setGlobalFilter(value);
    setPageIndex(0);
  };

  const handleFilterChange = (columnId: string, value: string) => {
    setFilters(prev => {
      const newFilters = { ...prev };
      if (value === 'all') {
        delete newFilters[columnId];
      } else {
        newFilters[columnId] = value;
      }
      return newFilters;
    });
    setPageIndex(0);
  };

  const getActiveFilters = () => {
    const activeFilters = Object.entries(filters).map(([key, value]) => {
      const label = key.toUpperCase();
      return `${label}: ${value}`;
    });
    return activeFilters.length ? activeFilters.join(', ') : 'Filter';
  };

  // Handle column visibility changes from the dropdown
  const handleColumnVisibilityChange = useCallback((columnId: string, isVisible: boolean) => {
    safeSetColumnVisibility({
      ...columnVisibility,
      [columnId]: isVisible
    });
  }, [columnVisibility, safeSetColumnVisibility]);

  return (
    <>
      <Navbar />
      <div className="space-y-4">
        <div className="flex flex-wrap items-center justify-between gap-2">
          <div className="flex gap-2">
            <Button 
              onClick={() => router.push('/leads/new')}
              className="h-9"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add New Lead
            </Button>
            <Button
              variant={beginnerMode ? 'secondary' : 'outline'}
              className="h-9 px-3"
              onClick={() => setBeginnerMode((v) => !v)}
            >
              <Sparkles className="h-4 w-4 mr-2" />
              {beginnerMode ? 'Beginner Mode' : 'Advanced Mode'}
            </Button>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline" className="h-9 px-3">
                  <Columns className="h-4 w-4 sm:mr-2" />
                  <span className="hidden sm:inline">Columns</span>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="w-48">
                <DropdownMenuLabel>Toggle Columns</DropdownMenuLabel>
                <DropdownMenuSeparator />
                {columns.map((column) => {
                  // Skip checkbox column
                  if (column.id === 'select') return null;
                  
                  // Skip columns without IDs
                  if (!column.id) return null;
                  
                  // Get a readable header name
                  let headerName = column.id;
                  if (typeof column.header === 'string') {
                    headerName = column.header;
                  } else if (column.id) {
                    // Format the ID as readable text
                    headerName = column.id
                      .replace(/([A-Z])/g, ' $1') // Insert space before capital letters
                      .replace(/^./, str => str.toUpperCase()); // Capitalize first letter
                  }
                  
                  return (
                    <DropdownMenuCheckboxItem
                      key={column.id}
                      checked={!!columnVisibility[column.id]}
                      onCheckedChange={(checked) => {
                        handleColumnVisibilityChange(column.id!, checked);
                      }}
                      disabled={beginnerMode}
                    >
                      {headerName}
                    </DropdownMenuCheckboxItem>
                  );
                })}
              </DropdownMenuContent>
            </DropdownMenu>
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline" className="h-9 px-3">
                  <Filter className="h-4 w-4 sm:mr-2" />
                  <span className="hidden sm:inline">{getActiveFilters()}</span>
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="w-48">
                <DropdownMenuLabel>Filter By</DropdownMenuLabel>
                <DropdownMenuSeparator />
                <DropdownMenuItem 
                  className={filters.status ? 'bg-muted' : ''}
                  onClick={() => setFilters(prev => {
                    const newFilters = { ...prev };
                    if (newFilters.status) {
                      delete newFilters.status;
                    } else {
                      newFilters.status = leadStatusEnum[0];
                    }
                    return newFilters;
                  })}
                >
                  Status {filters.status && 'âœ“'}
                </DropdownMenuItem>
                <DropdownMenuItem 
                  className={filters.source ? 'bg-muted' : ''}
                  onClick={() => setFilters(prev => {
                    const newFilters = { ...prev };
                    if (newFilters.source) {
                      delete newFilters.source;
                    } else {
                      newFilters.source = leadSourceEnum[0];
                    }
                    return newFilters;
                  })}
                >
                  Source {filters.source && 'âœ“'}
                </DropdownMenuItem>
                <DropdownMenuItem 
                  className={filters.bdr ? 'bg-muted' : ''}
                  onClick={() => setFilters(prev => {
                    const newFilters = { ...prev };
                    if (newFilters.bdr) {
                      delete newFilters.bdr;
                    } else {
                      newFilters.bdr = bdrs[0] || '';
                    }
                    return newFilters;
                  })}
                >
                  BDR {filters.bdr && 'âœ“'}
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                <DropdownMenuItem asChild>
                  <AddBdrDialog onAddBdr={handleAddBdr} />
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
          <LeadsSavedViews
            filters={filters}
            columnVisibility={columnVisibility as Record<string, boolean>}
            onApply={(view: LeadsView) => {
              setFilters(view.filters);
              safeSetColumnVisibility(view.columnVisibility as unknown as VisibilityState);
            }}
          />
        </div>
        {/* Enterprise control surface */}
        <div className="rounded-xl border border-white/30 dark:border-white/10 bg-white/60 dark:bg-white/[0.05] backdrop-blur p-3">
          <div className="text-sm text-muted-foreground">Use the search, filters and saved views to quickly segment leads.</div>
        </div>
        <LeadsTable
          data={leads}
          columns={columns}
          pageCount={pageCount}
          pageIndex={pageIndex}
          pageSize={pageSize}
          total={total}
          onPaginationChange={handlePaginationChange}
          onGlobalFilterChange={handleGlobalFilterChange}
          filters={filters}
          onFilterChange={handleFilterChange}
          isLoading={isLoading}
          columnVisibility={columnVisibility}
          onColumnVisibilityChange={safeSetColumnVisibility}
        />
      </div>
    </>
  );
}

export default function LeadsPage() {
  return (
    <ErrorBoundary FallbackComponent={ErrorFallback}>
      <LeadsContent />
    </ErrorBoundary>
  );
}

'use client';

import { useSession } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Progress } from '@/components/ui/progress';
import { CheckCircle, User, Shield, Key, ArrowRight, Eye, EyeOff } from 'lucide-react';
import { toast } from 'sonner';

interface OnboardingStep {
  id: string;
  title: string;
  description: string;
  completed: boolean;
}

export default function OnboardingPage() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const [currentStep, setCurrentStep] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    newPassword: '',
    confirmPassword: '',
  });

  const [steps, setSteps] = useState<OnboardingStep[]>([
    {
      id: 'welcome',
      title: 'Welcome to EMG CRM',
      description: 'Learn about your role and access',
      completed: false,
    },
    {
      id: 'profile',
      title: 'Complete Your Profile',
      description: 'Set up your name and preferences',
      completed: false,
    },
    {
      id: 'security',
      title: 'Set Up Security',
      description: 'Change your password for security',
      completed: false,
    },
    {
      id: 'finish',
      title: 'Ready to Start',
      description: 'Your account is configured',
      completed: false,
    },
  ]);

  useEffect(() => {
    if (session?.user) {
      setFormData(prev => ({
        ...prev,
        name: session.user.name || '',
      }));
    }
  }, [session]);

  // Redirect if already completed onboarding (user has a name set)
  useEffect(() => {
    if (status !== 'loading' && session?.user?.name && session.user.name.trim().length > 0) {
      router.push('/');
    }
  }, [session, status, router]);

  const markStepCompleted = (stepIndex: number) => {
    setSteps(prev => prev.map((step, index) => 
      index === stepIndex ? { ...step, completed: true } : step
    ));
  };

  const nextStep = () => {
    if (currentStep < steps.length - 1) {
      markStepCompleted(currentStep);
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleProfileUpdate = async () => {
    if (!formData.name.trim()) {
      toast.error('Please enter your full name');
      return;
    }

    try {
      const res = await fetch(`/api/admin/users/${session?.user?.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: formData.name }),
      });

      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to update profile');
      }

      toast.success('Profile updated successfully');
      nextStep();
    } catch (error) {
      toast.error(error instanceof Error ? error.message : 'An error occurred');
    }
  };

  const handlePasswordUpdate = async () => {
    if (!formData.newPassword || !formData.confirmPassword) {
      toast.error('Please enter and confirm your new password');
      return;
    }

    if (formData.newPassword.length < 6) {
      toast.error('Password must be at least 6 characters');
      return;
    }

    if (formData.newPassword !== formData.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    try {
      const res = await fetch(`/api/admin/users/${session?.user?.id}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ password: formData.newPassword }),
      });

      if (!res.ok) {
        const error = await res.json();
        throw new Error(error.error || 'Failed to update password');
      }

      toast.success('Password updated successfully');
      nextStep();
    } catch (error) {
      toast.error(error instanceof Error ? error.message : 'An error occurred');
    }
  };

  const finishOnboarding = () => {
    markStepCompleted(currentStep);
    toast.success('Welcome to EMG CRM! Your account is ready to use.');
    router.push('/');
  };

  // Handle loading and authentication states
  if (status === 'loading') {
    return <div className="min-h-screen bg-background flex items-center justify-center p-6">
      <div className="text-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
        <p>Loading...</p>
      </div>
    </div>;
  }

  if (status === 'unauthenticated' || !session) {
    if (typeof window !== 'undefined') {
      router.push('/auth/login');
    }
    return null;
  }

  const isAdmin = session.user?.role === 'ADMIN';
  const progress = ((currentStep + 1) / steps.length) * 100;

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-6">
      <div className="max-w-2xl w-full space-y-6">
        <div className="text-center space-y-2">
          <h1 className="text-3xl font-bold">Welcome to EMG CRM</h1>
          <p className="text-muted-foreground">Let's set up your account to get started</p>
        </div>

        {/* Progress Bar */}
        <Card>
          <CardContent className="pt-6">
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Setup Progress</span>
                <span>{Math.round(progress)}% Complete</span>
              </div>
              <Progress value={progress} />
            </div>
            
            <div className="mt-4 grid grid-cols-1 md:grid-cols-4 gap-2">
              {steps.map((step, index) => (
                <div
                  key={step.id}
                  className={`flex items-center gap-2 p-2 rounded text-xs ${
                    index === currentStep
                      ? 'bg-primary text-primary-foreground'
                      : step.completed
                      ? 'bg-green-100 text-green-800 dark:bg-green-800 dark:text-green-100'
                      : 'bg-muted text-muted-foreground'
                  }`}
                >
                  {step.completed ? (
                    <CheckCircle className="h-3 w-3" />
                  ) : (
                    <div className="h-3 w-3 rounded-full border border-current" />
                  )}
                  <span className="font-medium">{step.title}</span>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Step Content */}
        <Card className="min-h-96">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              {currentStep === 0 && <Shield className="h-5 w-5" />}
              {currentStep === 1 && <User className="h-5 w-5" />}
              {currentStep === 2 && <Key className="h-5 w-5" />}
              {currentStep === 3 && <CheckCircle className="h-5 w-5" />}
              {steps[currentStep].title}
            </CardTitle>
            <CardDescription>{steps[currentStep].description}</CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            
            {/* Welcome Step */}
            {currentStep === 0 && (
              <div className="space-y-4">
                <div className="flex items-center gap-3">
                  <h3 className="text-lg font-medium">Hello, {session.user.email}</h3>
                  <Badge variant={isAdmin ? "default" : "secondary"} className="gap-1">
                    {isAdmin ? <Shield className="h-3 w-3" /> : <User className="h-3 w-3" />}
                    {session.user.role}
                  </Badge>
                </div>
                
                <div className="space-y-3">
                  <p>Welcome to the EMG CRM system! You've been granted <strong>{session.user.role}</strong> access.</p>
                  
                  {isAdmin ? (
                    <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-4">
                      <h4 className="font-medium text-blue-900 dark:text-blue-100 mb-2">Admin Access</h4>
                      <ul className="text-sm text-blue-800 dark:text-blue-200 space-y-1">
                        <li>â€¢ Full access to all leads, pipeline, and financial data</li>
                        <li>â€¢ User management and system administration</li>
                        <li>â€¢ Advanced reporting and analytics</li>
                        <li>â€¢ System configuration and settings</li>
                      </ul>
                    </div>
                  ) : (
                    <div className="bg-green-50 dark:bg-green-900/20 border border-green-200 dark:border-green-800 rounded-lg p-4">
                      <h4 className="font-medium text-green-900 dark:text-green-100 mb-2">BDR Access</h4>
                      <ul className="text-sm text-green-800 dark:text-green-200 space-y-1">
                        <li>â€¢ Access to your assigned leads and pipeline items</li>
                        <li>â€¢ Track your activities and performance</li>
                        <li>â€¢ Manage your calls, agreements, and partner lists</li>
                        <li>â€¢ View your individual reporting dashboard</li>
                      </ul>
                    </div>
                  )}
                </div>
                
                <Button onClick={nextStep} className="w-full">
                  Get Started <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              </div>
            )}

            {/* Profile Step */}
            {currentStep === 1 && (
              <div className="space-y-4">
                <p>Let's set up your profile information to personalize your experience.</p>
                
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="full-name">Full Name</Label>
                    <Input
                      id="full-name"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      placeholder="Enter your full name"
                    />
                  </div>
                  
                  <div>
                    <Label>Email Address</Label>
                    <Input value={session.user.email || ''} disabled />
                    <p className="text-xs text-muted-foreground mt-1">Your email cannot be changed during onboarding.</p>
                  </div>
                  
                  <div>
                    <Label>Role</Label>
                    <div className="mt-1">
                      <Badge variant={isAdmin ? "default" : "secondary"} className="gap-1">
                        {isAdmin ? <Shield className="h-3 w-3" /> : <User className="h-3 w-3" />}
                        {session.user.role}
                      </Badge>
                    </div>
                  </div>
                </div>
                
                <Button onClick={handleProfileUpdate} className="w-full">
                  Continue <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              </div>
            )}

            {/* Security Step */}
            {currentStep === 2 && (
              <div className="space-y-4">
                <p>For security, please set a new password that you'll remember.</p>
                
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="new-password">New Password</Label>
                    <div className="relative">
                      <Input
                        id="new-password"
                        type={showPassword ? "text" : "password"}
                        value={formData.newPassword}
                        onChange={(e) => setFormData({ ...formData, newPassword: e.target.value })}
                        placeholder="Enter new password (min 6 characters)"
                      />
                      <Button
                        type="button"
                        variant="ghost"
                        size="sm"
                        className="absolute right-0 top-0 h-full px-3"
                        onClick={() => setShowPassword(!showPassword)}
                      >
                        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                      </Button>
                    </div>
                  </div>
                  
                  <div>
                    <Label htmlFor="confirm-password">Confirm New Password</Label>
                    <Input
                      id="confirm-password"
                      type={showPassword ? "text" : "password"}
                      value={formData.confirmPassword}
                      onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })}
                      placeholder="Confirm your new password"
                    />
                  </div>
                  
                  <div className="text-sm text-muted-foreground space-y-1">
                    <p>Password requirements:</p>
                    <ul className="list-disc list-inside space-y-0.5 ml-2">
                      <li>At least 6 characters long</li>
                      <li>Should be unique and memorable to you</li>
                    </ul>
                  </div>
                </div>
                
                <Button onClick={handlePasswordUpdate} className="w-full">
                  Set Password <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              </div>
            )}

            {/* Finish Step */}
            {currentStep === 3 && (
              <div className="space-y-6 text-center">
                <div className="mx-auto w-16 h-16 bg-green-100 dark:bg-green-900 rounded-full flex items-center justify-center">
                  <CheckCircle className="h-8 w-8 text-green-600 dark:text-green-400" />
                </div>
                
                <div>
                  <h3 className="text-xl font-semibold">You're All Set!</h3>
                  <p className="text-muted-foreground mt-2">Your EMG CRM account is ready to use.</p>
                </div>
                
                <div className="bg-muted rounded-lg p-4 text-left space-y-2">
                  <h4 className="font-medium">Next Steps:</h4>
                  <ul className="text-sm text-muted-foreground space-y-1">
                    <li>â€¢ Explore your dashboard and available tools</li>
                    <li>â€¢ {isAdmin ? 'Start managing users and system settings' : 'Begin working with your assigned leads and pipeline'}</li>
                    <li>â€¢ Check out the reporting section for insights</li>
                    <li>â€¢ Visit your profile anytime to update your information</li>
                  </ul>
                </div>
                
                <Button onClick={finishOnboarding} size="lg" className="w-full">
                  Enter EMG CRM <ArrowRight className="h-4 w-4 ml-2" />
                </Button>
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

'use client';

import { Suspense, useEffect, useState } from 'react';
import { ErrorBoundary } from 'react-error-boundary';
import { Button } from '@/components/ui/button';
import { Navbar } from '@/components/ui/navbar';
import { useSession } from 'next-auth/react';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Users, RefreshCcw, LayoutGrid, Table2, HelpCircle, Sparkles } from 'lucide-react';
import { PipelineBoardEnhanced } from '@/components/pipeline-board-enhanced';
import { PipelineTable } from '@/components/pipeline-table';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { usePipelineItems, useBdrManager } from '@/lib/hooks';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { useQueryClient } from '@tanstack/react-query';

// Define the BDR type for TypeScript
type BdrType = string;
type ViewType = 'board' | 'table';

function ErrorFallback({ error, resetErrorBoundary }: { error: Error; resetErrorBoundary: () => void }) {
  return (
    <div className="rounded-lg bg-destructive/10 p-6 text-center">
      <h2 className="mb-2 text-lg font-semibold">Error loading pipeline data</h2>
      <p className="mb-4 text-sm text-muted-foreground">{error.message}</p>
      <Button onClick={resetErrorBoundary}>Try again</Button>
    </div>
  );
}

function PipelineContent() {
  const { data: session } = useSession();
  const { bdrs, addBdr, getDefaultBdr } = useBdrManager(session?.user?.name);
  const [selectedBdr, setSelectedBdr] = useState<BdrType>(getDefaultBdr());
  const [viewType, setViewType] = useState<ViewType>('board');
  const queryClient = useQueryClient();

  const { data, isLoading, refetch } = usePipelineItems({
    bdr: selectedBdr,
    page: 1,
    pageSize: 50,
  });

  const pipelineItems = data?.items ?? [];

  // Handle refresh of data
  const handleRefresh = () => {
    // Invalidate all pipeline queries regardless of filters
    queryClient.invalidateQueries({ 
      predicate: (query) => {
        return Array.isArray(query.queryKey) && query.queryKey[0] === 'pipeline';
      }
    });
  };

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the selected BDR to the newly added one
      setSelectedBdr(newBdr);
    }
    return success;
  };

  // Update selected BDR when session loads or BDRs change
  useEffect(() => {
    const defaultBdr = getDefaultBdr();
    if (defaultBdr && selectedBdr !== defaultBdr) {
      setSelectedBdr(defaultBdr);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [session?.user?.name, bdrs, selectedBdr]);

  // Simple Mode for non-technical BDRs - start with default to avoid hydration mismatch
  const [simpleMode, setSimpleMode] = useState<boolean>(true);
  const [isMounted, setIsMounted] = useState(false);

  useEffect(() => {
    // Load from localStorage only after component mounts
    setIsMounted(true);
    const stored = window.localStorage.getItem('pipeline.simpleMode');
    if (stored) {
      setSimpleMode(stored === 'true');
    }
  }, []);

  useEffect(() => {
    // Save to localStorage when simpleMode changes (but only after initial mount)
    if (isMounted) {
      window.localStorage.setItem('pipeline.simpleMode', simpleMode ? 'true' : 'false');
    }
  }, [simpleMode, isMounted]);

  return (
    <>
      <Navbar />
      <div className="space-y-6">
        <div className="flex flex-col gap-2 sm:flex-row sm:items-center sm:justify-between">
          <div>
            <h1 className="text-2xl font-bold tracking-tight">Sales Pipeline</h1>
            <p className="text-muted-foreground">Track and manage your deals through the sales process</p>
          </div>
          <div className="flex items-center gap-2">
            {/* View Toggle */}
            <div className="flex items-center rounded-lg border border-white/30 dark:border-white/10 bg-white/60 dark:bg-white/[0.05] backdrop-blur">
              <Button
                variant={viewType === 'board' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewType('board')}
                className="rounded-r-none"
              >
                <LayoutGrid className="h-4 w-4 mr-1" />
                Board
              </Button>
              <Button
                variant={viewType === 'table' ? 'default' : 'ghost'}
                size="sm"
                onClick={() => setViewType('table')}
                className="rounded-l-none"
              >
                <Table2 className="h-4 w-4 mr-1" />
                Table
              </Button>
            </div>

            {/* Simple Mode Toggle */}
            <Button
              variant={simpleMode ? 'secondary' : 'outline'}
              size="sm"
              onClick={() => setSimpleMode(v => !v)}
              className="h-9"
              title="Toggle a simplified, guided interface"
            >
              <Sparkles className="h-4 w-4 mr-1" />
              {simpleMode ? 'Simple Mode' : 'Advanced Mode'}
            </Button>
            
            <div className="flex items-center">
              <Users className="mr-2 h-4 w-4 text-muted-foreground" />
              <span className="mr-2 text-sm text-muted-foreground">BDR:</span>
            </div>
            <div className="flex gap-2">
              <Select 
                value={selectedBdr} 
                onValueChange={(value: BdrType) => setSelectedBdr(value)}
              >
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Select BDR" />
                </SelectTrigger>
                <SelectContent>
                  {bdrs.map((bdr) => (
                    <SelectItem key={bdr} value={bdr}>
                      {bdr}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <AddBdrDialog onAddBdr={handleAddBdr} />
            </div>
            <Button variant="outline" size="icon" onClick={handleRefresh} className="ml-2">
              <RefreshCcw className="h-4 w-4" />
            </Button>

            {/* Help */}
            <Popover>
              <PopoverTrigger asChild>
                <Button variant="outline" size="icon" className="ml-1" title="Quick help">
                  <HelpCircle className="h-4 w-4" />
                </Button>
              </PopoverTrigger>
              <PopoverContent side="bottom" align="end" className="w-80">
                <div className="space-y-2 text-sm">
                  <p className="font-medium">How to use the Pipeline</p>
                  <ol className="list-decimal list-inside space-y-1 text-muted-foreground">
                    <li>Select your name in BDR.</li>
                    <li>Click Add Lead to create a new prospect.</li>
                    <li>Use Status to move items forward (Board or Table).</li>
                    <li>Click Updates to jot quick notes after each call.</li>
                  </ol>
                </div>
              </PopoverContent>
            </Popover>
          </div>
        </div>

        {viewType === 'board' ? (
          <PipelineBoardEnhanced
            items={pipelineItems}
            isLoading={isLoading}
            selectedBdr={selectedBdr}
            simpleMode={simpleMode}
            onRefresh={handleRefresh}
          />
        ) : (
          <PipelineTable 
            items={pipelineItems} 
            onRefresh={handleRefresh}
            selectedBdr={selectedBdr}
            simpleMode={simpleMode}
          />
        )}
      </div>
    </>
  );
}

export default function PipelinePage() {
  return (
    <ErrorBoundary FallbackComponent={ErrorFallback}>
      <Suspense>
        <PipelineContent />
      </Suspense>
    </ErrorBoundary>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
// Replace missing Alert import with inline alert UI
import { Navbar } from '@/components/ui/navbar';
import { 
  BarChart3, 
  TrendingUp, 
  Users, 
  Target, 
  Filter, 
  Bell, 
  Download,
  RefreshCw,
  AlertTriangle,
  CheckCircle,
  Clock,
  Award,
  Phone,
  Mail,
  Calendar,
  Zap
} from 'lucide-react';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar
} from 'recharts';

interface BDRPerformance {
  id: string;
  name: string;
  callsToday: number;
  callsWeek: number;
  callsMonth: number;
  agreementsToday: number;
  agreementsWeek: number;
  agreementsMonth: number;
  conversionRate: number;
  avgCallDuration: number;
  leadsAssigned: number;
  leadsContacted: number;
  followUpsScheduled: number;
  weeklyGoalProgress: number;
  monthlyRank: number;
  streak: number;
  lastWeekPerformance: number;
  territory: string;
  experience: string;
  status: 'active' | 'inactive' | 'on_leave';
}

interface Notification {
  id: string;
  type: 'success' | 'warning' | 'error' | 'info';
  title: string;
  message: string;
  timestamp: string;
  read: boolean;
  priority: 'low' | 'medium' | 'high';
}

interface FilterOptions {
  dateRange: string;
  territory: string;
  experience: string;
  status: string;
  minCalls: number;
  maxCalls: number;
  minConversion: number;
  maxConversion: number;
}

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const fetchBDRPerformanceData = async (filters: FilterOptions) => {
  const params = new URLSearchParams();
  Object.entries(filters).forEach(([key, value]) => {
    if (value) params.append(key, value.toString());
  });

  const response = await fetch(`/api/reporting/advanced/bdr-performance?${params.toString()}`);
  if (!response.ok) {
    throw new Error('Failed to fetch BDR performance data');
  }
  return response.json();
};

const fetchNotifications = async () => {
  const response = await fetch('/api/reporting/advanced/notifications');
  if (!response.ok) {
    throw new Error('Failed to fetch notifications');
  }
  return response.json();
};

export default function AdvancedReportingPage() {
  const [filters, setFilters] = useState<FilterOptions>({
    dateRange: 'week',
    territory: 'all',
    experience: 'all',
    status: 'all',
    minCalls: 0,
    maxCalls: 1000,
    minConversion: 0,
    maxConversion: 100
  });

  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [showNotifications, setShowNotifications] = useState(false);

  const { data: bdrData, isLoading, error, refetch } = useQuery({
    queryKey: ['bdrPerformance', filters],
    queryFn: () => fetchBDRPerformanceData(filters),
  });

  const { data: notificationData } = useQuery({
    queryKey: ['notifications'],
    queryFn: fetchNotifications,
    refetchInterval: 30000, // Refresh every 30 seconds
  });

  useEffect(() => {
    if (notificationData) {
      setNotifications(notificationData);
    }
  }, [notificationData]);

  const unreadNotifications = notifications.filter(n => !n.read);
  const highPriorityNotifications = notifications.filter(n => n.priority === 'high' && !n.read);

  const getPerformanceStatus = (performance: number) => {
    if (performance >= 90) return { status: 'excellent', color: 'text-green-600', bg: 'bg-green-50' };
    if (performance >= 75) return { status: 'good', color: 'text-blue-600', bg: 'bg-blue-50' };
    if (performance >= 60) return { status: 'average', color: 'text-yellow-600', bg: 'bg-yellow-50' };
    return { status: 'needs_improvement', color: 'text-red-600', bg: 'bg-red-50' };
  };

  const exportData = () => {
    const csvContent = "data:text/csv;charset=utf-8," + 
      "Name,Calls Today,Calls Week,Agreements Today,Agreements Week,Conversion Rate,Avg Call Duration,Weekly Goal Progress\n" +
      bdrData?.map((bdr: BDRPerformance) => 
        `${bdr.name},${bdr.callsToday},${bdr.callsWeek},${bdr.agreementsToday},${bdr.agreementsWeek},${bdr.conversionRate}%,${bdr.avgCallDuration}min,${bdr.weeklyGoalProgress}%`
      ).join('\n');
    
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'bdr_performance_report.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <>
      <Navbar />
      <div className="space-y-6 p-6">
        {/* Header with Notifications */}
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold">Advanced Business Intelligence</h1>
            <p className="text-muted-foreground">BDR Performance Comparison & Real-time Analytics</p>
          </div>
          <div className="flex items-center space-x-2 rounded-xl border border-white/30 dark:border-white/10 bg-white/60 dark:bg-white/[0.05] backdrop-blur p-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => refetch()}
              className="flex items-center space-x-2"
            >
              <RefreshCw className="h-4 w-4" />
              Refresh
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={exportData}
              className="flex items-center space-x-2"
            >
              <Download className="h-4 w-4" />
              Export
            </Button>
            <div className="relative">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowNotifications(!showNotifications)}
                className="flex items-center space-x-2"
              >
                <Bell className="h-4 w-4" />
                {unreadNotifications.length > 0 && (
                  <Badge variant="destructive" className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs">
                    {unreadNotifications.length}
                  </Badge>
                )}
              </Button>
              {showNotifications && (
                <div className="absolute right-0 top-12 w-80 bg-white/80 dark:bg-white/[0.06] backdrop-blur-xl border border-white/20 dark:border-white/10 rounded-lg shadow-[0_10px_30px_-15px_rgba(0,0,0,0.5)] z-50 max-h-96 overflow-y-auto">
                  <div className="p-4 border-b">
                    <h3 className="font-semibold">Notifications</h3>
                  </div>
                  {notifications.length === 0 ? (
                    <div className="p-4 text-center text-muted-foreground">
                      No notifications
                    </div>
                  ) : (
                    <div className="divide-y">
                      {notifications.map((notification) => (
                        <div key={notification.id} className={`p-4 ${!notification.read ? 'bg-muted/50' : ''}`}>
                          <div className="flex items-start space-x-3">
                            <div className={`mt-1 ${
                              notification.type === 'success' ? 'text-green-500' :
                              notification.type === 'warning' ? 'text-yellow-500' :
                              notification.type === 'error' ? 'text-red-500' : 'text-blue-500'
                            }`}>
                              {notification.type === 'success' && <CheckCircle className="h-4 w-4" />}
                              {notification.type === 'warning' && <AlertTriangle className="h-4 w-4" />}
                              {notification.type === 'error' && <AlertTriangle className="h-4 w-4" />}
                              {notification.type === 'info' && <Clock className="h-4 w-4" />}
                            </div>
                            <div className="flex-1">
                              <div className="flex items-center justify-between">
                                <h4 className="font-medium text-sm">{notification.title}</h4>
                                <Badge variant={notification.priority === 'high' ? 'destructive' : 'secondary'} className="text-xs">
                                  {notification.priority}
                                </Badge>
                              </div>
                              <p className="text-sm text-muted-foreground mt-1">{notification.message}</p>
                              <p className="text-xs text-muted-foreground mt-2">{notification.timestamp}</p>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* High Priority Alerts */}
        {highPriorityNotifications.length > 0 && (
          <div className="border-red-200 bg-red-50 border rounded p-3 flex items-start gap-2">
            <AlertTriangle className="h-4 w-4 text-red-600 mt-0.5" />
            <div className="text-sm text-red-800">
              <strong>{highPriorityNotifications.length} high priority notification(s)</strong> require immediate attention.
            </div>
          </div>
        )}

        {/* Advanced Filters */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <Filter className="h-5 w-5" />
              Advanced Filters
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <label className="text-sm font-medium">Date Range</label>
                <Select value={filters.dateRange} onValueChange={(value) => setFilters({...filters, dateRange: value})}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="today">Today</SelectItem>
                    <SelectItem value="week">This Week</SelectItem>
                    <SelectItem value="month">This Month</SelectItem>
                    <SelectItem value="quarter">This Quarter</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <label className="text-sm font-medium">Territory</label>
                <Select value={filters.territory} onValueChange={(value) => setFilters({...filters, territory: value})}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Territories</SelectItem>
                    <SelectItem value="north">North</SelectItem>
                    <SelectItem value="south">South</SelectItem>
                    <SelectItem value="east">East</SelectItem>
                    <SelectItem value="west">West</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <label className="text-sm font-medium">Experience Level</label>
                <Select value={filters.experience} onValueChange={(value) => setFilters({...filters, experience: value})}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Levels</SelectItem>
                    <SelectItem value="junior">Junior (0-1 year)</SelectItem>
                    <SelectItem value="mid">Mid-level (1-3 years)</SelectItem>
                    <SelectItem value="senior">Senior (3+ years)</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div>
                <label className="text-sm font-medium">Status</label>
                <Select value={filters.status} onValueChange={(value) => setFilters({...filters, status: value})}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="active">Active</SelectItem>
                    <SelectItem value="inactive">Inactive</SelectItem>
                    <SelectItem value="on_leave">On Leave</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mt-4">
              <div>
                <label className="text-sm font-medium">Min Calls</label>
                <Input
                  type="number"
                  value={filters.minCalls}
                  onChange={(e) => setFilters({...filters, minCalls: parseInt(e.target.value) || 0})}
                  placeholder="0"
                />
              </div>
              <div>
                <label className="text-sm font-medium">Max Calls</label>
                <Input
                  type="number"
                  value={filters.maxCalls}
                  onChange={(e) => setFilters({...filters, maxCalls: parseInt(e.target.value) || 1000})}
                  placeholder="1000"
                />
              </div>
              <div>
                <label className="text-sm font-medium">Min Conversion %</label>
                <Input
                  type="number"
                  value={filters.minConversion}
                  onChange={(e) => setFilters({...filters, minConversion: parseInt(e.target.value) || 0})}
                  placeholder="0"
                />
              </div>
              <div>
                <label className="text-sm font-medium">Max Conversion %</label>
                <Input
                  type="number"
                  value={filters.maxConversion}
                  onChange={(e) => setFilters({...filters, maxConversion: parseInt(e.target.value) || 100})}
                  placeholder="100"
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Main Content Tabs */}
        <Tabs defaultValue="comparison" className="space-y-4">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="comparison">Performance Comparison</TabsTrigger>
            <TabsTrigger value="analytics">Advanced Analytics</TabsTrigger>
            <TabsTrigger value="trends">Trends & Patterns</TabsTrigger>
            <TabsTrigger value="rankings">Rankings & Leaderboard</TabsTrigger>
          </TabsList>

          <TabsContent value="comparison" className="space-y-4">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Performance Comparison Chart */}
              <Card>
                <CardHeader>
                  <CardTitle>BDR Performance Comparison</CardTitle>
                  <CardDescription>Weekly call volume and agreement rates</CardDescription>
                </CardHeader>
                <CardContent>
                  {isLoading ? (
                    <div className="h-80 flex items-center justify-center">
                      <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                    </div>
                  ) : (
                    <ResponsiveContainer width="100%" height={320}>
                      <BarChart data={bdrData?.slice(0, 10)}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="name" />
                        <YAxis />
                        <Tooltip />
