                    <Bar key="comparison-calls-proposed" dataKey="callsProposed" fill="#8884d8" name="Calls Proposed" />
                    <Bar key="comparison-calls-booked" dataKey="callsBooked" fill="#82ca9d" name="Calls Booked" />
                    <Bar key="comparison-proposals-sent" dataKey="proposalsSent" fill="#ffc658" name="Proposals" />
                    <Bar key="comparison-agreements" dataKey="agreements" fill="#ff7c7c" name="Agreements" />
                    <Bar key="comparison-sold" dataKey="sold" fill="#00C49F" name="Sales Volume" />
                  </BarChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>

            {/* Daily Activity Trends */}
            <Card>
              <CardHeader>
                <CardTitle>Daily Activity Trends (Last 7 Days)</CardTitle>
              </CardHeader>
              <CardContent>
                <ResponsiveContainer width="100%" height={400}>
                  <LineChart data={data.activityAnalysis.dailyMetrics}>
                    <XAxis dataKey="date" />
                    <YAxis />
                    <Tooltip />
                    <Line key="activity-calls-proposed" type="monotone" dataKey="callsProposed" stroke="#8884d8" name="Calls Proposed" strokeWidth={2} />
                    <Line key="activity-calls-booked" type="monotone" dataKey="callsBooked" stroke="#82ca9d" name="Calls Booked" strokeWidth={2} />
                    <Line key="activity-proposals-sent" type="monotone" dataKey="proposalsSent" stroke="#ffc658" name="Proposals" strokeWidth={2} />
                    <Line key="activity-agreements" type="monotone" dataKey="agreements" stroke="#ff7c7c" name="Agreements" strokeWidth={2} />
                    <Line key="activity-sold" type="monotone" dataKey="sold" stroke="#00C49F" name="Sales Activity" strokeWidth={2} />
                    <Line key="activity-total" type="monotone" dataKey="totalActivity" stroke="#8B5CF6" name="Total Activity" strokeWidth={3} strokeDasharray="5 5" />
                  </LineChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </div>
        </TabsContent>
      </Tabs>
    </div>
    </>
  );
}

"use client";

import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { downloadCsv } from '@/lib/csv';
import { Navbar } from '@/components/ui/navbar';

const fetchListsOut = async (bdr: string, months: number) => {
  const params = new URLSearchParams();
  if (bdr && bdr !== 'all') params.append('bdr', bdr);
  params.append('months', months.toString());

  const res = await fetch(`/api/reporting/lists-out?${params.toString()}`);
  if (!res.ok) throw new Error('Failed to fetch lists out');
  return res.json();
};

const fetchBdrList = async () => {
  const response = await fetch('/api/reporting/executive-dashboard');
  if (!response.ok) {
    throw new Error('Failed to fetch BDR list');
  }
  const data = await response.json();
  return data.dashboard.bdrList;
}

export default function ListsOutReport() {
  const [bdr, setBdr] = useState('all');
  const [months, setMonths] = useState(4);

  const { data: listsOut, isLoading: isLoadingListsOut, error: listsOutError } = useQuery({
    queryKey: ['listsOut', bdr, months],
    queryFn: () => fetchListsOut(bdr, months),
  });

  const { data: bdrList, isLoading: isLoadingBdrList, error: bdrListError } = useQuery({
    queryKey: ['bdrList'],
    queryFn: fetchBdrList,
  });

  const handleExport = () => {
    if (!listsOut) return;
    const rows = listsOut.monthly.map((m: any) => ({ Month: m.month, Lists: m.lists, AvgSize: m.avgSize }));
    downloadCsv('lists-out.csv', rows);
  };

  return (
    <>
      <Navbar />
      <Card>
        <CardHeader>
          <CardTitle>Lists Out Report</CardTitle>
        </CardHeader>
      <CardContent>
        <div className="flex space-x-4 mb-4">
          <Select onValueChange={setBdr} value={bdr}>
            <SelectTrigger>
              <SelectValue placeholder="Select BDR" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All BDRs</SelectItem>
              {bdrList?.map((bdr: string) => (
                <SelectItem key={bdr} value={bdr}>{bdr}</SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Select value={months.toString()} onValueChange={(v) => setMonths(parseInt(v, 10))}>
            <SelectTrigger>
              <SelectValue placeholder="Months" />
            </SelectTrigger>
            <SelectContent>
              {[1,3,4,6,12].map((m) => (
                <SelectItem key={m} value={m.toString()}>Last {m} {m===1?'Month':'Months'}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
        {isLoadingListsOut && <p>Loading...</p>}
        {listsOutError && <p>Error loading lists out data</p>}
        {listsOut && (
          <div className="space-y-6">
            <div className="flex justify-end">
              <button onClick={handleExport} className="text-sm px-3 py-1 border rounded bg-blue-50 hover:bg-blue-100">Export CSV</button>
            </div>
            <Card className="shadow-none border">
              <CardHeader>
                <CardTitle className="text-sm">Monthly Lists-Out</CardTitle>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Month</TableHead>
                      <TableHead className="text-right">Lists Sent</TableHead>
                      <TableHead className="text-right">Avg Size</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {listsOut.monthly.map((m: any) => (
                      <TableRow key={m.month}>
                        <TableCell>{m.month}</TableCell>
                        <TableCell className="text-right">{m.lists}</TableCell>
                        <TableCell className="text-right">{m.avgSize}</TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </CardContent>
            </Card>

            <Card className="shadow-none border">
              <CardHeader>
                <CardTitle className="text-sm">Overall Summary</CardTitle>
              </CardHeader>
              <CardContent>
                <p>Total Lists Out: <strong>{listsOut.overall.totalLists}</strong></p>
                <p>Average List Size: <strong>{listsOut.overall.averageSize}</strong></p>
                <p>Conversion Rate (Sold): <strong>{listsOut.overall.conversionRate}%</strong></p>
              </CardContent>
            </Card>
          </div>
        )}
      </CardContent>
    </Card>
    </>
  );
}

'use client';

import { useState, useEffect } from 'react';
import Link from 'next/link';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { Navbar } from '@/components/ui/navbar';
import { 
  Phone, 
  FileText, 
  Users, 
  TrendingUp, 
  TrendingDown, 
  AlertCircle, 
  CheckCircle, 
  Clock,
  Target,
  DollarSign,
  Calendar,
  BarChart3,
  PieChart,
  Activity,
  Zap
} from 'lucide-react';

interface KPI {
  current: number;
  target: number;
  trend: 'up' | 'down' | 'stable';
  status: 'excellent' | 'good' | 'needs_attention' | 'critical';
}

interface ExecutiveDashboard {
  kpis: {
    steadyCallVolume: KPI;
    agreementRate: KPI;
    listsOut: KPI;
    overallConversion: KPI;
  };
  teamPerformance: {
    totalBDRs: number;
    activeBDRs: number;
    topPerformers: string[];
    needsSupport: string[];
    benchmarkMetrics: {
      avgCallsPerWeek: number;
      avgAgreementsPerMonth: number;
      avgListsPerMonth: number;
      teamConversionRate: number;
    };
  };
  pipelineHealth: {
    upcomingCalls: {
      nextWeek: number;
      next2Weeks: number;
      total: number;
    };
    pendingAgreements: {
      proposalsAwaitingResponse: number;
      agreementsAwaitingLists: number;
      overduePartnerLists: number;
    };
    activeListsOut: {
      total: number;
      smallLists: number;
      mediumLists: number;
      largeLists: number;
      averageListSize: number;
    };
    conversionFunnel: {
      callsBooked: number;
      callsConducted: number;
      proposalsSent: number;
      agreementsSigned: number;
      listsSent: number;
      salesGenerated: number;
    };
  };
  trends: {
    weeklyCallVolume: Array<{
      week: string;
      calls: number;
      target: number;
      variance: number;
    }>;
    monthlyAgreements: Array<{
      month: string;
      agreements: number;
      target: number;
      variance: number;
    }>;
  };
  criticalActions: Array<{
    priority: 'urgent' | 'high' | 'medium';
    category: string;
    action: string;
    assignedTo?: string;
    metric?: number;
    deadline?: string;
  }>;
  financialSummary: {
    monthlyRevenue: number;
    quarterlyRevenue: number;
    revenuePerBDR: number;
    revenuePerCall: number;
    revenuePerList: number;
    forecastAccuracy: number;
  };
  predictions: {
    expectedCallsNextWeek: number;
    expectedAgreementsNextMonth: number;
    expectedRevenueNextQuarter: number;
    riskFactors: string[];
    opportunities: string[];
  };
}

export default function SalesPerformancePage() {
  const [dashboardData, setDashboardData] = useState<ExecutiveDashboard | null>(null);
  const [callVolumeData, setCallVolumeData] = useState<any>(null);
  const [agreementData, setAgreementData] = useState<any>(null);
  const [partnerListData, setPartnerListData] = useState<any>(null);
  const [selectedBDR, setSelectedBDR] = useState<string>('all');
  const [selectedPeriod, setSelectedPeriod] = useState<string>('current');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchAllReportingData();
  }, [selectedBDR, selectedPeriod]);

  const fetchAllReportingData = async () => {
    setLoading(true);
    setError(null);
    
    try {
      const bdrParam = selectedBDR !== 'all' ? `?bdr=${selectedBDR}` : '';
      const periodParam = selectedPeriod !== 'current' ? `?period=${selectedPeriod}` : '';
      
      const [dashboardRes, callVolumeRes, agreementRes, partnerListRes] = await Promise.all([
        fetch(`/api/reporting/executive-dashboard${periodParam}`),
        fetch(`/api/reporting/call-volume${bdrParam}`),
        fetch(`/api/reporting/agreement-tracking${bdrParam}`),
        fetch(`/api/reporting/partner-list-analytics${bdrParam}`)
      ]);

      if (!dashboardRes.ok || !callVolumeRes.ok || !agreementRes.ok || !partnerListRes.ok) {
        throw new Error('Failed to fetch reporting data');
      }

      const [dashboard, callVolume, agreement, partnerList] = await Promise.all([
        dashboardRes.json(),
        callVolumeRes.json(),
        agreementRes.json(),
        partnerListRes.json()
      ]);

      setDashboardData(dashboard.dashboard);
      setCallVolumeData(callVolume);
      setAgreementData(agreement);
      setPartnerListData(partnerList);
    } catch (err) {
      console.error('Error fetching reporting data:', err);
      setError('Failed to load reporting data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'excellent': return 'text-green-600 bg-green-50 border-green-200';
      case 'good': return 'text-blue-600 bg-blue-50 border-blue-200';
      case 'needs_attention': return 'text-yellow-600 bg-yellow-50 border-yellow-200';
      case 'critical': return 'text-red-600 bg-red-50 border-red-200';
      default: return 'text-gray-600 bg-gray-50 border-gray-200';
    }
  };

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case 'up': return <TrendingUp className="h-4 w-4 text-green-600" />;
      case 'down': return <TrendingDown className="h-4 w-4 text-red-600" />;
      default: return <Activity className="h-4 w-4 text-gray-600" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800 border-red-300';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-300';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-300';
      default: return 'bg-gray-100 text-gray-800 border-gray-300';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <Activity className="h-8 w-8 animate-spin mx-auto mb-4" />
          <p>Loading comprehensive sales performance data...</p>
        </div>
      </div>
    );
  }

  if (error || !dashboardData) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <AlertCircle className="h-8 w-8 text-red-500 mx-auto mb-4" />
          <p className="text-red-600">{error || 'Failed to load dashboard data'}</p>
          <Button onClick={fetchAllReportingData} className="mt-4">
            Retry
          </Button>
        </div>
      </div>
    );
  }

  return (
    <>
      <Navbar />
      <div className="container mx-auto p-6 space-y-6">
        {/* Header */}
        <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Sales Performance Dashboard</h1>
          <p className="text-gray-600 mt-1">Comprehensive view of call volume, agreements, and partner list performance</p>
        </div>
        
        <div className="flex gap-4">
          <Select value={selectedBDR} onValueChange={setSelectedBDR}>
            <SelectTrigger className="w-48">
              <SelectValue placeholder="Select BDR" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All BDRs</SelectItem>
              {dashboardData.teamPerformance.topPerformers.map(bdr => (
                <SelectItem key={bdr} value={bdr}>{bdr}</SelectItem>
              ))}
            </SelectContent>
          </Select>
          
          <Select value={selectedPeriod} onValueChange={setSelectedPeriod}>
            <SelectTrigger className="w-36">
              <SelectValue placeholder="Period" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="current">Current</SelectItem>
              <SelectItem value="historical">Historical</SelectItem>
              <SelectItem value="forecast">Forecast</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      {/* Key Performance Indicators */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Call Volume KPI */}
        <Card className={`border-2 ${getStatusColor(dashboardData.kpis.steadyCallVolume.status)}`}>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Phone className="h-4 w-4" />
              Call Volume (This Week)
              {getTrendIcon(dashboardData.kpis.steadyCallVolume.trend)}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{dashboardData.kpis.steadyCallVolume.current}</div>
            <Progress 
              value={(dashboardData.kpis.steadyCallVolume.current / dashboardData.kpis.steadyCallVolume.target) * 100} 
              className="mt-2" 
            />
            <p className="text-xs text-gray-600 mt-1">
              Target: {dashboardData.kpis.steadyCallVolume.target}
            </p>
          </CardContent>
        </Card>

        {/* Agreements KPI */}
        <Card className={`border-2 ${getStatusColor(dashboardData.kpis.agreementRate.status)}`}>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <FileText className="h-4 w-4" />
              Agreements (This Month)
              {getTrendIcon(dashboardData.kpis.agreementRate.trend)}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{dashboardData.kpis.agreementRate.current}</div>
            <Progress 
              value={(dashboardData.kpis.agreementRate.current / dashboardData.kpis.agreementRate.target) * 100} 
              className="mt-2" 
            />
            <p className="text-xs text-gray-600 mt-1">
              Target: {dashboardData.kpis.agreementRate.target}
            </p>
          </CardContent>
        </Card>

        {/* Lists Out KPI */}
        <Card className={`border-2 ${getStatusColor(dashboardData.kpis.listsOut.status)}`}>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Users className="h-4 w-4" />
              Lists Out (This Month)
              {getTrendIcon(dashboardData.kpis.listsOut.trend)}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{dashboardData.kpis.listsOut.current}</div>
            <Progress 
              value={(dashboardData.kpis.listsOut.current / dashboardData.kpis.listsOut.target) * 100} 
              className="mt-2" 
            />
            <p className="text-xs text-gray-600 mt-1">
              Target: {dashboardData.kpis.listsOut.target}
            </p>
          </CardContent>
        </Card>

        {/* Overall Conversion KPI */}
        <Card className={`border-2 ${getStatusColor(dashboardData.kpis.overallConversion.status)}`}>
          <CardHeader className="pb-2">
            <CardTitle className="text-sm font-medium flex items-center gap-2">
              <Target className="h-4 w-4" />
              Conversion Rate
              {getTrendIcon(dashboardData.kpis.overallConversion.trend)}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{dashboardData.kpis.overallConversion.current}%</div>
            <Progress 
              value={(dashboardData.kpis.overallConversion.current / dashboardData.kpis.overallConversion.target) * 100} 
              className="mt-2" 
            />
            <p className="text-xs text-gray-600 mt-1">
              Target: {dashboardData.kpis.overallConversion.target}%
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Critical Actions */}
      {dashboardData.criticalActions.length > 0 && (
        <Card className="border-red-200 bg-red-50">
          <CardHeader>
            <CardTitle className="text-red-800 flex items-center gap-2">
              <AlertCircle className="h-5 w-5" />
              Critical Actions Required
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {dashboardData.criticalActions.slice(0, 3).map((action, index) => (
                <div key={index} className="flex items-start gap-3">
                  <Badge className={`${getPriorityColor(action.priority)} text-xs`}>
                    {action.priority.toUpperCase()}
                  </Badge>
                  <div className="flex-1">
                    <p className="font-medium">{action.action}</p>
                    <p className="text-sm text-gray-600">
                      {action.deadline && `Deadline: ${action.deadline}`}
