  const [achievements, setAchievements] = useState<Achievement[]>([]);
  const [recentActivity, setRecentActivity] = useState<RecentActivity[]>([]);
  const [loading, setLoading] = useState(true);

  const userName = session?.user?.name?.split(' ')[0] || 'User';

  useEffect(() => {
    const fetchBDRMetrics = async () => {
      if (!session?.user || session.user.role !== 'BDR') return;
      
      try {
        // Fetch real data from the executive dashboard API (BDR users get their own data automatically)
        const response = await fetch('/api/reporting/executive-dashboard');
        if (!response.ok) {
          throw new Error('Failed to fetch BDR metrics');
        }
        
        const data = await response.json();
        const dashboard = data.dashboard;
        
        if (!dashboard) {
          throw new Error('No dashboard data received');
        }
        
        // Transform executive dashboard data to BDR metrics format
        const bdrMetrics: BDRMetrics = {
          callsToday: dashboard.kpis?.thisWeek?.callVolume?.current || 0,
          callsTarget: dashboard.kpis?.thisWeek?.callVolume?.target || 50,
          callsWeek: dashboard.kpis?.thisWeek?.callVolume?.current || 0,
          callsWeekTarget: dashboard.kpis?.thisWeek?.callVolume?.target || 250,
          agreementsToday: dashboard.kpis?.thisWeek?.agreements?.current || 0,
          agreementsWeek: dashboard.kpis?.thisWeek?.agreements?.current || 0,
          agreementsWeekTarget: dashboard.kpis?.thisWeek?.agreements?.target || 10,
          conversionRate: dashboard.kpis?.thisMonth?.conversionRate?.current || 0,
          avgCallDuration: 4.2, // This would need to be calculated from actual call data
          leadsAssigned: 45, // This would need to come from the leads API
          leadsContacted: 38, // This would need to be calculated from lead activity
          followUpsScheduled: 12, // This would need to be calculated from pipeline upcoming calls
          weeklyGoalProgress: dashboard.kpis?.thisWeek?.callVolume?.current && dashboard.kpis?.thisWeek?.callVolume?.target 
            ? Math.round((dashboard.kpis.thisWeek.callVolume.current / dashboard.kpis.thisWeek.callVolume.target) * 100)
            : 0,
          monthlyRank: 1, // This would need team comparison data
          totalBDRs: dashboard.bdrList?.length || 1,
          streak: 5, // This would need historical daily performance data
          lastWeekPerformance: 95 // This would need to be calculated from previous week data
        };

        const mockAchievements: Achievement[] = [
          {
            id: '1',
            title: 'Call Champion',
            description: '50+ calls in a single day',
            icon: 'ðŸ“ž',
            earned: true,
            earnedDate: '2025-08-05'
          },
          {
            id: '2',
            title: 'Agreement Master',
            description: '10 agreements in one week',
            icon: 'ðŸŽ¯',
            earned: false
          },
          {
            id: '3',
            title: 'Hot Streak',
            description: '5 consecutive days meeting targets',
            icon: 'ðŸ”¥',
            earned: true,
            earnedDate: '2025-08-07'
          },
          {
            id: '4',
            title: 'Top Performer',
            description: 'Rank #1 for the month',
            icon: 'ðŸ‘‘',
            earned: false
          }
        ];

        const mockActivity: RecentActivity[] = [
          {
            id: '1',
            type: 'agreement',
            description: 'Closed agreement with TechStart Inc.',
            timestamp: '2 hours ago'
          },
          {
            id: '2',
            type: 'call',
            description: 'Completed call with DataFlow Solutions',
            timestamp: '3 hours ago'
          },
          {
            id: '3',
            type: 'follow-up',
            description: 'Scheduled follow-up with CloudTech Ltd.',
            timestamp: '4 hours ago'
          },
          {
            id: '4',
            type: 'lead',
            description: 'New lead assigned: Enterprise Corp',
            timestamp: '6 hours ago'
          }
        ];

        setMetrics(bdrMetrics);
        setAchievements(mockAchievements);
        setRecentActivity(mockActivity);
      } catch (error) {
        console.error('Error fetching BDR metrics:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchBDRMetrics();
  }, [session]);

  if (!session || session.user?.role !== 'BDR') {
    return null;
  }

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader className="pb-2">
                <div className="h-4 bg-muted rounded w-3/4"></div>
              </CardHeader>
              <CardContent>
                <div className="h-8 bg-muted rounded w-1/2 mb-2"></div>
                <div className="h-3 bg-muted rounded w-full"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const callProgress = metrics ? (metrics.callsToday / metrics.callsTarget) * 100 : 0;
  const weeklyCallProgress = metrics ? (metrics.callsWeek / metrics.callsWeekTarget) * 100 : 0;
  const agreementProgress = metrics ? (metrics.agreementsWeek / metrics.agreementsWeekTarget) * 100 : 0;
  const leadContactProgress = metrics ? (metrics.leadsContacted / metrics.leadsAssigned) * 100 : 0;

  return (
    <div className="space-y-6">
      {/* Welcome Section with Performance Summary */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h2 className="text-2xl font-bold">Good morning, {userName}! ðŸ‘‹</h2>
          <p className="text-muted-foreground">You're currently ranked #{metrics?.monthlyRank} out of {metrics?.totalBDRs} BDRs this month</p>
        </div>
        <div className="flex items-center gap-2">
          <Badge variant="outline" className="gap-1">
            <Zap className="h-3 w-3" />
            {metrics?.streak} day streak
          </Badge>
          <Badge variant="default" className="gap-1">
            <Star className="h-3 w-3" />
            {metrics?.weeklyGoalProgress}% weekly goal
          </Badge>
        </div>
      </div>

      {/* Primary KPI Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card className={`${callProgress >= 100 ? 'border-green-500 bg-green-50 dark:bg-green-950' : ''}`}>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Today's Calls</CardTitle>
            <Phone className={`h-4 w-4 ${callProgress >= 100 ? 'text-green-600' : 'text-muted-foreground'}`} />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics?.callsToday}</div>
            <div className="text-xs text-muted-foreground mb-2">
              Target: {metrics?.callsTarget} ({metrics?.callsTarget - (metrics?.callsToday || 0)} remaining)
            </div>
            <Progress value={callProgress} className="h-2" />
            {callProgress >= 100 && (
              <p className="text-xs text-green-600 mt-1">ðŸŽ‰ Target achieved!</p>
            )}
          </CardContent>
        </Card>

        <Card className={`${agreementProgress >= 100 ? 'border-blue-500 bg-blue-50 dark:bg-blue-950' : ''}`}>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Weekly Agreements</CardTitle>
            <CheckCircle className={`h-4 w-4 ${agreementProgress >= 100 ? 'text-blue-600' : 'text-muted-foreground'}`} />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics?.agreementsWeek}</div>
            <div className="text-xs text-muted-foreground mb-2">
              Target: {metrics?.agreementsWeekTarget}
            </div>
            <Progress value={agreementProgress} className="h-2" />
            <p className="text-xs text-muted-foreground mt-1">
              {metrics?.agreementsToday} agreements today
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Conversion Rate</CardTitle>
            <TrendingUp className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics?.conversionRate}%</div>
            <p className="text-xs text-muted-foreground">
              {metrics?.lastWeekPerformance ? 
                `${metrics.conversionRate - 6.8 > 0 ? '+' : ''}${(metrics.conversionRate - 6.8).toFixed(1)}% vs last week` :
                'vs last week'
              }
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Lead Progress</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{metrics?.leadsContacted}</div>
            <div className="text-xs text-muted-foreground mb-2">
              of {metrics?.leadsAssigned} leads assigned
            </div>
            <Progress value={leadContactProgress} className="h-2" />
          </CardContent>
        </Card>
      </div>

      {/* Detailed Metrics */}
      <div className="grid gap-4 md:grid-cols-3">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Performance Details
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="flex justify-between text-sm">
              <span>Weekly Calls:</span>
              <span className="font-medium">{metrics?.callsWeek}/{metrics?.callsWeekTarget}</span>
            </div>
            <Progress value={weeklyCallProgress} className="h-1" />
            
            <div className="flex justify-between text-sm">
              <span>Avg. Call Duration:</span>
              <span className="font-medium">{metrics?.avgCallDuration} min</span>
            </div>
            
            <div className="flex justify-between text-sm">
              <span>Follow-ups Scheduled:</span>
              <span className="font-medium">{metrics?.followUpsScheduled}</span>
            </div>
            
            <div className="pt-2">
              <Link href="/reporting?view=personal" className="w-full">
                <Button variant="outline" className="w-full">View Detailed Analytics</Button>
              </Link>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Award className="h-5 w-5" />
              Achievements
            </CardTitle>
            <CardDescription>Your recent accomplishments</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-2">
              {achievements.slice(0, 3).map((achievement) => (
                <div key={achievement.id} className="flex items-center gap-3 p-2 rounded-lg border">
                  <span className="text-lg">{achievement.icon}</span>
                  <div className="flex-1">
                    <p className="text-sm font-medium">{achievement.title}</p>
                    <p className="text-xs text-muted-foreground">{achievement.description}</p>
                  </div>
                  {achievement.earned ? (
                    <CheckCircle className="h-4 w-4 text-green-500" />
                  ) : (
                    <Clock className="h-4 w-4 text-muted-foreground" />
                  )}
                </div>
              ))}
            </div>
            <Button variant="ghost" className="w-full mt-2">
              View All Achievements
            </Button>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Activity className="h-5 w-5" />
              Recent Activity
            </CardTitle>
            <CardDescription>Your latest actions</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {recentActivity.slice(0, 4).map((activity) => (
                <div key={activity.id} className="flex items-start gap-3">
                  <div className={`mt-1 h-2 w-2 rounded-full ${
                    activity.type === 'agreement' ? 'bg-green-500' :
                    activity.type === 'call' ? 'bg-blue-500' :
                    activity.type === 'lead' ? 'bg-purple-500' :
                    'bg-orange-500'
                  }`} />
                  <div className="flex-1">
                    <p className="text-sm">{activity.description}</p>
                    <p className="text-xs text-muted-foreground">{activity.timestamp}</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Quick Actions */}
      <div className="grid gap-4 md:grid-cols-4">
        <Card>
          <CardContent className="pt-6">
            <Link href="/leads?filter=mine" className="w-full">
              <Button className="w-full">
                <Users className="h-4 w-4 mr-2" />
                My Leads
              </Button>
            </Link>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <Link href="/pipeline?view=today" className="w-full">
              <Button className="w-full">
                <Calendar className="h-4 w-4 mr-2" />
                Today's Schedule
              </Button>
            </Link>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <Link href="/pipeline?action=new" className="w-full">
              <Button className="w-full">
                <Phone className="h-4 w-4 mr-2" />
                Log New Call
              </Button>
            </Link>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <Link href="/reporting?view=personal" className="w-full">
              <Button variant="outline" className="w-full">
                <TrendingUp className="h-4 w-4 mr-2" />
                My Analytics
              </Button>
            </Link>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  BarChart3, 
  TrendingUp, 
  Users, 
  Target, 
  Filter, 
  Download,
  RefreshCw,
  Award,
  Phone,
  Mail,
  Calendar,
  Star,
  Zap,
  TrendingDown
} from 'lucide-react';
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  PieChart,
  Pie,
  Cell,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar
} from 'recharts';

interface BDRPerformance {
  id: string;
  name: string;
  callsToday: number;
  callsWeek: number;
  callsMonth: number;
  agreementsToday: number;
  agreementsWeek: number;
  agreementsMonth: number;
  conversionRate: number;
  avgCallDuration: number;
  leadsAssigned: number;
  leadsContacted: number;
  followUpsScheduled: number;
  weeklyGoalProgress: number;
  monthlyRank: number;
  streak: number;
  lastWeekPerformance: number;
  territory: string;
  experience: string;
  status: 'active' | 'inactive' | 'on_leave';
}

interface FilterOptions {
  dateRange: string;
  territory: string;
  experience: string;
  status: string;
  minCalls: number;
  maxCalls: number;
  minConversion: number;
  maxConversion: number;
  sortBy: string;
  sortOrder: 'asc' | 'desc';
}

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const fetchBDRPerformanceData = async (filters: FilterOptions) => {
  const params = new URLSearchParams();
  Object.entries(filters).forEach(([key, value]) => {
    if (value) params.append(key, value.toString());
  });

  const response = await fetch(`/api/reporting/advanced/bdr-performance?${params.toString()}`);
  if (!response.ok) {
    throw new Error('Failed to fetch BDR performance data');
  }
  return response.json();
};

export function BDRPerformanceComparison() {
  const [filters, setFilters] = useState<FilterOptions>({
    dateRange: 'week',
    territory: 'all',
    experience: 'all',
    status: 'all',
    minCalls: 0,
    maxCalls: 1000,
    minConversion: 0,
    maxConversion: 100,
    sortBy: 'weeklyGoalProgress',
    sortOrder: 'desc'
  });

  const [selectedBDRs, setSelectedBDRs] = useState<string[]>([]);
  const [comparisonMode, setComparisonMode] = useState<'all' | 'selected'>('all');

  const { data: bdrData, isLoading, error, refetch } = useQuery({
    queryKey: ['bdrPerformance', filters],
    queryFn: () => fetchBDRPerformanceData(filters),
  });

  const handleBDRSelection = (bdrId: string) => {
    if (selectedBDRs.includes(bdrId)) {
      setSelectedBDRs(selectedBDRs.filter(id => id !== bdrId));
    } else {
      setSelectedBDRs([...selectedBDRs, bdrId]);
    }
  };

  const getPerformanceStatus = (performance: number) => {
    if (performance >= 90) return { status: 'excellent', color: 'text-green-600', bg: 'bg-green-50' };
    if (performance >= 75) return { status: 'good', color: 'text-blue-600', bg: 'bg-blue-50' };
    if (performance >= 60) return { status: 'average', color: 'text-yellow-600', bg: 'bg-yellow-50' };
    return { status: 'needs_improvement', color: 'text-red-600', bg: 'bg-red-50' };
  };

  const getPerformanceTrend = (current: number, previous: number) => {
    const change = current - previous;
    const percentage = previous > 0 ? (change / previous) * 100 : 0;
    
    if (percentage > 5) return { trend: 'up', color: 'text-green-600', icon: TrendingUp };
    if (percentage < -5) return { trend: 'down', color: 'text-red-600', icon: TrendingDown };
    return { trend: 'stable', color: 'text-gray-600', icon: TrendingUp };
  };

  const exportComparisonData = () => {
    const dataToExport = comparisonMode === 'selected' 
      ? bdrData?.filter((bdr: BDRPerformance) => selectedBDRs.includes(bdr.id))
      : bdrData;

    const csvContent = "data:text/csv;charset=utf-8," + 
      "Name,Territory,Experience,Calls Today,Calls Week,Agreements Today,Agreements Week,Conversion Rate,Avg Call Duration,Weekly Goal Progress,Monthly Rank,Streak\n" +
      dataToExport?.map((bdr: BDRPerformance) => 
        `${bdr.name},${bdr.territory},${bdr.experience},${bdr.callsToday},${bdr.callsWeek},${bdr.agreementsToday},${bdr.agreementsWeek},${bdr.conversionRate}%,${bdr.avgCallDuration}min,${bdr.weeklyGoalProgress}%,${bdr.monthlyRank},${bdr.streak}`
      ).join('\n');
    
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement('a');
    link.setAttribute('href', encodedUri);
    link.setAttribute('download', 'bdr_performance_comparison.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  const displayData = comparisonMode === 'selected' 
    ? bdrData?.filter((bdr: BDRPerformance) => selectedBDRs.includes(bdr.id))
    : bdrData;

  return (
    <div className="space-y-6">
      {/* Filters */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            <Filter className="h-5 w-5" />
            Advanced Filters
          </CardTitle>
          <CardDescription>Filter and compare BDR performance data</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div>
              <label className="text-sm font-medium">Date Range</label>
              <Select value={filters.dateRange} onValueChange={(value) => setFilters({...filters, dateRange: value})}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="today">Today</SelectItem>
                  <SelectItem value="week">This Week</SelectItem>
                  <SelectItem value="month">This Month</SelectItem>
                  <SelectItem value="quarter">This Quarter</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="text-sm font-medium">Territory</label>
              <Select value={filters.territory} onValueChange={(value) => setFilters({...filters, territory: value})}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Territories</SelectItem>
                  <SelectItem value="north">North</SelectItem>
                  <SelectItem value="south">South</SelectItem>
                  <SelectItem value="east">East</SelectItem>
                  <SelectItem value="west">West</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="text-sm font-medium">Experience Level</label>
              <Select value={filters.experience} onValueChange={(value) => setFilters({...filters, experience: value})}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Levels</SelectItem>
                  <SelectItem value="junior">Junior (0-1 year)</SelectItem>
                  <SelectItem value="mid">Mid-level (1-3 years)</SelectItem>
                  <SelectItem value="senior">Senior (3+ years)</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <label className="text-sm font-medium">Status</label>
              <Select value={filters.status} onValueChange={(value) => setFilters({...filters, status: value})}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                  <SelectItem value="on_leave">On Leave</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mt-4">
            <div>
              <label className="text-sm font-medium">Min Calls</label>
              <Input
                type="number"
                value={filters.minCalls}
                onChange={(e) => setFilters({...filters, minCalls: parseInt(e.target.value) || 0})}
                placeholder="0"
              />
            </div>
            <div>
              <label className="text-sm font-medium">Max Calls</label>
              <Input
                type="number"
                value={filters.maxCalls}
                onChange={(e) => setFilters({...filters, maxCalls: parseInt(e.target.value) || 1000})}
                placeholder="1000"
              />
            </div>
            <div>
              <label className="text-sm font-medium">Min Conversion %</label>
              <Input
                type="number"
                value={filters.minConversion}
                onChange={(e) => setFilters({...filters, minConversion: parseInt(e.target.value) || 0})}
                placeholder="0"
              />
            </div>
            <div>
              <label className="text-sm font-medium">Max Conversion %</label>
              <Input
                type="number"
                value={filters.maxConversion}
                onChange={(e) => setFilters({...filters, maxConversion: parseInt(e.target.value) || 100})}
                placeholder="100"
              />
            </div>
          </div>
          <div className="flex items-center justify-between mt-4">
            <div className="flex items-center space-x-4">
              <Select value={filters.sortBy} onValueChange={(value) => setFilters({...filters, sortBy: value})}>
                <SelectTrigger className="w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="weeklyGoalProgress">Weekly Goal Progress</SelectItem>
                  <SelectItem value="callsWeek">Calls This Week</SelectItem>
                  <SelectItem value="agreementsWeek">Agreements This Week</SelectItem>
                  <SelectItem value="conversionRate">Conversion Rate</SelectItem>
                  <SelectItem value="monthlyRank">Monthly Rank</SelectItem>
                </SelectContent>
              </Select>
              <Select value={filters.sortOrder} onValueChange={(value: 'asc' | 'desc') => setFilters({...filters, sortOrder: value})}>
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="desc">Descending</SelectItem>
                  <SelectItem value="asc">Ascending</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-center space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => refetch()}
                className="flex items-center space-x-2"
              >
                <RefreshCw className="h-4 w-4" />
                Refresh
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={exportComparisonData}
                className="flex items-center space-x-2"
              >
                <Download className="h-4 w-4" />
                Export
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Comparison Mode Toggle */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <label className="text-sm font-medium">Comparison Mode:</label>
          <div className="flex items-center space-x-2">
            <Button
              variant={comparisonMode === 'all' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setComparisonMode('all')}
            >
              All BDRs
            </Button>
            <Button
              variant={comparisonMode === 'selected' ? 'default' : 'outline'}
              size="sm"
              onClick={() => setComparisonMode('selected')}
            >
              Selected BDRs ({selectedBDRs.length})
            </Button>
          </div>
        </div>
        {comparisonMode === 'selected' && selectedBDRs.length === 0 && (
          <p className="text-sm text-muted-foreground">Select BDRs to compare</p>
        )}
      </div>

      {/* Performance Comparison Charts */}
      <Tabs defaultValue="overview" className="space-y-4">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="charts">Charts</TabsTrigger>
          <TabsTrigger value="details">Details</TabsTrigger>
          <TabsTrigger value="rankings">Rankings</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Performance Comparison Chart */}
            <Card>
              <CardHeader>
                <CardTitle>Performance Comparison</CardTitle>
                <CardDescription>Weekly call volume and agreement rates</CardDescription>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="h-80 flex items-center justify-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                  </div>
                ) : (
                  <ResponsiveContainer width="100%" height={320}>
                    <BarChart data={displayData?.slice(0, 10)}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="callsWeek" fill="#8884d8" name="Calls" />
                      <Bar dataKey="agreementsWeek" fill="#82ca9d" name="Agreements" />
                    </BarChart>
                  </ResponsiveContainer>
                )}
              </CardContent>
            </Card>

            {/* Conversion Rate Analysis */}
            <Card>
              <CardHeader>
                <CardTitle>Conversion Rate Analysis</CardTitle>
                <CardDescription>Call to agreement conversion rates</CardDescription>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="h-80 flex items-center justify-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                  </div>
                ) : (
                  <ResponsiveContainer width="100%" height={320}>
                    <BarChart data={displayData?.slice(0, 10)}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" />
                      <YAxis />
                      <Tooltip />
                      <Bar dataKey="conversionRate" fill="#ffc658" name="Conversion Rate %" />
                    </BarChart>
                  </ResponsiveContainer>
                )}
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        <TabsContent value="charts" className="space-y-4">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Performance Distribution */}
            <Card>
              <CardHeader>
                <CardTitle>Performance Distribution</CardTitle>
                <CardDescription>Distribution of BDR performance levels</CardDescription>
              </CardHeader>
              <CardContent>
                {isLoading ? (
                  <div className="h-80 flex items-center justify-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                  </div>
                ) : (
                  <ResponsiveContainer width="100%" height={320}>
                    <PieChart>
