'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { toast } from 'sonner';

interface BulkPipelineDialogProps {
  isOpen: boolean;
  onClose: () => void;
  selectedLeads: { id: number; bdr: string | null }[];
  onConvert: () => Promise<void>;
}

export function BulkPipelineDialog({
  isOpen,
  onClose,
  selectedLeads,
  onConvert,
}: BulkPipelineDialogProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Group leads by BDR
  const leadsByBdr = selectedLeads.reduce<Record<string, number>>((acc, lead) => {
    if (lead.bdr) {
      acc[lead.bdr] = (acc[lead.bdr] || 0) + 1;
    }
    return acc;
  }, {});

  const unassignedCount = selectedLeads.filter(lead => !lead.bdr).length;

  const handleSubmit = async () => {
    try {
      setIsSubmitting(true);
      await onConvert();
      toast.success('Leads converted successfully');
      onClose();
    } catch (error) {
      toast.error('Failed to convert leads');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Convert Leads to Pipeline</DialogTitle>
          <div className="text-sm text-muted-foreground">
            {Object.entries(leadsByBdr).map(([bdr, count]) => (
              <div key={bdr}>
                {count} lead{count > 1 ? 's' : ''} for {bdr}
              </div>
            ))}
            {unassignedCount > 0 && (
              <div className="text-red-500">
                Warning: {unassignedCount} lead{unassignedCount > 1 ? 's' : ''} without BDR assignment
              </div>
            )}
          </div>
        </DialogHeader>

        <DialogFooter>
          <Button variant="outline" onClick={onClose}>
            Cancel
          </Button>
          <Button 
            onClick={handleSubmit} 
            disabled={isSubmitting || unassignedCount > 0}
          >
            Convert
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

"use client";
import { useMemo, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { UserSearchSelect } from './UserSearchSelect';
import { Separator } from '@/components/ui/separator';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Lock, LockOpen, MoreVertical, Users, UserPlus, Settings, Info } from 'lucide-react';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { toast } from 'sonner';

export function ChatHeader({ conversation, onChanged }: { conversation: any; onChanged: () => void }) {
  const [name, setName] = useState(conversation?.name || '');
  const [lock, setLock] = useState<boolean>(!!conversation?.isLocked);
  const [isEditing, setIsEditing] = useState(false);

  const isGroup = !!conversation?.isGroup || (conversation?.members?.length || 0) > 2 || !!name;
  const memberCount = conversation?.members?.length || 0;

  function getUserInitials(name?: string): string {
    if (!name) return 'U';
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  }

  async function save() {
    try {
      const res = await fetch(`/api/chat/conversations/${conversation.id}`, { 
        method: 'PATCH', 
        headers: { 'Content-Type': 'application/json' }, 
        body: JSON.stringify({ name: name.trim(), lock }) 
      });
      if (res.ok) {
        setIsEditing(false);
        onChanged();
        toast.success('Conversation updated');
      } else {
        toast.error('Failed to update conversation');
      }
    } catch {
      toast.error('Failed to update conversation');
    }
  }
  
  async function addMember(userId: string) {
    if (!userId) return;
    try {
      const res = await fetch(`/api/chat/conversations/${conversation.id}`, { 
        method: 'PATCH', 
        headers: { 'Content-Type': 'application/json' }, 
        body: JSON.stringify({ addMemberIds: [userId] }) 
      });
      if (res.ok) { 
        onChanged(); 
        toast.success('Member added');
      } else {
        toast.error('Failed to add member');
      }
    } catch {
      toast.error('Failed to add member');
    }
  }
  
  async function removeMember(userId: string) {
    try {
      const res = await fetch(`/api/chat/conversations/${conversation.id}`, { 
        method: 'PATCH', 
        headers: { 'Content-Type': 'application/json' }, 
        body: JSON.stringify({ removeMemberIds: [userId] }) 
      });
      if (res.ok) {
        onChanged();
        toast.success('Member removed');
      } else {
        toast.error('Failed to remove member');
      }
    } catch {
      toast.error('Failed to remove member');
    }
  }

  const displayTitle = conversation?.name || (isGroup ? 'Group Chat' : 'Direct Message');

  return (
    <div className="flex items-center gap-4 border-b bg-gradient-to-r from-card to-muted/20 px-6 py-4">
      {/* Left section - Conversation info */}
      <div className="flex items-center gap-4 min-w-0 flex-1">
        {/* Avatar */}
        <div className="relative">
          <Avatar className="h-10 w-10 ring-2 ring-border">
            <AvatarFallback className="bg-gradient-to-br from-blue-500 to-purple-600 text-white font-semibold">
              {getUserInitials(displayTitle)}
            </AvatarFallback>
          </Avatar>
          {/* Online indicator */}
          <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-green-500 rounded-full border-2 border-background"></div>
        </div>

        {/* Title and member count */}
        <div className="min-w-0 flex-1">
          {isEditing ? (
            <div className="flex items-center gap-2">
              <Input 
                value={name} 
                onChange={(e) => setName(e.target.value)}
                placeholder={isGroup ? 'Enter group name' : 'Enter conversation name'}
                className="h-8 text-sm"
                autoFocus
                onKeyDown={(e) => {
                  if (e.key === 'Enter') save();
                  if (e.key === 'Escape') { setName(conversation?.name || ''); setIsEditing(false); }
                }}
              />
              <Button size="sm" onClick={save}>Save</Button>
              <Button size="sm" variant="ghost" onClick={() => { setName(conversation?.name || ''); setIsEditing(false); }}>
                Cancel
              </Button>
            </div>
          ) : (
            <div className="space-y-1">
              <div className="flex items-center gap-2">
                <h1 className="font-semibold text-lg truncate">{displayTitle}</h1>
                {lock && <Lock className="h-4 w-4 text-amber-500" />}
                <Button 
                  variant="ghost" 
                  size="icon" 
                  className="h-6 w-6 opacity-60 hover:opacity-100"
                  onClick={() => setIsEditing(true)}
                >
                  <Settings className="h-3 w-3" />
                </Button>
              </div>
              <div className="flex items-center gap-4 text-sm text-muted-foreground">
                <div className="flex items-center gap-1">
                  <Users className="h-3 w-3" />
                  <span>{memberCount} member{memberCount !== 1 ? 's' : ''}</span>
                </div>
                <div className="hidden md:block">
                  {isGroup ? 'Group conversation' : 'Direct message'}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Right section - Actions */}
      <div className="flex items-center gap-2">
        {/* Member avatars preview */}
        <div className="hidden lg:flex items-center -space-x-2">
          {conversation?.members?.slice(0, 3).map((member: any) => (
            <Avatar key={member.userId} className="h-8 w-8 border-2 border-background">
              <AvatarFallback className="text-xs bg-gradient-to-br from-gray-500 to-gray-600 text-white">
                {getUserInitials(member.user?.name)}
              </AvatarFallback>
            </Avatar>
          ))}
          {memberCount > 3 && (
            <div className="h-8 w-8 rounded-full bg-muted border-2 border-background flex items-center justify-center">
              <span className="text-xs font-medium">+{memberCount - 3}</span>
            </div>
          )}
        </div>

        {/* Add member button */}
        <div className="hidden md:block w-48">
          <UserSearchSelect 
            onSelect={(u) => addMember(u.id)} 
            placeholder="Add member..."
          />
        </div>

        {/* More actions */}
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button 
              variant="ghost" 
              size="icon"
              className="rounded-full hover:bg-muted"
              aria-label="More options"
            >
              <MoreVertical className="h-4 w-4" />
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-56">
            {/* Conversation settings */}
            <DropdownMenuItem onClick={() => setIsEditing(true)}>
              <Settings className="h-4 w-4 mr-2" />
              Edit conversation
            </DropdownMenuItem>
            
            <DropdownMenuItem onClick={() => { setLock(!lock); save(); }}>
              {lock ? (
                <>
                  <LockOpen className="h-4 w-4 mr-2" />
                  Unlock conversation
                </>
              ) : (
                <>
                  <Lock className="h-4 w-4 mr-2" />
                  Lock conversation
                </>
              )}
            </DropdownMenuItem>

            <DropdownMenuSeparator />

            {/* Add member on mobile */}
            <div className="md:hidden">
              <DropdownMenuItem>
                <UserPlus className="h-4 w-4 mr-2" />
                Add member
              </DropdownMenuItem>
              <DropdownMenuSeparator />
            </div>

            {/* Members management */}
            {conversation?.members?.length > 0 && (
              <>
                <div className="px-2 py-1.5 text-xs font-medium text-muted-foreground">
                  Members ({memberCount})
                </div>
                {conversation.members.map((member: any) => (
                  <DropdownMenuItem
                    key={member.userId}
                    onClick={() => removeMember(member.userId)}
                    className="text-red-600 focus:text-red-600"
                  >
                    <div className="flex items-center gap-2 w-full">
                      <Avatar className="h-5 w-5">
                        <AvatarFallback className="text-xs bg-gradient-to-br from-gray-500 to-gray-600 text-white">
                          {getUserInitials(member.user?.name)}
                        </AvatarFallback>
                      </Avatar>
                      <span className="flex-1 truncate">{member.user?.name || member.userId}</span>
                      <span className="text-xs opacity-60">Remove</span>
                    </div>
                  </DropdownMenuItem>
                ))}
              </>
            )}
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </div>
  );
}

"use client";
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { useEffect, useMemo, useState } from 'react';
import { useQuery, useQueryClient } from '@tanstack/react-query';
import { toast } from 'sonner';
import { subscribeToUser } from '@/lib/realtime-client';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { ScrollArea } from '@/components/ui/scroll-area';
import { MessageCircle } from 'lucide-react';

type SidebarConversation = {
  id: string;
  name?: string | null;
  members?: Array<{ user?: { id: string; name?: string | null } }>;
  messages?: Array<any>;
  unreadCount?: number;
};

export function ChatSidebar({ activeId, onSelect }: { activeId?: string | null; onSelect: (id: string) => void }) {
  const qc = useQueryClient();
  const [q, setQ] = useState('');
  const [searchResults, setSearchResults] = useState<Array<{ id: string; name?: string; email?: string }>>([]);
  const { data } = useQuery<SidebarConversation[]>({
    queryKey: ['conversations'],
    queryFn: async () => {
      const res = await fetch('/api/chat/conversations');
      if (!res.ok) throw new Error('Failed to load conversations');
      return (await res.json()).conversations as SidebarConversation[];
    },
  });

  useEffect(() => {
    let channel: any | null = null;
    let cancelled = false;
    (async () => {
      try {
        const res = await fetch('/api/auth/session');
        if (!res.ok) return;
        const json = await res.json();
        const userId = json?.user?.id as string | undefined;
        if (cancelled || !userId) return;
        channel = subscribeToUser(userId);
        const handler = () => qc.invalidateQueries({ queryKey: ['conversations'] });
        channel.bind('conversation:updated', handler);
      } catch {}
    })();
    return () => { if (channel) { channel.unbind_all?.(); channel.unsubscribe?.(); } cancelled = true; };
  }, [qc]);

  // Live user search for starting new DMs
  useEffect(() => {
    let aborted = false;
    (async () => {
      const query = q.trim();
      if (!query) { setSearchResults([]); return; }
      try {
        const url = new URL('/api/users', window.location.origin);
        url.searchParams.set('search', query);
        const res = await fetch(url.toString());
        if (!res.ok) return;
        const json = await res.json();
        if (aborted) return;
        const list = (json?.users || json?.data?.users || []) as any[];
        setSearchResults(list.map((u: any) => ({ id: u.id, name: u.name, email: u.email })));
      } catch {}
    })();
    return () => { aborted = true; };
  }, [q]);

  async function startDirectMessage(userId: string) {
    try {
      const res = await fetch('/api/chat/conversations', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ memberIds: [userId] }) });
      if (!res.ok) { return; }
      const { conversation } = await res.json();
      setQ('');
      setSearchResults([]);
      onSelect(conversation.id);
      qc.invalidateQueries({ queryKey: ['conversations'] });
    } catch {}
  }

  function renderInitials(name?: string | null) {
    const text = (name || '').trim();
    if (!text) return 'DM';
    const parts = text.split(/\s+/).slice(0, 2);
    return parts.map((p) => p[0]?.toUpperCase() || '').join('');
  }

  function stripHtml(html?: string) {
    if (!html) return '';
    const div = document.createElement('div');
    div.innerHTML = html;
    return (div.textContent || div.innerText || '').trim();
  }

  function formatLastMessageTime(timestamp?: string) {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'now';
    if (diffInMinutes < 60) return `${diffInMinutes}m`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h`;
    return date.toLocaleDateString();
  }

  return (
    <div className="flex h-full w-full flex-col bg-gradient-to-b from-background to-muted/20">
      {/* Header */}
      <div className="p-4 border-b bg-card/50 backdrop-blur-sm">
        <h2 className="text-lg font-semibold mb-3 text-foreground">Messages</h2>
        <div className="relative">
          <Input 
            value={q} 
            onChange={(e)=>setQ(e.target.value)} 
            placeholder="Search conversations or start new chat..." 
            className="rounded-full border-2 focus:border-blue-400 transition-colors pl-4"
          />
          {q && searchResults.length > 0 && (
            <div className="absolute left-0 right-0 z-30 mt-2 rounded-xl border bg-popover shadow-lg overflow-hidden">
              <div className="p-2 text-xs text-muted-foreground font-medium border-b">Start new conversation</div>
              {searchResults.slice(0,6).map(u => (
                <button 
                  key={u.id} 
                  className="flex items-center gap-3 w-full text-left px-3 py-3 hover:bg-accent transition-colors" 
                  onClick={()=>startDirectMessage(u.id)}
                >
                  <Avatar className="h-8 w-8">
                    <AvatarFallback className="text-xs bg-gradient-to-br from-green-500 to-blue-600 text-white">
                      {renderInitials(u.name)}
                    </AvatarFallback>
                  </Avatar>
                  <div className="min-w-0 flex-1">
                    <div className="font-medium truncate">{u.name || u.email}</div>
                    <div className="text-xs text-muted-foreground truncate">{u.email}</div>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Conversations list */}
      <ScrollArea className="flex-1">
        <div className="p-2 space-y-0.5">
          {data && data.length === 0 ? (
            <div className="text-center py-8 px-4">
              <div className="text-muted-foreground text-sm">
                <MessageCircle className="h-12 w-12 mx-auto mb-3 opacity-50" />
                <p className="font-medium mb-1">No conversations yet</p>
                <p className="text-xs">Start a conversation by searching for users above</p>
              </div>
            </div>
          ) : (
            data?.map((c) => {
              const last = (c.messages && c.messages[0]) || undefined;
              const title = c.name || 'Direct message';
              const isActive = activeId === c.id;
              const hasUnread = c.unreadCount && c.unreadCount > 0;
              
              return (
                <button
                  key={c.id}
                  className={`
                    group w-full text-left rounded-xl px-3 py-3 transition-all duration-200 relative
                    ${isActive 
                      ? 'bg-blue-50 dark:bg-blue-900/30 ring-2 ring-blue-500/20' 
                      : 'hover:bg-accent/60'
                    }
                    ${hasUnread ? 'bg-accent/20' : ''}
                  `}
                  onClick={async () => {
                    onSelect(c.id);
                    try {
                      await fetch(`/api/chat/conversations/${c.id}/reads`, { method: 'POST' });
                      qc.invalidateQueries({ queryKey: ['conversations'] });
                    } catch {}
                  }}
                >
                  <div className="flex items-center gap-3">
                    <div className="relative">
                      <Avatar className={`h-12 w-12 transition-all ${isActive ? 'ring-2 ring-blue-400' : ''}`}>
                        <AvatarFallback className="text-sm bg-gradient-to-br from-blue-500 to-purple-600 text-white">
                          {renderInitials(title)}
                        </AvatarFallback>
                      </Avatar>
                      {/* Online indicator placeholder */}
                      <div className="absolute -bottom-0.5 -right-0.5 w-3 h-3 bg-green-500 rounded-full border-2 border-background"></div>
                    </div>
                    
                    <div className="min-w-0 flex-1">
                      <div className="flex items-center gap-2 mb-1">
                        <div className={`truncate font-medium leading-tight ${hasUnread ? 'font-semibold' : ''}`}>
                          {title}
                        </div>
                        <div className="text-xs text-muted-foreground ml-auto shrink-0">
                          {formatLastMessageTime(last?.createdAt)}
                        </div>
                      </div>
                      
                      <div className="flex items-center gap-2">
                        <div className={`truncate text-sm ${hasUnread ? 'text-foreground font-medium' : 'text-muted-foreground'}`}>
                          {stripHtml(last?.content) || 'No messages yet'}
                        </div>
                        {hasUnread && (
                          <span className="ml-auto inline-flex items-center justify-center rounded-full bg-blue-500 text-white text-[10px] font-bold px-2 py-0.5 shrink-0">
                            {c.unreadCount > 9 ? '9+' : c.unreadCount}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                </button>
              );
            })
          )}
        </div>
      </ScrollArea>
      
      {/* Footer with new chat button */}
      <div className="border-t p-4 bg-card/50 backdrop-blur-sm">
        <Button 
          className="w-full rounded-full bg-blue-500 hover:bg-blue-600 text-white shadow-md hover:shadow-lg transition-all duration-200" 
          onClick={async()=>{
            const res = await fetch('/api/chat/conversations', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ memberIds: [] }) });
            if (!res.ok) { toast.error('Failed to create conversation'); return; }
            const { conversation } = await res.json();
            onSelect(conversation.id);
            qc.invalidateQueries({ queryKey:['conversations'] });
          }}
        >
          <MessageCircle className="h-4 w-4 mr-2" />
          New Chat
        </Button>
      </div>
    </div>
  );
}

"use client";
import { useEffect, useMemo, useRef, useState } from 'react';
import { useQueryClient } from '@tanstack/react-query';
import { subscribeToConversation } from '@/lib/realtime-client';
import { toast } from 'sonner';
import { MessageComposer } from './MessageComposer';
import { MessageBubble } from './MessageBubble';
import { ScrollArea } from '@/components/ui/scroll-area';

export function ChatWindow({ conversationId, currentUserId }: { conversationId: string; currentUserId?: string }) {
  const qc = useQueryClient();
  const viewportRef = useRef<HTMLDivElement | null>(null);
  const { data, fetchNextPage, hasNextPage, isFetchingNextPage } = useInfiniteMessages(conversationId);
  const [typingBy, setTypingBy] = useState<Record<string, number>>({});

  useEffect(() => {
    const channel = subscribeToConversation(conversationId);
    const handler = () => {
      qc.invalidateQueries({ queryKey: ['messages', conversationId] });
      // Also trigger shim refresh if needed
      if (qc._shimRefresh?.[conversationId]) {
        qc._shimRefresh[conversationId]();
      }
    };
    channel.bind('message:new', handler);
    channel.bind('message:edit', handler);
    channel.bind('message:delete', handler);
    channel.bind('message:read', handler);
    channel.bind('presence:typing', (payload: any) => {
      if (!payload?.userId) return;
      setTypingBy((prev) => ({ ...prev, [payload.userId]: Date.now() }));
    });
    return () => { channel.unbind_all(); channel.unsubscribe(); };
  }, [conversationId, qc]);

  useEffect(() => {
    const interval = setInterval(() => {
      const now = Date.now();
      setTypingBy((prev) => {
        const next: Record<string, number> = {};
        for (const [k, v] of Object.entries(prev)) {
          if (now - v < 2500) next[k] = v;
        }
        return next;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    // Mark latest message read when viewing
    const firstPage = data?.pages?.[0];
    const latest = firstPage?.messages?.[0];
    if (!latest) return;
    fetch(`/api/chat/messages/${latest.id}/reads`, { method: 'POST' }).catch(()=>{});
    fetch(`/api/chat/conversations/${conversationId}/reads`, { method: 'POST' }).then(()=>{
      qc.invalidateQueries({ queryKey: ['conversations'] });
    }).catch(()=>{});
  }, [data]);

  useEffect(() => {
    // Auto-scroll to bottom on first load or on new page append
    if (!viewportRef.current) return;
    viewportRef.current.scrollTop = viewportRef.current.scrollHeight;
  }, [data]);

  async function onSend(payload: { content: string; attachments: any[] }) {
    try {
      const res = await fetch(`/api/chat/conversations/${conversationId}/messages`, { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(payload) });
      if (!res.ok) throw new Error(await res.text());
      
      // Force immediate refresh of messages
      qc.invalidateQueries({ queryKey: ['messages', conversationId] });
      qc.refetchQueries({ queryKey: ['messages', conversationId] });
      
      // Also trigger shim refresh for immediate update
      if (qc._shimRefresh?.[conversationId]) {
        qc._shimRefresh[conversationId]();
      }
    } catch (e) {
      toast.error('Failed to send');
    }
  }

  const messages = useMemo(() => {
    const allMessages = data?.pages?.flatMap((p: any) => p.messages) || [];
    // Remove duplicates based on message ID
    const uniqueMessages = allMessages.reduce((acc: any[], message: any) => {
      if (!acc.find(m => m.id === message.id)) {
        acc.push(message);
      }
      return acc;
    }, []);
    return uniqueMessages.slice().reverse();
  }, [data]);

  return (
    <div className="flex h-full min-h-0 flex-col">
      <div ref={viewportRef} className="flex-1 overflow-y-auto">
        {messages.map((m: any, index: number) => (
          <MessageBubble 
            key={`${m.id}-${index}`} 
            message={m} 
            isOwn={m.senderId === currentUserId} 
            onEdited={() => qc.invalidateQueries({ queryKey: ['messages', conversationId] })} 
          />
        ))}
        {hasNextPage && (
          <button className="mx-auto my-2 block rounded border px-3 py-1 text-xs" disabled={isFetchingNextPage} onClick={()=>fetchNextPage()}>
            {isFetchingNextPage ? 'Loading...' : 'Load older messages'}
          </button>
        )}
      </div>
      {Object.keys(typingBy).filter((id) => id !== (currentUserId || '')).length > 0 && (
        <div className="px-6 py-3">
          <div className="flex items-center gap-3">
            <div className="flex items-center gap-1">
              <div className="flex space-x-1">
                <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse"></div>
                <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse [animation-delay:0.2s]"></div>
                <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse [animation-delay:0.4s]"></div>
              </div>
            </div>
            <span className="text-sm text-muted-foreground">Someone is typing...</span>
          </div>
        </div>
      )}
      <MessageComposer conversationId={conversationId} onSend={onSend} onTyping={async()=>{ try{ await fetch('/api/chat/typing', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ conversationId }) }); } catch{} }} />
    </div>
  );
}

function useInfiniteMessages(conversationId: string) {
  const qc = useQueryClient();
  return (window as any).ReactQuery?.useInfiniteQuery || useInfiniteQueryShim(conversationId, qc);
}

function useInfiniteQueryShim(conversationId: string, qc: any) {
  const [pages, setPages] = useState<any[]>([]);
  const [cursor, setCursor] = useState<string | null>(null);
  const [hasNextPage, setHasNextPage] = useState(true);
  const [isFetchingNextPage, setIsFetchingNextPage] = useState(false);
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  // Expose refresh function to query client
  useEffect(() => {
    if (!qc._shimRefresh) qc._shimRefresh = {};
    qc._shimRefresh[conversationId] = () => setRefreshTrigger(prev => prev + 1);
    return () => {
      if (qc._shimRefresh) delete qc._shimRefresh[conversationId];
    };
  }, [conversationId, qc]);

  useEffect(() => {
    let cancelled = false;
    // Reset state when conversation changes or refresh is triggered
    setPages([]);
    setCursor(null);
    setHasNextPage(true);
    
    (async () => {
      try {
        const res = await fetch(`/api/chat/conversations/${conversationId}/messages`);
        if (!res.ok) return;
        const json = await res.json();
        if (cancelled) return;
        setPages([json]);
        setCursor(json.nextCursor);
        setHasNextPage(!!json.nextCursor);
      } catch (error) {
        console.error('Failed to fetch messages:', error);
      }
    })();
    return () => { cancelled = true; };
  }, [conversationId, refreshTrigger]);

  async function fetchNextPage() {
    if (!hasNextPage || isFetchingNextPage) return;
    setIsFetchingNextPage(true);
    try {
      const res = await fetch(`/api/chat/conversations/${conversationId}/messages` + (cursor ? `?cursor=${cursor}` : ''));
      if (!res.ok) return;
      const json = await res.json();
      setPages((prev) => [...prev, json]);
      setCursor(json.nextCursor);
      setHasNextPage(!!json.nextCursor);
    } finally {
      setIsFetchingNextPage(false);
    }
  }

  return { data: { pages }, fetchNextPage, hasNextPage, isFetchingNextPage } as any;
}

"use client";
import { useMemo, useState } from 'react';
import { ReactionsBar } from './ReactionsBar';
import { ReadReceipts } from './ReadReceipts';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { Button } from '@/components/ui/button';
import { MoreHorizontal, Pin, PinOff, Pencil, Trash2 } from 'lucide-react';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';

function getRelativeTime(date: Date): string {
  const now = new Date();
  const diffInMs = now.getTime() - date.getTime();
  const diffInMinutes = Math.floor(diffInMs / (1000 * 60));
  const diffInHours = Math.floor(diffInMs / (1000 * 60 * 60));
  const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24));

  if (diffInMinutes < 1) return 'Just now';
  if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
  if (diffInHours < 24) return `${diffInHours}h ago`;
  if (diffInDays < 7) return `${diffInDays}d ago`;
  
  return date.toLocaleDateString();
}

function getUserInitials(name?: string): string {
  if (!name) return 'U';
  return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
}

export function MessageBubble({ message, isOwn, onEdited }: { message: any; isOwn?: boolean; onEdited?: () => void }) {
  const [isEditing, setIsEditing] = useState(false);
  const [draft, setDraft] = useState<string>('');
  const [showFullTime, setShowFullTime] = useState(false);
  
  const content = useMemo(() => {
    if (message.isDeleted) return '<i class="text-muted-foreground">This message was deleted</i>';
    return message.content || '';
  }, [message]);

  const messageTime = useMemo(() => new Date(message.createdAt), [message.createdAt]);

  async function pin(toggle: boolean) {
    try {
      const url = `/api/chat/messages/${message.id}/pin`;
      const res = await fetch(url, { method: toggle ? 'POST' : 'DELETE' });
      if (!res.ok) return;
      onEdited?.();
    } catch {}
  }
  
  async function del() {
    try {
      const res = await fetch(`/api/chat/messages/${message.id}`, { method: 'DELETE' });
      if (!res.ok) return;
      onEdited?.();
    } catch {}
  }

  async function save() {
    try {
      const res = await fetch(`/api/chat/messages/${message.id}`, { method: 'PATCH', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify({ content: draft }) });
      if (!res.ok) return;
      setIsEditing(false);
      onEdited?.();
    } catch {}
  }

  return (
    <div className={`group flex gap-3 px-3 py-2 hover:bg-muted/30 transition-colors ${isOwn ? 'flex-row-reverse' : 'flex-row'}`}>
      {/* Avatar - only show for others' messages */}
      {!isOwn && (
        <Avatar className="w-8 h-8 shrink-0">
          <AvatarFallback className="text-xs bg-gradient-to-br from-blue-500 to-purple-600 text-white">
            {getUserInitials(message.sender?.name)}
          </AvatarFallback>
        </Avatar>
      )}
      
      <div className={`flex flex-col min-w-0 max-w-[85%] sm:max-w-[70%] ${isOwn ? 'items-end' : 'items-start'}`}>
        {/* Message bubble */}
        <div className={`
          relative rounded-2xl px-4 py-2.5 shadow-sm transition-all duration-200
          ${isOwn 
            ? 'bg-blue-500 text-white rounded-br-md' 
            : 'bg-white dark:bg-gray-800 border rounded-bl-md'
          }
          ${message.isPinned ? 'ring-2 ring-amber-400/50' : ''}
        `}>
          {/* Pinned indicator */}
          {message.isPinned && (
            <div className={`absolute -top-2 ${isOwn ? '-left-2' : '-right-2'} bg-amber-400 rounded-full p-1`}>
              <Pin className="w-3 h-3 text-white" />
            </div>
          )}
          
          {/* Name for group messages (non-own) */}
          {!isOwn && message.sender?.name && (
            <div className="text-sm font-medium mb-1 text-blue-600 dark:text-blue-400">
              {message.sender.name}
            </div>
          )}
          
          {/* Message content */}
          {isEditing ? (
            <div className="space-y-3">
              <Textarea 
                value={draft} 
                onChange={(e)=>setDraft(e.target.value)} 
                rows={3}
                className="min-h-[80px] resize-none"
                placeholder="Edit your message..."
              />
              <div className="flex gap-2 justify-end">
                <Button size="sm" variant="ghost" onClick={()=>{ setIsEditing(false); setDraft(''); }}>
                  Cancel
                </Button>
                <Button size="sm" onClick={save}>Save</Button>
              </div>
            </div>
          ) : (
            <div className={`prose prose-sm max-w-none ${isOwn ? 'prose-invert' : ''}`} 
                 dangerouslySetInnerHTML={{ __html: content }} />
          )}
          
          {/* Attachments */}
          {Array.isArray(message.attachments) && message.attachments.length > 0 && (
            <div className="mt-3 space-y-2">
              {message.attachments.map((a: any) => (
                <a 
                  key={a.id || a.url} 
                  href={a.url} 
                  target="_blank" 
                  rel="noopener noreferrer" 
                  className={`
                    flex items-center gap-2 p-2 rounded-lg border transition-colors
                    ${isOwn ? 'bg-white/20 border-white/30 hover:bg-white/30 text-white' : 'bg-gray-50 dark:bg-gray-700 hover:bg-gray-100 dark:hover:bg-gray-600'}
                  `}
                >
                  <div className="text-xs font-medium truncate">{a.fileName}</div>
                  <div className={`text-xs ${isOwn ? 'text-white/70' : 'text-muted-foreground'}`}>
                    ({Math.round((a.size || 0)/1024)}KB)
                  </div>
                </a>
              ))}
            </div>
          )}
        </div>
        
        {/* Message footer */}
        <div className={`flex items-center gap-2 mt-1 text-xs text-muted-foreground ${isOwn ? 'flex-row-reverse' : 'flex-row'}`}>
          {/* Timestamp */}
          <button 
            onClick={() => setShowFullTime(!showFullTime)}
            className="hover:text-foreground transition-colors"
            title={messageTime.toLocaleString()}
          >
            {showFullTime ? messageTime.toLocaleString() : getRelativeTime(messageTime)}
          </button>
          
          {/* Edited indicator */}
          {message.isEdited && (
            <span className="italic">edited</span>
          )}
          
          {/* Actions menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button 
                variant="ghost" 
                size="icon" 
                className="h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity hover:bg-muted"
              >
                <MoreHorizontal className="h-3 w-3" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align={isOwn ? "end" : "start"}>
              {isOwn && !message.isDeleted && (
                <DropdownMenuItem onClick={() => { 
                  setDraft((message.content || '').replace(/<[^>]+>/g, '')); 
                  setIsEditing(true); 
                }}>
                  <Pencil className="h-4 w-4 mr-2" />
                  Edit
                </DropdownMenuItem>
              )}
              <DropdownMenuItem onClick={() => pin(!message.isPinned)}>
                {message.isPinned ? (
                  <>
                    <PinOff className="h-4 w-4 mr-2" />
                    Unpin
                  </>
                ) : (
                  <>
                    <Pin className="h-4 w-4 mr-2" />
                    Pin
                  </>
                )}
              </DropdownMenuItem>
              <DropdownMenuItem onClick={del} className="text-red-600 focus:text-red-600">
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
        
        {/* Reactions and read receipts */}
        <div className={`${isOwn ? 'self-end' : 'self-start'} mt-1`}>
          <ReactionsBar messageId={message.id} onChanged={()=> onEdited?.()} />
          <ReadReceipts reads={message.reads || []} />
        </div>
      </div>
    </div>
  );
}

"use client";
import { useEffect, useRef, useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Smile, Paperclip, Send } from 'lucide-react';
import { toast } from 'sonner';
import { EmojiPicker } from '@/components/ui/emoji-picker';

const Picker = EmojiPicker as any;

type User = { id: string; name: string };

export function MessageComposer({
  conversationId,
  onSend,
  onTyping,
}: {
  conversationId: string;
  onSend: (payload: { content: string; attachments: any[] }) => Promise<void>;
  onTyping?: () => void;
}) {
  const [text, setText] = useState('');
  const [showEmoji, setShowEmoji] = useState(false);
  const [files, setFiles] = useState<File[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [mentionOpen, setMentionOpen] = useState(false);
  const [mentionQuery, setMentionQuery] = useState('');
  const [isUploading, setIsUploading] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const fileInputRef = useRef<HTMLInputElement | null>(null);
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  useEffect(() => {
    fetch('/api/users')
      .then((r) => r.json())
      .then((j) => {
        const list = (j?.data?.users || []).map((u: any) => ({ id: u.id, name: u.name || u.email }));
        setUsers(list);
      })
      .catch(() => {});
  }, []);

  // Auto-resize textarea
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${Math.min(textareaRef.current.scrollHeight, 120)}px`;
    }
  }, [text]);

  function handleKeyDown(e: React.KeyboardEvent<HTMLTextAreaElement>) {
    if (e.key === 'Enter') {
      if (!e.shiftKey && !mentionOpen) {
        e.preventDefault();
        doSend();
        return;
      }
      // Allow Shift+Enter for new lines
    }
    if (onTyping) onTyping();
  }

  // Drag and drop handlers
  function handleDragOver(e: React.DragEvent) {
    e.preventDefault();
    setDragOver(true);
  }

  function handleDragLeave(e: React.DragEvent) {
    e.preventDefault();
    setDragOver(false);
  }

  function handleDrop(e: React.DragEvent) {
    e.preventDefault();
    setDragOver(false);
    const droppedFiles = Array.from(e.dataTransfer.files);
    setFiles(prev => [...prev, ...droppedFiles]);
  }

  function insertEmoji(emoji: any) {
    setText((t) => `${t}${emoji.native}`);
    setShowEmoji(false);
  }

  function onChange(e: React.ChangeEvent<HTMLTextAreaElement>) {
    const val = e.target.value;
    setText(val);
    const atIndex = val.lastIndexOf('@');
    if (atIndex >= 0) {
      const q = val.slice(atIndex + 1).trim();
      setMentionQuery(q);
      setMentionOpen(q.length > 0);
    } else {
      setMentionOpen(false);
      setMentionQuery('');
    }
  }

  function selectMention(u: User) {
    const atIndex = text.lastIndexOf('@');
    if (atIndex >= 0) {
      const before = text.slice(0, atIndex);
      const after = text.slice(atIndex + 1 + mentionQuery.length);
      setText(`${before}@{${u.id}} ${after}`);
      setMentionOpen(false);
      setMentionQuery('');
    }
  }

  async function uploadFiles(): Promise<any[]> {
    if (files.length === 0) return [];
    
    setIsUploading(true);
    const results: any[] = [];
    for (const file of files) {
      try {
        const res = await fetch('/api/chat/uploads/create-url', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ filename: file.name, mime: file.type, size: file.size }),
        });
        if (!res.ok) throw new Error('upload url');
        const { uploadUrl, url } = await res.json();
        const put = await fetch(uploadUrl, { method: 'PUT', body: file });
        if (!put.ok) throw new Error('upload fail');
        results.push({ url, fileName: file.name, mimeType: file.type, size: file.size });
      } catch (e) {
        toast.error(`Failed to upload ${file.name}`);
      }
    }
    setIsUploading(false);
    return results;
  }

  async function doSend() {
    const trimmed = text.trim();
    if (!trimmed && files.length === 0) return;
    if (isUploading) return;
    
    try {
      const attachments = await uploadFiles();
      await onSend({ content: trimmed, attachments });
      setText('');
      setFiles([]);
      if (fileInputRef.current) fileInputRef.current.value = '';
      // Focus back to textarea after sending
      setTimeout(() => {
        textareaRef.current?.focus();
      }, 100);
    } catch (error) {
      console.error('Failed to send message:', error);
      toast.error('Failed to send message');
    }
  }

  function removeFile(index: number) {
    setFiles(prev => prev.filter((_, i) => i !== index));
  }

  const canSend = (text.trim() || files.length > 0) && !isUploading;

  return (
    <div 
      className={`relative border-t bg-gradient-to-r from-background to-muted/20 p-4 transition-colors ${dragOver ? 'bg-blue-50 dark:bg-blue-900/20 border-blue-300' : ''}`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      {/* File attachments preview */}
      {files.length > 0 && (
        <div className="mb-3 flex flex-wrap gap-2">
          {files.map((file, index) => (
            <div key={`${file.name}-${file.size}-${index}`} className="group flex items-center gap-2 rounded-lg border bg-muted/50 px-3 py-2 text-sm">
              <Paperclip className="h-3 w-3 text-muted-foreground" />
              <span className="font-medium truncate max-w-[150px]">{file.name}</span>
              <span className="text-xs text-muted-foreground">({Math.round(file.size / 1024)}KB)</span>
              <Button
                size="icon"
                variant="ghost"
                className="h-4 w-4 opacity-0 group-hover:opacity-100 transition-opacity"
                onClick={() => removeFile(index)}
              >
                Ã—
              </Button>
            </div>
          ))}
        </div>
      )}

      {/* Drag overlay */}
      {dragOver && (
        <div className="absolute inset-4 bg-blue-500/10 border-2 border-dashed border-blue-300 rounded-lg flex items-center justify-center z-10">
          <div className="text-blue-600 font-medium">Drop files to attach</div>
        </div>
      )}

      <div className="flex items-end gap-3">
        {/* Emoji picker */}
        <Popover open={showEmoji} onOpenChange={setShowEmoji}>
          <PopoverTrigger asChild>
            <Button 
              variant="ghost" 
              size="icon" 
              className="rounded-full hover:bg-muted"
              aria-label="Insert emoji"
            >
              <Smile className="h-5 w-5" />
            </Button>
          </PopoverTrigger>
          <PopoverContent className="p-0 w-auto" align="start">
            {Picker ? <Picker onSelect={insertEmoji} /> : null}
          </PopoverContent>
        </Popover>

        {/* File attachment */}
        <input 
          ref={fileInputRef} 
          type="file" 
          multiple 
          className="hidden" 
          onChange={(e) => setFiles(prev => [...prev, ...Array.from(e.target.files || [])])} 
        />
        <Button 
          type="button" 
          variant="ghost" 
          size="icon" 
          className="rounded-full hover:bg-muted"
          aria-label="Attach files" 
          onClick={() => fileInputRef.current?.click()}
        >
          <Paperclip className="h-5 w-5" />
        </Button>

        {/* Message input */}
        <div className="relative flex-1">
          <Textarea
            ref={textareaRef}
            value={text}
            onChange={onChange}
            onKeyDown={handleKeyDown}
            placeholder="Type a message... (Enter to send, Shift+Enter for new line)"
            className="resize-none min-h-[44px] max-h-[120px] rounded-2xl border-2 focus:border-blue-400 transition-colors pr-4 py-3"
            rows={1}
          />
          
          {/* Mention dropdown */}
          {mentionOpen && (
            <div className="absolute bottom-full mb-1 w-full rounded-lg border bg-popover shadow-lg z-20">
              {(users.filter((u) => u.name?.toLowerCase().includes(mentionQuery.toLowerCase())).slice(0, 6)).map((u) => (
                <button 
                  key={u.id} 
                  type="button" 
                  className="block w-full text-left px-3 py-2 hover:bg-accent first:rounded-t-lg last:rounded-b-lg transition-colors" 
                  onClick={() => selectMention(u)}
                >
                  <div className="font-medium">{u.name}</div>
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Send button */}
        <Button 
          onClick={doSend}
          disabled={!canSend}
          size="icon"
          className={`rounded-full transition-all duration-200 ${
            canSend 
              ? 'bg-blue-500 hover:bg-blue-600 text-white shadow-md hover:shadow-lg' 
              : 'bg-muted text-muted-foreground'
          }`}
          aria-label="Send message"
        >
          {isUploading ? (
            <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
          ) : (
            <Send className="h-4 w-4" />
          )}
        </Button>
      </div>
    </div>
  );
}

