} from "@/components/ui/select";

interface PipelineItemCardProps {
  item: PipelineItem;
  onRefresh?: () => void;
}

const PipelineItemCard = ({ item, onRefresh }: PipelineItemCardProps) => {
  const [phoneCopied, setPhoneCopied] = useState(false);
  const [emailCopied, setEmailCopied] = useState(false);
  const [detailOpen, setDetailOpen] = useState(false);
  const [showNotes, setShowNotes] = useState(false);
  const [moveOpen, setMoveOpen] = useState(false);
  const [isExpanded, setIsExpanded] = useState(true);
  const [selectedMoveCategory, setSelectedMoveCategory] = useState<string>(item.category);
  const [selectedMoveStatus, setSelectedMoveStatus] = useState<string>(item.status);

  const { mutate: createActivityLog } = useCreateActivityLog();
  const { mutate: movePipelineItem } = useMovePipelineItem();
  const { mutate: deletePipelineItem } = useDeletePipelineItem();

  // Close all dialogs when any dialog closes
  const handleDialogClose = () => {
    setDetailOpen(false);
    setShowNotes(false);
    setMoveOpen(false);
  };

  // Prevent detail dialog from opening when clicking inside other dialogs
  const preventDetailOpen = (e: React.MouseEvent) => {
    e.stopPropagation();
  };

  // Listen for dialog close events
  useEffect(() => {
    const handleCloseAllDialogs = () => {
      handleDialogClose();
    };

    window.addEventListener('closeAllDialogs', handleCloseAllDialogs);
    return () => {
      window.removeEventListener('closeAllDialogs', handleCloseAllDialogs);
    };
  }, []);

  // Update move selections when item changes
  useEffect(() => {
    setSelectedMoveCategory(item.category);
    setSelectedMoveStatus(item.status);
  }, [item.category, item.status]);

  const addedDate = typeof item.addedDate === 'string' 
    ? new Date(item.addedDate) 
    : item.addedDate;

  const lastUpdated = typeof item.lastUpdated === 'string' 
    ? new Date(item.lastUpdated) 
    : item.lastUpdated;

  const formattedAddedDate = formatDistanceToNow(addedDate, { addSuffix: true });
  const formattedLastUpdated = formatDistanceToNow(lastUpdated, { addSuffix: true });

  const copyToClipboard = async (text: string, type: 'phone' | 'email') => {
    try {
      await navigator.clipboard.writeText(text);
      if (type === 'phone') {
        setPhoneCopied(true);
        setTimeout(() => setPhoneCopied(false), 2000);
        toast.success('Phone number copied to clipboard');
      } else {
        setEmailCopied(true);
        setTimeout(() => setEmailCopied(false), 2000);
        toast.success('Email address copied to clipboard');
      }
    } catch (err) {
      toast.error('Failed to copy to clipboard');
    }
  };

  const handleManualUpdate = () => {
    createActivityLog({
      bdr: item.bdr,
      activityType: 'Note_Added',
      description: 'Manually updated last chase date',
      pipelineItemId: item.id,
      notes: 'Lead chased manually'
    }, {
      onSuccess: () => {
        toast.success('Last updated date refreshed');
        onRefresh?.();
      }
    });
  };

  const handleMoveItem = () => {
    if (selectedMoveCategory === item.category && selectedMoveStatus === item.status) {
      setMoveOpen(false);
      return;
    }

    movePipelineItem({
      id: item.id,
      newCategory: selectedMoveCategory,
      newStatus: selectedMoveStatus
    }, {
      onSuccess: () => {
        toast.success(`Moved to ${selectedMoveStatus}`);
        setMoveOpen(false);
        onRefresh?.();
      },
      onError: () => {
        toast.error('Failed to move item');
      }
    });
  };

  const handleDeleteItem = () => {
    if (confirm('Are you sure you want to delete this pipeline item? This action cannot be undone.')) {
      deletePipelineItem(item.id, {
        onSuccess: () => {
          toast.success('Pipeline item deleted successfully');
          onRefresh?.();
        },
        onError: () => {
          toast.error('Failed to delete pipeline item');
        }
      });
    }
  };

  const handleCategoryChange = (category: string) => {
    setSelectedMoveCategory(category);
    // Set the status to the first status in the new category
    const statuses = pipelineStatusEnum[category as keyof typeof pipelineStatusEnum];
    if (statuses && statuses.length > 0) {
      setSelectedMoveStatus(statuses[0]);
    }
  };

  const hasNotes = item.notes && item.notes.trim() !== '';
  const isSublistContainer = item.isSublist;
  const hasChildren = item.children && item.children.length > 0;

  return (
    <>
      {/* Regular Pipeline Item */}
      <div className="mb-2">
        <Card 
          className="cursor-pointer hover:shadow-md transition-shadow"
          onClick={(e) => {
            // Only open detail if we didn't click a button or dialog
            if (!(e.target as HTMLElement).closest('button, [role="dialog"]')) {
              handleDialogClose();
              setDetailOpen(true);
            }
          }}
        >
        <CardContent className="p-3">
          <div className="flex flex-col gap-2">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2 flex-1 min-w-0">
                                 <div onClick={preventDetailOpen}>
                   {hasChildren ? (
                     <div className="flex items-center gap-1">
                       <Button
                         variant="ghost"
                         size="sm"
                         className="h-5 w-5 p-0 hover:bg-muted"
                         onClick={(e) => {
                           e.stopPropagation();
                           setIsExpanded(!isExpanded);
                         }}
                       >
                         {isExpanded ? (
                           <ChevronDown className="h-3 w-3" />
                         ) : (
                           <ChevronRight className="h-3 w-3" />
                         )}
                       </Button>
                     </div>
                   ) : (
                     <SublistDialog
                       title="Add Sublist Item"
                       buttonLabel="+ Add subitem"
                       buttonVariant="ghost"
                       buttonSize="default"
                       buttonIcon={false}
                       defaultCategory={item.category}
                       defaultStatus={item.status}
                       defaultBdr={item.bdr}
                       parentId={item.id}
                       onSuccess={onRefresh}
                       className="h-5 px-2 text-xs hover:bg-muted"
                     />
                   )}
                 </div>
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2">
                    <PipelineEditableCell
                      value={item.name}
                      item={item}
                      field="name"
                      className="font-semibold"
                      placeholder="Name"
                    />
                    {hasChildren && (
                      <div className="flex items-center gap-1">
                        <div className="w-1.5 h-1.5 rounded-full bg-primary"></div>
                        <span className="text-xs text-muted-foreground">
                          {item.children?.length || 0} subitem{item.children && item.children.length !== 1 ? 's' : ''}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
              </div>
              <div className="flex items-center gap-1">
                <div onClick={preventDetailOpen}>
                  <UpdatesDialog 
                    pipelineItem={item}
                    trigger={
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        className="h-6 w-6 p-0"
                      >
                        <MessageSquare className="h-4 w-4" />
                      </Button>
                    }
                  />
                </div>
                {hasNotes && (
                  <TooltipProvider>
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <Button 
                          variant="ghost" 
                          size="sm" 
                          className="h-6 w-6 p-0" 
                          onClick={(e) => {
                            e.stopPropagation();
                            setShowNotes(!showNotes);
                          }}
                        >
                          <FileText className="h-4 w-4" />
                        </Button>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>View notes</p>
                      </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                )}
                <div onClick={preventDetailOpen}>
                  <TooltipProvider>
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <Popover open={moveOpen} onOpenChange={setMoveOpen}>
                          <PopoverTrigger asChild>
                            <Button 
                              variant="ghost" 
                              size="sm" 
                              className="h-6 w-6 p-0"
                              onClick={(e) => {
                                e.stopPropagation();
                                setMoveOpen(true);
                              }}
                            >
                              <Move className="h-4 w-4" />
                            </Button>
                          </PopoverTrigger>
                          <PopoverContent className="w-80 p-4" side="top" align="end">
                            <div className="space-y-4">
                              <h4 className="font-medium">Move to different group</h4>

                              <div className="space-y-3">
                                <div>
                                  <label className="text-sm font-medium">Category</label>
                                  <Select
                                    value={selectedMoveCategory}
                                    onValueChange={handleCategoryChange}
                                  >
                                    <SelectTrigger className="mt-1">
                                      <SelectValue />
                                    </SelectTrigger>
                                    <SelectContent>
                                      {pipelineCategoryEnum.map((category) => (
                                        <SelectItem key={category} value={category}>
                                          {category.replace('_', ' ')}
                                        </SelectItem>
                                      ))}
                                    </SelectContent>
                                  </Select>
                                </div>

                                <div>
                                  <label className="text-sm font-medium">Status</label>
                                  <Select
                                    value={selectedMoveStatus}
                                    onValueChange={(value) => setSelectedMoveStatus(value)}
                                  >
                                    <SelectTrigger className="mt-1">
                                      <SelectValue />
                                    </SelectTrigger>
                                    <SelectContent>
                                      {selectedMoveCategory && pipelineStatusEnum[selectedMoveCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                                        <SelectItem key={`${selectedMoveCategory}-${status}`} value={status}>
                                          {status}
                                        </SelectItem>
                                      ))}
                                    </SelectContent>
                                  </Select>
                                </div>
                              </div>

                              <div className="text-xs text-muted-foreground bg-muted/20 p-2 rounded">
                                Moving from <span className="font-medium">{item.category}</span> ({item.status}) to <span className="font-medium">{selectedMoveCategory}</span> ({selectedMoveStatus})
                              </div>

                              <div className="flex justify-end gap-2">
                                <Button 
                                  variant="outline" 
                                  size="sm"
                                  onClick={() => setMoveOpen(false)}
                                >
                                  Cancel
                                </Button>
                                <Button 
                                  size="sm"
                                  onClick={handleMoveItem}
                                  disabled={selectedMoveCategory === item.category && selectedMoveStatus === item.status}
                                >
                                  Move
                                </Button>
                              </div>
                            </div>
                          </PopoverContent>
                        </Popover>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>Move to different group</p>
                      </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                </div>
                <div onClick={preventDetailOpen}>
                  <TooltipProvider>
                    <Tooltip>
                      <TooltipTrigger asChild>
                        <Button 
                          variant="ghost" 
                          size="sm" 
                          className="h-6 w-6 p-0 text-destructive hover:text-destructive hover:bg-destructive/10"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleDeleteItem();
                          }}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </TooltipTrigger>
                      <TooltipContent>
                        <p>Delete item</p>
                      </TooltipContent>
                    </Tooltip>
                  </TooltipProvider>
                </div>
              </div>
            </div>
          </div>
        </CardContent>
        </Card>

        {/* Expanded Sublist Items */}
        {hasChildren && isExpanded && (
          <div className="ml-6 mt-2 space-y-2 border-l-2 border-muted pl-4">
            {item.children!.map((childItem) => (
              <PipelineItemCard 
                key={childItem.id} 
                item={childItem} 
                onRefresh={onRefresh} 
              />
            ))}

            {/* Add subitem row for existing sublists */}
            <div className="pt-2">
              <div className="flex items-center gap-2 pl-2">
                <div className="w-2 h-2 rounded-full bg-muted-foreground/30"></div>
                <SublistDialog
                  title="Add More Sublist Items"
                  buttonLabel="+ Add subitem"
                  buttonVariant="ghost"
                  buttonSize="default"
                  buttonIcon={false}
                  defaultCategory={item.category}
                  defaultStatus={item.status}
                  defaultBdr={item.bdr}
                  parentId={item.id}
                  onSuccess={onRefresh}
                  className="h-6 px-2 text-xs hover:bg-muted text-muted-foreground hover:text-foreground border border-dashed border-muted-foreground/30 hover:border-muted-foreground/50 rounded"
                />
              </div>
            </div>
          </div>
        )}
      </div>



      {/* Pipeline Item Detail Dialog */}
      <PipelineDetail 
        pipelineItemId={item.id} 
        open={detailOpen} 
        onOpenChange={(open) => {
          setDetailOpen(open);
          if (!open) handleDialogClose();
        }}
      />
    </>
  );
};

interface PipelineBoardProps {
  items: PipelineItem[];
  isLoading?: boolean;
  selectedBdr: string;
  onRefresh?: () => void;
}

export function PipelineBoard({ items, isLoading, selectedBdr, onRefresh }: PipelineBoardProps) {
  const categoryGroups = {
    Pipeline: pipelineStatusEnum.Pipeline,
    Lists_Media_QA: pipelineStatusEnum.Lists_Media_QA,
    Declined_Rescheduled: pipelineStatusEnum.Declined_Rescheduled,
    Calls: pipelineStatusEnum.Calls,
  };

  // Filter items by the selected BDR
  const filteredItems = items.filter(item => item.bdr === selectedBdr);

  // Get all pipeline item IDs for batch activity logs
  const pipelineItemIds = filteredItems.map(item => item.id);

  // Fetch batch activity logs for all pipeline items
  const { data: batchActivityData } = useBatchActivityLogs(pipelineItemIds);

  // Group items by category and status
  const groupedItems: Record<string, Record<string, PipelineItem[]>> = {};

  pipelineCategoryEnum.forEach(category => {
    groupedItems[category] = {};

    categoryGroups[category].forEach(status => {
      groupedItems[category][status] = filteredItems.filter(
        item => item.category === category && item.status === status
      );
    });
  });

  const renderSkeletonCards = (count: number) => {
    return Array(count)
      .fill(0)
      .map((_, i) => (
        <div key={i} className="mb-2">
          <Skeleton className="h-24 w-full" />
        </div>
      ));
  };

  const renderColumn = (category: string, status: string, items: PipelineItem[]) => {
    return (
      <div key={status} className="min-w-[250px] w-[250px] flex-shrink-0">
        <div className="mb-3 flex items-center justify-between">
          <h3 className="text-sm font-medium">{status} ({items.length})</h3>
          <PipelineDialog
            title={`Add ${status} Item`}
            buttonLabel="+"
            buttonVariant="outline"
            buttonSize="icon"
            buttonIcon={false}
            defaultCategory={category}
            defaultStatus={status}
            defaultBdr={selectedBdr}
            onSuccess={onRefresh}
            className="h-7 w-7"
          />
        </div>
        <div className="rounded-lg border bg-background/50 p-2 min-h-[200px]">
          {isLoading ? (
            renderSkeletonCards(3)
          ) : items.length > 0 ? (
            items.map((item) => <PipelineItemCard key={item.id} item={item} onRefresh={onRefresh} />)
          ) : (
            <div className="flex flex-col items-center justify-center h-24 text-sm text-muted-foreground gap-2">
              <span>No items</span>
              <PipelineDialog
                title={`Add ${status} Item`}
                buttonLabel="Add Lead"
                buttonVariant="outline"
                buttonSize="sm"
                buttonIcon={false}
                defaultCategory={category}
                defaultStatus={status}
                defaultBdr={selectedBdr}
                onSuccess={onRefresh}
                className="text-xs"
              />
            </div>
          )}
        </div>
      </div>
    );
  };

  const renderCallDateColumn = () => {
    const callItems = filteredItems.filter(item => item.category === 'Calls');

    return (
      <div className="min-w-[250px] w-[250px] flex-shrink-0">
        <div className="mb-3 flex items-center justify-between">
          <h3 className="text-sm font-medium">Call Dates ({callItems.length})</h3>
        </div>
        <div className="rounded-lg border bg-background/50 p-2 min-h-[200px]">
          {isLoading ? (
            renderSkeletonCards(3)
          ) : callItems.length > 0 ? (
            callItems.map((item) => (
              <Card key={item.id} className="mb-2 cursor-pointer hover:shadow-md transition-shadow">
                <CardContent className="p-3">
                  <div className="flex flex-col gap-2">
                    <div className="flex items-center justify-between">
                      <div className="flex-1 min-w-0">
                        <div className="font-semibold text-sm">{item.name}</div>
                      </div>
                    </div>

                    {item.company && (
                      <div className="text-sm text-muted-foreground">
                        {item.company}
                      </div>
                    )}

                    <div className="text-sm">
                      <PipelineEditableCell
                        value={item.callDate || ''}
                        item={item}
                        field="callDate"
                        placeholder="Set call date"
                        isDate={true}
                      />
                    </div>

                    <div className="flex items-center gap-2 mt-1 text-xs text-muted-foreground">
                      <span>{item.status}</span>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))
          ) : (
            <div className="flex flex-col items-center justify-center h-24 text-sm text-muted-foreground gap-2">
              <span>No call items</span>
              <PipelineDialog
                title="Add Call Item"
                buttonLabel="Add Call"
                buttonVariant="outline"
                buttonSize="sm"
                buttonIcon={false}
                defaultCategory="Calls"
                defaultStatus="Scheduled"
                defaultBdr={selectedBdr}
                onSuccess={onRefresh}
                className="text-xs"
              />
            </div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-8">
      {/* Floating Add New Lead Button */}
      <div className="sticky top-4 z-10 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60 border-b pb-4">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold">Pipeline Board</h1>
            <p className="text-sm text-muted-foreground">
              Managing pipeline for {selectedBdr}
            </p>
          </div>
          <PipelineDialog
            title="Add New Lead"
            buttonLabel="Add New Lead"
            buttonVariant="default"
            buttonIcon={true}
            defaultCategory="Pipeline"
            defaultStatus="New"
            defaultBdr={selectedBdr}
            onSuccess={onRefresh}
            className="shadow-lg"
          />
        </div>
      </div>

      {pipelineCategoryEnum.map((category) => (
        <div key={category} className="space-y-3">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold">
              {category.replace(/_/g, ', ')}
            </h2>
            <PipelineDialog
              title={`Add ${category.replace(/_/g, ' ')} Item`}
              buttonLabel="Add Item"
              buttonVariant="default"
              buttonIcon={true}
              defaultCategory={category}
              defaultStatus={categoryGroups[category][0]}
              defaultBdr={selectedBdr}
              onSuccess={onRefresh}
            />
          </div>
          <div className="flex gap-4 overflow-x-auto pb-4">
            {categoryGroups[category].map((status) =>
              renderColumn(category, status, groupedItems[category][status])
            )}
            {category === 'Calls' && renderCallDateColumn()}
          </div>
        </div>
      ))}

      {/* Floating Action Button for Quick Add */}
      <div className="fixed bottom-6 right-6 z-50">
        <PipelineDialog
          title="Add New Lead"
          buttonLabel=""
          buttonVariant="default"
          buttonSize="icon"
          buttonIcon={true}
          defaultCategory="Pipeline"
          defaultStatus="New"
          defaultBdr={selectedBdr}
          onSuccess={onRefresh}
          className="h-14 w-14 rounded-full shadow-lg hover:shadow-xl transition-shadow"
        />
      </div>
    </div>
  );
}

'use client';

import { useState, useCallback, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { 
  Search, 
  Building2, 
  User, 
  Mail, 
  Phone, 
  Calendar, 
  ExternalLink,
  Loader2,
} from 'lucide-react';
import { format } from 'date-fns';
import { useDebounce } from '@/lib/hooks/use-debounce';

interface SearchResult {
  id: string;
  type: 'lead' | 'pipeline';
  name: string;
  company?: string;
  email?: string;
  phone?: string;
  status: string;
  category?: string;
  value?: number;
  addedDate: string;
  lastActivity?: string;
  owner: {
    id: string;
    name: string;
    role: string;
  };
  relevanceScore: number;
}

interface SearchResponse {
  results: SearchResult[];
  totalFound: number;
  query: string;
  searchType: string;
}

export function DuplicateSearch() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchType, setSearchType] = useState('all');
  const [includeInactive, setIncludeInactive] = useState(false);
  const [results, setResults] = useState<SearchResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [totalFound, setTotalFound] = useState(0);

  const debouncedQuery = useDebounce(searchQuery, 300);

  const performSearch = useCallback(async (query: string, type: string, inactive: boolean) => {
    if (!query.trim() || query.length < 2) {
      setResults([]);
      setTotalFound(0);
      return;
    }

    setLoading(true);
    
    try {
      const params = new URLSearchParams({
        query: query.trim(),
        type,
        limit: '50',
        includeInactive: inactive.toString(),
      });

      const response = await fetch(`/api/duplicates/search?${params}`);
      
      if (response.ok) {
        const data: SearchResponse = await response.json();
        setResults(data.results);
        setTotalFound(data.totalFound);
      } else {
        console.error('Search failed:', response.statusText);
        setResults([]);
        setTotalFound(0);
      }
    } catch (error) {
      console.error('Search error:', error);
      setResults([]);
      setTotalFound(0);
    } finally {
      setLoading(false);
    }
  }, []);

  // Auto-search when debounced query changes
  useEffect(() => {
    performSearch(debouncedQuery, searchType, includeInactive);
  }, [debouncedQuery, searchType, includeInactive, performSearch]);

  const getTypeIcon = (type: string) => {
    switch (type) {
      case 'lead':
        return <User className="h-4 w-4 text-blue-500" />;
      case 'pipeline':
        return <Building2 className="h-4 w-4 text-green-500" />;
      default:
        return <Search className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status: string, type: string) => {
    if (type === 'pipeline') {
      if (status.includes('Won')) return 'default';
      if (status.includes('Lost') || status === 'Dead') return 'destructive';
      return 'secondary';
    }
    
    // Lead statuses
    if (status === 'Closed') return 'destructive';
    if (status.includes('Contacted')) return 'default';
    return 'secondary';
  };

  const formatValue = (value?: number) => {
    if (!value) return '';
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP',
      minimumFractionDigits: 0,
    }).format(value);
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy');
    } catch {
      return 'Invalid date';
    }
  };

  const getRelevanceColor = (score: number) => {
    if (score >= 0.9) return 'bg-green-100 text-green-800';
    if (score >= 0.7) return 'bg-yellow-100 text-yellow-800';
    return 'bg-gray-100 text-gray-800';
  };

  return (
    <Card className="w-full">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Search className="h-5 w-5" />
          Database Search
        </CardTitle>
        <CardDescription>
          Search for existing contacts and companies to identify potential duplicates
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Search Controls */}
        <div className="flex flex-col md:flex-row gap-4">
          <div className="flex-1">
            <Input
              placeholder="Search by name, company, email, or phone..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full"
            />
          </div>
          <div className="flex gap-2">
            <Select value={searchType} onValueChange={setSearchType}>
              <SelectTrigger className="w-[140px]">
                <SelectValue placeholder="Search type" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Types</SelectItem>
                <SelectItem value="company">Company</SelectItem>
                <SelectItem value="contact">Contact</SelectItem>
                <SelectItem value="email">Email</SelectItem>
                <SelectItem value="phone">Phone</SelectItem>
              </SelectContent>
            </Select>
            <Button
              variant={includeInactive ? 'default' : 'outline'}
              onClick={() => setIncludeInactive(!includeInactive)}
              className="whitespace-nowrap"
            >
              Include Inactive
            </Button>
          </div>
        </div>

        {/* Search Status */}
        <div className="flex items-center justify-between text-sm text-muted-foreground">
          <div>
            {loading ? (
              <div className="flex items-center gap-2">
                <Loader2 className="h-4 w-4 animate-spin" />
                Searching...
              </div>
            ) : (
              <div>
                {searchQuery ? (
                  `Found ${totalFound} result${totalFound !== 1 ? 's' : ''} for "${searchQuery}"`
                ) : (
                  'Enter search terms to find existing records'
                )}
              </div>
            )}
          </div>
        </div>

        {/* Results */}
        {results.length > 0 ? (
          <div className="border rounded-lg">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead className="w-[50px]">Type</TableHead>
                  <TableHead>Contact</TableHead>
                  <TableHead>Company</TableHead>
                  <TableHead>Contact Info</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Owner</TableHead>
                  <TableHead>Added</TableHead>
                  <TableHead className="w-[80px]">Match</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {results.map((result) => (
                  <TableRow key={result.id} className="hover:bg-gray-50/50">
                    <TableCell>
                      <div className="flex items-center justify-center">
                        {getTypeIcon(result.type)}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div>
                        <div className="font-medium">{result.name}</div>
                        {result.type === 'pipeline' && result.category && (
                          <div className="text-xs text-muted-foreground">
                            {result.category}
                          </div>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center gap-1">
                        <Building2 className="h-3 w-3 text-muted-foreground" />
                        {result.company || 'N/A'}
                      </div>
                      {result.value && (
                        <div className="text-xs text-muted-foreground">
                          {formatValue(result.value)}
                        </div>
                      )}
                    </TableCell>
                    <TableCell>
                      <div className="space-y-1 text-xs">
                        {result.email && (
                          <div className="flex items-center gap-1">
                            <Mail className="h-3 w-3 text-muted-foreground" />
                            <span className="truncate max-w-[150px]">{result.email}</span>
                          </div>
                        )}
                        {result.phone && (
                          <div className="flex items-center gap-1">
                            <Phone className="h-3 w-3 text-muted-foreground" />
                            <span>{result.phone}</span>
                          </div>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <Badge variant={getStatusColor(result.status, result.type)}>
                        {result.status}
                      </Badge>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm">
                        <div className="font-medium">{result.owner.name}</div>
                        <div className="text-xs text-muted-foreground capitalize">
                          {result.owner.role.toLowerCase()}
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-sm">
                        <div>{formatDate(result.addedDate)}</div>
                        {result.lastActivity && (
                          <div className="text-xs text-muted-foreground flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {formatDate(result.lastActivity)}
                          </div>
                        )}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="text-center">
                        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getRelevanceColor(result.relevanceScore)}`}>
                          {Math.round(result.relevanceScore * 100)}%
                        </span>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        ) : searchQuery && !loading && (
          <div className="text-center py-8 text-muted-foreground">
            <Search className="h-8 w-8 mx-auto mb-2 opacity-50" />
            <p>No results found for "{searchQuery}"</p>
            <p className="text-sm">Try different search terms or check your filters</p>
          </div>
        )}

        {!searchQuery && (
          <div className="text-center py-8 text-muted-foreground">
            <Search className="h-8 w-8 mx-auto mb-2 opacity-50" />
            <p>Start typing to search the database</p>
            <p className="text-sm">Search by company name, contact name, email, or phone number</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

'use client';

import { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Separator } from '@/components/ui/separator';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Checkbox } from '@/components/ui/checkbox';
import { 
  AlertTriangle, 
  Building2, 
  Mail, 
  Phone, 
  User, 
  Calendar, 
  ExternalLink,
  Clock,
  AlertCircle
} from 'lucide-react';
import { WarningSeverity, DuplicateType } from '@prisma/client';

export interface DuplicateMatch {
  id: string;
  matchType: DuplicateType;
  confidence: number;
  severity: WarningSeverity;
  matchDetails: {
    type: 'exact' | 'similar';
    field: string;
  };
  existingRecord: {
    type: 'lead' | 'pipeline' | 'company' | 'contact';
    company?: string;
    lastContactDate?: string;
    status?: string;
    isActive?: boolean;
    owner?: {
      name?: string;
    };
  };
}

export interface DuplicateWarningResult {
  hasWarning: boolean;
  severity: WarningSeverity;
  warningId?: string;
  message?: string;
  matches: DuplicateMatch[];
}

interface DuplicateWarningDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onProceed: (reason?: string) => void;
  onCancel: () => void;
  warning: DuplicateWarningResult;
  isProcessing?: boolean;
}

export function DuplicateWarningDialog({
  isOpen,
  onClose,
  onProceed,
  onCancel,
  warning,
  isProcessing = false,
}: DuplicateWarningDialogProps) {
  const [acknowledged, setAcknowledged] = useState(false);
  const [reason, setReason] = useState('');
  const [showReason, setShowReason] = useState(false);

  const handleProceed = () => {
    onProceed(reason.trim() || undefined);
    resetState();
  };

  const handleCancel = () => {
    onCancel();
    resetState();
  };

  const handleClose = () => {
    onClose();
    resetState();
  };

  const resetState = () => {
    setAcknowledged(false);
    setReason('');
    setShowReason(false);
  };

  const getSeverityIcon = (severity: WarningSeverity) => {
    switch (severity) {
      case WarningSeverity.CRITICAL:
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      case WarningSeverity.HIGH:
        return <AlertTriangle className="h-5 w-5 text-orange-500" />;
      case WarningSeverity.MEDIUM:
        return <AlertTriangle className="h-5 w-5 text-yellow-500" />;
      default:
        return <AlertTriangle className="h-5 w-5 text-blue-500" />;
    }
  };

  const getSeverityColor = (severity: WarningSeverity) => {
    switch (severity) {
      case WarningSeverity.CRITICAL:
        return 'destructive';
      case WarningSeverity.HIGH:
        return 'destructive';
      case WarningSeverity.MEDIUM:
        return 'secondary';
      default:
        return 'outline';
    }
  };

  const getMatchTypeIcon = (matchType: DuplicateType) => {
    switch (matchType) {
      case DuplicateType.COMPANY_NAME:
      case DuplicateType.COMPANY_DOMAIN:
        return <Building2 className="h-4 w-4" />;
      case DuplicateType.CONTACT_EMAIL:
        return <Mail className="h-4 w-4" />;
      case DuplicateType.CONTACT_PHONE:
        return <Phone className="h-4 w-4" />;
      case DuplicateType.CONTACT_NAME:
        return <User className="h-4 w-4" />;
      case DuplicateType.LINKEDIN_PROFILE:
        return <ExternalLink className="h-4 w-4" />;
      default:
        return <AlertTriangle className="h-4 w-4" />;
    }
  };

  const formatMatchType = (matchType: DuplicateType) => {
    return matchType
      .toLowerCase()
      .replace(/_/g, ' ')
      .replace(/\b\w/g, l => l.toUpperCase());
  };

  const formatTimeAgo = (dateString?: string) => {
    if (!dateString) return 'Unknown';
    
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000);
    
    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} minutes ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)} hours ago`;
    if (diffInSeconds < 2592000) return `${Math.floor(diffInSeconds / 86400)} days ago`;
    if (diffInSeconds < 31536000) return `${Math.floor(diffInSeconds / 2592000)} months ago`;
    
    return `${Math.floor(diffInSeconds / 31536000)} years ago`;
  };

  const highPriorityMatches = warning.matches.filter(m => 
    m.severity === WarningSeverity.CRITICAL || m.severity === WarningSeverity.HIGH
  );
  
  const mediumPriorityMatches = warning.matches.filter(m => 
    m.severity === WarningSeverity.MEDIUM
  );

  const shouldShowReason = warning.severity === WarningSeverity.CRITICAL || 
                          warning.severity === WarningSeverity.HIGH ||
                          highPriorityMatches.length > 0;

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl">
            {getSeverityIcon(warning.severity)}
            Potential Duplicate Detected
          </DialogTitle>
          <DialogDescription className="text-base">
            {warning.message || 
             `We found ${warning.matches.length} potential duplicate(s) in the system. Please review before proceeding.`}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* High Priority Matches */}
          {highPriorityMatches.length > 0 && (
            <div>
              <h3 className="text-lg font-semibold mb-3 flex items-center gap-2">
                <AlertCircle className="h-5 w-5 text-red-500" />
                High Priority Conflicts ({highPriorityMatches.length})
              </h3>
              <div className="space-y-3">
                {highPriorityMatches.map((match) => (
                  <Card key={match.id} className="border-red-200 dark:border-red-800">
                    <CardHeader className="pb-3">
                      <CardTitle className="text-sm flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          {getMatchTypeIcon(match.matchType)}
                          {formatMatchType(match.matchType)} Match
                        </div>
                        <Badge variant={getSeverityColor(match.severity)}>
                          {match.severity}
                        </Badge>
                      </CardTitle>
                    </CardHeader>
                    <CardContent className="pt-0">
                      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                        <div>
                          <Label className="text-xs text-muted-foreground">Company</Label>
                          <p className="font-medium">{match.existingRecord.company || 'N/A'}</p>
                        </div>
                        <div>
                          <Label className="text-xs text-muted-foreground">Last Contact</Label>
                          <p className="flex items-center gap-1">
                            <Clock className="h-3 w-3" />
                            {formatTimeAgo(match.existingRecord.lastContactDate)}
                          </p>
                        </div>
                        <div>
                          <Label className="text-xs text-muted-foreground">Owner</Label>
                          <p>{match.existingRecord.owner?.name || 'Unknown'}</p>
                        </div>
                      </div>
                      <div className="mt-3 flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <Badge variant="outline" className="text-xs">
                            {match.existingRecord.type.toUpperCase()}
                          </Badge>
                          <Badge 
                            variant={match.existingRecord.isActive ? 'default' : 'secondary'}
                            className="text-xs"
                          >
                            {match.existingRecord.status || 'Unknown Status'}
                          </Badge>
                        </div>
                        <div className="text-xs text-muted-foreground">
                          {Math.round(match.confidence * 100)}% match confidence
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}

          {/* Medium Priority Matches */}
          {mediumPriorityMatches.length > 0 && (
            <div>
              {highPriorityMatches.length > 0 && <Separator />}
              <h3 className="text-base font-semibold mb-3 flex items-center gap-2">
                <AlertTriangle className="h-4 w-4 text-yellow-500" />
                Additional Matches ({mediumPriorityMatches.length})
              </h3>
              <div className="space-y-2">
                {mediumPriorityMatches.map((match) => (
                  <Card key={match.id} className="border-yellow-200 dark:border-yellow-800">
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                          {getMatchTypeIcon(match.matchType)}
                          <div>
                            <p className="text-sm font-medium">
                              {formatMatchType(match.matchType)} - {match.existingRecord.company || 'Unknown Company'}
                            </p>
                            <p className="text-xs text-muted-foreground">
                              Last contact: {formatTimeAgo(match.existingRecord.lastContactDate)} by {match.existingRecord.owner?.name || 'Unknown'}
                            </p>
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <Badge variant="outline" className="text-xs">
                            {Math.round(match.confidence * 100)}%
                          </Badge>
                          <Badge variant={getSeverityColor(match.severity)} className="text-xs">
                            {match.severity}
                          </Badge>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </div>
          )}

          {/* Acknowledgment and Reason */}
          <Separator />
          
          <div className="space-y-4">
            <div className="flex items-center space-x-2">
              <Checkbox
                id="acknowledge"
                checked={acknowledged}
                onCheckedChange={(checked) => setAcknowledged(checked as boolean)}
              />
              <Label htmlFor="acknowledge" className="text-sm">
                I understand these may be duplicates and want to proceed anyway
              </Label>
            </div>

            {(shouldShowReason || showReason) && (
              <div>
                <Label htmlFor="reason" className="text-sm font-medium">
                  Reason for proceeding {shouldShowReason && <span className="text-red-500">*</span>}
                </Label>
                <Textarea
                  id="reason"
                  placeholder="Please explain why you're proceeding despite potential duplicates..."
                  value={reason}
                  onChange={(e) => setReason(e.target.value)}
                  className="mt-2"
                  rows={3}
                />
                <p className="text-xs text-muted-foreground mt-1">
                  This will be logged for audit purposes.
                </p>
              </div>
            )}

            {!shouldShowReason && !showReason && (
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setShowReason(true)}
                className="text-muted-foreground"
              >
                Add reason (optional)
              </Button>
            )}
          </div>
        </div>

        <DialogFooter className="flex-col sm:flex-row gap-2">
          <div className="flex flex-1 justify-start">
            <p className="text-xs text-muted-foreground">
              This decision will be logged for audit trail and analytics.
            </p>
          </div>
          
          <div className="flex gap-2">
            <Button
              variant="outline"
              onClick={handleCancel}
              disabled={isProcessing}
            >
              Cancel
            </Button>
            
            <Button
              onClick={handleProceed}
              disabled={
                !acknowledged || 
                (shouldShowReason && !reason.trim()) ||
                isProcessing
              }
              className="min-w-[120px]"
            >
              {isProcessing ? 'Processing...' : 'Proceed Anyway'}
            </Button>
          </div>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Badge } from '@/components/ui/badge';
import { Textarea } from '@/components/ui/textarea';
import { CalendarIcon, ExternalLink, Mail, Phone, Plus, Search, Trash2 } from 'lucide-react';
import { formatDistanceToNow } from 'date-fns';
import { toast } from 'sonner';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';

export interface EditorialBoardItem {
  id: number;
  name: string;
  title?: string;
  company?: string;
  email?: string;
  phone?: string;
  addedDate: string;
  lastUpdated: string;
  bdrId: string;
  status: string;
  notes?: string;
  link?: string;
  interviewDate?: string;
  qaSubmissionDate?: string;
  qaApprovedDate?: string;
  publicationDate?: string;
  publicationLink?: string;
  leadId?: number;
  pipelineItemId?: number;
  bdr: {
    name: string;
    email: string;
  };
  lead?: {
    name: string;
    company: string;
  };
  pipelineItem?: {
    name: string;
    company: string;
  };
}

interface EditorialBoardTableProps {
  items: EditorialBoardItem[];
  isLoading?: boolean;
  onRefresh: () => void;
  selectedBdr?: string;
  onBdrChange?: (bdr: string) => void;
  availableBdrs?: Array<{ id: string; name: string }>;
}

const statusConfig = {
  'LIST_OUT_QA_INTERVIEW_PROPOSED': { label: 'List Out/Q&A/Interview Proposed', color: 'bg-yellow-100 text-yellow-800' },
  'INTERVIEW_SCHEDULED': { label: 'Interview Scheduled', color: 'bg-blue-100 text-blue-800' },
  'INTERVIEW_COMPLETED': { label: 'Interview Completed', color: 'bg-purple-100 text-purple-800' },
  'QA_SUBMITTED': { label: 'Q&A Submitted', color: 'bg-orange-100 text-orange-800' },
  'QA_APPROVED': { label: 'Q&A Approved', color: 'bg-green-100 text-green-800' },
  'PUBLISHED': { label: 'Published', color: 'bg-emerald-100 text-emerald-800' },
  'DECLINED': { label: 'Declined', color: 'bg-red-100 text-red-800' },
};

export function EditorialBoardTable({ 
  items, 
  isLoading, 
  onRefresh, 
  selectedBdr, 
  onBdrChange, 
  availableBdrs 
}: EditorialBoardTableProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [newItem, setNewItem] = useState<Partial<EditorialBoardItem>>({
    name: '',
    title: '',
    company: '',
    email: '',
    phone: '',
    status: 'LIST_OUT_QA_INTERVIEW_PROPOSED',
    notes: '',
    link: '',
    bdrId: selectedBdr || '',
  });

  const filteredItems = items.filter(item => {
    const matchesSearch = !searchQuery || 
      item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      (item.company && item.company.toLowerCase().includes(searchQuery.toLowerCase())) ||
      (item.email && item.email.toLowerCase().includes(searchQuery.toLowerCase()));
    
    const matchesStatus = statusFilter === 'all' || item.status === statusFilter;
    
    return matchesSearch && matchesStatus;
  });

  const handleAddItem = async () => {
    try {
      const response = await fetch('/api/editorial', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newItem),
      });

      if (!response.ok) {
        throw new Error('Failed to create editorial item');
      }

      toast.success('Editorial item created successfully');
      setIsAddDialogOpen(false);
      setNewItem({
        name: '',
        title: '',
        company: '',
        email: '',
        phone: '',
        status: 'LIST_OUT_QA_INTERVIEW_PROPOSED',
        notes: '',
        link: '',
        bdrId: selectedBdr || '',
      });
      onRefresh();
    } catch (error) {
      console.error('Error creating editorial item:', error);
      toast.error('Failed to create editorial item');
    }
  };

  const handleDeleteItem = async (id: number) => {
    if (!confirm('Are you sure you want to delete this editorial item?')) {
      return;
    }

    try {
      const response = await fetch(`/api/editorial/${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete editorial item');
      }

      toast.success('Editorial item deleted successfully');
      onRefresh();
    } catch (error) {
      console.error('Error deleting editorial item:', error);
      toast.error('Failed to delete editorial item');
    }
  };

  const handleStatusUpdate = async (id: number, newStatus: string) => {
    try {
      const response = await fetch(`/api/editorial/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus }),
      });

      if (!response.ok) {
        throw new Error('Failed to update status');
      }

      toast.success('Status updated successfully');
      onRefresh();
    } catch (error) {
      console.error('Error updating status:', error);
      toast.error('Failed to update status');
    }
  };

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
            <div>
              <CardTitle className="flex items-center gap-2">
                ðŸ“ Editorial Board
              </CardTitle>
              <p className="text-sm text-muted-foreground mt-1">
                Manage leads for list out, Q&A, and interview opportunities
              </p>
            </div>
            
            <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button>
                  <Plus className="h-4 w-4 mr-2" />
                  Add Editorial Item
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-md">
                <DialogHeader>
                  <DialogTitle>Add Editorial Item</DialogTitle>
                  <DialogDescription>
                    Create a new editorial board entry for a lead.
                  </DialogDescription>
                </DialogHeader>
                
                <div className="space-y-4">
                  <div>
                    <Label htmlFor="name">Name *</Label>
                    <Input
                      id="name"
                      placeholder="Contact name"
                      value={newItem.name || ''}
                      onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                    />
                  </div>
                  
                  <div>
                    <Label htmlFor="title">Title</Label>
                    <Input
                      id="title"
                      placeholder="Job title"
                      value={newItem.title || ''}
                      onChange={(e) => setNewItem({ ...newItem, title: e.target.value })}
                    />
                  </div>
                  
                  <div>
                    <Label htmlFor="company">Company</Label>
                    <Input
                      id="company"
                      placeholder="Company name"
                      value={newItem.company || ''}
                      onChange={(e) => setNewItem({ ...newItem, company: e.target.value })}
                    />
                  </div>
                  
                  <div>
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder="Email address"
                      value={newItem.email || ''}
                      onChange={(e) => setNewItem({ ...newItem, email: e.target.value })}
                    />
                  </div>
                  
                  <div>
                    <Label htmlFor="status">Status</Label>
                    <Select
                      value={newItem.status}
                      onValueChange={(value) => setNewItem({ ...newItem, status: value })}
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {Object.entries(statusConfig).map(([key, config]) => (
                          <SelectItem key={key} value={key}>
                            {config.label}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {availableBdrs && (
                    <div>
                      <Label htmlFor="bdr">BDR</Label>
                      <Select
                        value={newItem.bdrId}
                        onValueChange={(value) => setNewItem({ ...newItem, bdrId: value })}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Select BDR" />
                        </SelectTrigger>
                        <SelectContent>
                          {availableBdrs.map((bdr) => (
                            <SelectItem key={bdr.id} value={bdr.id}>
                              {bdr.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  )}
                  
                  <div>
                    <Label htmlFor="notes">Notes</Label>
                    <Textarea
                      id="notes"
                      placeholder="Additional notes..."
                      value={newItem.notes || ''}
                      onChange={(e) => setNewItem({ ...newItem, notes: e.target.value })}
                      rows={3}
                    />
                  </div>
                  
                  <div className="flex gap-2 pt-4">
                    <Button onClick={handleAddItem} disabled={!newItem.name}>
                      Create Item
                    </Button>
                    <Button variant="outline" onClick={() => setIsAddDialogOpen(false)}>
                      Cancel
                    </Button>
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </CardHeader>
        
        <CardContent>
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search by name, company, or email..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            
            <Select value={statusFilter} onValueChange={setStatusFilter}>
              <SelectTrigger className="w-full sm:w-48">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                {Object.entries(statusConfig).map(([key, config]) => (
                  <SelectItem key={key} value={key}>
                    {config.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            {availableBdrs && onBdrChange && (
              <Select value={selectedBdr || 'all'} onValueChange={onBdrChange}>
                <SelectTrigger className="w-full sm:w-48">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All BDRs</SelectItem>
                  {availableBdrs.map((bdr) => (
                    <SelectItem key={bdr.id} value={bdr.id}>
                      {bdr.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            )}
          </div>

          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Company</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>BDR</TableHead>
                  <TableHead>Last Updated</TableHead>
                  <TableHead>Contact</TableHead>
                  <TableHead className="w-[100px]">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {isLoading ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8">
                      Loading editorial items...
                    </TableCell>
                  </TableRow>
                ) : filteredItems.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                      {searchQuery || statusFilter !== 'all' 
                        ? 'No editorial items match your filters'
                        : 'No editorial items found'
                      }
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredItems.map((item) => (
                    <TableRow key={item.id} className="hover:bg-muted/50">
                      <TableCell>
                        <div>
                          <div className="font-medium">{item.name}</div>
                          {item.title && (
                            <div className="text-sm text-muted-foreground">{item.title}</div>
                          )}
                        </div>
                      </TableCell>
                      
                      <TableCell>
                        <div>
                          <div>{item.company || 'Not specified'}</div>
                          {(item.lead || item.pipelineItem) && (
                            <div className="text-xs text-muted-foreground">
                              {item.lead && `Lead: ${item.lead.name}`}
                              {item.pipelineItem && `Pipeline: ${item.pipelineItem.name}`}
                            </div>
                          )}
                        </div>
                      </TableCell>
                      
                      <TableCell>
                        <Select
                          value={item.status}
                          onValueChange={(value) => handleStatusUpdate(item.id, value)}
                        >
                          <SelectTrigger className="w-auto border-0 p-0 h-auto">
                            <Badge className={statusConfig[item.status as keyof typeof statusConfig]?.color}>
                              {statusConfig[item.status as keyof typeof statusConfig]?.label}
                            </Badge>
                          </SelectTrigger>
                          <SelectContent>
                            {Object.entries(statusConfig).map(([key, config]) => (
                              <SelectItem key={key} value={key}>
                                {config.label}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </TableCell>
                      
                      <TableCell>
                        <div className="text-sm">
                          {item.bdr.name}
                        </div>
                      </TableCell>
                      
                      <TableCell>
                        <div className="text-sm text-muted-foreground">
                          {formatDistanceToNow(new Date(item.lastUpdated), { addSuffix: true })}
                        </div>
                      </TableCell>
                      
                      <TableCell>
                        <div className="flex gap-2">
                          {item.email && (
                            <a href={`mailto:${item.email}`} className="text-blue-500 hover:text-blue-700">
                              <Mail className="h-4 w-4" />
                            </a>
                          )}
                          {item.phone && (
                            <a href={`tel:${item.phone}`} className="text-blue-500 hover:text-blue-700">
                              <Phone className="h-4 w-4" />
                            </a>
                          )}
                          {item.link && (
                            <a href={item.link} target="_blank" rel="noopener noreferrer" className="text-blue-500 hover:text-blue-700">
                              <ExternalLink className="h-4 w-4" />
                            </a>
                          )}
                        </div>
                      </TableCell>
                      
                      <TableCell>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleDeleteItem(item.id)}
                          className="text-destructive hover:text-destructive"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                )}
              </TableBody>
            </Table>
          </div>
          
          <div className="flex items-center justify-between pt-4">
            <div className="text-sm text-muted-foreground">
              Showing {filteredItems.length} of {items.length} editorial items
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle, CardFooter } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { 
  DollarSign, 
  TrendingUp, 
  TrendingDown, 
  Activity, 
  Target, 
  Clock, 
  HelpCircle,
  Plus,
  AlertCircle
} from 'lucide-react';
import Link from 'next/link';

interface FinanceAnalytics {
  totalRevenue: number;
  ytdRevenue: number;
  monthlyRevenue: number;
  quarterlyRevenue: number;
  averageDealSize: number;
  conversionRate: number;
  overdueDays: number;
  monthlyGrowth: number;
  totalDeals: number;
  ytdDeals: number;
  monthlyDeals: number;
  averagePaymentTime: number;
  statusBreakdown: { [key: string]: number };
  monthlyTrends: Array<{ month: string; revenue: number; deals: number }>;
  bdrPerformance: Array<{ bdr: string; revenue: number; deals: number; avgDealSize: number }>;
  paymentStatus: {
    paid: number;
    pending: number;
    overdue: number;
  };
}

export function FinanceBoard() {
  const [analytics, setAnalytics] = useState<FinanceAnalytics | null>(null);
  const [loading, setLoading] = useState(true);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/finance?analytics=true');
      if (response.ok) {
        const data = await response.json();
        setAnalytics(data.analytics);
      }
    } catch (error) {
      console.error('Error fetching finance analytics:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnalytics();
  }, []);

  const getTrendIcon = (value: number) => {
    if (value > 0) return <TrendingUp className="h-4 w-4 text-emerald-600 dark:text-emerald-400" />;
    if (value < 0) return <TrendingDown className="h-4 w-4 text-rose-600 dark:text-rose-400" />;
    return <Activity className="h-4 w-4 text-muted-foreground" />;
  };

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-GB', { 
      style: 'currency', 
      currency: 'GBP' 
    }).format(amount);
  };

  if (loading) {
    return (
      <Card className="hover:shadow-lg transition-shadow flex flex-col h-full">
        <CardHeader className="pb-3 flex-shrink-0">
          <CardTitle className="flex items-center gap-2">
            <DollarSign className="h-5 w-5" />
            Finance Overview
          </CardTitle>
          <CardDescription>Loading financial metrics...</CardDescription>
        </CardHeader>
        <CardContent className="text-sm text-muted-foreground flex-grow">
          <div className="flex items-center justify-center py-8">
            <Activity className="h-6 w-6 animate-spin" />
          </div>
        </CardContent>
      </Card>
    );
  }

  if (!analytics) {
    return (
      <Card className="hover:shadow-lg transition-shadow flex flex-col h-full">
        <CardHeader className="pb-3 flex-shrink-0">
          <CardTitle className="flex items-center gap-2">
            <DollarSign className="h-5 w-5" />
            Finance Overview
          </CardTitle>
          <CardDescription>Financial performance and revenue tracking</CardDescription>
        </CardHeader>
        <CardContent className="text-sm text-muted-foreground flex-grow">
          <div className="flex items-center justify-center py-8 text-center">
            <div>
              <AlertCircle className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              <p className="text-gray-600">No finance data available</p>
              <p className="text-xs text-gray-500 mt-1">Add finance entries to see metrics</p>
            </div>
          </div>
        </CardContent>
        <CardFooter className="flex-shrink-0">
          <Link href="/finance" className="w-full">
            <Button className="w-full" size="lg">
              <Plus className="h-4 w-4 mr-2" />
              Go to Finance
            </Button>
          </Link>
        </CardFooter>
      </Card>
    );
  }

  return (
    <Card className="hover:shadow-lg transition-shadow flex flex-col h-full">
      <CardHeader className="pb-3 flex-shrink-0">
        <CardTitle className="flex items-center gap-2">
          <DollarSign className="h-5 w-5" />
          Finance Overview
          {getTrendIcon(analytics.monthlyGrowth)}
        </CardTitle>
        <CardDescription>Financial performance and revenue tracking</CardDescription>
      </CardHeader>
      <CardContent className="text-sm text-muted-foreground flex-grow">
        <div className="space-y-4">
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-muted/50 rounded-lg p-3 border">
              <div className="text-lg font-bold text-emerald-600 dark:text-emerald-400">{formatCurrency(analytics.ytdRevenue)}</div>
              <div className="text-xs text-muted-foreground">YTD Revenue</div>
            </div>
            <div className="bg-muted/50 rounded-lg p-3 border">
              <div className="text-lg font-bold text-blue-600 dark:text-blue-400">{formatCurrency(analytics.averageDealSize)}</div>
              <div className="text-xs text-muted-foreground">Avg Deal Size</div>
            </div>
          </div>
          
          <div className="space-y-2">
            <div className="flex items-center justify-between text-xs">
              <span>Payment Status</span>
              <span className="text-muted-foreground">{analytics.totalDeals} total deals</span>
            </div>
            <div className="grid grid-cols-3 gap-2">
              <div className="text-center">
                <div className="text-lg font-bold text-emerald-600 dark:text-emerald-400">{analytics.paymentStatus.paid}</div>
                <div className="text-xs text-muted-foreground">Paid</div>
              </div>
              <div className="text-center">
                <div className="text-lg font-bold text-amber-600 dark:text-amber-400">{analytics.paymentStatus.pending}</div>
                <div className="text-xs text-muted-foreground">Pending</div>
              </div>
              <div className="text-center">
                <div className="text-lg font-bold text-rose-600 dark:text-rose-400">{analytics.paymentStatus.overdue}</div>
                <div className="text-xs text-muted-foreground">Overdue</div>
              </div>
            </div>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between text-xs">
              <span>Monthly Growth</span>
              <span className={`font-medium ${analytics.monthlyGrowth >= 0 ? 'text-emerald-600 dark:text-emerald-400' : 'text-rose-600 dark:text-rose-400'}`}>
                {analytics.monthlyGrowth > 0 ? '+' : ''}{analytics.monthlyGrowth.toFixed(1)}%
              </span>
            </div>
            <div className="flex items-center gap-2 text-xs">
              <Clock className="h-3 w-3" />
              <span>Avg payment time: {Math.round(analytics.averagePaymentTime)} days</span>
            </div>
          </div>
        </div>
      </CardContent>
      <CardFooter className="flex-shrink-0">
        <Link href="/finance" className="w-full">
          <Button className="w-full" size="lg">
            <DollarSign className="h-4 w-4 mr-2" />
            Open Finance Dashboard
          </Button>
        </Link>
      </CardFooter>
    </Card>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { financeStatusEnum, leadBdrEnum } from '@/lib/validations';
import { formatDateForInput } from '@/lib/date-utils';
import { toast } from 'sonner';
import { exchangeRateService } from '@/lib/exchange-rate';

interface FinanceEntry {
  id?: number;
  company: string;
  bdr: string;
  leadGen: boolean;
  status: string;
  invoiceDate: Date | null;
  dueDate: Date | null;
  soldAmount: number | null;
  gbpAmount: number | null;
  exchangeRate: number | null;
  exchangeRateDate: Date | null;
  actualGbpReceived: number | null;
  notes: string | null;
  commissionPaid: boolean;
  month: string;
}

interface FinanceFormProps {
  entry?: FinanceEntry;
  onSubmit: (data: Omit<FinanceEntry, 'id'>) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export function FinanceForm({ entry, onSubmit, onCancel, isLoading }: FinanceFormProps) {
  const [formData, setFormData] = useState<Omit<FinanceEntry, 'id'>>({
    company: entry?.company || '',
    bdr: entry?.bdr || '',
    leadGen: entry?.leadGen || false,
    status: entry?.status || 'Awaiting Invoice',
    invoiceDate: entry?.invoiceDate || null,
    dueDate: entry?.dueDate || null,
    soldAmount: entry?.soldAmount || null,
    gbpAmount: entry?.gbpAmount || null,
    exchangeRate: entry?.exchangeRate || null,
    exchangeRateDate: entry?.exchangeRateDate || null,
    actualGbpReceived: entry?.actualGbpReceived || null,
    notes: entry?.notes || null,
    commissionPaid: entry?.commissionPaid || false,
    month: entry?.month || '2025-01',
  });

  const [isConverting, setIsConverting] = useState(false);
  const [exchangeRateInfo, setExchangeRateInfo] = useState<string>('');

  // Auto-convert USD to GBP when sold amount changes
  useEffect(() => {
    const convertUSDToGBP = async () => {
      if (formData.soldAmount && formData.soldAmount > 0) {
        // Only auto-convert if this is a new entry or if GBP amount is empty
        if (!entry || !formData.gbpAmount) {
          setIsConverting(true);
          try {
            const rate = await exchangeRateService.getCurrentRate();
            const gbpAmount = exchangeRateService.convertUSDToGBP(formData.soldAmount, rate);
            
            setFormData(prev => ({
              ...prev,
              gbpAmount,
              exchangeRate: rate,
              exchangeRateDate: new Date()
            }));
            
            setExchangeRateInfo(`Converted at rate: $1 = Â£${rate.toFixed(4)}`);
          } catch (error) {
            console.error('Error converting currency:', error);
            toast.error('Failed to convert currency. Please enter GBP amount manually.');
          } finally {
            setIsConverting(false);
          }
        }
      }
    };

    convertUSDToGBP();
  }, [formData.soldAmount, entry]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    
    if (!formData.company.trim()) {
      toast.error('Company is required');
      return;
    }
    
    if (!formData.bdr) {
      toast.error('BDR is required');
      return;
    }
    
    onSubmit(formData);
  };


  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="company">Company *</Label>
          <Input
            id="company"
            value={formData.company}
            onChange={(e) => setFormData({ ...formData, company: e.target.value })}
            placeholder="Enter company name"
            required
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="bdr">BDR *</Label>
          <Select value={formData.bdr} onValueChange={(value) => setFormData({ ...formData, bdr: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select BDR" />
            </SelectTrigger>
            <SelectContent>
              {leadBdrEnum.map((bdr) => (
                <SelectItem key={bdr} value={bdr}>
                  {bdr}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-2">
          <Label htmlFor="month">Month *</Label>
          <Select value={formData.month} onValueChange={(value) => setFormData({ ...formData, month: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select month" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="2025-01">January 2025</SelectItem>
              <SelectItem value="2025-02">February 2025</SelectItem>
              <SelectItem value="2025-03">March 2025</SelectItem>
              <SelectItem value="2025-04">April 2025</SelectItem>
              <SelectItem value="2025-05">May 2025</SelectItem>
              <SelectItem value="2025-06">June 2025</SelectItem>
              <SelectItem value="2025-07">July 2025</SelectItem>
              <SelectItem value="2025-08">August 2025</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-2">
          <Label htmlFor="status">Status *</Label>
          <Select value={formData.status} onValueChange={(value) => setFormData({ ...formData, status: value })}>
            <SelectTrigger>
              <SelectValue placeholder="Select status" />
            </SelectTrigger>
            <SelectContent>
              {financeStatusEnum.map((status) => (
                <SelectItem key={status} value={status}>
                  {status}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-2">
          <Label htmlFor="soldAmount">Sold Amount</Label>
          <Input
            id="soldAmount"
            type="number"
            step="0.01"
            value={formData.soldAmount || ''}
            onChange={(e) => setFormData({ 
              ...formData, 
              soldAmount: e.target.value ? parseFloat(e.target.value) : null 
            })}
            placeholder="Enter sold amount"
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="gbpAmount">
            GBP Amount
            {exchangeRateInfo && (
              <span className="text-xs text-gray-500 ml-2">({exchangeRateInfo})</span>
            )}
          </Label>
          <Input
            id="gbpAmount"
            type="number"
            step="0.01"
            value={formData.gbpAmount || ''}
            onChange={(e) => {
              const newGbpAmount = e.target.value ? parseFloat(e.target.value) : null;
              setFormData({ 
                ...formData, 
                gbpAmount: newGbpAmount,
                // Clear exchange rate info if manually edited
                exchangeRate: newGbpAmount ? formData.exchangeRate : null,
                exchangeRateDate: newGbpAmount ? formData.exchangeRateDate : null
              });
              if (newGbpAmount) {
                setExchangeRateInfo('Manually entered');
              }
            }}
            placeholder={isConverting ? "Converting..." : "Enter GBP amount"}
            disabled={isConverting}
          />
          {entry && formData.exchangeRate && (
            <p className="text-xs text-gray-500">
              Historical rate: $1 = Â£{formData.exchangeRate.toFixed(4)} 
              ({formData.exchangeRateDate ? new Date(formData.exchangeRateDate).toLocaleDateString() : 'N/A'})
            </p>
          )}
        </div>

        <div className="space-y-2">
          <Label htmlFor="actualGbpReceived">Actual GBP Received</Label>
          <Input
            id="actualGbpReceived"
            type="number"
            step="0.01"
            value={formData.actualGbpReceived || ''}
            onChange={(e) => setFormData({ 
              ...formData, 
              actualGbpReceived: e.target.value ? parseFloat(e.target.value) : null 
            })}
            placeholder="Enter actual GBP received"
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="invoiceDate">Invoice Date</Label>
          <Input
            id="invoiceDate"
            type="date"
            value={formatDateForInput(formData.invoiceDate)}
            onChange={(e) => setFormData({ 
              ...formData, 
              invoiceDate: e.target.value ? new Date(e.target.value) : null 
            })}
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="dueDate">Due Date</Label>
          <Input
            id="dueDate"
            type="date"
            value={formatDateForInput(formData.dueDate)}
            onChange={(e) => setFormData({ 
              ...formData, 
              dueDate: e.target.value ? new Date(e.target.value) : null 
            })}
          />
        </div>
      </div>

      <div className="flex items-center space-x-2">
        <Checkbox
          id="leadGen"
          checked={formData.leadGen}
          onCheckedChange={(checked) => setFormData({ ...formData, leadGen: !!checked })}
        />
        <Label htmlFor="leadGen">Lead Gen</Label>
      </div>

      <div className="flex items-center space-x-2">
        <Checkbox
          id="commissionPaid"
          checked={formData.commissionPaid}
          onCheckedChange={(checked) => setFormData({ ...formData, commissionPaid: !!checked })}
        />
        <Label htmlFor="commissionPaid">Commission Paid</Label>
      </div>

      <div className="space-y-2">
        <Label htmlFor="notes">Notes</Label>
        <Textarea
          id="notes"
          value={formData.notes || ''}
          onChange={(e) => setFormData({ ...formData, notes: e.target.value || null })}
          placeholder="Enter any additional notes"
          rows={3}
        />
      </div>

      <div className="flex justify-end space-x-2">
        <Button type="button" variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button type="submit" disabled={isLoading}>
          {isLoading ? 'Saving...' : entry ? 'Update' : 'Create'}
        </Button>
      </div>
    </form>
  );
}

'use client';

import React, { useState, useMemo } from 'react';
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
} from '@tanstack/react-table';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { 
  Filter, 
  Download, 
  Upload, 
  Trash2, 
  Edit, 
  ChevronLeft, 
  ChevronRight,
  Plus,
  LayoutGrid,
  List,
  ChevronDown,
  ChevronUp
} from 'lucide-react';
import { financeStatusEnum, leadBdrEnum } from '@/lib/validations';
import { formatDateUK, getMonthName } from '@/lib/date-utils';
import { toast } from 'sonner';

interface FinanceEntry {
  id: number;
  company: string;
  bdr: string;
  leadGen: boolean;
  status: string;
  invoiceDate: Date | null;
  dueDate: Date | null;
  soldAmount: number | null;
  gbpAmount: number | null;
  exchangeRate: number | null;
  exchangeRateDate: Date | null;
  actualGbpReceived: number | null;
  notes: string | null;
  commissionPaid: boolean;
  month: string;
  createdAt: Date;
  updatedAt: Date;
}

interface FinanceTableProps {
  data?: FinanceEntry[];
  groupedData?: { [key: string]: FinanceEntry[] };
  isLoading: boolean;
  onEdit: (entry: FinanceEntry) => void;
  onDelete: (id: number) => void;
  onCreate: () => void;
  pagination?: {
    page: number;
    pageSize: number;
    total: number;
    totalPages: number;
  };
  onPaginationChange?: (page: number, pageSize: number) => void;
  filters: {
    search: string;
    status: string;
    bdr: string;
    month?: string;
  };
  onFiltersChange: (filters: any) => void;
  viewMode: 'table' | 'groups';
  onViewModeChange: (mode: 'table' | 'groups') => void;
}

export function FinanceTable({
  data,
  groupedData,
  isLoading,
  onEdit,
  onDelete,
  onCreate,
  pagination,
  onPaginationChange,
  filters,
  onFiltersChange,
  viewMode,
  onViewModeChange,
}: FinanceTableProps) {
  const [selectedRows, setSelectedRows] = useState<number[]>([]);
  const [collapsedMonths, setCollapsedMonths] = useState<Set<string>>(new Set());

  const toggleMonthCollapse = (month: string) => {
    const newCollapsed = new Set(collapsedMonths);
    if (newCollapsed.has(month)) {
      newCollapsed.delete(month);
    } else {
      newCollapsed.add(month);
    }
    setCollapsedMonths(newCollapsed);
  };

  const calculateMonthSummary = (entries: FinanceEntry[]) => {
    const totalEntries = entries.length;
    const totalValue = entries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
    const paidEntries = entries.filter(entry => entry.status === 'Paid').length;
    return { totalEntries, totalValue, paidEntries };
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case 'Paid':
        return 'default';
      case 'Cancelled':
        return 'destructive';
      case 'Late':
        return 'destructive';
      case 'Invoiced':
        return 'secondary';
      case 'Pending Clearance':
        return 'outline';
      default:
        return 'secondary';
    }
  };

  const columns: ColumnDef<FinanceEntry>[] = [
    {
      id: 'select',
      header: ({ table }) => (
        <Checkbox
          checked={table.getIsAllPageRowsSelected()}
          onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
          aria-label="Select all"
        />
      ),
      cell: ({ row }) => (
        <Checkbox
          checked={row.getIsSelected()}
          onCheckedChange={(value) => row.toggleSelected(!!value)}
          aria-label="Select row"
        />
      ),
    },
    {
      accessorKey: 'company',
      header: 'Company',
      cell: ({ row }) => (
        <div className="font-medium">{row.getValue('company')}</div>
      ),
    },
    {
      accessorKey: 'bdr',
      header: 'BDR',
      cell: ({ row }) => (
        <div className="text-sm">{row.getValue('bdr')}</div>
      ),
    },
    {
      accessorKey: 'month',
      header: 'Month',
      cell: ({ row }) => (
        <div className="text-sm">{getMonthName(row.getValue('month'))}</div>
      ),
    },
    {
      accessorKey: 'leadGen',
      header: 'Lead Gen',
      cell: ({ row }) => (
        <div className="text-center">
          {row.getValue('leadGen') ? 'âœ“' : 'âœ—'}
        </div>
      ),
    },
    {
      accessorKey: 'status',
      header: 'Status',
      cell: ({ row }) => (
        <Badge variant={getStatusBadgeVariant(row.getValue('status'))}>
          {row.getValue('status')}
        </Badge>
      ),
    },
    {
      accessorKey: 'invoiceDate',
      header: 'Invoice Date',
      cell: ({ row }) => {
        const date = row.getValue('invoiceDate') as Date | null;
        return formatDateUK(date);
      },
    },
    {
      accessorKey: 'dueDate',
      header: 'Due Date',
      cell: ({ row }) => {
        const date = row.getValue('dueDate') as Date | null;
        return formatDateUK(date);
      },
    },
    {
      accessorKey: 'soldAmount',
      header: 'Sold Amount',
      cell: ({ row }) => {
        const amount = row.getValue('soldAmount') as number | null;
        return amount ? `$${amount.toLocaleString()}` : '-';
      },
    },
    {
      accessorKey: 'gbpAmount',
      header: 'GBP Amount',
      cell: ({ row }) => {
        const amount = row.getValue('gbpAmount') as number | null;
        const exchangeRate = row.getValue('exchangeRate') as number | null;
        const exchangeRateDate = row.getValue('exchangeRateDate') as Date | null;

        if (!amount) return '-';

        return (
          <div className="space-y-1">
            <div className="font-medium">Â£{amount.toLocaleString()}</div>
            {exchangeRate && (
              <div className="text-xs text-gray-500">
                Rate: $1 = Â£{exchangeRate.toFixed(4)}
                {exchangeRateDate && (
                  <span className="ml-1">
                    ({new Date(exchangeRateDate).toLocaleDateString()})
                  </span>
                )}
              </div>
            )}
          </div>
        );
      },
    },
    {
      accessorKey: 'exchangeRate',
      header: 'Exchange Rate',
      cell: ({ row }) => {
        const rate = row.getValue('exchangeRate') as number | null;
        return rate ? `$1 = Â£${rate.toFixed(4)}` : '-';
      },
    },
    {
      accessorKey: 'exchangeRateDate',
      header: 'Exchange Rate Date',
      cell: ({ row }) => {
        const date = row.getValue('exchangeRateDate') as Date | null;
        return date ? new Date(date).toLocaleDateString() : '-';
      },
    },
    {
      accessorKey: 'actualGbpReceived',
      header: 'Actual GBP Received',
      cell: ({ row }) => {
        const amount = row.getValue('actualGbpReceived') as number | null;
        return amount ? `Â£${amount.toLocaleString()}` : '-';
      },
    },
    {
      accessorKey: 'commissionPaid',
      header: 'Commission Paid',
      cell: ({ row }) => (
        <div className="text-center">
          {row.getValue('commissionPaid') ? 'âœ“' : 'âœ—'}
        </div>
      ),
    },
    {
      accessorKey: 'notes',
      header: 'Notes',
      cell: ({ row }) => {
        const notes = row.getValue('notes') as string | null;
        return notes ? (
          <div className="max-w-[200px] truncate" title={notes}>
            {notes}
          </div>
        ) : '-';
      },
    },
    {
      id: 'actions',
      cell: ({ row }) => (
        <div className="flex items-center gap-1">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => onEdit(row.original)}
          >
            <Edit className="h-4 w-4" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => {
              if (confirm('Are you sure you want to delete this entry?')) {
                onDelete(row.original.id);
              }
            }}
          >
            <Trash2 className="h-4 w-4" />
          </Button>
        </div>
      ),
    },
  ];

  const table = useReactTable({
    data: data || [],
    columns,
    getCoreRowModel: getCoreRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getSortedRowModel: getSortedRowModel(),
  });

  const renderMonthlyGroups = () => {
    if (!groupedData) return null;

    const sortedMonths = Object.keys(groupedData).sort((a, b) => b.localeCompare(a));

    return (
      <div className="space-y-4">
        {sortedMonths.map((month) => {
          const entries = groupedData[month];
          const isCollapsed = collapsedMonths.has(month);
          const summary = calculateMonthSummary(entries);

          return (
            <Card key={month} className="overflow-hidden">
              <div
                className="flex items-center justify-between p-4 bg-muted/50 cursor-pointer hover:bg-muted/70 transition-colors"
                onClick={() => toggleMonthCollapse(month)}
              >
                <div className="flex items-center space-x-3">
                  {isCollapsed ? (
                    <ChevronRight className="h-5 w-5 text-muted-foreground" />
                  ) : (
                    <ChevronDown className="h-5 w-5 text-muted-foreground" />
                  )}
                  <div>
                    <h3 className="text-lg font-semibold text-primary">
                      {getMonthName(month)}
                    </h3>
                    <div className="flex items-center space-x-4 text-sm text-muted-foreground mt-1">
                      <span>{summary.totalEntries} entries</span>
                      <span>Â£{summary.totalValue.toLocaleString()}</span>
                      <span>{summary.paidEntries} paid</span>
                    </div>
                  </div>
                </div>
                <Badge variant="outline" className="ml-2">
                  {entries.length}
                </Badge>
              </div>

              {!isCollapsed && (
                <div className="border-t">
                  <Table>
                    <TableHeader>
                      <TableRow className="bg-muted/20">
                        <TableHead className="w-[50px]">
                          <Checkbox
                            checked={entries.every(entry => selectedRows.includes(entry.id))}
                            onCheckedChange={(value) => {
                              if (value) {
                                setSelectedRows(prev => [...new Set([...prev, ...entries.map(e => e.id)])]);
                              } else {
                                setSelectedRows(prev => prev.filter(id => !entries.map(e => e.id).includes(id)));
                              }
                            }}
                          />
                        </TableHead>
                        <TableHead>Company</TableHead>
                        <TableHead>BDR</TableHead>
                        <TableHead>Lead Gen</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Invoice Date</TableHead>
                        <TableHead>Due Date</TableHead>
                        <TableHead>Sold Amount</TableHead>
                        <TableHead>GBP Amount</TableHead>
                        <TableHead>Exchange Rate</TableHead>
                        <TableHead>Exchange Rate Date</TableHead>
                        <TableHead>Actual GBP</TableHead>
                        <TableHead>Commission</TableHead>
                        <TableHead>Notes</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {entries.map((entry) => (
                        <TableRow key={entry.id} className="hover:bg-muted/30">
                          <TableCell>
                            <Checkbox
                              checked={selectedRows.includes(entry.id)}
                              onCheckedChange={(value) => {
                                if (value) {
                                  setSelectedRows(prev => [...prev, entry.id]);
                                } else {
                                  setSelectedRows(prev => prev.filter(id => id !== entry.id));
                                }
                              }}
                            />
                          </TableCell>
                          <TableCell className="font-medium">{entry.company}</TableCell>
                          <TableCell>{entry.bdr}</TableCell>
                          <TableCell className="text-center">
                            {entry.leadGen ? 'âœ“' : 'âœ—'}
                          </TableCell>
                          <TableCell>
                            <Badge variant={getStatusBadgeVariant(entry.status)}>
                              {entry.status}
                            </Badge>
                          </TableCell>
                          <TableCell>
                            {formatDateUK(entry.invoiceDate)}
                          </TableCell>
                          <TableCell>
                            {formatDateUK(entry.dueDate)}
                          </TableCell>
                          <TableCell>
