"use client";

import { useActivityLogs, useCreateActivityLog } from "@/lib/hooks";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { activityTypeEnum, leadBdrEnum } from "@/lib/validations";
import { formatDateUK } from "@/lib/date-utils";
import { useState } from "react";
import { toast } from "sonner";

export function LeadActivityTimeline({ leadId }: { leadId: number }) {
  const { data, isLoading } = useActivityLogs({ leadId, page: 1, pageSize: 20 });
  const { mutate: createLog, isPending } = useCreateActivityLog();
  const [activityType, setActivityType] = useState<string>(activityTypeEnum[0]);
  const [description, setDescription] = useState("");
  const [notes, setNotes] = useState("");

  const onAdd = () => {
    if (!description.trim()) {
      toast.error("Please enter a description");
      return;
    }
    createLog(
      {
        bdr: leadBdrEnum[0],
        activityType: activityType as any,
        description,
        notes,
        leadId,
      },
      {
        onSuccess: () => {
          setDescription("");
          setNotes("");
          toast.success("Activity added");
        },
      }
    );
  };

  return (
    <Card>
      <CardHeader>
        <CardTitle>Activity</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="mb-4 grid gap-2 sm:grid-cols-3">
          <Select value={activityType} onValueChange={setActivityType}>
            <SelectTrigger>
              <SelectValue placeholder="Type" />
            </SelectTrigger>
            <SelectContent>
              {activityTypeEnum.map((t) => (
                <SelectItem key={t} value={t}>{t}</SelectItem>
              ))}
            </SelectContent>
          </Select>
          <Input
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <div className="flex justify-end">
            <Button onClick={onAdd} disabled={isPending}>Add</Button>
          </div>
          <div className="sm:col-span-3">
            <Textarea
              placeholder="Notes (optional)"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
            />
          </div>
        </div>
        <div className="space-y-3">
          {isLoading ? (
            <div>Loading activity...</div>
          ) : (
            data?.logs.map((log) => (
              <div key={log.id} className="rounded-md border p-3">
                <div className="flex items-center justify-between">
                  <div className="font-medium">{log.activityType}</div>
                  <div className="text-xs text-muted-foreground">{formatDateUK(log.timestamp)}</div>
                </div>
                <div className="text-sm">{log.description}</div>
                {log.notes && <div className="text-sm text-muted-foreground whitespace-pre-wrap">{log.notes}</div>}
              </div>
            ))
          )}
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { ColumnDef } from "@tanstack/react-table";
import { Lead, useBdrManager } from "@/lib/hooks";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { formatDateUK } from "@/lib/date-utils";
import { createSelectColumn } from "./leads-table";
import { useRouter } from "next/navigation";
import { useDeleteLead } from "@/lib/hooks";
import { toast } from "sonner";
import { ExternalLink, MoreHorizontal, CheckCircle2 } from "lucide-react";
import { NotesPopover } from "@/components/ui/notes-popover";
import { EditableCell } from "@/components/ui/editable-cell";
import { leadSourceEnum, leadStatusEnum } from "@/lib/validations";
import { LeadToPipelineDialog } from "./lead-to-pipeline-dialog";
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { Badge } from '@/components/ui/badge';
import { LeadUpdatesDialog } from './lead-updates-dialog';

// Custom type for column meta data
interface ColumnMeta {
  priority: 'high' | 'medium' | 'low';
}

export function useLeadColumns(): ColumnDef<Lead, unknown>[] {
  const router = useRouter();
  const { mutate: deleteLead } = useDeleteLead();
  const { bdrs } = useBdrManager();

  function handleDelete(id: number) {
    if (confirm("Are you sure you want to delete this lead?")) {
      deleteLead(id);
    }
  }

  return [
    // Actions column - high priority
    {
      id: "actions",
      cell: ({ row }) => {
        const lead = row.original;

        return (
          <div className="flex items-center">
            <LeadToPipelineDialog lead={lead} />
            <LeadUpdatesDialog lead={lead} />
            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                  <span className="sr-only">Open menu</span>
                  <MoreHorizontal className="h-4 w-4" />
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end">
                <DropdownMenuItem onClick={() => router.push(`/leads/${lead.id}/edit`)}>
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => handleDelete(lead.id)}>
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        );
      },
      meta: { priority: 'high' } as ColumnMeta,
    },

    // Add checkbox column - highest priority
    {
      ...createSelectColumn<Lead>(),
      meta: { priority: 'high' } as ColumnMeta,
    },

    // Name column - highest priority
    {
      accessorKey: "name",
      header: "Name",
      cell: ({ row }) => (
        <div className="flex items-center space-x-2">
          <EditableCell value={row.getValue("name")} row={row} column={{ id: "name" }} />
          {row.original.notes && <NotesPopover notes={row.original.notes} />}
          {row.original.link && (
            <a 
              href={row.original.link} 
              target="_blank" 
              rel="noopener noreferrer"
              className="text-blue-500 hover:text-blue-700 flex-shrink-0"
            >
              <ExternalLink className="h-4 w-4" />
            </a>
          )}
        </div>
      ),
      meta: { priority: 'high' } as ColumnMeta,
    },

    // Company column - medium priority, with conflict badge
    {
      accessorKey: "company",
      header: "Company",
      cell: ({ row }) => {
        const company: string | null = row.getValue("company") as any;
        const [hasConflict, setHasConflict] = (require('react') as typeof import('react')).useState<boolean>(false);
        const ReactLib = require('react') as typeof import('react');
        ReactLib.useEffect(() => {
          if (!company) { setHasConflict(false); return; }
          const params = new URLSearchParams();
          params.append('company', company);
          params.append('days', '14');
          fetch(`/api/duplicates/company-conflicts?${params.toString()}`)
            .then(r => r.ok ? r.json() : null)
            .then(data => setHasConflict(Boolean(data?.conflicts?.[company!])))
            .catch(() => setHasConflict(false));
        }, [company]);
        return (
          <div className="flex items-center gap-2">
            <EditableCell value={company} row={row} column={{ id: "company" }} />
            {hasConflict && (
              <span className="inline-flex items-center rounded border px-1.5 py-0.5 text-[10px] bg-red-50 text-red-700 border-red-200">Conflict</span>
            )}
          </div>
        );
      },
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Status column - high priority
    {
      accessorKey: "status",
      header: "Status",
      cell: ({ row }) => (
        <div>
          <EditableCell 
            value={row.getValue("status")} 
            row={row} 
            column={{ id: "status" }}
            isSelect={true}
            options={[...leadStatusEnum]}
          />
        </div>
      ),
      meta: { priority: 'high' } as ColumnMeta,
    },

    // BDR column - medium priority
    {
      accessorKey: "bdr",
      header: "BDR",
      cell: ({ row }) => (
        <div>
          <EditableCell 
            value={row.getValue("bdr")} 
            row={row} 
            column={{ id: "bdr" }}
            isSelect={true}
            options={[...bdrs]}
          />
        </div>
      ),
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Title column - medium priority
    {
      accessorKey: "title",
      header: "Title",
      cell: ({ row }) => (
        <div>
          <EditableCell value={row.getValue("title")} row={row} column={{ id: "title" }} />
        </div>
      ),
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Source column - medium priority
    {
      accessorKey: "source",
      header: "Source",
      cell: ({ row }) => (
        <div>
          <EditableCell 
            value={row.getValue("source")} 
            row={row} 
            column={{ id: "source" }}
            isSelect={true}
            options={[...leadSourceEnum]}
          />
        </div>
      ),
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Added Date column - medium priority
    {
      accessorKey: "addedDate",
      header: "Date Added",
      cell: ({ row }) => {
        const date = row.getValue("addedDate") as string;
        return date ? (
          <div className="whitespace-nowrap">{formatDateUK(date)}</div>
        ) : (
          "-"
        );
      },
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Email column - low priority
    {
      accessorKey: "email",
      header: "Email",
      cell: ({ row }) => (
        <div>
          <EditableCell value={row.getValue("email")} row={row} column={{ id: "email" }} />
        </div>
      ),
      meta: { priority: 'low' } as ColumnMeta,
    },

    // Last Updated column - medium priority
    {
      accessorKey: "lastUpdated",
      header: "Last Updated",
      cell: ({ row }) => {
        const date = row.getValue("lastUpdated") as string;
        return date ? (
          <div className="whitespace-nowrap">{formatDateUK(date)}</div>
        ) : (
          "-"
        );
      },
      meta: { priority: 'medium' } as ColumnMeta,
    },

    // Phone column - low priority
    {
      accessorKey: "phone",
      header: "Phone",
      cell: ({ row }) => (
        <div>
          <EditableCell value={row.getValue("phone")} row={row} column={{ id: "phone" }} />
        </div>
      ),
      meta: { priority: 'low' } as ColumnMeta,
    },

    // Pipeline indicator column - highest priority
    {
      id: "pipeline",
      header: "Pipeline",
      cell: ({ row }) => {
        const lead = row.original;
        if (!lead.inPipeline) return null;

        return (
          <div className="flex items-center gap-2">
            <CheckCircle2 className="h-4 w-4 text-green-500" />
            <Badge variant="outline" className="whitespace-nowrap">
              {lead.pipelineCategory}
            </Badge>
          </div>
        );
      },
      meta: { priority: 'high' } as ColumnMeta,
    },
  ];
}

'use client';

import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { useEffect, useCallback, useMemo } from 'react';
import { Button } from '@/components/ui/button';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Lead, LeadCreateInput, LeadUpdateInput, useBdrManager } from '@/lib/hooks';
import { leadSchema, leadSourceEnum, leadStatusEnum } from '@/lib/validations';
import { useRouter } from 'next/navigation';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { AlertTriangle, Loader2 } from 'lucide-react';
import { useDuplicateDetection } from '@/lib/hooks/use-duplicate-detection';
import { DuplicateWarningDialog } from '@/components/duplicate-warning-dialog';
import { useDebounce } from '@/lib/hooks/use-debounce';

interface LeadFormProps {
  lead?: Lead;
  onSubmit: (data: LeadCreateInput | LeadUpdateInput) => void;
  onSaveAndAddToPipeline?: (data: LeadCreateInput | LeadUpdateInput) => void;
  isSubmitting?: boolean;
}

type FormData = z.infer<typeof leadSchema>;

export function LeadForm({ lead, onSubmit, onSaveAndAddToPipeline, isSubmitting }: LeadFormProps) {
  const router = useRouter();
  const isEditing = Boolean(lead);
  const { bdrs, addBdr } = useBdrManager();
  
  // Duplicate detection
  const {
    isChecking,
    currentWarning,
    isWarningOpen,
    isProcessing,
    checkDuplicates,
    handleProceed,
    handleCancel,
    closeWarning,
    reset: resetDuplicateState,
    hasWarning,
  } = useDuplicateDetection({
    onDecisionMade: (decision, warningId) => {
    }
  });

  // Create form with validation
  const form = useForm<FormData>({
    resolver: zodResolver(leadSchema),
    defaultValues: {
      name: lead?.name || '',
      title: lead?.title || '',
      bdr: (lead?.bdr as string | null) || null,
      company: lead?.company || '',
      source: lead?.source || 'LinkedIn',
      status: lead?.status || 'BDR Followed Up',
      link: lead?.link || '',
      phone: lead?.phone || '',
      notes: lead?.notes || '',
      email: lead?.email || '',
    },
  });

  // Watch form values for duplicate checking
  const formValues = form.watch();
  
  // Debounced values for duplicate checking
  const debouncedName = useDebounce(formValues.name || '', 500);
  const debouncedEmail = useDebounce(formValues.email || '', 500);
  const debouncedCompany = useDebounce(formValues.company || '', 500);
  const debouncedPhone = useDebounce(formValues.phone || '', 500);
  const debouncedLinkedIn = useDebounce(formValues.link || '', 500);

  // Create duplicate check input
  const duplicateCheckInput = useMemo(() => ({
    name: debouncedName,
    email: debouncedEmail,
    company: debouncedCompany,
    phone: debouncedPhone,
    linkedinUrl: debouncedLinkedIn,
    title: formValues.title || undefined,
  }), [debouncedName, debouncedEmail, debouncedCompany, debouncedPhone, debouncedLinkedIn, formValues.title]);

  // Auto-check for duplicates when form values change (but not when editing existing lead)
  useEffect(() => {
    if (!isEditing && (debouncedName || debouncedEmail || debouncedCompany)) {
      const hasMinimumData = debouncedName.length >= 2 || 
                            debouncedEmail.length >= 5 || 
                            debouncedCompany.length >= 2;
                            
      if (hasMinimumData) {
        checkDuplicates(duplicateCheckInput, 'LEAD_CREATE');
      }
    }
  }, [duplicateCheckInput, isEditing, checkDuplicates, debouncedName, debouncedEmail, debouncedCompany]);

  // Handle form submission with duplicate checking
  const handleSubmitWithDuplicateCheck = useCallback(async (values: FormData) => {
    // Reset any previous duplicate state
    resetDuplicateState();
    
    // If editing, skip duplicate check and submit directly
    if (isEditing && lead) {
      onSubmit({
        id: lead.id,
        ...values,
      });
      return;
    }

    // For new leads, check for duplicates first
    const duplicateResult = await checkDuplicates({
      name: values.name,
      email: values.email || undefined,
      company: values.company || undefined,
      phone: values.phone || undefined,
      linkedinUrl: values.link || undefined,
      title: values.title || undefined,
    }, 'LEAD_CREATE');

    // If no warning or user already handled warning, submit
    if (!duplicateResult?.hasWarning) {
      onSubmit(values);
    }
    // If there's a warning, the dialog will handle the flow
  }, [isEditing, lead, onSubmit, checkDuplicates, resetDuplicateState]);

  // Handle duplicate proceed - submit the form
  const handleDuplicateProceed = useCallback(async (reason?: string) => {
    const success = await handleProceed(reason);
    if (success) {
      // Get current form values and submit
      const currentValues = form.getValues();
      onSubmit(currentValues);
    }
  }, [handleProceed, form, onSubmit]);

  // Handle duplicate cancel - just close dialog
  const handleDuplicateCancel = useCallback(async () => {
    await handleCancel();
    // Form stays as-is, user can modify and try again
  }, [handleCancel]);

  // Handle form submission
  function handleSubmit(values: FormData) {
    handleSubmitWithDuplicateCheck(values);
  }

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the form field with the new BDR
      form.setValue('bdr', newBdr);
    }
    return success;
  };

  return (
    <>
      <Form {...form}>
        <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
          {/* Duplicate checking indicator */}
          {!isEditing && (isChecking || hasWarning) && (
            <div className="mb-4">
              {isChecking && (
                <div className="flex items-center gap-2 p-3 bg-blue-50 border border-blue-200 rounded-lg">
                  <Loader2 className="h-4 w-4 animate-spin text-blue-600" />
                  <span className="text-sm text-blue-700">Checking for duplicates...</span>
                </div>
              )}
              {hasWarning && !isChecking && (
                <div className="flex items-center gap-2 p-3 bg-yellow-50 border border-yellow-200 rounded-lg">
                  <AlertTriangle className="h-4 w-4 text-yellow-600" />
                  <span className="text-sm text-yellow-700">
                    Potential duplicates detected. Review warning before submitting.
                  </span>
                  <Badge variant="outline" className="ml-auto">
                    {currentWarning?.matches.length} match(es)
                  </Badge>
                </div>
              )}
            </div>
          )}
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Name */}
          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Name*</FormLabel>
                <FormControl>
                  <Input placeholder="John Doe" {...field} className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Title */}
          <FormField
            control={form.control}
            name="title"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Title</FormLabel>
                <FormControl>
                  <Input placeholder="CEO" {...field} value={field.value || ''} className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Company */}
          <FormField
            control={form.control}
            name="company"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Company</FormLabel>
                <FormControl>
                  <Input placeholder="Acme Inc." {...field} value={field.value || ''} className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* BDR */}
          <FormField
            control={form.control}
            name="bdr"
            render={({ field }) => (
              <FormItem>
                <FormLabel>BDR Owner</FormLabel>
                <div className="flex gap-2 items-center">
                  <Select
                    onValueChange={(value) => field.onChange(value === "none" ? null : value)}
                    value={field.value || "none"}
                  >
                    <FormControl>
                      <SelectTrigger className="flex-1 h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur">
                        <SelectValue placeholder="Select a BDR" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="none">None</SelectItem>
                      {bdrs.map((bdr) => (
                        <SelectItem key={bdr} value={bdr}>
                          {bdr}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <AddBdrDialog 
                    onAddBdr={handleAddBdr}
                  />
                </div>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Source */}
          <FormField
            control={form.control}
            name="source"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Source*</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  value={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur">
                      <SelectValue placeholder="Select a source" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {leadSourceEnum.map((source) => (
                      <SelectItem key={source} value={source}>
                        {source}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Status */}
          <FormField
            control={form.control}
            name="status"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Status*</FormLabel>
                <Select
                  onValueChange={field.onChange}
                  value={field.value}
                >
                  <FormControl>
                    <SelectTrigger className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur">
                      <SelectValue placeholder="Select a status" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {leadStatusEnum.map((status) => (
                      <SelectItem key={status} value={status}>
                        {status}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Email */}
          <FormField
            control={form.control}
            name="email"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Email</FormLabel>
                <FormControl>
                  <Input
                    placeholder="john@example.com"
                    type="email"
                    {...field}
                    value={field.value || ''}
                    className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70"
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Phone */}
          <FormField
            control={form.control}
            name="phone"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Phone</FormLabel>
                <FormControl>
                  <Input
                    placeholder="+44 7700 900123"
                    {...field}
                    value={field.value || ''}
                    className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70"
                    onChange={(e) => {
                      // lightweight mask: keep digits and + space
                      const raw = e.target.value;
                      const masked = raw.replace(/[^+\d\s]/g, "");
                      field.onChange(masked);
                    }}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Link */}
          <FormField
            control={form.control}
            name="link"
            render={({ field }) => (
              <FormItem className="col-span-1 md:col-span-2">
                <FormLabel>LinkedIn Profile</FormLabel>
                <FormControl>
                  <Input placeholder="https://linkedin.com/in/johndoe" {...field} value={field.value || ''} className="h-10 bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70" />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          {/* Notes */}
          <FormField
            control={form.control}
            name="notes"
            render={({ field }) => (
              <FormItem className="col-span-1 md:col-span-2">
                <FormLabel>Notes</FormLabel>
                <FormControl>
                  <Textarea 
                    placeholder="Additional notes..."
                    className="min-h-[120px] bg-white/60 dark:bg-white/[0.04] backdrop-blur placeholder:text-muted-foreground/70"
                    {...field}
                    value={field.value || ''}
                  />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>
        
        <div className="flex justify-end space-x-2">
          <Button 
            variant="outline" 
            onClick={() => router.push('/leads')} 
            type="button"
            className="h-10"
          >
            Cancel
          </Button>
          {onSaveAndAddToPipeline && (
            <Button
              type="button"
              variant="secondary"
              disabled={isSubmitting}
              className="h-10"
              onClick={form.handleSubmit((values) => {
                if (lead) {
                  onSaveAndAddToPipeline({ id: lead.id, ...values });
                } else {
                  onSaveAndAddToPipeline(values);
                }
              })}
            >
              Save + Add to Pipeline
            </Button>
          )}
          <Button 
            type="submit" 
            disabled={isSubmitting || isProcessing} 
            className="h-10"
          >
            {isSubmitting || isProcessing ? (
              <>
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                {isProcessing ? 'Processing...' : 'Submitting...'}
              </>
            ) : (
              <>
                {isEditing ? 'Update Lead' : 'Create Lead'}
              </>
            )}
          </Button>
        </div>
      </form>
    </Form>

    {/* Duplicate Warning Dialog */}
    {currentWarning && (
      <DuplicateWarningDialog
        isOpen={isWarningOpen}
        onClose={closeWarning}
        onProceed={handleDuplicateProceed}
        onCancel={handleDuplicateCancel}
        warning={currentWarning}
        isProcessing={isProcessing}
      />
    )}
    </>
  );
}

'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Lead, useConvertLeadToPipeline } from '@/lib/hooks';
import { ArrowRightCircle } from 'lucide-react';
import { pipelineCategoryEnum, pipelineStatusEnum } from '@/lib/validations';

interface LeadToPipelineDialogProps {
  lead: Lead;
  onSuccess?: () => void;
}

export function LeadToPipelineDialog({
  lead,
  onSuccess,
}: LeadToPipelineDialogProps) {
  const [open, setOpen] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState<string>(pipelineCategoryEnum[0]);
  const [selectedStatus, setSelectedStatus] = useState<string>(pipelineStatusEnum.Pipeline[0]);
  const [error, setError] = useState<string | null>(null);

  const { mutate: convertLeadToPipeline, isPending } = useConvertLeadToPipeline();

  // Update selected status when category changes
  const handleCategoryChange = (category: string) => {
    setSelectedCategory(category);
    const statuses = pipelineStatusEnum[category as keyof typeof pipelineStatusEnum];
    setSelectedStatus(statuses[0]);
    setError(null);
  };

  const handleConvert = () => {
    // Make sure we have a BDR assigned to the lead
    if (!lead.bdr) {
      setError("A BDR must be assigned to this lead before moving it to the pipeline");
      return;
    }

    convertLeadToPipeline(
      {
        leadId: lead.id,
        category: selectedCategory,
        status: selectedStatus,
      },
      {
        onSuccess: () => {
          setOpen(false);
          onSuccess?.();
        },
        onError: (err: any) => {
          setError(err.message || "Failed to move lead to pipeline");
        }
      }
    );
  };

  return (
    <>
      <Button
        variant="ghost"
        size="sm"
        onClick={() => setOpen(true)}
        className="h-8 px-2"
        disabled={!lead.bdr}
        title={lead.bdr ? "Move to Pipeline" : "Assign a BDR before moving to pipeline"}
      >
        <ArrowRightCircle className="h-4 w-4 mr-1" />
        Pipeline
      </Button>
      <Dialog open={open} onOpenChange={setOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Move to Pipeline</DialogTitle>
          </DialogHeader>
          <div className="space-y-4 py-2">
            <p className="text-sm text-muted-foreground">
              Select the pipeline category and status for <span className="font-medium">{lead.name}</span>.
            </p>

            {error && (
              <div className="bg-destructive/10 text-destructive text-sm p-2 rounded">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium mb-1 block">Category</label>
                <Select 
                  value={selectedCategory} 
                  onValueChange={handleCategoryChange}
                >
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select Category" />
                  </SelectTrigger>
                  <SelectContent>
                    {pipelineCategoryEnum.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category.replace(/_/g, ', ')}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-1 block">Status</label>
                <Select 
                  value={selectedStatus} 
                  onValueChange={setSelectedStatus}
                >
                  <SelectTrigger className="w-full">
                    <SelectValue placeholder="Select Status" />
                  </SelectTrigger>
                  <SelectContent>
                    {selectedCategory && 
                      pipelineStatusEnum[selectedCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                        <SelectItem key={`${selectedCategory}-${status}`} value={status}>
                          {status}
                        </SelectItem>
                      ))}
                  </SelectContent>
                </Select>
              </div>

              <div>
                <label className="text-sm font-medium mb-1 block">Lead Information</label>
                <div className="text-sm bg-muted/50 p-3 rounded">
                  <p><span className="font-medium">BDR:</span> {lead.bdr || "Not assigned"}</p>
                  {lead.company && <p><span className="font-medium">Company:</span> {lead.company}</p>}
                  {lead.email && <p><span className="font-medium">Email:</span> {lead.email}</p>}
                  {lead.phone && <p><span className="font-medium">Phone:</span> {lead.phone}</p>}
                </div>
              </div>
            </div>
          </div>
          <div className="flex justify-end gap-2 mt-4">
            <Button type="button" variant="outline" onClick={() => setOpen(false)} disabled={isPending}>
              Cancel
            </Button>
            <Button type="button" onClick={handleConvert} disabled={isPending}>
              {isPending ? "Moving..." : "Move to Pipeline"}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

'use client';

import React, { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Separator } from '@/components/ui/separator';
import { MessageSquare, Send, Clock, User } from 'lucide-react';
import { useActivityLogs, useCreateActivityLog, Lead } from '@/lib/hooks';
import { formatDistanceToNow, format } from 'date-fns';
import { toast } from 'sonner';
import { Badge } from '@/components/ui/badge';
import { ActivityLogsSkeleton } from '@/components/ui/activity-log-skeleton';

interface LeadUpdatesDialogProps {
  lead: Lead;
  trigger?: React.ReactNode;
}

export function LeadUpdatesDialog({ lead, trigger }: LeadUpdatesDialogProps) {
  const [open, setOpen] = useState(false);
  
  const handleOpenChange = (newOpen: boolean) => {
    setOpen(newOpen);
    // Don't trigger any navigation when dialog closes
    if (!newOpen) {
      // Prevent any navigation by stopping event propagation
      const event = new CustomEvent('closeAllDialogs');
      window.dispatchEvent(event);
    }
  };
  const [updateText, setUpdateText] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch activity logs for this lead - only when dialog is open
  const { data: activityLogsData, refetch } = useActivityLogs({
    leadId: lead.id,
    page: 1,
    pageSize: 50,
  }, {
    enabled: open, // Only fetch when dialog is open
    queryKey: ['activityLogs', { leadId: lead.id, page: 1, pageSize: 50 }]
  });

  const { mutate: createActivityLog } = useCreateActivityLog();

  // Filter for BDR updates and other relevant activities
  const relevantActivities = activityLogsData?.logs?.filter(log => 
    log.activityType === 'BDR_Update' || 
    log.activityType === 'Note_Added' ||
    log.activityType === 'Status_Change' ||
    log.activityType === 'Lead_Created'
  ) || [];

  const handleSubmitUpdate = async () => {
    if (!updateText.trim()) {
      toast.error('Please enter an update message');
      return;
    }

    setIsSubmitting(true);
    
    createActivityLog({
      bdr: (lead.bdr as any) || 'Dan Reeves',
      activityType: 'BDR_Update',
      description: 'BDR added an update',
      notes: updateText.trim(),
      leadId: lead.id,
    }, {
      onSuccess: () => {
        toast.success('Update added successfully');
        setUpdateText('');
        refetch();
      },
      onError: (error) => {
        toast.error('Failed to add update');
        console.error('Error adding update:', error);
      },
      onSettled: () => {
        setIsSubmitting(false);
      }
    });
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && (e.metaKey || e.ctrlKey)) {
      e.preventDefault();
      handleSubmitUpdate();
    }
  };

  const getActivityIcon = (activityType: string) => {
    switch (activityType) {
      case 'BDR_Update':
        return <MessageSquare className="h-4 w-4 text-blue-600" />;
      case 'Status_Change':
        return <Clock className="h-4 w-4 text-orange-600" />;
      case 'Lead_Created':
        return <Clock className="h-4 w-4 text-green-600" />;
      default:
        return <User className="h-4 w-4 text-gray-600" />;
    }
  };

  const getActivityBadgeColor = (activityType: string) => {
    switch (activityType) {
      case 'BDR_Update':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200';
      case 'Status_Change':
        return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
      case 'Lead_Created':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200';
    }
  };

  const defaultTrigger = (
    <Button variant="outline" size="sm" className="h-8 px-2">
      <MessageSquare className="h-4 w-4 mr-1" />
      Updates
      {relevantActivities.length > 0 && (
        <Badge variant="secondary" className="ml-2 h-5 text-xs">
          {relevantActivities.length}
        </Badge>
      )}
    </Button>
  );

  return (
    <Dialog open={open} onOpenChange={handleOpenChange}>
      <DialogTrigger asChild>
        <div onClick={(e) => {
          e.stopPropagation();
          e.preventDefault();
          setOpen(true);
        }}>
          {trigger || defaultTrigger}
        </div>
      </DialogTrigger>
      <DialogContent className="max-w-2xl max-h-[80vh] flex flex-col">
        <DialogHeader className="flex-shrink-0">
          <DialogTitle className="flex items-center gap-2">
            <MessageSquare className="h-5 w-5" />
            Updates for {lead.name}
          </DialogTitle>
          <DialogDescription>
            Add updates and view the timeline for this lead
          </DialogDescription>
        </DialogHeader>

        <div className="flex flex-col gap-4 flex-1 min-h-0 overflow-hidden">
          {/* Add Update Section */}
          <div className="space-y-3 flex-shrink-0">
            <div className="flex items-center gap-2">
              <User className="h-4 w-4 text-muted-foreground" />
              <span className="text-sm font-medium">{lead.bdr || 'Unknown'}</span>
            </div>
            <Textarea
              placeholder="What's the latest update on this lead? (Ctrl/Cmd + Enter to send)"
              value={updateText}
              onChange={(e) => setUpdateText(e.target.value)}
              onKeyDown={handleKeyDown}
              className="min-h-[80px] resize-none"
            />
            <div className="flex justify-end">
              <Button 
                onClick={handleSubmitUpdate}
                disabled={isSubmitting || !updateText.trim()}
                size="sm"
                className="gap-2"
              >
                <Send className="h-4 w-4" />
                {isSubmitting ? 'Adding...' : 'Add Update'}
              </Button>
            </div>
          </div>

          <Separator className="flex-shrink-0" />

          {/* Timeline Section */}
          <div className="flex-1 min-h-0 flex flex-col">
            <h4 className="text-sm font-medium mb-3 flex items-center gap-2 flex-shrink-0">
              <Clock className="h-4 w-4" />
              Timeline ({relevantActivities.length} {relevantActivities.length === 1 ? 'update' : 'updates'})
            </h4>
            <div className="flex-1 min-h-0 overflow-y-auto">
              {!activityLogsData ? (
                <ActivityLogsSkeleton count={3} />
              ) : relevantActivities.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <MessageSquare className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p className="text-sm">No updates yet</p>
                  <p className="text-xs">Be the first to add an update!</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {relevantActivities.map((activity, index) => (
                    <div key={activity.id} className="flex gap-3">
                      <div className="flex flex-col items-center">
                        <div className="flex h-8 w-8 items-center justify-center rounded-full bg-muted">
                          {getActivityIcon(activity.activityType)}
                        </div>
                        {index < relevantActivities.length - 1 && (
                          <div className="mt-2 h-full w-px bg-border" />
                        )}
                      </div>
                      <div className="flex-1 space-y-2 pb-4">
                        <div className="flex items-center gap-2 flex-wrap">
                          <span className="font-medium text-sm">{activity.bdr}</span>
                          <Badge 
                            variant="secondary" 
                            className={`text-xs ${getActivityBadgeColor(activity.activityType)}`}
                          >
                            {activity.activityType === 'BDR_Update' ? 'Update' : activity.activityType.replace('_', ' ')}
                          </Badge>
                          <span className="text-xs text-muted-foreground">
                            {formatDistanceToNow(new Date(activity.timestamp), { addSuffix: true })}
                          </span>
                        </div>
                        <p className="text-sm text-muted-foreground">
                          {activity.description}
                        </p>
                        {activity.notes && (
                          <div className="bg-muted/50 rounded-md p-3">
                            <p className="text-sm whitespace-pre-wrap">{activity.notes}</p>
                          </div>
                        )}
                        <div className="text-xs text-muted-foreground">
                          {format(new Date(activity.timestamp), 'PPP p')}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import React, { useMemo } from 'react';
import { ColumnDef, VisibilityState } from '@tanstack/react-table';
import { Download, Upload, Trash2, Move, CheckCircle2 } from 'lucide-react';
import { Lead, useBdrManager } from '@/lib/hooks';
import { leadSourceEnum, leadStatusEnum } from '@/lib/validations';
import { toast } from 'sonner';
import { BulkPipelineDialog } from './bulk-pipeline-dialog';
import { LeadUpdatesDialog } from './lead-updates-dialog';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { DataTable, FilterConfig, BulkAction } from '@/components/ui/data-table';
import { 
  useBulkUpdateLeads, 
  useBulkDeleteLeads, 
  useBulkConvertLeads,
  createBulkStatusAction,
  createBulkDeleteAction,
  createBulkConvertAction 
} from '@/lib/bulk-operations';
import { useState } from 'react';
import { Button } from '@/components/ui/button';

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[];
  data: TData[];
  filters: Record<string, string>;
  total: number;
  pageCount: number;
  pageIndex: number;
  pageSize: number;
  onPaginationChange: (pageIndex: number, pageSize: number) => void;
  onGlobalFilterChange: (value: string) => void;
  onFilterChange: (columnId: string, value: string) => void;
  isLoading?: boolean;
  columnVisibility?: VisibilityState;
  onColumnVisibilityChange?: (state: VisibilityState) => void;
}

export function LeadsTableRefactored<TData, TValue>({
  columns,
  data,
  filters,
  total,
  pageCount,
  pageIndex,
  pageSize,
  onPaginationChange,
  onGlobalFilterChange,
  onFilterChange,
  isLoading,
}: DataTableProps<TData, TValue>) {
  const { bdrs, addBdr } = useBdrManager();
  const [bulkPipelineOpen, setBulkPipelineOpen] = useState(false);
  const [selectedRowsForDialog, setSelectedRowsForDialog] = useState<Lead[]>([]);

  // Bulk operation hooks
  const bulkUpdateLeads = useBulkUpdateLeads();
  const bulkDeleteLeads = useBulkDeleteLeads();
  const bulkConvertLeads = useBulkConvertLeads();

  // Filter configurations
  const filterConfigs: FilterConfig[] = useMemo(() => [
    {
      key: 'status',
      label: 'Status',
      options: leadStatusEnum.map(status => ({ value: status, label: status })),
      placeholder: 'All Statuses'
    },
    {
      key: 'source',
      label: 'Source',
      options: leadSourceEnum.map(source => ({ value: source, label: source })),
      placeholder: 'All Sources'
    },
    {
      key: 'bdr',
      label: 'BDR',
      options: bdrs.map(bdr => ({ value: bdr, label: bdr })),
      placeholder: 'All BDRs'
    }
  ], [bdrs]);

  // Bulk actions configuration
  const bulkActions: BulkAction<Lead>[] = useMemo(() => [
    {
      label: 'Mark as Fresh',
      icon: <CheckCircle2 className="h-4 w-4 mr-2" />,
      onClick: (selectedRows) => {
        const selectedIds = selectedRows.map(row => row.id);
        bulkUpdateLeads.mutate(
          { ids: selectedIds, status: 'Fresh' },
          {
            onSuccess: () => {
              toast.success(`Updated ${selectedRows.length} leads to Fresh`);
            },
          }
        );
      }
    },
    {
      label: 'Mark as Qualified',
      icon: <CheckCircle2 className="h-4 w-4 mr-2" />,
      onClick: (selectedRows) => {
        const selectedIds = selectedRows.map(row => row.id);
        bulkUpdateLeads.mutate(
          { ids: selectedIds, status: 'Qualified' },
          {
            onSuccess: () => {
              toast.success(`Updated ${selectedRows.length} leads to Qualified`);
            },
          }
        );
      }
    },
    {
      label: 'Convert to Pipeline',
      icon: <Move className="h-4 w-4 mr-2" />,
      onClick: (selectedRows) => {
        if (selectedRows.length === 0) {
          toast.error('Please select at least one lead to convert');
          return;
        }
        setSelectedRowsForDialog(selectedRows);
        setBulkPipelineOpen(true);
      }
    },
    {
      label: 'Convert All to Pipeline',
      icon: <Move className="h-4 w-4 mr-2" />,
      onClick: (selectedRows) => {
        const selectedIds = selectedRows.map(row => row.id);
        bulkConvertLeads.mutate(
          {
            leadIds: selectedIds,
            category: 'Pipeline',
            status: 'Proposal - Media',
          },
          {
            onSuccess: () => {
              toast.success(`Converted ${selectedRows.length} leads to pipeline`);
            },
          }
        );
      }
    },
    {
      label: 'Delete',
      icon: <Trash2 className="h-4 w-4 mr-2" />,
      variant: 'destructive' as const,
      onClick: (selectedRows) => {
        if (selectedRows.length === 0) {
          toast.error('Please select at least one lead to delete');
          return;
        }

        if (confirm(`Are you sure you want to delete ${selectedRows.length} leads?`)) {
          const selectedIds = selectedRows.map(row => row.id);
          bulkDeleteLeads.mutate(
            { ids: selectedIds },
            {
              onSuccess: () => {
                toast.success(`Deleted ${selectedRows.length} leads`);
              },
            }
          );
        }
      }
    }
  ], [bulkUpdateLeads, bulkDeleteLeads, bulkConvertLeads]);

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the BDR filter if it's currently set
      if (filters.bdr && filters.bdr !== 'all') {
        onFilterChange('bdr', newBdr);
      }
    }
    return success;
  };

  // Export functionality (placeholder - would need actual implementation)
  const exportButton = (
    <Button variant="outline" size="sm">
      <Download className="h-4 w-4 mr-2" />
      Export CSV
    </Button>
  );

  // Additional actions
  const additionalActions = (
    <div className="flex items-center space-x-2">
      <AddBdrDialog onAddBdr={handleAddBdr} />
      <LeadUpdatesDialog
        trigger={
          <Button variant="outline" size="sm">
            <Upload className="h-4 w-4 mr-2" />
            Import Updates
          </Button>
        }
      />
    </div>
  );

  return (
    <>
      <DataTable
        columns={columns}
        data={data}
        filters={filters}
        total={total}
        pageCount={pageCount}
        pageIndex={pageIndex}
        pageSize={pageSize}
        onPaginationChange={onPaginationChange}
        onGlobalFilterChange={onGlobalFilterChange}
        onFilterChange={onFilterChange}
        isLoading={isLoading}
        enableRowSelection={true}
        bulkActions={bulkActions}
        filterConfigs={filterConfigs}
        searchPlaceholder="Search leads..."
        title="Leads Management"
        exportButton={exportButton}
        additionalActions={additionalActions}
        emptyStateMessage="No leads found"
        virtualizeRows
        virtualRowEstimatedHeight={44}
      />

      <BulkPipelineDialog
        isOpen={bulkPipelineOpen}
        onClose={() => {
          setBulkPipelineOpen(false);
          setSelectedRowsForDialog([]);
        }}
        selectedLeads={selectedRowsForDialog}
      />
    </>
  );
}

'use client';

import React, { useState, useMemo, useEffect } from 'react';
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  VisibilityState,
} from '@tanstack/react-table';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Filter, Download, Upload, Trash2, Move, CheckCircle2, ChevronLeft, ChevronRight } from 'lucide-react';
import { Lead, useBdrManager } from '@/lib/hooks';
import { useBulkUpdateLeads, useBulkDeleteLeads, useBulkConvertLeads } from '@/lib/bulk-operations';
import { leadSourceEnum, leadStatusEnum } from '@/lib/validations';
import { toast } from 'sonner';
import { BulkPipelineDialog } from './bulk-pipeline-dialog';
import { LeadUpdatesDialog } from './lead-updates-dialog';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';
import { Skeleton } from '@/components/ui/skeleton';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { ConfirmDialog } from '@/components/ui/confirm-dialog';

// Custom type for column meta data
interface ColumnMeta {
  priority: 'high' | 'medium' | 'low';
}

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[];
  data: TData[];
  filters: Record<string, string>;
  total: number;
  pageCount: number;
  pageIndex: number;
  pageSize: number;
  onPaginationChange: (pageIndex: number, pageSize: number) => void;
  onGlobalFilterChange: (value: string) => void;
  onFilterChange: (columnId: string, value: string) => void;
  isLoading?: boolean;
  columnVisibility?: VisibilityState;
  onColumnVisibilityChange?: (state: VisibilityState) => void;
}

function TableSkeleton() {
  return (
    <div className="space-y-3">
      {Array.from({ length: 10 }).map((_, i) => (
        <div key={i} className="flex items-center space-x-4">
          <Skeleton className="h-4 w-4" />
          <Skeleton className="h-4 w-[200px]" />
          <Skeleton className="h-4 w-[150px]" />
          <Skeleton className="h-4 w-[100px]" />
          <Skeleton className="h-4 w-[120px]" />
        </div>
      ))}
    </div>
  );
}

export function LeadsTable<TData, TValue>({
  columns,
  data,
  filters,
  total,
  pageCount,
  pageIndex,
  pageSize,
  onPaginationChange,
  onGlobalFilterChange,
  onFilterChange,
  isLoading,
  columnVisibility,
  onColumnVisibilityChange,
}: DataTableProps<TData, TValue>) {
  const { bdrs, addBdr } = useBdrManager();
  const [rowSelection, setRowSelection] = useState({});
  const [showFilters, setShowFilters] = useState(false);
  const [bulkPipelineOpen, setBulkPipelineOpen] = useState(false);
  const bulkUpdateLeads = useBulkUpdateLeads();
  const bulkDeleteLeads = useBulkDeleteLeads();
  const bulkConvertLeads = useBulkConvertLeads();
  const [confirmOpen, setConfirmOpen] = useState(false);

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onRowSelectionChange: setRowSelection,
    onColumnVisibilityChange,
    state: {
      rowSelection,
      columnVisibility: columnVisibility || {},
    },
  });

  // Get selected rows (must be declared before effects that depend on it)
  const selectedRowIds = useMemo(() => Object.keys(rowSelection), [rowSelection]);
  const selectedRowData = useMemo(() => {
    return data.filter((_, index) => 
      selectedRowIds.includes(index.toString())
    ) as Lead[];
  }, [data, selectedRowIds]);
  const selectedRows = selectedRowData;

  // Keyboard shortcuts: / focus search, Del delete, Shift+P convert
  useEffect(() => {
    const onKey = (e: KeyboardEvent) => {
      if (e.key === '/' && !e.ctrlKey && !e.metaKey) {
        e.preventDefault();
        const el = document.getElementById('leads-search') as HTMLInputElement | null;
        el?.focus();
      }
      if ((e.key === 'Delete' || e.key === 'Backspace') && selectedRows.length > 0) {
        e.preventDefault();
        setConfirmOpen(true);
      }
      if (((e as any).key?.toLowerCase?.() === 'p' && (e.shiftKey || e.altKey)) && selectedRows.length > 0) {
        e.preventDefault();
        handleConvertAll();
      }
    };
    window.addEventListener('keydown', onKey);
    return () => window.removeEventListener('keydown', onKey);
  }, [selectedRows.length]);

  // Note: Virtualization removed to ensure consistent column layout and header alignment
  const tableContainerRef = React.useRef<HTMLDivElement | null>(null);

  function handleStatusChange(status: string) {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one lead to update');
      return;
    }
    const selectedIds = selectedRows.map(row => row.id);
    const previousStatusById = new Map<number, string>(
      selectedRows.map(r => [r.id, r.status])
    );

    bulkUpdateLeads.mutate(
      { ids: selectedIds, status: status as any },
      {
        onSuccess: () => {
          setRowSelection({});
          toast.success(
            `Updated ${selectedRows.length} leads to ${status}`,
            {
              action: {
                label: 'Undo',
                onClick: () => {
                  const rollbackIds = Array.from(previousStatusById.keys());
                  // Apply rollback to previous per-lead status is not supported by the bulk API in one call if statuses differ;
                  // best effort: revert all to the first previous status
                  const firstPrev = previousStatusById.values().next().value as string;
                  bulkUpdateLeads.mutate({ ids: rollbackIds, status: firstPrev as any });
                },
              },
              duration: 4000,
            }
          );
        },
      }
    );
  }

  const handleBulkConvert = async () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one lead to convert');
      return;
    }
    setBulkPipelineOpen(true);
  };

  const handleConvertAll = async () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one lead to convert');
      return;
    }

    const selectedIds = selectedRows.map(row => row.id);
    bulkConvertLeads.mutate(
      { leadIds: selectedIds, category: 'Pipeline', status: 'Proposal - Media' },
      {
        onSuccess: () => {
          setRowSelection({});
          toast.success(`Converted ${selectedRows.length} leads to pipeline`);
        },
      }
    );
  };

  const handleBulkDelete = () => {
    if (selectedRows.length === 0) {
      toast.error('Please select at least one lead to delete');
      return;
    }
    setConfirmOpen(true);
  };

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the BDR filter if it's currently set
      if (filters.bdr && filters.bdr !== 'all') {
        onFilterChange('bdr', newBdr);
      }
    }
    return success;
  };

  // Render skeleton conditionally, but without changing hook order above
  const maybeSkeleton = isLoading ? <TableSkeleton /> : null;

  

  return (
    <div className="space-y-4">
      {/* Loading Skeleton */}
      {maybeSkeleton}

      {/* Bulk Actions */}
      {selectedRows.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CheckCircle2 className="h-5 w-5 text-green-500" />
              {selectedRows.length} Lead{selectedRows.length !== 1 ? 's' : ''} Selected
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2">
              <Select onValueChange={handleStatusChange}>
                <SelectTrigger className="w-[200px]">
                  <SelectValue placeholder="Update Status" />
                </SelectTrigger>
                <SelectContent>
                  {leadStatusEnum.map((status) => (
                    <SelectItem key={`bulk-${status}`} value={status}>
                      {status}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <BulkPipelineDialog 
                isOpen={bulkPipelineOpen}
                onClose={() => setBulkPipelineOpen(false)}
                selectedLeads={selectedRows.map(lead => ({
                  id: lead.id,
                  bdr: lead.bdr || null
                }))}
                onConvert={handleBulkConvert}
              />

              <Tooltip>
                <TooltipTrigger asChild>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={handleConvertAll}
                    className="flex items-center gap-2"
                  >
                    <Move className="h-4 w-4" />
                    Convert All to Pipeline
                  </Button>
                </TooltipTrigger>
                <TooltipContent>
                  <p>Convert all selected leads to pipeline with default settings</p>
                </TooltipContent>
              </Tooltip>

              <Button
                variant="destructive"
                size="sm"
                onClick={handleBulkDelete}
                className="flex items-center gap-2"
              >
                <Trash2 className="h-4 w-4" />
                Delete Selected
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Confirm bulk delete dialog */}
      <ConfirmDialog
        open={confirmOpen}
        onOpenChange={setConfirmOpen}
        title="Delete selected leads?"
        description={`This will permanently remove ${selectedRows.length} lead${selectedRows.length !== 1 ? 's' : ''}. This action cannot be undone.`}
        confirmLabel="Delete"
        onConfirm={() => {
          const ids = selectedRows.map(r => r.id);
          const snapshot = [...selectedRows];
          bulkDeleteLeads.mutate(
            { ids },
            {
              onSuccess: () => {
                setConfirmOpen(false);
                setRowSelection({});
                toast.success(`Deleted ${snapshot.length} lead${snapshot.length !== 1 ? 's' : ''}`);
              },
            }
          );
        }}
      />

      {/* Filters */}
      <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center justify-between pb-16 sm:pb-0">
        <div className="flex flex-col sm:flex-row gap-4 items-start sm:items-center">
          <div className="relative">
            <Input
              id="leads-search"
              placeholder="Search leads..."
              value={filters.search || ''}
              onChange={(e) => onGlobalFilterChange(e.target.value)}
              className="w-full sm:w-[300px]"
            />
          </div>

          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
            className="flex items-center gap-2"
          >
            <Filter className="h-4 w-4" />
            Filters
          </Button>
        </div>

        {showFilters && (
          <div className="flex flex-wrap gap-4 items-center">
            <Select
              value={filters.status || 'all'}
              onValueChange={(value) => onFilterChange('status', value)}
            >
              <SelectTrigger className="w-full min-w-[160px] max-w-[180px]">
                <SelectValue placeholder="Status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Statuses</SelectItem>
                {leadStatusEnum.map((status) => (
                  <SelectItem key={`filter-${status}`} value={status}>
                    {status}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select
              value={filters.source || 'all'}
              onValueChange={(value) => onFilterChange('source', value)}
            >
              <SelectTrigger className="w-full min-w-[160px] max-w-[180px]">
                <SelectValue placeholder="Source" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Sources</SelectItem>
                {leadSourceEnum.map((source) => (
                  <SelectItem key={source} value={source}>
                    {source}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>

            <Select
              value={filters.bdr || 'all'}
              onValueChange={(value) => onFilterChange('bdr', value)}
            >
              <SelectTrigger className="w-full min-w-[160px] max-w-[180px]">
                <SelectValue placeholder="BDR" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All BDRs</SelectItem>
                {bdrs.map((bdr) => (
                  <SelectItem key={bdr} value={bdr}>
                    {bdr}
                  </SelectItem>
                ))}
                <AddBdrDialog onAddBdr={handleAddBdr} />
              </SelectContent>
            </Select>
          </div>
        )}
      </div>
      
      <div className="max-h-[70vh] overflow-auto" ref={tableContainerRef}>
          <Table className="w-full">
            <TableHeader>
              {table.getHeaderGroups().map((headerGroup) => (
                <TableRow key={headerGroup.id}>
                  {headerGroup.headers.map((header) => (
                    <TableHead
                      key={header.id}
                      className="whitespace-nowrap px-4 text-foreground/80"
                    >
                      {header.isPlaceholder
                        ? null
                        : flexRender(
                            header.column.columnDef.header,
                            header.getContext()
                          )}
                    </TableHead>
                  ))}
                </TableRow>
              ))}
            </TableHeader>
            <TableBody>
              {table.getRowModel().rows?.length ? (
                table.getRowModel().rows.map((row) => (
                  <TableRow
                    key={row.id}
                    data-state={row.getIsSelected() && "selected"}
                    className="hover:bg-white/50 dark:hover:bg-white/5 transition-colors"
                  >
                    {row.getVisibleCells().map((cell) => (
                      <TableCell key={cell.id} className="px-4">
                        {flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext()
                        )}
                      </TableCell>
                    ))}
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell
                    colSpan={columns.length}
                    className="h-24 text-center"
                  >
                    No results.
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
      </div>

      <div className="flex flex-col sm:flex-row items-center justify-between gap-4">
        <div className="text-sm text-muted-foreground">
          {selectedRows.length} of{" "}
          {table.getFilteredRowModel().rows.length} row(s) selected.
        </div>
        <div className="flex flex-wrap items-center gap-2 sm:gap-4">
          <div className="flex items-center gap-2">
            <p className="text-sm font-medium whitespace-nowrap">Rows per page</p>
            <Select
              value={`${table.getState().pagination.pageSize}`}
              onValueChange={(value) => {
                onPaginationChange(0, Number(value));
              }}
            >
              <SelectTrigger className="h-8 w-[70px]">
                <SelectValue placeholder={table.getState().pagination.pageSize} />
              </SelectTrigger>
              <SelectContent side="top">
                {[10, 20, 30, 40, 50].map((pageSize) => (
                  <SelectItem key={pageSize} value={`${pageSize}`}>
                    {pageSize}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div className="flex items-center whitespace-nowrap text-sm font-medium">
            Page {pageIndex + 1} of {pageCount}
          </div>
          <div className="flex items-center gap-1">
            <Button
              variant="outline"
              className="hidden h-8 w-8 p-0 sm:flex"
              onClick={() => onPaginationChange(0, pageSize)}
              disabled={pageIndex === 0}
            >
              <span className="sr-only">Go to first page</span>
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="outline"
              className="h-8 w-8 p-0"
              onClick={() => onPaginationChange(pageIndex - 1, pageSize)}
              disabled={pageIndex === 0}
            >
              <span className="sr-only">Go to previous page</span>
              <ChevronLeft className="h-4 w-4" />
            </Button>
            <Button
              variant="outline"
              className="h-8 w-8 p-0"
              onClick={() => onPaginationChange(pageIndex + 1, pageSize)}
              disabled={pageIndex === pageCount - 1}
            >
              <span className="sr-only">Go to next page</span>
              <ChevronRight className="h-4 w-4" />
            </Button>
            <Button
              variant="outline"
              className="hidden h-8 w-8 p-0 sm:flex"
              onClick={() => onPaginationChange(pageCount - 1, pageSize)}
              disabled={pageIndex === pageCount - 1}
            >
              <span className="sr-only">Go to last page</span>
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>

      {/* Sticky bottom action bar for mobile when rows are selected */}
      {selectedRows.length > 0 && (
        <div className="sm:hidden fixed bottom-0 left-0 right-0 z-50 border-t bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
          <div className="mx-auto max-w-screen-sm px-4 py-3">
            <div className="flex items-center justify-between gap-2">
              <div className="text-sm font-medium">
                {selectedRows.length} selected
              </div>
              <div className="flex items-center gap-2">
                <Button size="sm" variant="outline" onClick={() => setShowFilters(!showFilters)}>
                  <Filter className="h-4 w-4" />
                </Button>
                <Button size="sm" variant="outline" onClick={handleConvertAll}>
                  <Move className="h-4 w-4" />
                </Button>
                <Button size="sm" variant="destructive" onClick={handleBulkDelete}>
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Remove the duplicate BulkPipelineDialog */}
    </div>
  );
}

// Helper function to create a checkbox column
export function createSelectColumn<T>(): ColumnDef<T, any> {
  return {
    id: "select",
    header: ({ table }) => (
      <Checkbox
        checked={
          table.getIsAllPageRowsSelected() ||
          (table.getIsSomePageRowsSelected() && "indeterminate")
        }
        onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
        aria-label="Select all"
      />
    ),
    cell: ({ row }) => (
      <Checkbox
        checked={row.getIsSelected()}
        onCheckedChange={(value) => row.toggleSelected(!!value)}
        aria-label="Select row"
      />
    ),
    enableSorting: false,
    enableHiding: false,
  };
}

'use client';

import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Skeleton } from '@/components/ui/skeleton';
import { FileText, MessageSquare, Move, ChevronDown, ChevronRight, Trash2, Plus, Clock, ExternalLink, BookOpen } from 'lucide-react';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';

import { PipelineItem, useCreateActivityLog, useBatchActivityLogs, useMovePipelineItem, useMoveItemToSublist, useDeletePipelineItem, useActivityLogs } from '@/lib/hooks';
import { formatDistanceToNow } from 'date-fns';
import { pipelineCategoryEnum, pipelineStatusEnum } from '@/lib/validations';
import { PipelineDialog } from './pipeline-dialog';
import { PipelineDetail } from './pipeline-detail';
import { PipelineEditableCell } from './ui/pipeline-editable-cell';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { toast } from 'sonner';
import { 
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { UpdatesDialog } from './updates-dialog';
import { SublistDialog } from './sublist-dialog';
import { NotesPopover } from './ui/notes-popover';
import { ScrollArea } from "@/components/ui/scroll-area";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { useRouter } from 'next/navigation';

interface PipelineBoardEnhancedProps {
  items: PipelineItem[];
  isLoading?: boolean;
  selectedBdr: string;
  onRefresh?: () => void;
  simpleMode?: boolean;
}

interface GroupedSection {
  id: string;
  label: string;
  icon: string;
  color: string;
  categories: string[];
  statuses: string[];
}

export function PipelineBoardEnhanced({ items, isLoading, selectedBdr, onRefresh, simpleMode = true }: PipelineBoardEnhancedProps) {
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set(['Pipeline', 'Lists_Media_QA', 'Calls', 'Declined_Rescheduled']));
  const [expandedItems, setExpandedItems] = useState<Set<number>>(new Set());
  const [detailOpen, setDetailOpen] = useState<number | null>(null);
  const [moveOpen, setMoveOpen] = useState<number | null>(null);
  const [selectedMoveCategory, setSelectedMoveCategory] = useState<string>('');
  const [selectedMoveStatus, setSelectedMoveStatus] = useState<string>('');
  const [showActivityLog, setShowActivityLog] = useState<number | null>(null);

  const router = useRouter();
  const { mutate: movePipelineItem } = useMovePipelineItem();
  const { mutate: deletePipelineItem } = useDeletePipelineItem();
  const { mutate: createActivityLog } = useCreateActivityLog();

  // Fetch activity logs for the selected item
  const { data: activityLogsData } = useActivityLogs({
    pipelineItemId: showActivityLog || undefined,
    pageSize: 50,
  });

  // Define the grouped sections with proper labels and ordering
  const groupedSections: GroupedSection[] = [
    {
      id: 'Pipeline',
      label: 'Pipeline (Agreements & Proposals)',
      icon: 'ðŸ“‹',
      categories: ['Pipeline'],
      statuses: ['Proposal - Media', 'Proposal - Profile', 'Agreement - Media', 'Agreement - Profile', 'Partner List Pending'],
      color: 'bg-blue-50 dark:bg-blue-950/30 border-l-blue-500 dark:border-l-blue-400'
    },
    {
      id: 'Lists_Media_QA',
      label: 'Lists, Media Sales & Q&A',
      icon: 'ðŸ“Š',
      categories: ['Lists_Media_QA'],
      statuses: ['Partner List Sent', 'List Out', 'List Out - Not Sold', 'Media Sales', 'Q&A', 'Free Q&A Offered', 'Sold'],
      color: 'bg-green-50 dark:bg-green-950/30 border-l-green-500 dark:border-l-green-400'
    },
    {
      id: 'Calls',
      label: 'Calls',
      icon: 'ðŸ“ž',
      categories: ['Calls'],
      statuses: ['Call Proposed', 'Call Booked', 'Call Conducted'],
      color: 'bg-purple-50 dark:bg-purple-950/30 border-l-purple-500 dark:border-l-purple-400'
    },
    {
      id: 'Declined_Rescheduled',
      label: 'Declined & Rescheduled',
      icon: 'â¸ï¸',
      categories: ['Declined_Rescheduled'],
      statuses: ['Declined_Rescheduled', 'Rescheduled', 'Lost'],
      color: 'bg-orange-50 dark:bg-orange-950/30 border-l-orange-500 dark:border-l-orange-400'
    }
  ];
  
  // Filter items by the selected BDR
  const filteredItems = items.filter(item => item.bdr === selectedBdr);
  
  // Group and sort items by sections and statuses
  const groupedAndSortedItems: Record<string, PipelineItem[]> = {};
  
  groupedSections.forEach(section => {
    const sectionItems = filteredItems.filter(
      item => section.categories.includes(item.category)
    );
    
    // Sort by status order within section, then by lastUpdated (most recent first)
    groupedAndSortedItems[section.id] = sectionItems.sort((a, b) => {
      // First sort by status order
      const statusOrderA = section.statuses.indexOf(a.status);
      const statusOrderB = section.statuses.indexOf(b.status);
      
      if (statusOrderA !== statusOrderB) {
        return statusOrderA - statusOrderB;
      }
      
      // Then sort by lastUpdated within same status
      const dateA = new Date(a.lastUpdated || a.addedDate || 0);
      const dateB = new Date(b.lastUpdated || b.addedDate || 0);
      return dateB.getTime() - dateA.getTime();
    });
  });

  const toggleSection = (sectionId: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(sectionId)) {
      newExpanded.delete(sectionId);
    } else {
      newExpanded.add(sectionId);
    }
    setExpandedSections(newExpanded);
  };

  const toggleExpanded = (itemId: number) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);
  };

  const handleItemClick = (id: number) => {
    router.push(`/pipeline/${id}`);
  };

  const handleMoveItem = (item: PipelineItem) => {
    if (selectedMoveCategory === item.category && selectedMoveStatus === item.status) {
      setMoveOpen(null);
      return;
    }

    movePipelineItem({
      id: item.id,
      newCategory: selectedMoveCategory,
      newStatus: selectedMoveStatus
    }, {
      onSuccess: () => {
        toast.success(`Moved to ${selectedMoveStatus}`);
        setMoveOpen(null);
        onRefresh?.();
      },
      onError: () => {
        toast.error('Failed to move item');
      }
    });
  };

  function getNextStep(item: PipelineItem): { category: string; status: string; label: string } | null {
    const { category, status } = item;
    if (category === 'Calls') {
      if (status === 'Call Proposed') return { category, status: 'Call Booked', label: 'Book call' };
      if (status === 'Call Booked') return { category, status: 'Call Conducted', label: 'Conducted' };
    }
    if (category === 'Pipeline') {
      if (status === 'Proposal - Profile') return { category, status: 'Agreement - Profile', label: 'Send agreement (Profile)' };
      if (status === 'Proposal - Media') return { category, status: 'Agreement - Media', label: 'Send agreement (Media)' };
      if (status === 'Partner List Pending') return { category: 'Lists_Media_QA', status: 'Partner List Sent', label: 'Send partner list' };
    }
    if (category === 'Lists_Media_QA') {
      if (status === 'Partner List Sent') return { category, status: 'List Out', label: 'Mark list out' };
      if (status === 'List Out') return { category, status: 'Media Sales', label: 'Move to media sales' };
      if (status === 'Media Sales') return { category, status: 'Sold', label: 'Mark sold' };
    }
    return null;
  }

  function handleQuickMove(item: PipelineItem) {
    const next = getNextStep(item);
    if (!next) return;
    movePipelineItem({ id: item.id, newCategory: next.category, newStatus: next.status }, {
      onSuccess: () => {
        toast.success(`Moved to ${next.status}`);
        onRefresh?.();
      },
      onError: () => toast.error('Failed to move item'),
    });
  }

  function handleQuickLogCall(item: PipelineItem) {
    createActivityLog({
      bdr: item.bdr,
      activityType: 'Call_Completed',
      description: 'Call completed',
      pipelineItemId: item.id,
    }, {
      onSuccess: () => {
        toast.success('Logged call');
        onRefresh?.();
      },
      onError: () => toast.error('Failed to log call'),
    });
  }

  async function handlePushToEditorial(item: PipelineItem) {
    try {
      const response = await fetch('/api/editorial/sync', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ pipelineItemId: item.id }),
      });

      const result = await response.json();

      if (response.ok) {
        toast.success(`"${item.name}" pushed to editorial board`);
        onRefresh?.();
      } else if (response.status === 409) {
        toast.warning(`"${item.name}" is already in editorial board`);
      } else {
        throw new Error(result.error || 'Failed to push to editorial board');
      }
    } catch (error) {
      console.error('Error pushing to editorial board:', error);
      toast.error('Failed to push to editorial board');
    }
  }

  const handleCategoryChange = (category: string) => {
    setSelectedMoveCategory(category);
    const statuses = pipelineStatusEnum[category as keyof typeof pipelineStatusEnum];
    if (statuses && statuses.length > 0) {
      setSelectedMoveStatus(statuses[0]);
    }
  };

  const openMoveDialog = (item: PipelineItem) => {
    setSelectedMoveCategory(item.category);
    setSelectedMoveStatus(item.status);
    setMoveOpen(item.id);
  };

  const handleDeleteItem = (e: React.MouseEvent, item: PipelineItem) => {
    e.stopPropagation();
    if (confirm('Are you sure you want to delete this pipeline item? This action cannot be undone.')) {
      deletePipelineItem(item.id, {
        onSuccess: () => {
          toast.success('Pipeline item deleted successfully');
          onRefresh?.();
        },
        onError: () => {
          toast.error('Failed to delete pipeline item');
        }
      });
    }
  };

  // Recursive function to render items and their children
  const renderItemsRecursively = (itemList: PipelineItem[], level: number = 0): React.ReactElement[] => {
    const elements: React.ReactElement[] = [];
    
    itemList.forEach((item) => {
      const hasChildren = item.children && item.children.length > 0;
      const isExpanded = expandedItems.has(item.id);
      
      elements.push(
        <TableRow
          key={item.id}
          className="cursor-pointer hover:bg-muted/50 h-12 border-l-2"
          style={{
            borderLeftColor: 
              item.status === 'Call Booked' ? '#16a34a' :
              item.status === 'Call Proposed' ? '#f59e0b' :
              item.status === 'Sold' ? '#059669' :
              item.status.includes('Agreement') ? '#3b82f6' :
              item.status.includes('Proposal') ? '#8b5cf6' :
              item.status.includes('Declined') ? '#ef4444' :
              item.status.includes('Rescheduled') ? '#f97316' :
              '#6b7280'
          }}
          onClick={() => handleItemClick(item.id)}
        >
          <TableCell className="py-2">
            <div className="flex items-center gap-2" style={{ paddingLeft: `${level * 20}px` }}>
              <div onClick={(e) => e.stopPropagation()}>
                {hasChildren ? (
                  <div className="flex items-center gap-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-4 w-4 p-0 hover:bg-muted"
                      onClick={() => toggleExpanded(item.id)}
                    >
                      {isExpanded ? (
                        <ChevronDown className="h-3 w-3" />
                      ) : (
                        <ChevronRight className="h-3 w-3" />
                      )}
                    </Button>
                  </div>
                ) : (
                  <SublistDialog
                    title="Add Sublist Item"
                    buttonLabel="+"
                    buttonVariant="ghost"
                    buttonSize="sm"
                    buttonIcon={true}
                    defaultCategory={item.category}
                    defaultStatus={item.status}
                    defaultBdr={item.bdr}
                    parentId={item.id}
                    onSuccess={onRefresh}
                    className="h-4 w-4 p-0 hover:bg-muted"
                  />
                )}
              </div>
              <PipelineEditableCell
                value={item.name}
                item={item}
                field="name"
                placeholder="Name"
              />
              {hasChildren && (
                <div className="flex items-center gap-1 ml-2">
                  <div className="w-1 h-1 rounded-full bg-primary"></div>
                  <span className="text-xs text-muted-foreground">
                    {item.children?.length || 0} subitem{item.children && item.children.length !== 1 ? 's' : ''}
                  </span>
                </div>
              )}
            </div>
          </TableCell>
          <TableCell className="py-2">
            <PipelineEditableCell
              value={item.company}
              item={item}
              field="company"
              placeholder="Company"
            />
          </TableCell>
          {!simpleMode && (
          <TableCell className="py-2">
            <PipelineEditableCell
              value={item.title}
              item={item}
              field="title"
              placeholder="Title"
            />
          </TableCell>
          )}
          <TableCell className="py-2">
            <div className="flex items-center gap-2">
              <div className={`px-2 py-1 rounded-full text-xs font-medium ${
                item.status === 'Call Booked' ? 'bg-green-100 text-green-800' :
                item.status === 'Call Proposed' ? 'bg-yellow-100 text-yellow-800' :
                item.status === 'Sold' ? 'bg-emerald-100 text-emerald-800' :
                item.status.includes('Agreement') ? 'bg-blue-100 text-blue-800' :
                item.status.includes('Proposal') ? 'bg-purple-100 text-purple-800' :
                item.status.includes('Declined') ? 'bg-red-100 text-red-800' :
                item.status.includes('Rescheduled') ? 'bg-orange-100 text-orange-800' :
                'bg-muted text-muted-foreground'
              }`}>
                {item.status}
              </div>
              {simpleMode && getNextStep(item) && (
                <div className="inline-flex items-center gap-2">
                  <span className="text-xs text-muted-foreground">Next:</span>
                  <span className="text-xs font-medium">
                    {getNextStep(item)!.status}
                  </span>
                </div>
              )}
            </div>
          </TableCell>
          <TableCell className="py-2">
            <PipelineEditableCell
              value={item.callDate || ''}
              item={item}
              field="callDate"
              placeholder="Set call date"
              isDate={true}
            />
          </TableCell>
          {!simpleMode && (
          <TableCell className="py-2" onClick={(e) => e.stopPropagation()}>
            <Popover open={showActivityLog === item.id} onOpenChange={(open) => !open && setShowActivityLog(null)}>
              <PopoverTrigger asChild>
                <div 
                  className="cursor-pointer hover:bg-muted/50 p-1 rounded"
                  onClick={() => setShowActivityLog(item.id)}
                >
                  {item.latestActivityLog ? (
                    <div className="flex flex-col gap-0.5">
                      <span className="text-xs font-medium">
                        {item.latestActivityLog.activityType === 'BDR_Update' ? 'Update' : 
                         item.latestActivityLog.activityType.replace('_', ' ')}
                      </span>
                      <span className="text-xs text-muted-foreground">
                        {item.latestActivityLog.notes ? 
                          item.latestActivityLog.notes.length > 30 ? 
                            `${item.latestActivityLog.notes.substring(0, 30)}...` : 
                            item.latestActivityLog.notes
                          : item.latestActivityLog.description
                        }
                      </span>
                      <span className="text-xs text-muted-foreground">
                        by {item.latestActivityLog.bdr} {formatDistanceToNow(new Date(item.latestActivityLog.timestamp), { addSuffix: true })}
                      </span>
                    </div>
                  ) : (
                    <span className="text-xs text-muted-foreground">No updates</span>
                  )}
                </div>
              </PopoverTrigger>
              <PopoverContent className="w-96" side="right">
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <h4 className="font-medium flex items-center gap-2">
                      <Clock className="h-4 w-4" />
                      Activity History
                    </h4>
                  </div>
                  <ScrollArea className="h-[300px]">
                    <div className="space-y-4">
                      {activityLogsData?.logs.map((log) => (
                        <div key={log.id} className="space-y-1">
                          <div className="flex items-center justify-between">
                            <span className="text-sm font-medium">
                              {log.activityType === 'BDR_Update' ? 'Update' : 
                               log.activityType.replace('_', ' ')}
                            </span>
                            <span className="text-xs text-muted-foreground">
                              {formatDistanceToNow(new Date(log.timestamp), { addSuffix: true })}
                            </span>
                          </div>
                          <p className="text-sm text-muted-foreground">
                            {log.notes || log.description}
                          </p>
                          <p className="text-xs text-muted-foreground">
                            by {log.bdr}
                          </p>
                          {(log.previousStatus !== log.newStatus || log.previousCategory !== log.newCategory) && (
                            <p className="text-xs text-muted-foreground mt-1">
                              {log.previousCategory && log.newCategory && (
                                <>Moved from {log.previousCategory} to {log.newCategory}<br /></>
                              )}
                              {log.previousStatus && log.newStatus && (
                                <>Status changed from {log.previousStatus} to {log.newStatus}</>
                              )}
                            </p>
                          )}
                        </div>
                      ))}
                    </div>
                  </ScrollArea>
                </div>
              </PopoverContent>
            </Popover>
          </TableCell>
          )}
          {!simpleMode && (
          <TableCell className="py-2">
            {item.email && (
              <a 
                href={`mailto:${item.email}`}
                onClick={(e) => e.stopPropagation()}
                className="text-blue-500 hover:text-blue-700 transition-colors text-xs"
              >
                {item.email}
              </a>
            )}
          </TableCell>
          )}
          {!simpleMode && (
          <TableCell className="py-2">
            {item.phone && (
              <a 
                href={`tel:${item.phone}`}
                onClick={(e) => e.stopPropagation()}
                className="text-blue-500 hover:text-blue-700 transition-colors text-xs"
              >
                {item.phone}
              </a>
            )}
          </TableCell>
          )}
          <TableCell className="py-2">
            <PipelineEditableCell
              value={item.notes}
              item={item}
              field="notes"
              isTextarea={true}
              placeholder="Add notes..."
            />
          </TableCell>
          <TableCell className="py-2">
            <div className="flex items-center space-x-1" onClick={e => e.stopPropagation()}>
              <UpdatesDialog 
                pipelineItem={item}
                trigger={
                  <Button 
                    variant="outline" 
                    size="sm" 
                    className="h-6 px-2 text-xs"
                  >
                    <MessageSquare className="h-3 w-3 mr-1" />
                    Updates
                  </Button>
                }
              />
              
              {!simpleMode && (
              <Popover open={moveOpen === item.id} onOpenChange={(open) => !open && setMoveOpen(null)}>
                <PopoverTrigger asChild>
                  <Button 
                    variant="outline" 
                    size="sm" 
                    className="h-6 px-2 text-xs"
                    onClick={(e) => {
                      e.stopPropagation();
                      openMoveDialog(item);
                    }}
                  >
                    <Move className="h-3 w-3 mr-1" />
                    Move
                  </Button>
                </PopoverTrigger>
                <PopoverContent className="w-80 p-4" side="top" align="end">
                  <div className="space-y-4">
                    <h4 className="font-medium">Move to different group</h4>
                    
                    <div className="space-y-3">
                      <div>
                        <label className="text-sm font-medium">Category</label>
                        <Select
                          value={selectedMoveCategory}
                          onValueChange={handleCategoryChange}
                        >
                          <SelectTrigger className="mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {pipelineCategoryEnum.map((category) => (
                              <SelectItem key={category} value={category}>
                                {category.replace('_', ' ')}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                      
                      <div>
                        <label className="text-sm font-medium">Status</label>
                        <Select
                          value={selectedMoveStatus}
                          onValueChange={(value) => setSelectedMoveStatus(value)}
                        >
                          <SelectTrigger className="mt-1">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {selectedMoveCategory && pipelineStatusEnum[selectedMoveCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                              <SelectItem key={`${selectedMoveCategory}-${status}`} value={status}>
                                {status}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    </div>
                    
                    <div className="text-xs text-muted-foreground bg-muted/20 p-2 rounded">
                      Moving from <span className="font-medium">{item.category}</span> ({item.status}) to <span className="font-medium">{selectedMoveCategory}</span> ({selectedMoveStatus})
                    </div>
                    
                    <div className="flex justify-end gap-2">
                      <Button 
                        variant="outline" 
                        size="sm"
                        onClick={() => setMoveOpen(null)}
                      >
                        Cancel
                      </Button>
                      <Button 
                        size="sm"
                        onClick={() => handleMoveItem(item)}
                        disabled={selectedMoveCategory === item.category && selectedMoveStatus === item.status}
                      >
                        Move
                      </Button>
                    </div>
                  </div>
                </PopoverContent>
              </Popover>
              )}
              
              {!simpleMode && item.notes && <NotesPopover notes={item.notes} />}
              {!simpleMode && item.link && (
                <a 
                  href={item.link} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="text-blue-500 hover:text-blue-700 transition-colors"
                >
                  <ExternalLink className="h-3 w-3" />
                </a>
              )}
              {simpleMode && getNextStep(item) && (
                <Button size="sm" className="h-6 px-2 text-xs" onClick={() => handleQuickMove(item)}>
                  Move: {getNextStep(item)!.status}
                </Button>
              )}
              {simpleMode && (
                <Button variant="outline" size="sm" className="h-6 px-2 text-xs" onClick={() => handleQuickLogCall(item)}>
                  Log Call
                </Button>
              )}
              <Button 
                variant="outline" 
                size="sm" 
                className="h-6 px-2 text-xs bg-purple-50 hover:bg-purple-100 text-purple-700 border-purple-200" 
                onClick={() => handlePushToEditorial(item)}
                title="Push to Editorial Board"
              >
                <BookOpen className="h-3 w-3 mr-1" />
                Editorial
              </Button>
              {!simpleMode && (
                <Button
                  variant="outline"
                  size="sm"
                  className="h-6 px-2 text-destructive hover:text-destructive hover:bg-destructive/10"
                  onClick={(e) => handleDeleteItem(e, item)}
                >
                  <Trash2 className="h-3 w-3" />
                </Button>
              )}
            </div>
          </TableCell>
        </TableRow>
      );

      // Add children if expanded
      if (hasChildren && isExpanded && item.children) {
        elements.push(...renderItemsRecursively(item.children, level + 1));
        
        // Add "Add subitem" row after the children
        elements.push(
          <TableRow key={`add-subitem-${item.id}`} className="hover:bg-muted/50 h-10">
            <TableCell className="py-1">
              <div className="flex items-center gap-2" style={{ paddingLeft: `${(level + 1) * 20}px` }}>
                <div className="w-2 h-2 rounded-full bg-muted-foreground/30"></div>
                <SublistDialog
                  title="Add More Sublist Items"
                  buttonLabel="+ Add subitem"
                  buttonVariant="ghost"
                  buttonSize="default"
                  buttonIcon={false}
                  defaultCategory={item.category}
                  defaultStatus={item.status}
                  defaultBdr={item.bdr}
                  parentId={item.id}
                  onSuccess={onRefresh}
                  className="h-5 px-2 text-xs hover:bg-muted text-muted-foreground hover:text-foreground border border-dashed border-muted-foreground/30 hover:border-muted-foreground/50 rounded"
                />
              </div>
            </TableCell>
            <TableCell className="py-1"></TableCell>
            {!simpleMode && <TableCell className="py-1"></TableCell>}
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            {!simpleMode && <TableCell className="py-1"></TableCell>}
            {!simpleMode && <TableCell className="py-1"></TableCell>}
            {!simpleMode && <TableCell className="py-1"></TableCell>}
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
          </TableRow>
        );
      }
    });

    return elements;
  };

  const renderSkeletonRows = (count: number) => {
    return Array(count)
      .fill(0)
      .map((_, i) => (
        <TableRow key={i}>
          <TableCell><Skeleton className="h-4 w-32" /></TableCell>
          <TableCell><Skeleton className="h-4 w-24" /></TableCell>
          {!simpleMode && <TableCell><Skeleton className="h-4 w-20" /></TableCell>}
          <TableCell><Skeleton className="h-4 w-20" /></TableCell>
          <TableCell><Skeleton className="h-4 w-16" /></TableCell>
          {!simpleMode && <TableCell><Skeleton className="h-4 w-32" /></TableCell>}
          {!simpleMode && <TableCell><Skeleton className="h-4 w-24" /></TableCell>}
          {!simpleMode && <TableCell><Skeleton className="h-4 w-20" /></TableCell>}
          <TableCell><Skeleton className="h-4 w-32" /></TableCell>
          <TableCell><Skeleton className="h-4 w-24" /></TableCell>
        </TableRow>
      ));
  };
  
  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="sticky top-4 z-10 border-b pb-4 bg-white/40 dark:bg-white/[0.03] backdrop-blur-xl supports-[backdrop-filter]:bg-white/30">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold">Enhanced Pipeline Board</h1>
            <p className="text-sm text-muted-foreground">
              Managing pipeline for {selectedBdr} â€¢ Grouped by workflow stages
            </p>
          </div>
          <PipelineDialog
            title="Add New Lead"
            buttonLabel="Add New Lead"
            buttonVariant="default"
            buttonIcon={true}
            defaultCategory="Pipeline"
            defaultStatus="Proposal - Profile"
            defaultBdr={selectedBdr}
            onSuccess={onRefresh}
            className="shadow-[0_15px_40px_-20px_rgba(0,0,0,0.35)]"
          />
        </div>
      </div>

      {/* Grouped Sections */}
      {groupedSections.map((section) => {
        const sectionItems = groupedAndSortedItems[section.id] || [];
        const isExpanded = expandedSections.has(section.id);
        
        return (
          <div key={section.id} className="space-y-0">
            {/* Section Header */}
            <div 
              className={`p-4 rounded-t-lg cursor-pointer hover:bg-opacity-80 transition-colors border-l-4 ${section.color}`}
              onClick={() => toggleSection(section.id)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                  <Button
                    variant="ghost"
                    size="sm"
                    className="h-6 w-6 p-0"
                  >
                    {isExpanded ? (
                      <ChevronDown className="h-4 w-4" />
                    ) : (
                      <ChevronRight className="h-4 w-4" />
                    )}
                  </Button>
                  <span className="text-xl">{section.icon}</span>
                  <div>
                    <h3 className="text-lg font-semibold text-foreground">
                      {section.label}
                    </h3>
                    <p className="text-sm text-muted-foreground">
                      {sectionItems.length} items
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-2" onClick={e => e.stopPropagation()}>
                  <PipelineDialog
                    title={`Add ${section.label} Item`}
                    buttonLabel="Add Item"
                    buttonVariant="outline"
                    buttonIcon={true}
                    defaultCategory={section.categories[0]}
                    defaultStatus={section.statuses[0]}
                    defaultBdr={selectedBdr}
                    onSuccess={onRefresh}
                  />
                </div>
              </div>
            </div>

            {/* Section Table */}
            {isExpanded && (
              <div className="rounded-b-xl border border-t-0 overflow-hidden bg-white/40 dark:bg-white/[0.03] backdrop-blur">
                <Table>
                  <TableHeader>
                    <TableRow className="h-10 bg-white/60 dark:bg-white/[0.06]">
                      <TableHead className="w-[200px]">Name</TableHead>
                      <TableHead className="w-[150px]">Company</TableHead>
                      {!simpleMode && <TableHead className="w-[150px]">Title</TableHead>}
                      <TableHead className="w-[120px]">Status</TableHead>
                      <TableHead className="w-[100px]">Call Date</TableHead>
                      {!simpleMode && <TableHead className="w-[150px]">Last Update</TableHead>}
                      {!simpleMode && <TableHead className="w-[120px]">Email</TableHead>}
                      {!simpleMode && <TableHead className="w-[120px]">Number</TableHead>}
                      <TableHead className="w-[200px]">Notes</TableHead>
                      <TableHead className="w-[160px]">Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {isLoading ? (
                      renderSkeletonRows(3)
                    ) : sectionItems.length > 0 ? (
                      renderItemsRecursively(sectionItems)
                    ) : (
                      <TableRow>
                        <TableCell colSpan={simpleMode ? 6 : 10} className="text-center py-8 text-muted-foreground">
                          <div className="flex flex-col items-center gap-2">
                            <span>No items in this section</span>
                            <PipelineDialog
                              title={`Add ${section.label} Item`}
                              buttonLabel="Add First Item"
                              buttonVariant="outline"
                              buttonSize="sm"
                              buttonIcon={false}
                              defaultCategory={section.categories[0]}
                              defaultStatus={section.statuses[0]}
                              defaultBdr={selectedBdr}
                              onSuccess={onRefresh}
                              className="text-xs"
                            />
                          </div>
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </div>
            )}
          </div>
        );
      })}
      
      {/* Pipeline Item Detail Dialog */}
      <PipelineDetail 
        pipelineItemId={detailOpen || 0} 
        open={!!detailOpen} 
        onOpenChange={(open) => {
          setDetailOpen(open ? detailOpen : null);
        }}
      />
      
      {/* Floating Action Button for Quick Add */}
      <div className="fixed bottom-6 right-6 z-50">
        <PipelineDialog
          title="Add New Lead"
          buttonLabel=""
          buttonVariant="default"
          buttonSize="icon"
          buttonIcon={true}
          defaultCategory="Pipeline"
          defaultStatus="Proposal - Profile"
          defaultBdr={selectedBdr}
          onSuccess={onRefresh}
          className="h-14 w-14 rounded-full shadow-lg hover:shadow-xl transition-shadow"
        />
      </div>
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { PipelineItem, usePipelineItem, useUpdatePipelineItem, useMovePipelineItem, useCreateActivityLog, useActivityLogs, useDeletePipelineItem } from '@/lib/hooks';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { formatDateUK, formatDateForInput } from '@/lib/date-utils';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { pipelineCategoryEnum, pipelineStatusEnum } from '@/lib/validations';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { toast } from 'sonner';
import { Trash2 } from 'lucide-react';
import { useRouter } from 'next/navigation';

interface PipelineDetailProps {
  pipelineItemId: number;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function PipelineDetail({ pipelineItemId, open, onOpenChange }: PipelineDetailProps) {
  const { data: pipelineItem, isLoading } = usePipelineItem(pipelineItemId);
  const { mutate: updatePipelineItem } = useUpdatePipelineItem();
  const { mutate: movePipelineItem } = useMovePipelineItem();
  const { mutate: createActivityLog } = useCreateActivityLog();
  const { mutate: deletePipelineItem } = useDeletePipelineItem();
  const { data: activityData } = useActivityLogs({ 
    pipelineItemId, 
    page: 1, 
    pageSize: 10 
  }, {
    enabled: open, // Only fetch when dialog is open
    queryKey: ['activityLogs', { pipelineItemId, page: 1, pageSize: 10 }]
  });
  const router = useRouter();
  
  const [editMode, setEditMode] = useState(false);
  const [formData, setFormData] = useState<Partial<PipelineItem>>({});
  const [selectedCategory, setSelectedCategory] = useState<string>('');
  const [selectedStatus, setSelectedStatus] = useState<string>('');

  // Initialize form data when pipeline item data is loaded
  React.useEffect(() => {
    if (pipelineItem) {
      setFormData({
        name: pipelineItem.name,
        title: pipelineItem.title,
        company: pipelineItem.company,
        bdr: pipelineItem.bdr,
        notes: pipelineItem.notes,
        email: pipelineItem.email,
        phone: pipelineItem.phone,
        link: pipelineItem.link,
        expectedCloseDate: pipelineItem.expectedCloseDate 
          ? formatDateForInput(pipelineItem.expectedCloseDate)
          : undefined,
      });
      
      setSelectedCategory(pipelineItem.category);
      setSelectedStatus(pipelineItem.status);
    }
  }, [pipelineItem]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSelectChange = (name: string, value: string) => {
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = () => {
    if (!pipelineItem) return;

    const updatedItem = {
      ...pipelineItem,
      ...formData
    };
    
    updatePipelineItem(updatedItem, {
      onSuccess: () => {
        setEditMode(false);
        
        // Log the activity
        createActivityLog({
          bdr: pipelineItem.bdr,
          activityType: 'Note_Added',
          description: 'Updated pipeline item details',
          pipelineItemId: pipelineItem.id,
          notes: 'Details updated'
        });
      }
    });
  };

  const handleMoveItem = () => {
    if (!pipelineItem || selectedCategory === pipelineItem.category && selectedStatus === pipelineItem.status) return;
    
    movePipelineItem({
      id: pipelineItem.id,
      newCategory: selectedCategory,
      newStatus: selectedStatus
    }, {
      onSuccess: () => {
        toast.success(`Item moved to ${selectedStatus}`);
        onOpenChange(false);
      }
    });
  };

  const handleDeleteItem = () => {
    if (!pipelineItem) return;
    
    if (confirm('Are you sure you want to delete this pipeline item? This action cannot be undone.')) {
      deletePipelineItem(pipelineItem.id, {
        onSuccess: () => {
          toast.success('Pipeline item deleted successfully');
          onOpenChange(false);
          // Refresh the page to update the pipeline view
          router.refresh();
        },
        onError: () => {
          toast.error('Failed to delete pipeline item');
        }
      });
    }
  };

  // Handle category change and update available statuses
  const handleCategoryChange = (category: string) => {
    setSelectedCategory(category);
    // Set the status to the first status in the new category
    const statuses = pipelineStatusEnum[category as keyof typeof pipelineStatusEnum];
    if (statuses && statuses.length > 0) {
      setSelectedStatus(statuses[0]);
    }
  };

  if (isLoading) {
    return (
      <Dialog open={open} onOpenChange={onOpenChange}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Loading...</DialogTitle>
          </DialogHeader>
          <div className="h-40 flex items-center justify-center">
            Loading pipeline item details...
          </div>
        </DialogContent>
      </Dialog>
    );
  }

  if (!pipelineItem) {
    return (
      <Dialog open={open} onOpenChange={onOpenChange}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Error</DialogTitle>
          </DialogHeader>
          <div>Pipeline item not found or error loading data.</div>
        </DialogContent>
      </Dialog>
    );
  }

  const activityLogs = activityData?.logs || [];

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h=[90vh] overflow-y-auto bg-white/70 dark:bg-white/[0.06] backdrop-blur-xl border border-white/20 dark:border-white/10">
        <DialogHeader>
          <DialogTitle className="text-xl flex items-center justify-between">
            <span>{editMode ? 'Edit ' : ''}{pipelineItem.name}</span>
            <div className="flex space-x-2">
              {editMode ? (
                <>
                  <Button variant="outline" size="sm" onClick={() => setEditMode(false)}>
                    Cancel
                  </Button>
                  <Button size="sm" onClick={handleSave}>
                    Save Changes
                  </Button>
                </>
              ) : (
                <>
                  <Button size="sm" onClick={() => setEditMode(true)}>
                    Edit
                  </Button>
                  <Button 
                    variant="destructive" 
                    size="sm" 
                    onClick={handleDeleteItem}
                    className="flex items-center gap-1"
                  >
                    <Trash2 className="h-4 w-4" />
                    Delete
                  </Button>
                </>
              )}
            </div>
          </DialogTitle>
        </DialogHeader>

        <Tabs defaultValue="details">
          <TabsList>
            <TabsTrigger value="details">Details</TabsTrigger>
            <TabsTrigger value="move">Move</TabsTrigger>
            <TabsTrigger value="activity">Activity Log ({activityLogs.length})</TabsTrigger>
          </TabsList>
          
          <TabsContent value="details" className="space-y-4 py-4">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="name">Name</Label>
                {editMode ? (
                  <Input 
                    id="name" 
                    name="name" 
                    value={formData.name || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">{pipelineItem.name}</div>
                )}
              </div>
              
              <div>
                <Label htmlFor="title">Title</Label>
                {editMode ? (
                  <Input 
                    id="title" 
                    name="title" 
                    value={formData.title || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">{pipelineItem.title || 'N/A'}</div>
                )}
              </div>
              
              <div>
                <Label htmlFor="company">Company</Label>
                {editMode ? (
                  <Input 
                    id="company" 
                    name="company" 
                    value={formData.company || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">{pipelineItem.company || 'N/A'}</div>
                )}
              </div>
              
              <div>
                <Label htmlFor="bdr">BDR</Label>
                <div className="p-2 border rounded-md">{pipelineItem.bdr}</div>
              </div>
              
              <div>
                <Label htmlFor="category">Category</Label>
                <div className="p-2 border rounded-md">{pipelineItem.category.replace('_', ' ')}</div>
              </div>
              
              <div>
                <Label htmlFor="status">Status</Label>
                <div className="p-2 border rounded-md">{pipelineItem.status}</div>
              </div>
              
              <div>
                <Label htmlFor="expectedCloseDate">Expected Close Date</Label>
                {editMode ? (
                  <Input 
                    id="expectedCloseDate" 
                    name="expectedCloseDate" 
                    type="date" 
                    value={formData.expectedCloseDate?.toString() || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">
                    {pipelineItem.expectedCloseDate 
                      ? formatDateUK(pipelineItem.expectedCloseDate)
                      : 'N/A'}
                  </div>
                )}
              </div>
              
              <div>
                <Label htmlFor="phone">Phone</Label>
                {editMode ? (
                  <Input 
                    id="phone" 
                    name="phone" 
                    value={formData.phone || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">{pipelineItem.phone || 'N/A'}</div>
                )}
              </div>
              
              <div>
                <Label htmlFor="email">Email</Label>
                {editMode ? (
                  <Input 
                    id="email" 
                    name="email" 
                    type="email"
                    value={formData.email || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">{pipelineItem.email || 'N/A'}</div>
                )}
              </div>
              
              <div className="sm:col-span-2">
                <Label htmlFor="link">Link</Label>
                {editMode ? (
                  <Input 
                    id="link" 
                    name="link" 
                    value={formData.link || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md">
                    {pipelineItem.link ? (
                      <a href={pipelineItem.link} target="_blank" rel="noopener noreferrer" className="text-blue-600 hover:underline">
                        {pipelineItem.link}
                      </a>
                    ) : 'N/A'}
                  </div>
                )}
              </div>
              
              <div className="sm:col-span-2">
                <Label htmlFor="notes">Notes</Label>
                {editMode ? (
                  <Textarea 
                    id="notes" 
                    name="notes" 
                    rows={5}
                    value={formData.notes || ''} 
                    onChange={handleInputChange} 
                  />
                ) : (
                  <div className="p-2 border rounded-md min-h-[100px] whitespace-pre-wrap">
                    {pipelineItem.notes || 'No notes available'}
                  </div>
                )}
              </div>
              
              <div className="sm:col-span-2">
                <Label>Added Date</Label>
                <div className="p-2 border rounded-md">
                  {formatDateUK(pipelineItem.addedDate)}
                </div>
              </div>
            </div>
          </TabsContent>
          
          <TabsContent value="move" className="space-y-4 py-4">
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              <div>
                <Label htmlFor="moveCategory">Select Category</Label>
                <Select
                  value={selectedCategory}
                  onValueChange={handleCategoryChange}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select Category" />
                  </SelectTrigger>
                  <SelectContent>
                    {pipelineCategoryEnum.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category.replace('_', ' ')}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              
              <div>
                <Label htmlFor="moveStatus">Select Status</Label>
                <Select
                  value={selectedStatus}
                  onValueChange={(value) => setSelectedStatus(value)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select Status" />
                  </SelectTrigger>
                  <SelectContent>
                    {selectedCategory && pipelineStatusEnum[selectedCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                      <SelectItem key={`${selectedCategory}-${status}`} value={status}>
                        {status}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
            
            <div className="mt-4 border rounded-md p-4 bg-muted/20">
              <p className="text-sm mb-2">
                Moving this item will:
              </p>
              <ul className="list-disc list-inside text-sm space-y-1 text-muted-foreground">
                <li>Change its category from <span className="font-medium">{pipelineItem.category}</span> to <span className="font-medium">{selectedCategory}</span></li>
                <li>Change its status from <span className="font-medium">{pipelineItem.status}</span> to <span className="font-medium">{selectedStatus}</span></li>
                <li>Create an activity log entry</li>
                <li>Maintain all other item details</li>
              </ul>
            </div>
            
            <div className="flex justify-end mt-4">
              <Button
                onClick={handleMoveItem}
                disabled={selectedCategory === pipelineItem.category && selectedStatus === pipelineItem.status}
              >
                Move Item
              </Button>
            </div>
          </TabsContent>
          
          <TabsContent value="activity" className="py-4">
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Activity History</h3>
              
              {activityLogs.length === 0 ? (
                <p className="text-muted-foreground text-sm">No activity logs found for this item.</p>
              ) : (
                <div className="border rounded-md divide-y">
                  {activityLogs.map((log) => (
                    <div key={log.id} className="p-3">
                      <div className="flex justify-between items-start">
                        <div>
                          <p className="font-medium">{log.activityType.replace('_', ' ')}</p>
                          <p className="text-sm text-muted-foreground">{log.description}</p>
                        </div>
                        <div className="text-sm text-right">
                          <p>{formatDateUK(log.timestamp)}</p>
                          <p className="text-muted-foreground">{new Date(log.timestamp).toLocaleString('en-GB', { hour: '2-digit', minute: '2-digit' })}</p>
                        </div>
                      </div>
                      {log.notes && (
                        <p className="mt-2 text-sm bg-muted/20 p-2 rounded">{log.notes}</p>
                      )}
                      {(log.previousCategory && log.newCategory) && (
                        <p className="mt-1 text-xs text-muted-foreground">
                          Moved from {log.previousCategory} to {log.newCategory}
                        </p>
                      )}
                      {log.bdr && (
                        <p className="mt-1 text-xs text-muted-foreground">
                          By: {log.bdr}
                        </p>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { Button } from '@/components/ui/button';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { ConfirmDialog } from '@/components/ui/confirm-dialog';
import { PipelineItem, useBdrManager } from '@/lib/hooks';
import { 
  pipelineSchema, 
  pipelineCategoryEnum, 
  pipelineStatusEnum 
} from '@/lib/validations';
import { useRouter } from 'next/navigation';
import { AddBdrDialog } from '@/components/ui/add-bdr-dialog';
import { Separator } from '@/components/ui/separator';
import { toast } from 'sonner';

interface PipelineFormProps {
  initialData?: PipelineItem;
  onSuccess?: () => void;
  onCancel?: () => void;
  defaultCategory?: string;
  defaultStatus?: string;
  defaultBdr?: string;
}

type FormValues = z.infer<typeof pipelineSchema>;

export function PipelineForm({
  initialData,
  onSuccess,
  onCancel,
  defaultCategory,
  defaultStatus,
  defaultBdr,
}: PipelineFormProps) {
  const router = useRouter();
  const { bdrs, addBdr } = useBdrManager();
  const [selectedCategory, setSelectedCategory] = useState<string | null>(
    initialData?.category || defaultCategory || null
  );
  const [duplicateInfo, setDuplicateInfo] = useState<{
    error: string;
    details: string;
    duplicateId: number;
    duplicateBdr: string;
    formValues: FormValues;
  } | null>(null);
  const [showDuplicateDialog, setShowDuplicateDialog] = useState(false);

  const form = useForm<FormValues>({
    resolver: zodResolver(pipelineSchema),
    defaultValues: {
      name: initialData?.name || '',
      company: initialData?.company || '',
      bdr: initialData?.bdr || defaultBdr || '',
      category: initialData?.category || defaultCategory || 'Pipeline',
      status: initialData?.status || defaultStatus || 'Proposal - Media',
      value: initialData?.value || 0,
      notes: initialData?.notes || '',
      link: initialData?.link || '',
      phone: initialData?.phone || '',
      email: initialData?.email || '',
    },
  });

  // Watch category changes to update available statuses
  const watchedCategory = form.watch('category');
  useEffect(() => {
    setSelectedCategory(watchedCategory);
  }, [watchedCategory]);

  const onSubmit = async (values: FormValues) => {
    const submitData = {
      ...values,
      value: Number(values.value),
    };
    

    if (initialData) {
      // Update existing pipeline item
      try {
        const response = await fetch(`/api/pipeline/${initialData.id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(submitData),
        });
        
        if (response.ok) {
          toast.success('Pipeline item updated successfully');
          onSuccess?.();
          router.push('/pipeline');
        } else {
          const error = await response.json();
          toast.error(error.error || 'Failed to update pipeline item');
        }
      } catch (error) {
        console.error('Error updating pipeline item:', error);
        toast.error('Failed to update pipeline item');
      }
    } else {
      // Create new pipeline item
      try {
        const response = await fetch('/api/pipeline', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(submitData),
        });
        
        if (response.ok) {
          toast.success('Pipeline item created successfully');
          onSuccess?.();
          router.push('/pipeline');
        } else if (response.status === 409) {
          // Handle duplicate detection
          const duplicateError = await response.json();
          if (duplicateError.type === 'DUPLICATE_LEAD') {
            setDuplicateInfo({
              error: duplicateError.error,
              details: duplicateError.details,
              duplicateId: duplicateError.duplicateId,
              duplicateBdr: duplicateError.duplicateBdr,
              formValues: values
            });
            setShowDuplicateDialog(true);
          } else {
            toast.error(duplicateError.error || 'Failed to create pipeline item');
          }
        } else {
          const error = await response.json();
          toast.error(error.error || 'Failed to create pipeline item');
        }
      } catch (error) {
        console.error('Error creating pipeline item:', error);
        toast.error('Failed to create pipeline item');
      }
    }
  };

  const handleAddBdr = (newBdr: string) => {
    const success = addBdr(newBdr);
    if (success) {
      // Update the form field with the new BDR
      form.setValue('bdr', newBdr);
    }
    return success;
  };

  const handleForceCreate = async () => {
    if (!duplicateInfo) return;
    
    try {
      const response = await fetch('/api/pipeline?force=true', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...duplicateInfo.formValues,
          value: Number(duplicateInfo.formValues.value),
        }),
      });
      
      if (response.ok) {
        toast.success('Pipeline item created successfully (duplicate allowed)');
        setShowDuplicateDialog(false);
        setDuplicateInfo(null);
        onSuccess?.();
        router.push('/pipeline');
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to create pipeline item');
      }
    } catch (error) {
      console.error('Error force creating pipeline item:', error);
      toast.error('Failed to create pipeline item');
    }
  };

  return (
    <Form {...form}>
      <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Name*</FormLabel>
                <FormControl>
                  <Input placeholder="John Doe" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="company"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Company</FormLabel>
                <FormControl>
                  <Input placeholder="Acme Inc." {...field} value={field.value || ''} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="bdr"
            render={({ field }) => (
              <FormItem>
                <FormLabel>BDR</FormLabel>
                <div className="flex gap-2">
                  <Select 
                    onValueChange={field.onChange} 
                    defaultValue={field.value}
                    value={field.value}
                  >
                    <FormControl>
                      <SelectTrigger className="flex-1">
                        <SelectValue placeholder="Select BDR" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {bdrs.map((bdr) => (
                        <SelectItem key={bdr} value={bdr}>
                          {bdr}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <AddBdrDialog 
                    onAddBdr={handleAddBdr}
                  />
                </div>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="category"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Category</FormLabel>
                <Select 
                  onValueChange={field.onChange} 
                  defaultValue={field.value}
                  value={field.value}
                >
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue placeholder="Select Category" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {pipelineCategoryEnum.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category.replace(/_/g, ', ')}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="status"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Status</FormLabel>
                <Select 
                  onValueChange={field.onChange} 
                  defaultValue={field.value}
                  value={field.value}
                >
                  <FormControl>
                    <SelectTrigger>
                      <SelectValue placeholder="Select Status" />
                    </SelectTrigger>
                  </FormControl>
                  <SelectContent>
                    {selectedCategory && 
                      pipelineStatusEnum[selectedCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                        <SelectItem key={`${selectedCategory}-${status}`} value={status}>
                          {status}
                        </SelectItem>
                      ))}
                  </SelectContent>
                </Select>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <FormField
            control={form.control}
            name="value"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Deal Value ($)</FormLabel>
                <FormControl>
                  <Input type="number" placeholder="0.00" {...field} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="phone"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Phone</FormLabel>
                <FormControl>
                  <Input placeholder="+1234567890" {...field} value={field.value || ''} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="email"
            render={({ field }) => (
              <FormItem>
                <FormLabel>Email</FormLabel>
                <FormControl>
                  <Input placeholder="john@example.com" {...field} value={field.value || ''} />
                </FormControl>
                <FormMessage />
              </FormItem>
            )}
          />
        </div>

        <FormField
          control={form.control}
          name="link"
          render={({ field }) => (
            <FormItem>
              <FormLabel>LinkedIn Profile</FormLabel>
              <FormControl>
                <Input placeholder="https://linkedin.com/in/johndoe" {...field} value={field.value || ''} />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <FormField
          control={form.control}
          name="notes"
          render={({ field }) => (
            <FormItem>
              <FormLabel>Notes</FormLabel>
              <FormControl>
                <Textarea 
                  placeholder="Additional notes..."
                  className="min-h-[100px]"
                  {...field}
                  value={field.value || ''}
                />
              </FormControl>
              <FormMessage />
            </FormItem>
          )}
        />

        <div className="flex justify-end space-x-2">
          <Button 
            variant="outline" 
            onClick={() => {
              onCancel?.();
              router.push('/pipeline');
            }} 
            type="button"
          >
            Cancel
          </Button>
          <Button type="submit">
            {initialData ? 'Update Pipeline Item' : 'Create Pipeline Item'}
          </Button>
        </div>
      </form>

      {/* Duplicate Detection Dialog */}
      <ConfirmDialog
        open={showDuplicateDialog}
        title="Duplicate Lead Detected"
        description={`${duplicateInfo?.details}\n\nDo you want to continue and create this as a duplicate entry, or would you like to cancel and review the existing lead?`}
        confirmLabel="Create Anyway"
        cancelLabel="Cancel"
        onConfirm={handleForceCreate}
        onOpenChange={(open) => {
          setShowDuplicateDialog(open);
          if (!open) {
            setDuplicateInfo(null);
          }
        }}
      />
    </Form>
  );
}

'use client';

import React, { useState, useMemo } from 'react';
import { ColumnDef } from '@tanstack/react-table';
import { Filter, ExternalLink, MessageSquare, Move, Clock, Plus, Trash2 } from 'lucide-react';
import { PipelineItem, useMovePipelineItem, useDeletePipelineItem } from '@/lib/hooks';
import { pipelineCategoryEnum, allPipelineStatusValues } from '@/lib/validations';
import { useRouter } from 'next/navigation';
import { toast } from 'sonner';
import { UpdatesDialog } from '@/components/updates-dialog';
import { SublistDialog } from '@/components/sublist-dialog';
import { PipelineDialog } from '@/components/pipeline-dialog';
import { DataTable, FilterConfig, BulkAction } from '@/components/ui/data-table';
import { useBulkUpdatePipeline, useBulkDeletePipeline } from '@/lib/bulk-operations';
import { Button } from '@/components/ui/button';

interface PipelineTableProps {
  items: PipelineItem[];
  onRefresh?: () => void;
  selectedBdr?: string;
}

export function PipelineTableRefactored({ items, onRefresh, selectedBdr }: PipelineTableProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const router = useRouter();
  const { mutate: movePipelineItem } = useMovePipelineItem();
  const { mutate: deletePipelineItem } = useDeletePipelineItem();
  
  // Bulk operation hooks
  const bulkUpdatePipeline = useBulkUpdatePipeline();
  const bulkDeletePipeline = useBulkDeletePipeline();

  // Filter configurations
  const filterConfigs: FilterConfig[] = useMemo(() => [
    {
      key: 'category',
      label: 'Category',
      options: pipelineCategoryEnum.map(category => ({ 
        value: category, 
        label: category.replace('_', ' ') 
      })),
      placeholder: 'All Categories'
    },
    {
      key: 'status',
      label: 'Status',
      options: allPipelineStatusValues.map(status => ({ value: status, label: status })),
      placeholder: 'All Statuses'
    },
    {
      key: 'bdr',
      label: 'BDR',
      options: [...new Set(items.map(item => item.bdr).filter(Boolean))].map(bdr => ({ 
        value: bdr, 
        label: bdr 
      })),
      placeholder: 'All BDRs'
    }
  ], [items]);

  // Bulk actions configuration
  const bulkActions: BulkAction<PipelineItem>[] = useMemo(() => [
    {
      label: 'Move to Pipeline',
      icon: <Move className="h-4 w-4 mr-2" />,
      onClick: (selectedRows) => {
        // For simplicity, move all to a default Pipeline status
        // In a real implementation, you'd want a dialog to select target status
        const selectedIds = selectedRows.map(row => row.id);
        selectedRows.forEach(item => {
          movePipelineItem({
            id: item.id,
            newCategory: 'Pipeline',
            newStatus: 'Proposal - Media'
          }, {
            onSuccess: () => {
              toast.success(`Moved ${selectedRows.length} items to Pipeline`);
              onRefresh?.();
            },
            onError: () => {
              toast.error('Failed to move items');
            }
          });
        });
      }
    },
    {
      label: 'Delete Selected',
      icon: <Trash2 className="h-4 w-4 mr-2" />,
      variant: 'destructive' as const,
      onClick: (selectedRows) => {
        if (selectedRows.length === 0) {
          toast.error('Please select at least one item to delete');
          return;
        }

        if (confirm(`Are you sure you want to delete ${selectedRows.length} pipeline items? This action cannot be undone.`)) {
          selectedRows.forEach(item => {
            deletePipelineItem(item.id, {
              onSuccess: () => {
                toast.success('Pipeline items deleted successfully');
                onRefresh?.();
              },
              onError: () => {
                toast.error('Failed to delete pipeline items');
              }
            });
          });
        }
      }
    }
  ], [movePipelineItem, deletePipelineItem, onRefresh]);

  // Filter items based on search query
  const filteredItems = useMemo(() => {
    if (!searchQuery) return items;
    
    return items.filter(item => 
      item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.company?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.bdr?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      item.email?.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [items, searchQuery]);

  // Convert to flat table data (this loses the hierarchical structure)
  // For a full implementation, you'd need custom row rendering in DataTable
  const tableData = useMemo(() => {
    const flattenItems = (items: PipelineItem[], level = 0): (PipelineItem & { level: number })[] => {
      const result: (PipelineItem & { level: number })[] = [];
      
      items.forEach(item => {
        result.push({ ...item, level });
        if (item.children && item.children.length > 0) {
          result.push(...flattenItems(item.children, level + 1));
        }
      });
      
      return result;
    };
    
    return flattenItems(filteredItems);
  }, [filteredItems]);

  // Mock pagination props (in real implementation, these would come from parent)
  const mockFilters = {
    search: searchQuery,
    category: 'all',
    status: 'all',
    bdr: 'all'
  };

  const handleRowClick = (row: any) => {
    router.push(`/pipeline/${row.original.id}`);
  };

  // Additional actions
  const additionalActions = (
    <PipelineDialog
      title="Add New Lead"
      buttonLabel="Add New Lead"
      buttonVariant="default"
      buttonIcon={true}
      defaultCategory="Pipeline"
      defaultStatus="New"
      defaultBdr={selectedBdr}
      onSuccess={onRefresh}
      className="shadow-lg"
    />
  );

  // Create mock columns for demonstration
  // In a real implementation, you'd define proper columns with all the pipeline-specific functionality
  const columns: ColumnDef<PipelineItem & { level: number }>[] = useMemo(() => [
    {
      accessorKey: 'name',
      header: 'Name',
      cell: ({ row }) => (
        <div style={{ paddingLeft: `${row.original.level * 20}px` }} className="flex items-center gap-2">
          <span>{row.original.name}</span>
          {row.original.company && (
            <span className="text-sm text-muted-foreground">({row.original.company})</span>
          )}
        </div>
      ),
    },
    {
      accessorKey: 'category',
      header: 'Category',
      cell: ({ row }) => row.original.category.replace('_', ' '),
    },
    {
      accessorKey: 'status',
      header: 'Status',
    },
    {
      accessorKey: 'bdr',
      header: 'BDR',
    },
    {
      accessorKey: 'value',
      header: 'Value',
      cell: ({ row }) => row.original.value ? `Â£${row.original.value.toLocaleString()}` : '-',
    },
    {
      id: 'actions',
      header: 'Actions',
      cell: ({ row }) => (
        <div className="flex items-center space-x-1" onClick={e => e.stopPropagation()}>
          <UpdatesDialog 
            pipelineItem={row.original}
            trigger={
              <Button 
                variant="outline" 
                size="sm" 
                className="h-6 px-2 text-xs"
              >
                <MessageSquare className="h-3 w-3 mr-1" />
                Updates
              </Button>
            }
          />
          <Button 
            variant="outline" 
            size="sm" 
            className="h-6 px-2 text-xs"
            onClick={(e) => {
              e.stopPropagation();
              // Handle move action - in real implementation, this would open a move dialog
              console.log('Move item:', row.original);
            }}
          >
            <Move className="h-3 w-3 mr-1" />
            Move
          </Button>
        </div>
      ),
    },
  ], []);

  return (
    <div className="space-y-4">
      <DataTable
        columns={columns}
        data={tableData}
        filters={mockFilters}
        total={tableData.length}
        pageCount={1}
        pageIndex={0}
        pageSize={50}
        onPaginationChange={() => {}}
        onGlobalFilterChange={setSearchQuery}
        onFilterChange={() => {}}
        isLoading={false}
        enableRowSelection={true}
        bulkActions={bulkActions}
        filterConfigs={filterConfigs}
        searchPlaceholder="Search pipeline..."
        title="Pipeline Management"
        additionalActions={additionalActions}
        emptyStateMessage="No pipeline items found"
        onRowClick={handleRowClick}
        getRowId={(row) => row.id.toString()}
        virtualizeRows
        virtualRowEstimatedHeight={44}
      />
      
      <div className="text-sm text-muted-foreground">
        Note: This refactored version shows a flattened view. The original hierarchical structure 
        with expandable sublists would require custom row rendering in the DataTable component.
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Card, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { PipelineItem, useMovePipelineItem, useActivityLogs, useDeletePipelineItem } from '@/lib/hooks';
import { allPipelineStatusValues, pipelineCategoryEnum, pipelineStatusEnum } from '@/lib/validations';
import { formatDistanceToNow } from 'date-fns';
import { useRouter } from 'next/navigation';
import { Filter, ExternalLink, MessageSquare, Move, Clock, ChevronDown, ChevronRight, Trash2, Plus } from 'lucide-react';
import { NotesPopover } from '@/components/ui/notes-popover';
import { UpdatesDialog } from '@/components/updates-dialog';
import { SublistDialog } from '@/components/sublist-dialog';
import { PipelineEditableCell } from '@/components/ui/pipeline-editable-cell';
import { PipelineDialog } from '@/components/pipeline-dialog';
import { 
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { ScrollArea } from "@/components/ui/scroll-area";
import { toast } from 'sonner';
import { Badge } from '@/components/ui/badge';

interface PipelineTableProps {
  items: PipelineItem[];
  onRefresh?: () => void;
  selectedBdr?: string;
  simpleMode?: boolean;
}

export function PipelineTable({ items, onRefresh, selectedBdr, simpleMode = true }: PipelineTableProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [moveOpen, setMoveOpen] = useState<number | null>(null);
  const [selectedMoveCategory, setSelectedMoveCategory] = useState<string>('');
  const [selectedMoveStatus, setSelectedMoveStatus] = useState<string>('');
  const [expandedItems, setExpandedItems] = useState<Set<number>>(new Set());
  const router = useRouter();
  const { mutate: movePipelineItem } = useMovePipelineItem();
  const { mutate: deletePipelineItem } = useDeletePipelineItem();
  const [showActivityLog, setShowActivityLog] = useState<number | null>(null);

  // Fetch activity logs for the selected item
  const { data: activityLogsData } = useActivityLogs({
    pipelineItemId: showActivityLog || undefined,
    pageSize: 50,
  });

  // Function to toggle item expansion
  const toggleExpanded = (itemId: number) => {
    const newExpanded = new Set(expandedItems);
    if (newExpanded.has(itemId)) {
      newExpanded.delete(itemId);
    } else {
      newExpanded.add(itemId);
    }
    setExpandedItems(newExpanded);
  };

  // Track companies with recent conflicts
  const [conflictCompanies, setConflictCompanies] = useState<Set<string>>(new Set());
  useState(() => { return; });

  // Fetch conflict flags for companies displayed
  // Lightweight: fire once per render set; for production, memoize by items hash
  React.useEffect(() => {
    const companies = Array.from(new Set(items.map(i => i.company).filter(Boolean) as string[]));
    if (companies.length === 0) { setConflictCompanies(new Set()); return; }
    const params = new URLSearchParams();
    companies.forEach(c => params.append('company', c));
    params.append('days', '14');
    fetch(`/api/duplicates/company-conflicts?${params.toString()}`)
      .then(r => r.ok ? r.json() : null)
      .then(data => {
        if (!data?.conflicts) return;
        const flagged = new Set<string>();
        Object.entries<boolean>(data.conflicts).forEach(([company, has]) => { if (has) flagged.add(company); });
        setConflictCompanies(flagged);
      })
      .catch(() => {});
  }, [items]);

  // Recursive function to render items and their children
  const renderItemsRecursively = (itemList: PipelineItem[], level: number = 0): React.ReactElement[] => {
          const elements: React.ReactElement[] = [];
    
    itemList.forEach((item) => {
      const hasChildren = item.children && item.children.length > 0;
      const isExpanded = expandedItems.has(item.id);
      
      elements.push(
                      <TableRow
                        key={item.id}
                        className="cursor-pointer hover:bg-muted/50 h-12"
                        onClick={() => handleItemClick(item.id)}
          style={{ paddingLeft: `${level * 20}px` }}
                      >
                        <TableCell className="py-2">
            <div className="flex items-center gap-2" style={{ paddingLeft: `${level * 20}px` }}>
              <div onClick={(e) => e.stopPropagation()}>
                {hasChildren ? (
                  <div className="flex items-center gap-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-4 w-4 p-0 hover:bg-muted"
                      onClick={() => toggleExpanded(item.id)}
                    >
                      {isExpanded ? (
                        <ChevronDown className="h-3 w-3" />
                      ) : (
                        <ChevronRight className="h-3 w-3" />
                      )}
                    </Button>
                  </div>
                ) : (
                  <SublistDialog
                    title="Add Sublist Item"
                    buttonLabel="+"
                    buttonVariant="ghost"
                    buttonSize="sm"
                    buttonIcon={true}
                    defaultCategory={item.category}
                    defaultStatus={item.status}
                    defaultBdr={item.bdr}
                    parentId={item.id}
                    onSuccess={onRefresh}
                    className="h-4 w-4 p-0 hover:bg-muted"
                  />
                )}
              </div>
                          <PipelineEditableCell
                            value={item.name}
                            item={item}
                            field="name"
                            placeholder="Name"
                          />
                          {conflictCompanies.has(item.company || '') && (
                            <Badge variant="destructive" className="ml-2">Conflict</Badge>
                          )}
                          {hasChildren && (
                            <div className="flex items-center gap-1 ml-2">
                              <div className="w-1 h-1 rounded-full bg-primary"></div>
                              <span className="text-xs text-muted-foreground">
                                {item.children?.length || 0} subitem{item.children && item.children.length !== 1 ? 's' : ''}
                              </span>
                            </div>
                          )}
            </div>
                        </TableCell>
                        {!simpleMode && (
                        <TableCell className="py-2">
                          <PipelineEditableCell
                            value={item.company}
                            item={item}
                            field="company"
                            placeholder="Company"
                          />
                        </TableCell>
                        )}
                        {!simpleMode && (
                        <TableCell className="py-2">
                          <PipelineEditableCell
                            value={item.title}
                            item={item}
                            field="title"
                            placeholder="Title"
                          />
                        </TableCell>
                        )}
                        <TableCell className="py-2">
                          <PipelineEditableCell
                            value={item.callDate || ''}
                            item={item}
                            field="callDate"
                            placeholder="Set call date"
                            isDate={true}
                          />
                        </TableCell>
                        {!simpleMode && (
                        <TableCell className="py-2" onClick={(e) => e.stopPropagation()}>
                          <Popover open={showActivityLog === item.id} onOpenChange={(open) => !open && setShowActivityLog(null)}>
                            <PopoverTrigger asChild>
                              <div 
                                className="cursor-pointer hover:bg-muted/50 p-1 rounded"
                                onClick={() => setShowActivityLog(item.id)}
                              >
                                {item.latestActivityLog ? (
                                  <div className="flex flex-col gap-0.5">
                                    <span className="text-xs font-medium">
                                      {item.latestActivityLog.activityType === 'BDR_Update' ? 'Update' : 
                                       item.latestActivityLog.activityType.replace('_', ' ')}
                                    </span>
                                    <span className="text-xs text-muted-foreground">
                                      {item.latestActivityLog.notes ? 
                                        item.latestActivityLog.notes.length > 30 ? 
                                          `${item.latestActivityLog.notes.substring(0, 30)}...` : 
                                          item.latestActivityLog.notes
                                        : item.latestActivityLog.description
                                      }
                                    </span>
                                    <span className="text-xs text-muted-foreground">
                                      by {item.latestActivityLog.bdr} {formatDistanceToNow(new Date(item.latestActivityLog.timestamp), { addSuffix: true })}
                                    </span>
                                  </div>
                                ) : (
                                  <span className="text-xs text-muted-foreground">No updates</span>
                                )}
                              </div>
                            </PopoverTrigger>
                            <PopoverContent className="w-96" side="right">
                              <div className="space-y-4">
                                <div className="flex items-center justify-between">
                                  <h4 className="font-medium flex items-center gap-2">
                                    <Clock className="h-4 w-4" />
                                    Activity History
                                  </h4>
                                </div>
                                <ScrollArea className="h-[300px]">
                                  <div className="space-y-4">
                                    {activityLogsData?.logs.map((log) => (
                                      <div key={log.id} className="space-y-1">
                                        <div className="flex items-center justify-between">
                                          <span className="text-sm font-medium">
                                            {log.activityType === 'BDR_Update' ? 'Update' : 
                                             log.activityType.replace('_', ' ')}
                                          </span>
                                          <span className="text-xs text-muted-foreground">
                                            {formatDistanceToNow(new Date(log.timestamp), { addSuffix: true })}
                                          </span>
                                        </div>
                                        <p className="text-sm text-muted-foreground">
                                          {log.notes || log.description}
                                        </p>
                                        <p className="text-xs text-muted-foreground">
                                          by {log.bdr}
                                        </p>
                                        {(log.previousStatus !== log.newStatus || log.previousCategory !== log.newCategory) && (
                                          <p className="text-xs text-muted-foreground mt-1">
                                            {log.previousCategory && log.newCategory && (
                                              <>Moved from {log.previousCategory} to {log.newCategory}<br /></>
                                            )}
                                            {log.previousStatus && log.newStatus && (
                                              <>Status changed from {log.previousStatus} to {log.newStatus}</>
                                            )}
                                          </p>
                                        )}
                                      </div>
                                    ))}
                                  </div>
                                </ScrollArea>
                              </div>
                            </PopoverContent>
                          </Popover>
                        </TableCell>
                        )}

                        {!simpleMode && (
                        <TableCell className="py-2">
                          {item.email && (
                            <a 
                              href={`mailto:${item.email}`}
                              onClick={(e) => e.stopPropagation()}
                              className="text-blue-500 hover:text-blue-700 transition-colors text-xs"
                            >
                              {item.email}
                            </a>
                          )}
                        </TableCell>
                        )}
                        {!simpleMode && (
                        <TableCell className="py-2">
                          {item.phone && (
                            <a 
                              href={`tel:${item.phone}`}
                              onClick={(e) => e.stopPropagation()}
                              className="text-blue-500 hover:text-blue-700 transition-colors text-xs"
                            >
                              {item.phone}
                            </a>
                          )}
                        </TableCell>
                        )}
                        <TableCell className="py-2">
                          <PipelineEditableCell
                            value={item.notes}
                            item={item}
                            field="notes"
                            isTextarea={true}
                            placeholder="Add notes..."
                          />
                        </TableCell>
                        <TableCell className="py-2">
                          <div className="flex items-center space-x-1" onClick={e => e.stopPropagation()}>
                            <UpdatesDialog 
                              pipelineItem={item}
                              trigger={
                                <Button 
                                  variant="outline" 
                                  size="sm" 
                                  className="h-6 px-2 text-xs"
                                >
                                  <MessageSquare className="h-3 w-3 mr-1" />
                                  Updates
                                </Button>
                              }
                            />
                            
                            <Popover open={moveOpen === item.id} onOpenChange={(open) => !open && setMoveOpen(null)}>
                              <PopoverTrigger asChild>
                                <Button 
                                  variant="outline" 
                                  size="sm" 
                                  className="h-6 px-2 text-xs"
                                  onClick={(e) => {
                                    e.stopPropagation();
                                    openMoveDialog(item);
                                  }}
                                >
                                  <Move className="h-3 w-3 mr-1" />
                                  Move
                                </Button>
                              </PopoverTrigger>
                              <PopoverContent className="w-80 p-4" side="top" align="end">
                                <div className="space-y-4">
                                  <h4 className="font-medium">Move to different group</h4>
                                  
                                  <div className="space-y-3">
                                    <div>
                                      <label className="text-sm font-medium">Category</label>
                                      <Select
                                        value={selectedMoveCategory}
                                        onValueChange={handleCategoryChange}
                                      >
                                        <SelectTrigger className="mt-1">
                                          <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                          {pipelineCategoryEnum.map((category) => (
                                            <SelectItem key={category} value={category}>
                                              {category.replace('_', ' ')}
                                            </SelectItem>
                                          ))}
                                        </SelectContent>
                                      </Select>
                                    </div>
                                    
                                    <div>
                                      <label className="text-sm font-medium">Status</label>
                                      <Select
                                        value={selectedMoveStatus}
                                        onValueChange={(value) => setSelectedMoveStatus(value)}
                                      >
                                        <SelectTrigger className="mt-1">
                                          <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                          {selectedMoveCategory && pipelineStatusEnum[selectedMoveCategory as keyof typeof pipelineStatusEnum]?.map((status) => (
                                            <SelectItem key={`${selectedMoveCategory}-${status}`} value={status}>
                                              {status}
                                            </SelectItem>
                                          ))}
                                        </SelectContent>
                                      </Select>
                                    </div>
                                  </div>
                                  
                                  <div className="text-xs text-muted-foreground bg-muted/20 p-2 rounded">
                                    Moving from <span className="font-medium">{item.category}</span> ({item.status}) to <span className="font-medium">{selectedMoveCategory}</span> ({selectedMoveStatus})
                                  </div>
                                  
                                  <div className="flex justify-end gap-2">
                                    <Button 
                                      variant="outline" 
                                      size="sm"
                                      onClick={() => setMoveOpen(null)}
                                    >
                                      Cancel
                                    </Button>
                                    <Button 
                                      size="sm"
                                      onClick={() => handleMoveItem(item)}
                                      disabled={selectedMoveCategory === item.category && selectedMoveStatus === item.status}
                                    >
                                      Move
                                    </Button>
                                  </div>
                                </div>
                              </PopoverContent>
                            </Popover>
                            
                            {item.notes && <NotesPopover notes={item.notes} />}
                            {item.link && (
                              <a 
                                href={item.link} 
                                target="_blank" 
                                rel="noopener noreferrer"
                                className="text-blue-500 hover:text-blue-700 transition-colors"
                              >
                                <ExternalLink className="h-3 w-3" />
                              </a>
                            )}
                            <Button
                              variant="outline"
                              size="sm"
                              className="h-6 px-2 text-destructive hover:text-destructive hover:bg-destructive/10"
                              onClick={(e) => handleDeleteItem(e, item)}
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    );

      // Add children if expanded
      if (hasChildren && isExpanded && item.children) {
        elements.push(...renderItemsRecursively(item.children, level + 1));
        
        // Add "Add subitem" row after the children
        elements.push(
          <TableRow key={`add-subitem-${item.id}`} className="hover:bg-muted/50 h-10">
            <TableCell className="py-1">
              <div className="flex items-center gap-2" style={{ paddingLeft: `${(level + 1) * 20}px` }}>
                <div className="w-2 h-2 rounded-full bg-muted-foreground/30"></div>
                <SublistDialog
                  title="Add More Sublist Items"
                  buttonLabel="+ Add subitem"
                  buttonVariant="ghost"
                  buttonSize="default"
                  buttonIcon={false}
                  defaultCategory={item.category}
                  defaultStatus={item.status}
                  defaultBdr={item.bdr}
                  parentId={item.id}
                  onSuccess={onRefresh}
                  className="h-5 px-2 text-xs hover:bg-muted text-muted-foreground hover:text-foreground border border-dashed border-muted-foreground/30 hover:border-muted-foreground/50 rounded"
                />
              </div>
            </TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
            <TableCell className="py-1"></TableCell>
          </TableRow>
        );
      }
    });

    return elements;
  };

  // Group items by status
  const itemsByStatus = allPipelineStatusValues.reduce<Record<string, PipelineItem[]>>(
    (acc, status) => {
      acc[status] = items.filter(item => 
        item.status === status &&
        (searchQuery === '' || 
          item.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          item.company?.toLowerCase().includes(searchQuery.toLowerCase()) ||
          item.title?.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
      return acc;
    },
    {} as Record<string, PipelineItem[]>
  );

  const handleItemClick = (id: number) => {
    router.push(`/pipeline/${id}`);
  };

  const handleMoveItem = (item: PipelineItem) => {
    if (selectedMoveCategory === item.category && selectedMoveStatus === item.status) {
      setMoveOpen(null);
      return;
    }

    movePipelineItem({
      id: item.id,
      newCategory: selectedMoveCategory,
      newStatus: selectedMoveStatus
    }, {
      onSuccess: () => {
        toast.success(`Moved to ${selectedMoveStatus}`);
        setMoveOpen(null);
        onRefresh?.();
      },
      onError: () => {
        toast.error('Failed to move item');
      }
    });
  };

  const handleCategoryChange = (category: string) => {
    setSelectedMoveCategory(category);
    // Set the status to the first status in the new category
    const statuses = pipelineStatusEnum[category as keyof typeof pipelineStatusEnum];
    if (statuses && statuses.length > 0) {
      setSelectedMoveStatus(statuses[0]);
    }
  };

  const openMoveDialog = (item: PipelineItem) => {
    setSelectedMoveCategory(item.category);
    setSelectedMoveStatus(item.status);
    setMoveOpen(item.id);
  };

  const handleDeleteItem = (e: React.MouseEvent, item: PipelineItem) => {
    e.stopPropagation();
    if (confirm('Are you sure you want to delete this pipeline item? This action cannot be undone.')) {
      deletePipelineItem(item.id, {
        onSuccess: () => {
          toast.success('Pipeline item deleted successfully');
          onRefresh?.();
        },
        onError: () => {
          toast.error('Failed to delete pipeline item');
        }
      });
    }
  };

  return (
    <div className="space-y-8">
      {/* Search Bar and Add New Lead */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2 rounded-xl border border-white/30 dark:border-white/10 bg-white/60 dark:bg-white/[0.05] backdrop-blur p-2">
          <Input
            placeholder="Search pipeline..."
            className="max-w-xs h-9"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <Button 
            variant="outline" 
            size="icon" 
            className="h-9 w-9"
            onClick={() => setShowFilters(!showFilters)}
          >
            <Filter className="h-4 w-4" />
          </Button>
        </div>
        
        {/* Add New Lead Button */}
        <PipelineDialog
          title="Add New Lead"
          buttonLabel="Add New Lead"
          buttonVariant="default"
          buttonIcon={true}
          defaultCategory="Pipeline"
          defaultStatus="New"
          defaultBdr={selectedBdr}
          onSuccess={onRefresh}
          className="shadow-lg"
        />
      </div>

      {/* Pipeline Sections */}
      {allPipelineStatusValues.map(status => {
        const statusItems = itemsByStatus[status] || [];
        if (statusItems.length === 0) return null;

        return (
          <div key={status} className="space-y-4">
            <Card>
              <CardHeader className="py-3">
                <CardTitle className="text-lg font-semibold flex items-center justify-between">
                  <span>{status}</span>
                  <div className="flex items-center gap-4">
                    <div className="text-xs text-muted-foreground">
                      {statusItems.length} items
                    </div>
                  </div>
                </CardTitle>
              </CardHeader>
            </Card>

            <div className="rounded-xl border border-white/20 dark:border-white/10 bg-white/40 dark:bg-white/[0.03] backdrop-blur">
              <Table>
                <TableHeader>
                  <TableRow className="h-10 bg-white/60 dark:bg-white/[0.06]">
                    <TableHead className="w-[200px]">Name</TableHead>
                    {!simpleMode && <TableHead className="w-[150px]">Company</TableHead>}
                    {!simpleMode && <TableHead className="w-[150px]">Title</TableHead>}
                    <TableHead className="w-[100px]">Call Date</TableHead>
                    {!simpleMode && <TableHead className="w-[150px]">Last Update</TableHead>}
                    {!simpleMode && <TableHead className="w-[120px]">Email</TableHead>}
                    {!simpleMode && <TableHead className="w-[120px]">Number</TableHead>}
                    <TableHead className="w-[200px]">Notes</TableHead>
                    <TableHead className="w-[160px]">Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {renderItemsRecursively(statusItems)}
                </TableBody>
              </Table>
            </div>
          </div>
        );
      })}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Bell, X, CheckCircle, AlertTriangle, Info, Clock } from 'lucide-react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Popover, PopoverTrigger, PopoverContent } from '@/components/ui/popover';

interface Notification {
  id: string;
  type: 'success' | 'warning' | 'error' | 'info';
  title: string;
  message: string;
  timestamp: string;
  read: boolean;
  priority: 'low' | 'medium' | 'high';
  userId?: string;
}

interface RealTimeNotificationsProps {
  userId?: string;
  showHighPriorityOnly?: boolean;
  maxNotifications?: number;
  autoRefresh?: boolean;
  refreshInterval?: number;
  // When true, renders an inline high-priority banner under the bell.
  // In compact placements like the sidebar, this should be false to avoid layout overflow.
  showAlertBanner?: boolean;
}

const fetchNotifications = async (userId?: string) => {
  const params = new URLSearchParams();
  if (userId) params.append('userId', userId);

  const response = await fetch(`/api/reporting/advanced/notifications?${params.toString()}`);
  if (!response.ok) {
    throw new Error('Failed to fetch notifications');
  }
  return response.json();
};

const markNotificationAsRead = async (notificationId: string) => {
  const response = await fetch('/api/reporting/advanced/notifications', {
    method: 'PATCH',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ notificationId, read: true }),
  });
  
  if (!response.ok) {
    throw new Error('Failed to mark notification as read');
  }
  
  return response.json();
};

export function RealTimeNotifications({
  userId,
  showHighPriorityOnly = false,
  maxNotifications = 10,
  autoRefresh = true,
  refreshInterval = 30000,
  showAlertBanner = false,
}: RealTimeNotificationsProps) {
  const [open, setOpen] = useState(false);
  const [notifications, setNotifications] = useState<Notification[]>([]);

  const { data: notificationData, refetch } = useQuery({
    queryKey: ['notifications', userId],
    queryFn: () => fetchNotifications(userId),
    refetchInterval: autoRefresh ? refreshInterval : false,
    enabled: true,
  });

  useEffect(() => {
    if (notificationData) {
      let filteredNotifications = notificationData;
      
      if (showHighPriorityOnly) {
        filteredNotifications = notificationData.filter((n: Notification) => n.priority === 'high');
      }
      
      setNotifications(filteredNotifications.slice(0, maxNotifications));
    }
  }, [notificationData, showHighPriorityOnly, maxNotifications]);

  const unreadNotifications = notifications.filter(n => !n.read);
  const highPriorityNotifications = notifications.filter(n => n.priority === 'high' && !n.read);

  const handleMarkAsRead = async (notificationId: string) => {
    try {
      await markNotificationAsRead(notificationId);
      setNotifications(prev => 
        prev.map(n => n.id === notificationId ? { ...n, read: true } : n)
      );
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const handleMarkAllAsRead = async () => {
    try {
      await Promise.all(
        unreadNotifications.map(n => markNotificationAsRead(n.id))
      );
      setNotifications(prev => 
        prev.map(n => ({ ...n, read: true }))
      );
    } catch (error) {
      console.error('Error marking all notifications as read:', error);
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'success':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4 text-yellow-500" />;
      case 'error':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'info':
      default:
        return <Info className="h-4 w-4 text-blue-500" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return date.toLocaleDateString();
  };

  return (
    <div className="relative">
      <Popover open={open} onOpenChange={setOpen}>
        {/* Notification Bell */}
        <PopoverTrigger asChild>
          <Button variant="outline" size="sm" className="relative">
            <Bell className="h-4 w-4" />
            {unreadNotifications.length > 0 && (
              <Badge 
                variant="destructive" 
                className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs"
              >
                {unreadNotifications.length}
              </Badge>
            )}
          </Button>
        </PopoverTrigger>

        {/* Notifications Panel */}
        <PopoverContent side="top" align="end" sideOffset={8} className="z-[70] w-96 p-0 border-0 shadow-lg">
          <Card className="border">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg">Notifications</CardTitle>
                <div className="flex items-center space-x-2">
                  {unreadNotifications.length > 0 && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={handleMarkAllAsRead}
                      className="text-xs"
                    >
                      Mark all read
                    </Button>
                  )}
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setOpen(false)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent className="p-0">
              {notifications.length === 0 ? (
                <div className="p-6 text-center text-muted-foreground">
                  <Bell className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p>No notifications</p>
                </div>
              ) : (
                <ScrollArea className="h-80">
                  <div className="divide-y">
                    {notifications.map((notification) => (
                      <div 
                        key={notification.id} 
                        className={`p-4 hover:bg-muted/50 transition-colors ${
                          !notification.read ? 'bg-muted/30' : ''
                        }`}
                      >
                        <div className="flex items-start space-x-3">
                          <div className="mt-1">
                            {getNotificationIcon(notification.type)}
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between mb-1">
                              <h4 className="font-medium text-sm truncate">
                                {notification.title}
                              </h4>
                              <div className="flex items-center space-x-2">
                                <Badge 
                                  variant="outline" 
                                  className={`text-xs ${getPriorityColor(notification.priority)}`}
                                >
                                  {notification.priority}
                                </Badge>
                                {!notification.read && (
                                  <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                                )}
                              </div>
                            </div>
                            <p className="text-sm text-muted-foreground mb-2">
                              {notification.message}
                            </p>
                            <div className="flex items-center justify-between">
                              <div className="flex items-center space-x-2 text-xs text-muted-foreground">
                                <Clock className="h-3 w-3" />
                                <span>{formatTimestamp(notification.timestamp)}</span>
                              </div>
                              {!notification.read && (
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => handleMarkAsRead(notification.id)}
                                  className="text-xs h-6 px-2"
                                >
                                  Mark read
                                </Button>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </ScrollArea>
              )}
            </CardContent>
          </Card>
        </PopoverContent>
      </Popover>

      {/* High Priority Alert Banner (opt-in; hidden by default to prevent sidebar layout issues) */}
      {showAlertBanner && highPriorityNotifications.length > 0 && !open && (
        <Alert className="mt-2 border-red-200 bg-red-50">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            <strong>{highPriorityNotifications.length} high priority notification(s)</strong> require immediate attention.
            <Button
              variant="link"
              size="sm"
              onClick={() => setOpen(true)}
              className="p-0 h-auto font-semibold ml-1"
            >
              View now
            </Button>
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
}

// Standalone notification component for displaying individual notifications
export function NotificationItem({ notification }: { notification: Notification }) {
  const [isRead, setIsRead] = useState(notification.read);

  const handleMarkAsRead = async () => {
    try {
      await markNotificationAsRead(notification.id);
      setIsRead(true);
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case 'success':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'warning':
        return <AlertTriangle className="h-4 w-4 text-yellow-500" />;
      case 'error':
        return <AlertTriangle className="h-4 w-4 text-red-500" />;
      case 'info':
      default:
        return <Info className="h-4 w-4 text-blue-500" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800 border-red-200';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffInMinutes = Math.floor((now.getTime() - date.getTime()) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return date.toLocaleDateString();
  };

  return (
    <div className={`p-4 border rounded-lg ${!isRead ? 'bg-muted/30' : ''}`}>
      <div className="flex items-start space-x-3">
        <div className="mt-1">
          {getNotificationIcon(notification.type)}
        </div>
        <div className="flex-1">
          <div className="flex items-center justify-between mb-2">
            <h4 className="font-medium">{notification.title}</h4>
            <Badge 
              variant="outline" 
              className={`text-xs ${getPriorityColor(notification.priority)}`}
            >
              {notification.priority}
            </Badge>
          </div>
          <p className="text-sm text-muted-foreground mb-2">
            {notification.message}
          </p>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2 text-xs text-muted-foreground">
              <Clock className="h-3 w-3" />
              <span>{formatTimestamp(notification.timestamp)}</span>
            </div>
            {!isRead && (
              <Button
                variant="ghost"
                size="sm"
                onClick={handleMarkAsRead}
                className="text-xs h-6 px-2"
              >
                Mark read
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useCreateSublist } from '@/lib/hooks';
import { pipelineCategoryEnum, pipelineStatusEnum } from '@/lib/validations';
import { Plus, List, ChevronDown } from 'lucide-react';
import { toast } from 'sonner';

interface SublistDialogProps {
  title: string;
  buttonLabel: string;
  buttonVariant?: 'default' | 'outline' | 'secondary' | 'ghost' | 'link' | 'destructive';
  buttonSize?: 'default' | 'sm' | 'lg' | 'icon';
  buttonIcon?: boolean;
  defaultCategory?: string;
  defaultStatus?: string;
  defaultBdr?: string;
  parentId?: number | null;
  onSuccess?: () => void;
  className?: string;
}

export function SublistDialog({
  title,
  buttonLabel,
  buttonVariant = 'default',
  buttonSize = 'default',
  buttonIcon = false,
  defaultCategory = 'prospect',
  defaultStatus = 'new',
  defaultBdr = '',
  parentId = null,
  onSuccess,
  className = ''
}: SublistDialogProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const inputRef = useRef<HTMLInputElement>(null);
  const createSublist = useCreateSublist();

  const handleButtonClick = () => {
    setIsEditing(true);
    setInputValue('');
    // Focus the input after a short delay to ensure it's rendered
    setTimeout(() => {
      inputRef.current?.focus();
    }, 0);
  };

  const handleInputKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSave();
    } else if (e.key === 'Escape') {
      e.preventDefault();
      handleCancel();
    }
  };

  const handleSave = async () => {
    if (!inputValue.trim()) {
      handleCancel();
      return;
    }

    try {
      await createSublist.mutateAsync({
        name: inputValue.trim(),
        category: defaultCategory,
        status: defaultStatus,
        bdr: defaultBdr,
        parentId: parentId
      });

      toast.success('Sublist item created successfully');
      setIsEditing(false);
      setInputValue('');
      onSuccess?.();
    } catch (error) {
      toast.error('Failed to create sublist item');
      console.error('Error creating sublist:', error);
    }
  };

  const handleCancel = () => {
    setIsEditing(false);
    setInputValue('');
  };

  // Handle click outside to cancel
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (inputRef.current && !inputRef.current.contains(event.target as Node)) {
        handleCancel();
      }
    };

    if (isEditing) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => {
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }
  }, [isEditing]);

  if (isEditing) {
    return (
      <div className="inline-flex items-center gap-2">
        <Input
          ref={inputRef}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleInputKeyDown}
          placeholder="Enter sublist item name..."
          className="h-6 px-2 text-xs min-w-[200px]"
          autoFocus
        />
        <Button
          size="sm"
          variant="outline"
          onClick={handleCancel}
          className="h-6 px-2 text-xs"
        >
          Cancel
        </Button>
      </div>
    );
  }

  return (
    <Button
      variant={buttonVariant}
      size={buttonSize}
      onClick={handleButtonClick}
      className={className}
      disabled={createSublist.isPending}
    >
      {buttonIcon ? (
        <Plus className="h-3 w-3" />
      ) : (
        buttonLabel
      )}
    </Button>
  );
}

