'use client';

import React, { useState, useMemo, ReactNode } from 'react';
import { useVirtualizer } from '@tanstack/react-virtual';
import {
  ColumnDef,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  VisibilityState,
  Row,
} from '@tanstack/react-table';
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Filter, ChevronLeft, ChevronRight } from 'lucide-react';
import { Skeleton } from '@/components/ui/skeleton';

// Custom type for column meta data
interface ColumnMeta {
  priority: 'high' | 'medium' | 'low';
}

// Generic filter option interface
export interface FilterOption {
  value: string;
  label: string;
}

// Generic bulk action interface
export interface BulkAction<TData> {
  label: string;
  icon?: ReactNode;
  variant?: 'default' | 'destructive' | 'outline' | 'secondary' | 'ghost' | 'link';
  onClick: (selectedRows: TData[]) => void;
  disabled?: (selectedRows: TData[]) => boolean;
}

// Generic filter config interface
export interface FilterConfig {
  key: string;
  label: string;
  options: FilterOption[];
  placeholder?: string;
}

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[];
  data: TData[];
  filters: Record<string, string>;
  total: number;
  pageCount: number;
  pageIndex: number;
  pageSize: number;
  onPaginationChange: (pageIndex: number, pageSize: number) => void;
  onGlobalFilterChange: (value: string) => void;
  onFilterChange: (columnId: string, value: string) => void;
  isLoading?: boolean;
  columnVisibility?: VisibilityState;
  onColumnVisibilityChange?: (state: VisibilityState) => void;
  
  // Bulk operations
  enableRowSelection?: boolean;
  bulkActions?: BulkAction<TData>[];
  
  // Filtering
  filterConfigs?: FilterConfig[];
  searchPlaceholder?: string;
  
  // Customization
  title?: string;
  exportButton?: ReactNode;
  additionalActions?: ReactNode;
  emptyStateMessage?: string;
  
  // Custom row handling
  onRowClick?: (row: Row<TData>) => void;
  getRowId?: (row: TData) => string;

  // Virtualization
  virtualizeRows?: boolean;
  virtualRowEstimatedHeight?: number;
}

function TableSkeleton() {
  return (
    <div className="space-y-3">
      {Array.from({ length: 10 }).map((_, i) => (
        <div key={i} className="flex items-center space-x-4">
          <Skeleton className="h-4 w-4" />
          <Skeleton className="h-4 w-[200px]" />
          <Skeleton className="h-4 w-[150px]" />
          <Skeleton className="h-4 w-[100px]" />
          <Skeleton className="h-4 w-[120px]" />
        </div>
      ))}
    </div>
  );
}

export function DataTable<TData, TValue>({
  columns,
  data,
  filters,
  total,
  pageCount,
  pageIndex,
  pageSize,
  onPaginationChange,
  onGlobalFilterChange,
  onFilterChange,
  isLoading,
  columnVisibility,
  onColumnVisibilityChange,
  enableRowSelection = false,
  bulkActions = [],
  filterConfigs = [],
  searchPlaceholder = "Search...",
  title,
  exportButton,
  additionalActions,
  emptyStateMessage = "No data available",
  onRowClick,
  getRowId,
  virtualizeRows = false,
  virtualRowEstimatedHeight = 44,
}: DataTableProps<TData, TValue>) {
  const [rowSelection, setRowSelection] = useState({});
  const [showFilters, setShowFilters] = useState(false);

  // Add selection column if enabled
  const tableColumns = useMemo(() => {
    if (!enableRowSelection) return columns;
    
    const selectionColumn: ColumnDef<TData, TValue> = {
      id: "select",
      header: ({ table }) => (
        <Checkbox
          checked={table.getIsAllPageRowsSelected()}
          onCheckedChange={(value) => table.toggleAllPageRowsSelected(!!value)}
          aria-label="Select all"
        />
      ),
      cell: ({ row }) => (
        <Checkbox
          checked={row.getIsSelected()}
          onCheckedChange={(value) => row.toggleSelected(!!value)}
          aria-label="Select row"
        />
      ),
      enableSorting: false,
      enableHiding: false,
    };
    
    return [selectionColumn, ...columns];
  }, [columns, enableRowSelection]);

  const table = useReactTable({
    data,
    columns: tableColumns,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onRowSelectionChange: enableRowSelection ? setRowSelection : undefined,
    onColumnVisibilityChange,
    getRowId: getRowId ? (row, index) => getRowId(row) : undefined,
    state: {
      rowSelection: enableRowSelection ? rowSelection : {},
      columnVisibility,
    },
  });

  // Get selected rows
  const selectedRowIds = useMemo(() => Object.keys(rowSelection), [rowSelection]);
  const selectedRowData = useMemo(() => {
    return data.filter((_, index) => 
      selectedRowIds.includes(index.toString())
    );
  }, [data, selectedRowIds]);

  if (isLoading) {
    return <TableSkeleton />;
  }

  const tableContainerRef = React.useRef<HTMLDivElement | null>(null);

  const rowVirtualizer = virtualizeRows
    ? useVirtualizer({
        count: table.getRowModel().rows.length,
        getScrollElement: () => tableContainerRef.current,
        estimateSize: () => virtualRowEstimatedHeight,
        overscan: 10,
      })
    : null;

  return (
    <div className="space-y-4">
      {/* Header with Title and Actions */}
      {(title || exportButton || additionalActions) && (
        <div className="flex justify-between items-center">
          {title && <h2 className="text-xl font-semibold">{title}</h2>}
          <div className="flex items-center space-x-2">
            {exportButton}
            {additionalActions}
          </div>
        </div>
      )}

      {/* Bulk Actions */}
      {enableRowSelection && bulkActions.length > 0 && selectedRowData.length > 0 && (
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm">
              Bulk Actions ({selectedRowData.length} selected)
            </CardTitle>
          </CardHeader>
          <CardContent className="pt-0">
            <div className="flex flex-wrap gap-2">
              {bulkActions.map((action, index) => (
                <Button
                  key={index}
                  size="sm"
                  variant={action.variant || 'outline'}
                  onClick={() => action.onClick(selectedRowData)}
                  disabled={action.disabled ? action.disabled(selectedRowData) : false}
                >
                  {action.icon}
                  {action.label}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Search and Filters */}
      <div className="flex flex-col space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2 flex-1">
            <Input
              placeholder={searchPlaceholder}
              value={filters.search || ''}
              onChange={(event) => onGlobalFilterChange(event.target.value)}
              className="max-w-sm"
            />
            {filterConfigs.length > 0 && (
              <Button
                variant="outline"
                size="sm"
                onClick={() => setShowFilters(!showFilters)}
              >
                <Filter className="h-4 w-4 mr-2" />
                Filters
                {Object.values(filters).filter(Boolean).length > 0 && (
                  <Badge variant="secondary" className="ml-2 h-5 w-5 rounded-full p-0 text-xs">
                    {Object.values(filters).filter(Boolean).length}
                  </Badge>
                )}
              </Button>
            )}
          </div>
          <div className="text-sm text-muted-foreground">
            Showing {data.length} of {total} results
          </div>
        </div>

        {showFilters && filterConfigs.length > 0 && (
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4 p-4 border rounded-lg bg-muted/20">
            {filterConfigs.map((config) => (
              <div key={config.key} className="space-y-2">
                <label className="text-sm font-medium">{config.label}</label>
                <Select
                  value={filters[config.key] || 'all'}
                  onValueChange={(value) => onFilterChange(config.key, value)}
                >
                  <SelectTrigger>
                    <SelectValue placeholder={config.placeholder} />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All {config.label}</SelectItem>
                    {config.options.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Table */}
      <div className="rounded-md border max-h-[70vh] overflow-auto" ref={tableContainerRef}>
        <Table style={virtualizeRows ? { position: 'relative' } : undefined}>
          <TableHeader>
            {table.getHeaderGroups().map((headerGroup) => (
              <TableRow key={headerGroup.id}>
                {headerGroup.headers.map((header) => (
                  <TableHead key={header.id}>
                    {header.isPlaceholder
                      ? null
                      : flexRender(
                          header.column.columnDef.header,
                          header.getContext()
                        )}
                  </TableHead>
                ))}
              </TableRow>
            ))}
          </TableHeader>
          <TableBody>
            {table.getRowModel().rows?.length ? (
              virtualizeRows && rowVirtualizer ? (
                <>
                  <tr>
                    <td style={{ height: rowVirtualizer.getTotalSize() }} />
                  </tr>
                  {rowVirtualizer.getVirtualItems().map((virtualRow) => {
                    const row = table.getRowModel().rows[virtualRow.index];
                    return (
                      <TableRow
                        key={row.id}
                        data-index={virtualRow.key}
                        data-state={row.getIsSelected() && "selected"}
                        className={onRowClick ? "cursor-pointer hover:bg-muted/50" : ""}
                        onClick={() => onRowClick && onRowClick(row)}
                        style={{
                          position: 'absolute',
                          top: 0,
                          left: 0,
                          width: '100%',
                          transform: `translateY(${virtualRow.start}px)`,
                        }}
                      >
                        {row.getVisibleCells().map((cell) => (
                          <TableCell key={cell.id}>
                            {flexRender(cell.column.columnDef.cell, cell.getContext())}
                          </TableCell>
                        ))}
                      </TableRow>
                    );
                  })}
                </>
              ) : (
                table.getRowModel().rows.map((row) => (
                  <TableRow
                    key={row.id}
                    data-state={row.getIsSelected() && "selected"}
                    className={onRowClick ? "cursor-pointer hover:bg-muted/50" : ""}
                    onClick={() => onRowClick && onRowClick(row)}
                  >
                    {row.getVisibleCells().map((cell) => (
                      <TableCell key={cell.id}>
                        {flexRender(cell.column.columnDef.cell, cell.getContext())}
                      </TableCell>
                    ))}
                  </TableRow>
                ))
              )
            ) : (
              <TableRow>
                <TableCell colSpan={columns.length} className="h-24 text-center">
                  {emptyStateMessage}
                </TableCell>
              </TableRow>
            )}
          </TableBody>
        </Table>
      </div>

      {/* Pagination */}
      {pageCount > 1 && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            Page {pageIndex + 1} of {pageCount}
          </div>
          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => onPaginationChange(Math.max(0, pageIndex - 1), pageSize)}
              disabled={pageIndex === 0}
            >
              <ChevronLeft className="h-4 w-4 mr-2" />
              Previous
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={() => onPaginationChange(Math.min(pageCount - 1, pageIndex + 1), pageSize)}
              disabled={pageIndex >= pageCount - 1}
            >
              Next
              <ChevronRight className="h-4 w-4 ml-2" />
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useRef, useEffect, useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Tooltip, TooltipContent, TooltipTrigger } from '@/components/ui/tooltip';

interface FunnelStage {
  name: string;
  value: number;
  color: string;
  conversionRate?: number;
}

interface FunnelChartProps {
  data: FunnelStage[];
  className?: string;
}

export function ResponsiveFunnelChart({ data, className = '' }: FunnelChartProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const [dimensions, setDimensions] = useState({ width: 600, height: 400 });

  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        const rect = containerRef.current.getBoundingClientRect();
        setDimensions({
          width: rect.width,
          height: Math.max(400, rect.height)
        });
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  if (!data || data.length === 0) {
    return (
      <div className={`flex items-center justify-center h-96 ${className}`}>
        <p className="text-muted-foreground">No data available</p>
      </div>
    );
  }

  const { width, height } = dimensions;
  
  // Calculate the maximum value for scaling
  const maxValue = Math.max(...data.map(d => d.value));
  
  // Calculate funnel dimensions
  const funnelWidth = width * 0.8;
  const funnelHeight = height * 0.7;
  const stageHeight = funnelHeight / data.length;
  const centerX = width / 2;
  
  // Calculate conversion rates
  const stagesWithRates = data.map((stage, index) => {
    const nextStage = data[index + 1];
    const conversionRate = nextStage ? ((nextStage.value / stage.value) * 100) : null;
    return { ...stage, conversionRate };
  });

  return (
    <div ref={containerRef} className={`w-full h-96 ${className}`}>
      <svg width={width} height={height} className="overflow-visible">
        {stagesWithRates.map((stage, index) => {
          // Calculate funnel shape for this stage
          const progress = index / (data.length - 1);
          const stageWidth = funnelWidth * (1 - progress * 0.6); // Funnel gets narrower
          const y = (height - funnelHeight) / 2 + index * stageHeight;
          
          return (
            <g key={stage.name}>
              {/* Funnel segment */}
              <Tooltip>
                <TooltipTrigger asChild>
                  <rect
                    x={centerX - stageWidth / 2}
                    y={y}
                    width={stageWidth}
                    height={stageHeight - 4}
                    rx={8}
                    fill={stage.color}
                    fillOpacity="0.8"
                    className="cursor-pointer transition-all hover:opacity-100"
                    stroke="currentColor"
                    strokeWidth="1"
                    strokeOpacity="0.2"
                  />
                </TooltipTrigger>
                <TooltipContent>
                  <div className="space-y-1">
                    <p className="font-semibold">{stage.name}</p>
                    <p>Count: {stage.value.toLocaleString()}</p>
                    {stage.conversionRate !== null && (
                      <p>Conversion: {stage.conversionRate.toFixed(1)}%</p>
                    )}
                  </div>
                </TooltipContent>
              </Tooltip>
              
              {/* Stage label */}
              <text
                x={centerX}
                y={y + stageHeight / 2 + 4}
                textAnchor="middle"
                className="text-xs font-medium fill-foreground"
              >
                {stage.name}
              </text>
              
              {/* Value label */}
              <text
                x={centerX}
                y={y + stageHeight / 2 - 8}
                textAnchor="middle"
                className="text-sm font-bold fill-foreground"
              >
                {stage.value.toLocaleString()}
              </text>
              
              {/* Conversion rate indicator */}
              {stage.conversionRate !== null && (
                <g>
                  <line
                    x1={centerX + stageWidth / 2 + 10}
                    y1={y + stageHeight / 2}
                    x2={centerX + stageWidth / 2 + 30}
                    y2={y + stageHeight / 2}
                    stroke="currentColor"
                    strokeWidth="2"
                    strokeOpacity="0.4"
                  />
                  <text
                    x={centerX + stageWidth / 2 + 35}
                    y={y + stageHeight / 2 + 4}
                    className="text-xs fill-muted-foreground"
                  >
                    {stage.conversionRate.toFixed(1)}%
                  </text>
                </g>
              )}
            </g>
          );
        })}
      </svg>
      
      {/* Legend */}
      <div className="absolute bottom-0 left-0 right-0 flex justify-center gap-4 text-xs text-muted-foreground">
        {data.map((stage, index) => (
          <div key={stage.name} className="flex items-center gap-1">
            <div 
              className="w-3 h-3 rounded"
              style={{ backgroundColor: stage.color }}
            />
            <span>{stage.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

