}

// Simple custom calendar component
const SimpleCalendar = ({ selected, onSelect }: { selected?: Date; onSelect: (date: Date) => void }) => {
  const [currentDate, setCurrentDate] = useState(selected || new Date());
  
  const today = new Date();
  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();
  
  const firstDay = new Date(year, month, 1);
  const lastDay = new Date(year, month + 1, 0);
  const startDate = new Date(firstDay);
  startDate.setDate(startDate.getDate() - firstDay.getDay());
  
  const days = [];
  const current = new Date(startDate);
  
  // Generate 42 days (6 weeks)
  for (let i = 0; i < 42; i++) {
    days.push(new Date(current));
    current.setDate(current.getDate() + 1);
  }
  
  const monthNames = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];
  
  const dayNames = ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'];
  
  const goToPrevMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
  };
  
  const goToNextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
  };
  
  return (
    <div className="p-3 w-64">
      {/* Header */}
      <div className="flex justify-center items-center mb-4 relative">
        <Button
          variant="outline"
          size="sm"
          className="absolute left-0 h-7 w-7 p-0"
          onClick={goToPrevMonth}
        >
          <ChevronLeft className="h-4 w-4" />
        </Button>
        <div className="text-sm font-medium">
          {monthNames[month]} {year}
        </div>
        <Button
          variant="outline"
          size="sm"
          className="absolute right-0 h-7 w-7 p-0"
          onClick={goToNextMonth}
        >
          <ChevronRight className="h-4 w-4" />
        </Button>
      </div>
      
      {/* Day headers */}
      <div className="grid grid-cols-7 gap-1 mb-2">
        {dayNames.map((day) => (
          <div key={day} className="text-center text-xs font-medium text-muted-foreground p-2">
            {day}
          </div>
        ))}
      </div>
      
      {/* Calendar grid */}
      <div className="grid grid-cols-7 gap-1">
        {days.map((day, index) => {
          const isToday = day.toDateString() === today.toDateString();
          const isSelected = selected && day.toDateString() === selected.toDateString();
          const isCurrentMonth = day.getMonth() === month;
          
          return (
            <Button
              key={index}
              variant="ghost"
              className={cn(
                "h-8 w-8 p-0 text-xs",
                isToday && "bg-accent text-accent-foreground",
                isSelected && "bg-primary text-primary-foreground",
                !isCurrentMonth && "text-muted-foreground opacity-50"
              )}
              onClick={() => onSelect(day)}
            >
              {day.getDate()}
            </Button>
          );
        })}
      </div>
    </div>
  );
};

export function PipelineEditableCell({
  value,
  item,
  field,
  isSelect = false,
  isTextarea = false,
  isNumber = false,
  isDate = false,
  options = [],
  className = '',
  placeholder = '',
}: PipelineEditableCellProps) {
  const [isEditing, setIsEditing] = useState(false);
  const [tempValue, setTempValue] = useState(value || '');
  const [showEditorialConfirm, setShowEditorialConfirm] = useState(false);
  const [pendingUpdate, setPendingUpdate] = useState<any>(null);
  const { mutate: updatePipelineItem } = useUpdatePipelineItem();
  const { mutate: createActivityLog } = useCreateActivityLog();

  useEffect(() => {
    setTempValue(value || '');
  }, [value]);

  const handleSave = () => {
    if (tempValue === value) {
      setIsEditing(false);
      return;
    }

    let processedValue = tempValue;
    if (isDate && tempValue) {
      // Convert date string to ISO string for storage
      processedValue = new Date(tempValue).toISOString();
    } else if (isNumber) {
      processedValue = Number(tempValue) || null;
    } else {
      processedValue = tempValue || null;
    }

    const updatedItem = {
      ...item,
      [field]: processedValue,
    };

    // Check if this is a status update that should trigger editorial sync confirmation
    if (field === 'status' && shouldSyncToEditorial(value || '', tempValue)) {
      setPendingUpdate(updatedItem);
      setShowEditorialConfirm(true);
      return;
    }

    // Proceed with normal update
    performUpdate(updatedItem);
  };

  const performUpdate = (updatedItem: any) => {
    updatePipelineItem(updatedItem, {
      onSuccess: () => {
        setIsEditing(false);
        setPendingUpdate(null);
        
        // Log the activity
        createActivityLog({
          bdr: item.bdr,
          activityType: 'Note_Added',
          description: `Updated ${field}: ${value || 'empty'} â†’ ${updatedItem[field] || 'empty'}`,
          pipelineItemId: item.id,
          notes: `Field ${field} updated`
        });
      },
      onError: (error) => {
        toast.error(`Failed to update ${field}`);
        setTempValue(value || '');
        setPendingUpdate(null);
      }
    });
  };

  const handleCancel = () => {
    setTempValue(value || '');
    setIsEditing(false);
    setPendingUpdate(null);
  };

  const handleEditorialConfirm = async () => {
    if (!pendingUpdate) return;
    
    // Update the pipeline item first
    performUpdate(pendingUpdate);
    setShowEditorialConfirm(false);

    // Then sync to editorial board
    try {
      const response = await fetch('/api/editorial/sync', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pipelineItemId: item.id,
        }),
      });

      if (response.ok) {
        toast.success('Pipeline item synced to editorial board');
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to sync to editorial board');
      }
    } catch (error) {
      toast.error('Failed to sync to editorial board');
    }
  };

  const handleEditorialDecline = () => {
    if (!pendingUpdate) return;
    
    // Update the pipeline item without syncing to editorial
    performUpdate(pendingUpdate);
    setShowEditorialConfirm(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !isTextarea) {
      e.preventDefault();
      handleSave();
    } else if (e.key === 'Escape') {
      handleCancel();
    }
  };

  if (isEditing) {
    return (
      <div className={`flex items-center gap-1 ${className}`} onClick={(e) => e.stopPropagation()}>
        {isSelect ? (
          <Select value={tempValue} onValueChange={setTempValue}>
            <SelectTrigger className="h-6 text-xs">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              {options.map((option) => (
                <SelectItem key={option} value={option}>
                  {option}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        ) : isTextarea ? (
          <Textarea
            value={tempValue}
            onChange={(e) => setTempValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className="min-h-[40px] text-xs"
            autoFocus
          />
        ) : (
          <Input
            type={isNumber ? 'number' : 'text'}
            value={tempValue}
            onChange={(e) => setTempValue(e.target.value)}
            onKeyDown={handleKeyDown}
            className="h-6 text-xs"
            placeholder={placeholder}
            autoFocus
          />
        )}
        <Button
          variant="ghost"
          size="sm"
          className="h-5 w-5 p-0"
          onClick={handleSave}
        >
          <Check className="h-3 w-3" />
        </Button>
        <Button
          variant="ghost"
          size="sm"
          className="h-5 w-5 p-0"
          onClick={handleCancel}
        >
          <X className="h-3 w-3" />
        </Button>
      </div>
    );
  }

  return (
    <>
      <div
        className={`group cursor-pointer hover:bg-muted/50 rounded px-1 py-0.5 flex items-center gap-1 ${className}`}
        onClick={(e) => e.stopPropagation()}
      >
        {isDate ? (
          <Popover>
            <PopoverTrigger asChild>
              <Button
                variant="ghost"
                className={cn(
                  "h-auto p-0 text-xs justify-start text-left font-normal hover:bg-transparent",
                  !value && "text-muted-foreground"
                )}
              >
                {value ? (
                  formatDateUKShort(value)
                ) : (
                  <span>{placeholder || 'Click to set date'}</span>
                )}
                <Edit className="h-3 w-3 opacity-0 group-hover:opacity-50 transition-opacity ml-1" />
              </Button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0" align="start" onClick={(e) => e.stopPropagation()}>
              <SimpleCalendar
                selected={value ? new Date(value) : undefined}
                onSelect={(date) => {
                  if (date) {
                    const dateString = date.toISOString();
                    setTempValue(dateString);
                    // Update the item directly without going through the editing state
                    const updatedItem = {
                      ...item,
                      [field]: dateString,
                    };
                    updatePipelineItem(updatedItem, {
                      onSuccess: () => {
                        // Log the activity
                        createActivityLog({
                          bdr: item.bdr,
                          activityType: 'Note_Added',
                          description: `Updated ${field}: ${value || 'empty'} â†’ ${dateString}`,
                          pipelineItemId: item.id,
                          notes: `Field ${field} updated`
                        });
                      },
                      onError: (error) => {
                        toast.error(`Failed to update ${field}`);
                      }
                    });
                  }
                }}
              />
            </PopoverContent>
          </Popover>
        ) : (
          <div
            onClick={(e) => {
              e.stopPropagation();
              setIsEditing(true);
            }}
          >
            <span className="text-xs truncate">
              {value || placeholder || 'Click to edit'}
            </span>
            <Edit className="h-3 w-3 opacity-0 group-hover:opacity-50 transition-opacity" />
          </div>
        )}
      </div>

      <ConfirmDialog
        open={showEditorialConfirm}
        title="Add to Editorial Board?"
        description={`This will update the status to "${tempValue}" and add this item to the editorial board. Do you want to proceed?`}
        confirmLabel="Yes, Add to Editorial"
        cancelLabel="Just Update Status"
        onConfirm={handleEditorialConfirm}
        onOpenChange={(open) => {
          if (!open) {
            handleEditorialDecline();
          }
        }}
      />
    </>
  );
}

'use client'

import React, { useCallback, useEffect, useRef, useState } from 'react'
import { Button } from './button'
import { cn } from '@/lib/utils'
import { sanitizeEmailHtml } from '@/lib/html'
import { Bold, Italic, Underline, List, ListOrdered, Link as LinkIcon, Heading1, Heading2, Heading3, Undo2, Redo2, Eraser } from 'lucide-react'

type RichTextEditorProps = {
  value: string
  onChange: (html: string) => void
  placeholder?: string
  className?: string
  heightClassName?: string
}

export function RichTextEditor({ value, onChange, placeholder, className, heightClassName }: RichTextEditorProps) {
  const [internal, setInternal] = useState<string>(value || '')
  const ref = useRef<HTMLDivElement | null>(null)

  useEffect(() => {
    setInternal(value || '')
  }, [value])

  const focusEditor = useCallback(() => {
    if (!ref.current) return
    ref.current.focus()
    const selection = window.getSelection()
    if (selection && selection.rangeCount === 0) {
      const range = document.createRange()
      range.selectNodeContents(ref.current)
      range.collapse(false)
      selection.removeAllRanges()
      selection.addRange(range)
    }
  }, [])

  const exec = useCallback((command: string, valueArg?: string) => {
    focusEditor()
    // eslint-disable-next-line deprecation/deprecation
    document.execCommand(command, false, valueArg)
  }, [focusEditor])

  const handleInput = useCallback(() => {
    if (!ref.current) return
    // Do NOT sanitize on every keystroke; it interferes with caret movement and backspace.
    const html = ref.current.innerHTML
    setInternal(html)
    onChange(html)
  }, [onChange])

  const plainTextToHtml = useCallback((text: string) => {
    const normalized = text
      .replace(/\r/g, '')
      .replace(/[\t\u00A0]+/g, ' ') // tabs & nbsp -> space
      .trim()
    const paragraphs = normalized
      .split(/\n{2,}/)
      .map(p => `<p>${p.replace(/\n/g, '<br>')}</p>`)
      .join('')
    return paragraphs || ''
  }, [])

  const handlePaste = useCallback((e: React.ClipboardEvent<HTMLDivElement>) => {
    e.preventDefault()
    const fromHtml = e.clipboardData.getData('text/html')
    const fromText = e.clipboardData.getData('text/plain')
    const rawHtml = fromHtml && fromHtml.length > 0 ? fromHtml : plainTextToHtml(fromText)
    const sanitized = sanitizeEmailHtml(rawHtml)
    // eslint-disable-next-line deprecation/deprecation
    document.execCommand('insertHTML', false, sanitized)
    setTimeout(handleInput, 0)
  }, [handleInput, plainTextToHtml])

  const handleBlur = useCallback(() => {
    if (!ref.current) return
    const sanitized = sanitizeEmailHtml(ref.current.innerHTML)
    // Only update if changed to avoid unnecessary selection resets
    if (sanitized !== internal) {
      ref.current.innerHTML = sanitized
      setInternal(sanitized)
      onChange(sanitized)
    }
  }, [internal, onChange])

  return (
    <div className={cn('w-full', className)}>
      <div className="flex flex-wrap items-center gap-1 mb-2 sticky top-0 bg-background/80 backdrop-blur z-10 py-1">
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('bold')} title="Bold"><Bold className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('italic')} title="Italic"><Italic className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('underline')} title="Underline"><Underline className="h-4 w-4" /></Button>
        <span className="mx-1" />
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('insertUnorderedList')} title="Bulleted list"><List className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('insertOrderedList')} title="Numbered list"><ListOrdered className="h-4 w-4" /></Button>
        <span className="mx-1" />
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('formatBlock', 'h1')} title="Heading 1"><Heading1 className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('formatBlock', 'h2')} title="Heading 2"><Heading2 className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('formatBlock', 'h3')} title="Heading 3"><Heading3 className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => {
          const url = window.prompt('Enter URL') || ''
          if (url) exec('createLink', url)
        }} title="Insert link"><LinkIcon className="h-4 w-4" /></Button>
        <span className="mx-1" />
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('undo')} title="Undo"><Undo2 className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('redo')} title="Redo"><Redo2 className="h-4 w-4" /></Button>
        <Button type="button" size="icon" variant="outline" className="h-8 w-8" onClick={() => exec('removeFormat')} title="Clear formatting"><Eraser className="h-4 w-4" /></Button>
      </div>
      <div
        ref={ref}
        contentEditable
        suppressContentEditableWarning
        className={cn(
          'rounded-md border border-input bg-background px-3 py-2 text-sm focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 min-h-[200px] max-h-[60vh] overflow-auto break-words',
          heightClassName
        )}
        data-placeholder={placeholder || ''}
        onInput={handleInput}
        onPaste={handlePaste}
        onBlur={handleBlur}
        dangerouslySetInnerHTML={{ __html: internal }}
      />
    </div>
  )
}

export default RichTextEditor

'use client';

import { useMutation, useQueryClient } from '@tanstack/react-query';
import { toast } from 'sonner';

// Generic API mutation hook
export function useApiMutation<TData = any, TVariables = any>(
  endpoint: string,
  method: 'POST' | 'PUT' | 'DELETE' = 'POST',
  options: {
    invalidateKeys?: string[];
    successMessage?: string;
    errorMessage?: string;
    onSuccess?: (data: TData) => void;
    onError?: (error: Error) => void;
  } = {}
) {
  const queryClient = useQueryClient();

  return useMutation<TData, Error, TVariables>({
    mutationFn: async (variables: TVariables) => {
      const response = await fetch(endpoint, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: method !== 'DELETE' ? JSON.stringify(variables) : undefined,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: (data) => {
      // Invalidate specified query keys
      if (options.invalidateKeys) {
        options.invalidateKeys.forEach(key => {
          queryClient.invalidateQueries({ queryKey: [key] });
        });
      }

      // Show success message
      if (options.successMessage) {
        toast.success(options.successMessage);
      }

      // Call custom success handler
      options.onSuccess?.(data);
    },
    onError: (error) => {
      console.error('API mutation failed:', error);
      
      // Show error message
      const errorMessage = options.errorMessage || error.message || 'Operation failed';
      toast.error(errorMessage);

      // Call custom error handler
      options.onError?.(error);
    },
  });
}

