export function calculateTeamPerformance(pipelineItems: any[], activityLogs: any[], financeEntries: any[] = []) {
  // Get unique BDRs from the provided data
  const bdrSet = new Set<string>();
  pipelineItems.forEach(item => item.bdr?.name && bdrSet.add(item.bdr.name));
  activityLogs.forEach(log => log.bdr?.name && bdrSet.add(log.bdr.name));
  financeEntries.forEach(entry => entry.bdr?.name && bdrSet.add(entry.bdr.name));
  
  const allBDRs = Array.from(bdrSet);
  
  const activeBDRs = allBDRs.filter(bdr => {
    const recentActivity = activityLogs.filter(log => 
      log.bdr?.name === bdr && 
      log.timestamp >= subDays(new Date(), 7)
    );
    return recentActivity.length > 0;
  });
  
  // Calculate individual BDR performance
  const bdrPerformance = allBDRs.map(bdr => {
    const bdrItems = pipelineItems.filter(item => item.bdr?.name === bdr);
    const bdrActivities = activityLogs.filter(log => log.bdr?.name === bdr);
    const bdrFinanceEntries = financeEntries.filter(entry => entry.bdr?.name === bdr);
    
    // Use enhanced call completion logic for BDR performance
    const allCallCompletions = getAllCallCompletions(pipelineItems, activityLogs, new Date(0), new Date());
    const bdrCallCompletions = allCallCompletions.filter(completion => completion.bdr?.name === bdr);
    const calls = bdrCallCompletions.length;
    
    const agreements = bdrActivities.filter(log => log.activityType === 'Agreement_Sent').length;
    const lists = bdrActivities.filter(log => log.activityType === 'Partner_List_Sent').length;
    // Count all sales generated by this BDR
    const sales = bdrFinanceEntries.length;
    
    const score = calls * 1 + agreements * 3 + lists * 2 + sales * 5;
    
    return { bdr, score, calls, agreements, lists, sales };
  });
  
  // Sort by performance score
  bdrPerformance.sort((a, b) => b.score - a.score);
  
  const topPerformers = bdrPerformance.slice(0, 3).map(p => p.bdr);
  const needsSupport = bdrPerformance.filter(p => p.score < 10 && p.calls < 5).map(p => p.bdr);
  
  // Calculate benchmark metrics using enhanced call completion logic
  const allCallCompletions = getAllCallCompletions(pipelineItems, activityLogs, new Date(0), new Date());
  const totalCalls = allCallCompletions.length;
  const totalAgreements = activityLogs.filter(log => log.activityType === 'Agreement_Sent').length;
  const totalLists = activityLogs.filter(log => log.activityType === 'Partner_List_Sent').length;
  // Count all sales generated
  const totalSales = financeEntries.length;
  
  return {
    totalBDRs: allBDRs.length,
    activeBDRs: activeBDRs.length,
    topPerformers,
    needsSupport,
    benchmarkMetrics: {
      avgCallsPerWeek: allBDRs.length > 0 ? Math.round((totalCalls / allBDRs.length) * 10) / 10 : 0,
      avgAgreementsPerMonth: allBDRs.length > 0 ? Math.round((totalAgreements / allBDRs.length) * 10) / 10 : 0,
      avgListsPerMonth: allBDRs.length > 0 ? Math.round((totalLists / allBDRs.length) * 10) / 10 : 0,
      teamConversionRate: totalCalls > 0 ? Math.round((totalSales / totalCalls) * 100 * 100) / 100 : 0
    }
  };
}

export function assessPipelineHealth(pipelineItems: any[], activityLogs: any[], currentDate: Date, financeEntries: any[] = []) {
  const now = currentDate;
  const nextWeekEnd = endOfWeek(addDays(now, 7), { weekStartsOn: 1 });
  const next2WeeksEnd = endOfWeek(addDays(now, 14), { weekStartsOn: 1 });
  
  // Upcoming calls
  const upcomingCallsNextWeek = pipelineItems.filter(item => 
    item.callDate && item.callDate > now && item.callDate <= nextWeekEnd
  ).length;
  
  const upcomingCallsNext2Weeks = pipelineItems.filter(item => 
    item.callDate && item.callDate > nextWeekEnd && item.callDate <= next2WeeksEnd
  ).length;
  
  const totalUpcomingCalls = pipelineItems.filter(item => 
    item.callDate && item.callDate > now
  ).length;
  
  // Pending agreements
  const proposalsAwaitingResponse = pipelineItems.filter(item => 
    item.status && item.status.includes('Proposal') && !item.status.includes('Agreement')
  ).length;
  
  const agreementsAwaitingLists = pipelineItems.filter(item => 
    item.status && item.status.includes('Agreement') && !item.partnerListSentDate
  ).length;
  
  const overduePartnerLists = pipelineItems.filter(item => 
    item.expectedCloseDate && item.expectedCloseDate < now && 
    !item.partnerListSentDate && item.status && item.status.includes('Agreement')
  ).length;
  
  // Active lists out
  const activeListsTotal = pipelineItems.filter(item => 
    item.partnerListSentDate && 
    !['Sold', 'List Out - Not Sold', 'Free Q&A Offered'].includes(item.status || '')
  ).length;
  
  const activeLists = pipelineItems.filter(item => 
    item.partnerListSentDate && item.partnerListSize &&
    !['Sold', 'List Out - Not Sold', 'Free Q&A Offered'].includes(item.status || '')
  );
  
  const smallLists = activeLists.filter(item => item.partnerListSize! >= 3 && item.partnerListSize! <= 8).length;
  const mediumLists = activeLists.filter(item => item.partnerListSize! >= 9 && item.partnerListSize! <= 15).length;
  const largeLists = activeLists.filter(item => item.partnerListSize! >= 16).length;
  
  const averageListSize = activeLists.length > 0 ? 
    activeLists.reduce((sum, item) => sum + (item.partnerListSize || 0), 0) / activeLists.length : 0;
  
  // Conversion funnel - use enhanced call completion logic
  const callsBooked = pipelineItems.filter(item => 
    item.category === 'Calls' && item.status === 'Call Booked'
  ).length;
  
  const allCallCompletions = getAllCallCompletions(pipelineItems, activityLogs, new Date(0), new Date());
  const callsConducted = allCallCompletions.length;
  
  const proposalsSent = activityLogs.filter(log => 
    log.activityType === 'Proposal_Sent'
  ).length;
  
  const agreementsSigned = activityLogs.filter(log => 
    log.activityType === 'Agreement_Sent'
  ).length;
  
  const listsSent = activityLogs.filter(log => 
    log.activityType === 'Partner_List_Sent'
  ).length;
  
  // Count all sales generated, regardless of payment status
  const salesGenerated = financeEntries.length;
  
  return {
    upcomingCalls: {
      nextWeek: upcomingCallsNextWeek,
      next2Weeks: upcomingCallsNext2Weeks,
      total: totalUpcomingCalls
    },
    pendingAgreements: {
      proposalsAwaitingResponse,
      agreementsAwaitingLists,
      overduePartnerLists
    },
    activeListsOut: {
      total: activeListsTotal,
      smallLists,
      mediumLists,
      largeLists,
      averageListSize: Math.round(averageListSize * 10) / 10
    },
    conversionFunnel: {
      callsBooked,
      callsConducted,
      proposalsSent,
      agreementsSigned,
      listsSent,
      salesGenerated
    }
  };
}

export function calculateTrends(pipelineItems: any[], activityLogs: any[], currentDate: Date, financeEntries: any[] = []) {
  const now = currentDate;
  
  // Weekly call volume trend (last 4 weeks) - use enhanced call completion logic
  const weeklyCallVolume = [];
  for (let i = 3; i >= 0; i--) {
    const weekStart = startOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
    const weekEnd = endOfWeek(subWeeks(now, i), { weekStartsOn: 1 });
    
    const callCompletions = getAllCallCompletions(pipelineItems, activityLogs, weekStart, weekEnd);
    const calls = callCompletions.length;
    
    const target = 40; // Target calls per week
    const variance = ((calls - target) / target) * 100;
    
    weeklyCallVolume.push({
      week: format(weekStart, 'MMM dd'),
      calls,
      target,
      variance: Math.round(variance)
    });
  }
  
  // Monthly agreements trend (last 4 months)
  const monthlyAgreements = [];
  for (let i = 3; i >= 0; i--) {
    const monthStart = startOfMonth(subMonths(now, i));
    const monthEnd = endOfMonth(subMonths(now, i));
    
    const agreements = activityLogs.filter(log => 
      log.activityType === 'Agreement_Sent' && 
      log.timestamp >= monthStart && log.timestamp <= monthEnd
    ).length;
    
    const target = 20; // Target agreements per month
    const variance = ((agreements - target) / target) * 100;
    
    monthlyAgreements.push({
      month: format(monthStart, 'MMM yyyy'),
      agreements,
      target,
      variance: Math.round(variance)
    });
  }
  
  // Quarterly lists out trend (current and previous quarters) - use finance entries for sales data
  const quarterlyListsOut = [];
  for (let i = 1; i >= 0; i--) {
    const quarterStart = startOfQuarter(subMonths(now, i * 3));
    const quarterEnd = endOfQuarter(subMonths(now, i * 3));
    
    const lists = activityLogs.filter(log => 
      log.activityType === 'Partner_List_Sent' && 
      log.timestamp >= quarterStart && log.timestamp <= quarterEnd
    ).length;
    
    // Count all sales generated in the quarter
    const quarterSales = financeEntries.filter(entry => 
      entry.createdAt >= quarterStart && entry.createdAt <= quarterEnd
    );
    const conversions = quarterSales.length;
    
    const revenue = quarterSales.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
    
    quarterlyListsOut.push({
      quarter: `Q${Math.floor((quarterStart.getMonth()) / 3) + 1} ${quarterStart.getFullYear()}`,
      lists,
      conversions,
      revenue
    });
  }
  
  return {
    weeklyCallVolume,
    monthlyAgreements,
    quarterlyListsOut
  };
}

export function identifyCriticalActions(pipelineItems: any[], activityLogs: any[], teamPerformance: any, currentDate: Date): Array<{
  priority: 'urgent' | 'high' | 'medium';
  category: 'calls' | 'agreements' | 'lists' | 'team';
  action: string;
  assignedTo?: string;
  metric?: number;
  deadline?: string;
}> {
  const actions: Array<{
    priority: 'urgent' | 'high' | 'medium';
    category: 'calls' | 'agreements' | 'lists' | 'team';
    action: string;
    assignedTo?: string;
    metric?: number;
    deadline?: string;
  }> = [];
  const now = currentDate;
  
  // Check for overdue partner lists
  const overdueCount = pipelineItems.filter(item => 
    item.expectedCloseDate && item.expectedCloseDate < now && 
    !item.partnerListSentDate && item.status && item.status.includes('Agreement')
  ).length;
  
  if (overdueCount > 0) {
    actions.push({
      priority: 'urgent' as const,
      category: 'lists' as const,
      action: `Send ${overdueCount} overdue partner lists immediately`,
      metric: overdueCount,
      deadline: 'Today'
    });
  }
  
  // Check for low call volume - use enhanced call completion logic
  const thisWeekCallCompletions = getAllCallCompletions(pipelineItems, activityLogs, startOfWeek(now, { weekStartsOn: 1 }), now);
  const thisWeekCalls = thisWeekCallCompletions.length;
  
  if (thisWeekCalls < 25) {
    actions.push({
      priority: 'high' as const,
      category: 'calls' as const,
      action: 'Boost call volume - current week significantly below target',
      metric: thisWeekCalls,
      deadline: 'End of week'
    });
  }
  
  // Check for team members needing support
  if (teamPerformance.needsSupport.length > 0) {
    actions.push({
      priority: 'medium' as const,
      category: 'team' as const,
      action: `Provide support to underperforming BDRs: ${teamPerformance.needsSupport.join(', ')}`,
      metric: teamPerformance.needsSupport.length,
      deadline: 'This week'
    });
  }
  
  // Check for upcoming calls without enough pipeline
  const upcomingCalls = pipelineItems.filter(item => 
    item.callDate && item.callDate > now && item.callDate <= addDays(now, 7)
  ).length;
  
  if (upcomingCalls < 30) {
    actions.push({
      priority: 'high' as const,
      category: 'calls' as const,
      action: 'Schedule more calls for next week to maintain pipeline',
      metric: upcomingCalls,
      deadline: 'End of week'
    });
  }
  
  return actions.sort((a, b) => {
    const priorityOrder = { urgent: 3, high: 2, medium: 1 };
    return priorityOrder[b.priority] - priorityOrder[a.priority];
  });
}

export function calculateFinancialSummary(pipelineItems: any[], activityLogs: any[], currentDate: Date, financeEntries: any[] = []) {
  const now = currentDate;
  const thisMonthStart = startOfMonth(now);
  const thisQuarterStart = startOfQuarter(now);
  
  // Use all finance entries for revenue calculations
  const monthlyRevenue = financeEntries
    .filter(entry => entry.createdAt >= thisMonthStart)
    .reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
  
  const quarterlyRevenue = financeEntries
    .filter(entry => entry.createdAt >= thisQuarterStart)
    .reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
  
  const totalRevenue = financeEntries.reduce((sum, entry) => sum + (entry.gbpAmount || 0), 0);
  
  const activeBDRs = Array.from(new Set(pipelineItems.map(item => item.bdr).filter(Boolean))).length;
  
  // Use enhanced call completion logic for financial metrics
  const allCallCompletions = getAllCallCompletions(pipelineItems, activityLogs, new Date(0), new Date());
  const totalCalls = allCallCompletions.length;
  
  const totalLists = activityLogs.filter(log => log.activityType === 'Partner_List_Sent').length;
  
  return {
    monthlyRevenue,
    quarterlyRevenue,
    revenuePerBDR: activeBDRs > 0 ? Math.round(totalRevenue / activeBDRs) : 0,
    revenuePerCall: totalCalls > 0 ? Math.round(totalRevenue / totalCalls) : 0,
    revenuePerList: totalLists > 0 ? Math.round(totalRevenue / totalLists) : 0,
    forecastAccuracy: 85 // Would require historical forecasting data to calculate
  };
}

export function generatePredictiveInsights(pipelineItems: any[], activityLogs: any[], trends: any, currentDate: Date) {
  const now = currentDate;
  
  // Simple predictions based on current trends
  const avgWeeklyCalls = trends.weeklyCallVolume.reduce((sum: number, week: any) => sum + week.calls, 0) / trends.weeklyCallVolume.length;
  const expectedCallsNextWeek = Math.round(avgWeeklyCalls * 1.1); // Slight optimistic projection
  
  const avgMonthlyAgreements = trends.monthlyAgreements.reduce((sum: number, month: any) => sum + month.agreements, 0) / trends.monthlyAgreements.length;
  const expectedAgreementsNextMonth = Math.round(avgMonthlyAgreements * 1.05);
  
  const avgQuarterlyRevenue = trends.quarterlyListsOut.reduce((sum: number, quarter: any) => sum + quarter.revenue, 0) / trends.quarterlyListsOut.length;
  const expectedRevenueNextQuarter = Math.round(avgQuarterlyRevenue * 1.1);
  
  // Risk factors
  const riskFactors = [];
  const lowCallWeeks = trends.weeklyCallVolume.filter((week: any) => week.calls < week.target * 0.8).length;
  if (lowCallWeeks > 1) {
    riskFactors.push('Declining call volume trend could impact future pipeline');
  }
  
  const upcomingCalls = pipelineItems.filter(item => 
    item.callDate && item.callDate > now && item.callDate <= addDays(now, 14)
  ).length;
  if (upcomingCalls < 40) {
    riskFactors.push('Insufficient upcoming calls scheduled for next 2 weeks');
  }
  
  // Opportunities
  const opportunities = [];
  const strongPerformers: { [key: string]: number } = {};
  const recentLogs = activityLogs.filter(log => log.timestamp >= subDays(now, 30));
  for (const log of recentLogs) {
    if (log.bdr) {
      strongPerformers[log.bdr] = (strongPerformers[log.bdr] || 0) + 1;
    }
  }
  
  const topBDR = Object.entries(strongPerformers).sort(([,a], [,b]) => b - a)[0];
  if (topBDR) {
    opportunities.push(`${topBDR[0]} showing strong activity - consider replicating their approach`);
  }
  
  return {
    expectedCallsNextWeek,
    expectedAgreementsNextMonth,
    expectedRevenueNextQuarter,
    riskFactors,
    opportunities
  };
}

import { startOfWeek, endOfWeek, startOfMonth, endOfMonth, subWeeks, subMonths, format, addDays, subDays } from 'date-fns';

/**
 * Call Analytics Module
 * Handles all call-related calculations and analysis
 */

// Type definitions for call analytics
export interface CallCompletion {
  id: number;
  bdr: string;
  timestamp: Date;
  pipelineItemId?: number;
  leadId?: number;
  previousStatus?: string;
  newStatus?: string;
  description: string;
  activityType: string;
  isAutomatic?: boolean;
}

export interface CallMetrics {
  total: number;
  byBdr: { [bdr: string]: number };
  byPeriod: { [period: string]: number };
  conversionRate: number;
  averageCallsPerBdr: number;
}

/**
 * Detects automatic call completions based on status transitions
 * A call is considered automatically completed when status changes from "Call Booked" to any valid completion status
 */
export function detectAutomaticCallCompletions(
  pipelineItems: any[], 
  activityLogs: any[], 
  startDate: Date, 
  endDate: Date
): CallCompletion[] {
  const completedCalls: CallCompletion[] = [];
  
  // Get all pipeline items that had status changes during the period
  const statusChangeLogs = activityLogs.filter(log => 
    log.activityType === 'Status_Change' &&
    log.timestamp >= startDate && 
    log.timestamp <= endDate &&
    log.previousStatus === 'Call Booked' &&
    log.newStatus && 
    log.newStatus !== 'Call Booked' &&
    !['no show', 'rescheduled', 'No Show', 'Rescheduled'].includes(log.newStatus.toLowerCase())
  );
  
  // For each status change from "Call Booked" to a valid completion status
  for (const log of statusChangeLogs) {
    completedCalls.push({
      id: log.id,
      bdr: log.bdr,
      timestamp: log.timestamp,
      pipelineItemId: log.pipelineItemId,
      leadId: log.leadId,
      previousStatus: log.previousStatus,
      newStatus: log.newStatus,
      description: `Automatic call completion: ${log.previousStatus} â†’ ${log.newStatus}`,
      activityType: 'Call_Completed',
      isAutomatic: true
    });
  }
  
  return completedCalls;
}

/**
 * Gets all call completions (both manual and automatic)
 * Combines manual call completion logs with detected automatic completions
 */
export function getAllCallCompletions(
  pipelineItems: any[], 
  activityLogs: any[], 
  startDate: Date, 
  endDate: Date
): CallCompletion[] {
  // Get manual call completions (existing logic)
  const manualCallCompletions = activityLogs.filter(log => 
    log.activityType === 'Call_Completed' && 
    log.timestamp >= startDate && log.timestamp <= endDate
  );
  
  // Get automatic call completions (new logic)
  const automaticCallCompletions = detectAutomaticCallCompletions(pipelineItems, activityLogs, startDate, endDate);
  
  // Combine both types of call completions
  const allCallCompletions = [...manualCallCompletions, ...automaticCallCompletions];
  
  // Remove duplicates based on pipelineItemId and timestamp (in case both manual and automatic exist)
  const uniqueCallCompletions = allCallCompletions.filter((completion, index, self) => 
    index === self.findIndex(c => 
      c.pipelineItemId === completion.pipelineItemId && 
      Math.abs(c.timestamp.getTime() - completion.timestamp.getTime()) < 60000 // Within 1 minute
    )
  );
  
  return uniqueCallCompletions;
}

/**
 * Calculates comprehensive call metrics
 */
export function calculateCallMetrics(
  pipelineItems: any[], 
  activityLogs: any[], 
  startDate: Date, 
  endDate: Date
): CallMetrics {
  const callCompletions = getAllCallCompletions(pipelineItems, activityLogs, startDate, endDate);
  
  // Count calls by BDR
  const callsByBdr: { [bdr: string]: number } = {};
  callCompletions.forEach(call => {
    if (call.bdr) {
      callsByBdr[call.bdr] = (callsByBdr[call.bdr] || 0) + 1;
    }
  });
  
  // Count calls by day/week/month
  const callsByPeriod: { [period: string]: number } = {};
  callCompletions.forEach(call => {
    const day = format(call.timestamp, 'yyyy-MM-dd');
    const week = format(startOfWeek(call.timestamp, { weekStartsOn: 1 }), 'yyyy-MM-dd');
    const month = format(call.timestamp, 'yyyy-MM');
    
    callsByPeriod[`day-${day}`] = (callsByPeriod[`day-${day}`] || 0) + 1;
    callsByPeriod[`week-${week}`] = (callsByPeriod[`week-${week}`] || 0) + 1;
    callsByPeriod[`month-${month}`] = (callsByPeriod[`month-${month}`] || 0) + 1;
  });
  
  // Calculate conversion rate (calls that led to agreements)
  const agreements = activityLogs.filter(log => 
    log.activityType === 'Agreement_Sent' && 
    log.timestamp >= startDate && 
    log.timestamp <= endDate
  );
  
  const conversionRate = callCompletions.length > 0 ? (agreements.length / callCompletions.length) * 100 : 0;
  
  // Calculate average calls per BDR
  const activeBdrs = Object.keys(callsByBdr).length;
  const averageCallsPerBdr = activeBdrs > 0 ? callCompletions.length / activeBdrs : 0;
  
  return {
    total: callCompletions.length,
    byBdr: callsByBdr,
    byPeriod: callsByPeriod,
    conversionRate,
    averageCallsPerBdr,
  };
}

/**
 * Calculates call volume trends over different time periods
 */
export function calculateCallVolumeTrends(
  pipelineItems: any[], 
  activityLogs: any[], 
  currentDate: Date
) {
  const now = currentDate;
  const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
  const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });
  const lastWeekStart = startOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
  const lastWeekEnd = endOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
  const thisMonthStart = startOfMonth(now);
  const thisMonthEnd = endOfMonth(now);
  const lastMonthStart = startOfMonth(subMonths(now, 1));
  const lastMonthEnd = endOfMonth(subMonths(now, 1));
  
  // Get call completions for different periods
  const thisWeekCalls = getAllCallCompletions(pipelineItems, activityLogs, thisWeekStart, thisWeekEnd);
  const lastWeekCalls = getAllCallCompletions(pipelineItems, activityLogs, lastWeekStart, lastWeekEnd);
  const thisMonthCalls = getAllCallCompletions(pipelineItems, activityLogs, thisMonthStart, thisMonthEnd);
  const lastMonthCalls = getAllCallCompletions(pipelineItems, activityLogs, lastMonthStart, lastMonthEnd);
  
  // Calculate trends
  const weeklyTrend = lastWeekCalls.length > 0 
    ? ((thisWeekCalls.length - lastWeekCalls.length) / lastWeekCalls.length) * 100 
    : thisWeekCalls.length > 0 ? 100 : 0;
    
  const monthlyTrend = lastMonthCalls.length > 0 
    ? ((thisMonthCalls.length - lastMonthCalls.length) / lastMonthCalls.length) * 100 
    : thisMonthCalls.length > 0 ? 100 : 0;
  
  return {
    thisWeek: {
      count: thisWeekCalls.length,
      period: `${format(thisWeekStart, 'MMM d')} - ${format(thisWeekEnd, 'MMM d')}`,
    },
    lastWeek: {
      count: lastWeekCalls.length,
      period: `${format(lastWeekStart, 'MMM d')} - ${format(lastWeekEnd, 'MMM d')}`,
    },
    thisMonth: {
      count: thisMonthCalls.length,
      period: format(thisMonthStart, 'MMMM yyyy'),
    },
    lastMonth: {
      count: lastMonthCalls.length,
      period: format(lastMonthStart, 'MMMM yyyy'),
    },
    trends: {
      weekly: weeklyTrend,
      monthly: monthlyTrend,
    },
  };
}

/**
 * Identifies call-related performance issues and opportunities
 */
export function analyzeCallPerformance(
  pipelineItems: any[], 
  activityLogs: any[], 
  kpiTargets: { [key: string]: number },
  currentDate: Date
) {
  const callMetrics = calculateCallMetrics(pipelineItems, activityLogs, subDays(currentDate, 30), currentDate);
  const trends = calculateCallVolumeTrends(pipelineItems, activityLogs, currentDate);
  
  const issues: string[] = [];
  const opportunities: string[] = [];
  
  // Check if call volume is below targets
  const weeklyTarget = kpiTargets.weeklyCalls || 10;
  if (trends.thisWeek.count < weeklyTarget) {
    issues.push(`Weekly call volume (${trends.thisWeek.count}) is below target (${weeklyTarget})`);
  }
  
  // Check conversion rate
  if (callMetrics.conversionRate < 20) {
    issues.push(`Call conversion rate (${callMetrics.conversionRate.toFixed(1)}%) is below 20%`);
  } else if (callMetrics.conversionRate > 30) {
    opportunities.push(`Strong call conversion rate (${callMetrics.conversionRate.toFixed(1)}%)`);
  }
  
  // Check for declining trends
  if (trends.trends.weekly < -10) {
    issues.push(`Weekly calls declining by ${Math.abs(trends.trends.weekly).toFixed(1)}%`);
  }
  
  if (trends.trends.monthly < -10) {
    issues.push(`Monthly calls declining by ${Math.abs(trends.trends.monthly).toFixed(1)}%`);
  }
  
  // Check for improving trends
  if (trends.trends.weekly > 20) {
    opportunities.push(`Weekly calls improving by ${trends.trends.weekly.toFixed(1)}%`);
  }
  
  return {
    metrics: callMetrics,
    trends,
    issues,
    opportunities,
  };
}

import { startOfWeek, endOfWeek, startOfMonth, endOfMonth, subWeeks, subMonths, subDays } from 'date-fns';
import { getAllCallCompletions } from './call-analytics';

/**
 * KPI Calculator Module
 * Handles all KPI calculations and target tracking
 */

// Type definitions for KPI calculations
export interface KPIMetric {
  current: number;
  target: number;
  status: 'excellent' | 'good' | 'needs_attention' | 'critical';
}

export interface ConversionMetric {
  current: number; // percentage
  target: number; // percentage
  status: 'excellent' | 'good' | 'needs_attention' | 'critical';
}

export interface PeriodKPIs {
  callVolume: KPIMetric;
  agreements: KPIMetric;
  listsOut: KPIMetric;
  sales?: KPIMetric;
  conversionRate?: ConversionMetric;
}

export interface KPITargets {
  weeklyCalls?: number;
  weeklyAgreements?: number;
  weeklyListsOut?: number;
  weeklySales?: number;
  monthlyCalls?: number;
  monthlyAgreements?: number;
  monthlyListsOut?: number;
  monthlySales?: number;
}

export interface TeamTargets {
  calls: number;
  agreements: number;
  listsOut: number;
  sales: number;
}

/**
 * Calculates team targets based on active BDRs and individual targets
 */
export function calculateTeamTargets(
  pipelineItems: any[], 
  activityLogs: any[], 
  kpiTargets: KPITargets
): { weekly: TeamTargets; monthly: TeamTargets; activeBdrs: string[] } {
  // Get unique BDRs for team calculation
  const bdrSet = new Set<string>();
  pipelineItems.forEach(item => item.bdr && bdrSet.add(item.bdr));
  activityLogs.forEach(log => log.bdr && bdrSet.add(log.bdr));
  const allBDRs = Array.from(bdrSet);
  
  // Filter for active BDRs (those with activity in the last 7 days)
  const activeBDRs = allBDRs.filter(bdr => {
    const recentActivity = activityLogs.filter(log => 
      log.bdr === bdr && 
      log.timestamp >= subDays(new Date(), 7)
    );
    return recentActivity.length > 0;
  });
  
  // Calculate team targets based on number of active BDRs
  const teamWeeklyTargets: TeamTargets = {
    calls: activeBDRs.length * (kpiTargets.weeklyCalls || 10),
    agreements: activeBDRs.length * (kpiTargets.weeklyAgreements || 3),
    listsOut: activeBDRs.length * (kpiTargets.weeklyListsOut || 1),
    sales: activeBDRs.length * (kpiTargets.weeklySales || 0.5)
  };
  
  const teamMonthlyTargets: TeamTargets = {
    calls: activeBDRs.length * (kpiTargets.monthlyCalls || 40),
    agreements: activeBDRs.length * (kpiTargets.monthlyAgreements || 12),
    listsOut: activeBDRs.length * (kpiTargets.monthlyListsOut || 4),
    sales: activeBDRs.length * (kpiTargets.monthlySales || 2)
  };

  return {
    weekly: teamWeeklyTargets,
    monthly: teamMonthlyTargets,
    activeBdrs: activeBDRs,
  };
}

/**
 * Detects sales indicators in text
 */
export function detectSalesIndicators(text: string): boolean {
  if (!text) return false;
  const lowerText = text.toLowerCase();
  
  // Check for sales indicators
  const salesKeywords = ['sold', 'deal', 'purchase', 'bought', 'payment', 'invoice', 'revenue'];
  const currencySymbols = ['Â£', '$', 'â‚¬'];
  
  // Check for sales keywords
  const hasSalesKeywords = salesKeywords.some(keyword => lowerText.includes(keyword));
  
  // Check for currency symbols
  const hasCurrencySymbols = currencySymbols.some(symbol => text.includes(symbol));
  
  return hasSalesKeywords || hasCurrencySymbols;
}

/**
 * Calculates KPI for a specific period and activity type
 */
export function calculateKPIForPeriod(
  activityType: string,
  startDate: Date,
  endDate: Date,
  target: number,
  pipelineItems: any[],
  activityLogs: any[]
): KPIMetric {
  let count: number;

  if (activityType === 'Call_Completed') {
    // Use enhanced call completion logic
    const callCompletions = getAllCallCompletions(pipelineItems, activityLogs, startDate, endDate);
    count = callCompletions.length;
  } else if (activityType === 'Agreement_Sent') {
    // Prefer pipeline agreementDate if present, fallback to activity logs
    const pipelineCount = pipelineItems.filter((item) => item.agreementDate && item.agreementDate >= startDate && item.agreementDate <= endDate).length;
    const logCount = activityLogs.filter((log) => log.activityType === 'Agreement_Sent' && log.timestamp >= startDate && log.timestamp <= endDate).length;
    count = Math.max(pipelineCount, logCount);
  } else if (activityType === 'Partner_List_Sent') {
    // Prefer pipeline partnerListSentDate if present, fallback to activity logs
    const pipelineCount = pipelineItems.filter((item) => item.partnerListSentDate && item.partnerListSentDate >= startDate && item.partnerListSentDate <= endDate).length;
    const logCount = activityLogs.filter((log) => log.activityType === 'Partner_List_Sent' && log.timestamp >= startDate && log.timestamp <= endDate).length;
    count = Math.max(pipelineCount, logCount);
  } else {
    // Use existing logic for other activity types
    count = activityLogs.filter(
      (log) => log.activityType === activityType && log.timestamp >= startDate && log.timestamp <= endDate
    ).length;
  }
  
  const status = count >= target * 1.25 ? 'excellent' : 
                 count >= target ? 'good' : 
                 count >= target * 0.5 ? 'needs_attention' : 'critical';
  
  return {
    current: count,
    target: target,
    status: status as 'excellent' | 'good' | 'needs_attention' | 'critical'
  };
}

/**
 * Calculates conversion rate for a specific period
 */
export function calculateConversionForPeriod(
  startDate: Date,
  endDate: Date,
  pipelineItems: any[],
  activityLogs: any[]
): ConversionMetric {
  // Use enhanced call completion logic for conversion rate calculation
  const callsInPeriod = getAllCallCompletions(pipelineItems, activityLogs, startDate, endDate).length;
  
  // Track unique pipeline items that have sales indicators to avoid double-counting
  const salesPipelineItems = new Set<number>();
  
  // Check pipeline items for sales indicators in notes
  // Only count if the item was updated during the period AND has sales indicators
  pipelineItems.forEach(item => {
    if (item.notes && detectSalesIndicators(item.notes)) {
      // Only count if the item was updated during the period
      if (item.lastUpdated >= startDate && item.lastUpdated <= endDate) {
        salesPipelineItems.add(item.id);
      }
    }
  });
  
  // Check activity logs for sales indicators in notes
  // Only count if the activity log was created during the period AND has sales indicators
  activityLogs.forEach(log => {
    if (log.notes && detectSalesIndicators(log.notes)) {
      // Only count if the activity log was created during the period
      if (log.timestamp >= startDate && log.timestamp <= endDate) {
        // If this activity log is associated with a pipeline item, add it to our set
        if (log.pipelineItemId) {
          salesPipelineItems.add(log.pipelineItemId);
        }
      }
    }
  });
  
  // Also include pipeline items with 'Sold' status that were updated during the period
  pipelineItems.forEach(item => {
    if (item.status === 'Sold' && 
        item.lastUpdated >= startDate && 
        item.lastUpdated <= endDate) {
      salesPipelineItems.add(item.id);
    }
  });
  
  const salesInPeriod = salesPipelineItems.size;
  
  const conversionRate = callsInPeriod > 0 ? (salesInPeriod / callsInPeriod) * 100 : 0;
  
  const status = conversionRate >= 25 ? 'excellent' : 
                 conversionRate >= 18 ? 'good' : 
                 conversionRate >= 12 ? 'needs_attention' : 'critical';
  
  return {
    current: Math.round(conversionRate * 100) / 100,
    target: 20,
    status: status as 'excellent' | 'good' | 'needs_attention' | 'critical'
  };
}

/**
 * Calculates sales for a specific period using finance entries
 */
export function calculateSalesForPeriod(
  startDate: Date,
  endDate: Date,
  target: number,
  pipelineItems: any[],
  activityLogs: any[],
  financeEntries: any[] = []
): KPIMetric {
  // Count all sales generated in the period based on invoice date, regardless of payment status
  const salesInPeriod = financeEntries.filter(entry => 
    entry.invoiceDate && 
    entry.invoiceDate >= startDate && 
    entry.invoiceDate <= endDate
  ).length;
  
  const status = salesInPeriod >= target * 1.25 ? 'excellent' : 
                 salesInPeriod >= target ? 'good' : 
                 salesInPeriod >= target * 0.5 ? 'needs_attention' : 'critical';
  
  return {
    current: salesInPeriod,
    target: target,
    status: status as 'excellent' | 'good' | 'needs_attention' | 'critical'
  };
}

/**
 * Main KPI calculation function
 */
export function calculateKPIs(
  pipelineItems: any[],
  activityLogs: any[],
  kpiTargets: KPITargets,
  currentDate: Date,
  financeEntries: any[] = []
) {
  const now = currentDate;
  const thisWeekStart = startOfWeek(now, { weekStartsOn: 1 });
  const thisWeekEnd = endOfWeek(now, { weekStartsOn: 1 });
  const lastWeekStart = startOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
  const lastWeekEnd = endOfWeek(subWeeks(now, 1), { weekStartsOn: 1 });
  const thisMonthStart = startOfMonth(now);
  const thisMonthEnd = endOfMonth(now);
  const lastMonthStart = startOfMonth(subMonths(now, 1));
  const lastMonthEnd = endOfMonth(subMonths(now, 1));
  
  // Calculate team targets
  const { weekly: teamWeeklyTargets, monthly: teamMonthlyTargets } = calculateTeamTargets(
    pipelineItems,
    activityLogs,
    kpiTargets
  );

  return {
    thisWeek: {
      callVolume: calculateKPIForPeriod('Call_Completed', thisWeekStart, thisWeekEnd, teamWeeklyTargets.calls, pipelineItems, activityLogs),
      agreements: calculateKPIForPeriod('Agreement_Sent', thisWeekStart, thisWeekEnd, teamWeeklyTargets.agreements, pipelineItems, activityLogs),
      listsOut: calculateKPIForPeriod('Partner_List_Sent', thisWeekStart, thisWeekEnd, teamWeeklyTargets.listsOut, pipelineItems, activityLogs),
      sales: calculateSalesForPeriod(thisWeekStart, thisWeekEnd, teamWeeklyTargets.sales, pipelineItems, activityLogs, financeEntries)
    } as PeriodKPIs,
    lastWeek: {
      callVolume: calculateKPIForPeriod('Call_Completed', lastWeekStart, lastWeekEnd, teamWeeklyTargets.calls, pipelineItems, activityLogs),
      agreements: calculateKPIForPeriod('Agreement_Sent', lastWeekStart, lastWeekEnd, teamWeeklyTargets.agreements, pipelineItems, activityLogs),
      listsOut: calculateKPIForPeriod('Partner_List_Sent', lastWeekStart, lastWeekEnd, teamWeeklyTargets.listsOut, pipelineItems, activityLogs),
      sales: calculateSalesForPeriod(lastWeekStart, lastWeekEnd, teamWeeklyTargets.sales, pipelineItems, activityLogs, financeEntries)
    } as PeriodKPIs,
    thisMonth: {
      callVolume: calculateKPIForPeriod('Call_Completed', thisMonthStart, thisMonthEnd, teamMonthlyTargets.calls, pipelineItems, activityLogs),
      agreements: calculateKPIForPeriod('Agreement_Sent', thisMonthStart, thisMonthEnd, teamMonthlyTargets.agreements, pipelineItems, activityLogs),
      listsOut: calculateKPIForPeriod('Partner_List_Sent', thisMonthStart, thisMonthEnd, teamMonthlyTargets.listsOut, pipelineItems, activityLogs),
      conversionRate: calculateConversionForPeriod(thisMonthStart, thisMonthEnd, pipelineItems, activityLogs)
    } as PeriodKPIs,
    lastMonth: {
      callVolume: calculateKPIForPeriod('Call_Completed', lastMonthStart, lastMonthEnd, teamMonthlyTargets.calls, pipelineItems, activityLogs),
      agreements: calculateKPIForPeriod('Agreement_Sent', lastMonthStart, lastMonthEnd, teamMonthlyTargets.agreements, pipelineItems, activityLogs),
      listsOut: calculateKPIForPeriod('Partner_List_Sent', lastMonthStart, lastMonthEnd, teamMonthlyTargets.listsOut, pipelineItems, activityLogs),
      conversionRate: calculateConversionForPeriod(lastMonthStart, lastMonthEnd, pipelineItems, activityLogs)
    } as PeriodKPIs,
    teamTargets: {
      weekly: teamWeeklyTargets,
      monthly: teamMonthlyTargets,
    },
  };
}

import { prisma } from './db';
import { getServerSession } from 'next-auth';
import { authOptions } from './auth-options';
import { Role, Resource, Action } from '@prisma/client';
import { NextRequest } from 'next/server';
import { getEffectivePermissions } from './permissions';

export interface SecurityContext {
  userId: string;
  role: Role;
  territoryId?: string;
  managedTerritoryIds: string[];
  permissions: string[];
}

export interface AuditLogData {
  action: string;
  resource: string;
  resourceId?: string;
  details?: any;
  success?: boolean;
  errorMsg?: string;
  ipAddress?: string;
  userAgent?: string;
  sessionId?: string;
}

export class SecurityService {
  static async getSecurityContext(): Promise<SecurityContext | null> {
    const session = await getServerSession(authOptions);
    if (!session?.user) return null;

    const user = session.user as any; // Type assertion for extended user properties

    // Get user permissions
    const userPermissions = await prisma.userPermission.findMany({
      where: {
        userId: user.id,
        OR: [
          { expiresAt: null },
          { expiresAt: { gt: new Date() } }
        ]
      },
      include: {
        permission: true
      }
    });

    // Get role permissions
    const rolePermissions = await prisma.rolePermission.findMany({
      where: { role: user.role },
      include: {
        permission: true
      }
    });

    const assignedPermissionStrings = [
      ...userPermissions.map(up => `${up.permission.resource}:${up.permission.action}`),
      ...rolePermissions.map(rp => `${rp.permission.resource}:${rp.permission.action}`)
    ];

    // Always include baseline defaults for the user's role so missing DB rows don't block core features
    const defaultEffective = getEffectivePermissions({
      id: user.id,
      role: user.role as Role,
      territoryId: user.territoryId || undefined,
      managedTerritories: user.managedTerritories || [],
      permissions: [],
    }).map(p => `${p.resource}:${p.action}`);

    const allPermissions = Array.from(new Set([...assignedPermissionStrings, ...defaultEffective]));

    return {
      userId: user.id,
      role: user.role,
      territoryId: user.territoryId || undefined,
      managedTerritoryIds: user.managedTerritoryIds || [],
      permissions: allPermissions
    };
  }

  static async canAccessResource(
    context: SecurityContext,
    resource: Resource,
    action: Action,
    resourceData?: any
  ): Promise<boolean> {
    // Allow authenticated users to use messaging features; row-level checks are enforced per endpoint
    if (resource === 'MESSAGING') {
      return true;
    }

    const permission = `${resource}:${action}`;
    // Check if user has the permission
    if (!context.permissions.includes(permission)) {
      return false;
    }

    // Apply row-level security based on resource type and user context
    return this.applyRowLevelSecurity(context, resource, action, resourceData);
  }

  private static async applyRowLevelSecurity(
    context: SecurityContext,
    resource: Resource,
    action: Action,
    resourceData?: any
  ): Promise<boolean> {
    // Admin can access everything
    if (context.role === 'ADMIN') {
      return true;
    }

    switch (resource) {
      case 'LEADS':
        return this.canAccessLeads(context, resourceData);

      case 'PIPELINE':
        return this.canAccessPipeline(context, resourceData);

      case 'FINANCE':
        return this.canAccessFinance(context, resourceData);

      case 'USERS':
        return this.canAccessUsers(context, action, resourceData);

      case 'REPORTS':
        return this.canAccessReports(context, resourceData);
      case 'MESSAGING':
        // High-level permission was already checked. Row-level membership is enforced in endpoints.
        return true;
      case 'TEMPLATES':
        return this.canAccessTemplates(context, action, resourceData);

      default:
        return false;
    }
  }

  private static canAccessLeads(context: SecurityContext, leadData?: any): boolean {
    if (!leadData) return true; // For general access

    // BDRs can only see their own leads
    if (context.role === 'BDR') {
      return leadData.bdrId === context.userId;
    }

    // Team leads can see their team's leads
    if (context.role === 'TEAM_LEAD' && context.territoryId) {
      return leadData.bdr?.territoryId === context.territoryId;
    }

    // Managers can see leads in territories they manage
    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return context.managedTerritoryIds.includes(leadData.bdr?.territoryId);
    }

    // Directors can see all leads
    return context.role === 'DIRECTOR';
  }

  private static canAccessPipeline(context: SecurityContext, pipelineData?: any): boolean {
    if (!pipelineData) return true;

    // BDRs can only see their own pipeline items
    if (context.role === 'BDR') {
      return pipelineData.bdrId === context.userId;
    }

    // Team leads can see their team's pipeline
    if (context.role === 'TEAM_LEAD' && context.territoryId) {
      return pipelineData.bdr?.territoryId === context.territoryId;
    }

    // Managers can see pipeline in territories they manage
    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return context.managedTerritoryIds.includes(pipelineData.bdr?.territoryId);
    }

    return context.role === 'DIRECTOR';
  }

  private static canAccessFinance(context: SecurityContext, financeData?: any): boolean {
    // Finance data is more restricted
    if (context.role === 'BDR') {
      return financeData?.bdrId === context.userId;
    }

    // Only managers and above can see team finance data
    if (context.role === 'TEAM_LEAD') {
      return false; // Team leads cannot see finance by default
    }

    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return context.managedTerritoryIds.includes(financeData?.bdr?.territoryId);
    }

    return context.role === 'DIRECTOR';
  }

  private static canAccessUsers(context: SecurityContext, action: Action, userData?: any): boolean {
    // Only managers and above can manage users
    if (action === 'CREATE' || action === 'DELETE' || action === 'MANAGE') {
      return ['MANAGER', 'DIRECTOR'].includes(context.role);
    }

    // BDRs can only see basic info of users in their territory
    if (context.role === 'BDR') {
      return userData?.territoryId === context.territoryId;
    }

    if (context.role === 'TEAM_LEAD' && context.territoryId) {
      return userData?.territoryId === context.territoryId;
    }

    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return context.managedTerritoryIds.includes(userData?.territoryId);
    }

    return context.role === 'DIRECTOR';
  }

  private static canAccessReports(context: SecurityContext, reportData?: any): boolean {
    // BDRs can only see their own reports
    if (context.role === 'BDR') {
      return reportData?.scope === 'self';
    }

    // Team leads can see team reports
    if (context.role === 'TEAM_LEAD') {
      return ['self', 'team'].includes(reportData?.scope);
    }

    return ['MANAGER', 'DIRECTOR'].includes(context.role);
  }

  private static canAccessTemplates(context: SecurityContext, action: Action, templateData?: any): boolean {
    // All authenticated roles can READ templates by default if they have READ permission assigned
    if (action === 'READ') return true;
    // For CREATE/UPDATE/DELETE, allow BDR and above if they have the permission assigned
    return ['BDR', 'TEAM_LEAD', 'MANAGER', 'DIRECTOR', 'ADMIN'].includes(context.role);
  }

  static async logAction(data: AuditLogData, request?: NextRequest): Promise<void> {
    try {
      const context = await this.getSecurityContext();
      if (!context) return;

      const ipAddress = data.ipAddress || this.getClientIP(request);
      const userAgent = data.userAgent || request?.headers.get('user-agent') || undefined;

      await prisma.auditLog.create({
        data: {
          userId: context.userId,
          action: typeof data.action === 'string' ? data.action : String(data.action),
          resource: typeof data.resource === 'string' ? data.resource : String(data.resource ?? 'UNKNOWN'),
          resourceId: data.resourceId,
          details: data.details ? JSON.parse(JSON.stringify(data.details)) : null,
          success: data.success ?? true,
          errorMsg: data.errorMsg,
          ipAddress,
          userAgent,
          sessionId: data.sessionId
        }
      });
    } catch (error) {
      console.error('Failed to log audit action:', error);
      // Don't throw - audit logging shouldn't break the main functionality
    }
  }

  private static getClientIP(request?: NextRequest): string | undefined {
    if (!request) return undefined;

    // Check for IP in various headers
    const forwarded = request.headers.get('x-forwarded-for');
    const real = request.headers.get('x-real-ip');
    const remote = request.headers.get('x-remote-addr');

    if (forwarded) {
      return forwarded.split(',')[0].trim();
    }
    if (real) {
      return real;
    }
    if (remote) {
      return remote;
    }

    return undefined;
  }

  static async createDataAccessPolicy(
    userId: string,
    resource: Resource,
    conditions: any,
    createdBy: string
  ): Promise<void> {
    await prisma.dataAccessPolicy.create({
      data: {
        userId,
        resource,
        conditions: JSON.parse(JSON.stringify(conditions)),
        createdBy
      }
    });
  }

  static async getDataAccessPolicies(userId: string, resource?: Resource) {
    return prisma.dataAccessPolicy.findMany({
      where: {
        userId,
        resource,
        isActive: true
      }
    });
  }

  static buildSecureQuery(
    baseQuery: any,
    context: SecurityContext,
    resource: Resource
  ): any {
    if (context.role === 'ADMIN') {
      return baseQuery; // Admin sees everything
    }

    switch (resource) {
      case 'LEADS':
        return this.buildLeadsQuery(baseQuery, context);
      case 'PIPELINE':
        return this.buildPipelineQuery(baseQuery, context);
      case 'FINANCE':
        return this.buildFinanceQuery(baseQuery, context);
      case 'MESSAGING':
        return baseQuery;
      case 'TEMPLATES':
        return baseQuery;
      default:
        return baseQuery;
    }
  }

  private static buildLeadsQuery(baseQuery: any, context: SecurityContext): any {
    if (context.role === 'BDR') {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdrId: context.userId
        }
      };
    }

    if (context.role === 'TEAM_LEAD' && context.territoryId) {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdr: {
            territoryId: context.territoryId
          }
        }
      };
    }

    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdr: {
            territoryId: {
              in: context.managedTerritoryIds
            }
          }
        }
      };
    }

    return baseQuery; // Directors see all
  }

  private static buildPipelineQuery(baseQuery: any, context: SecurityContext): any {
    if (context.role === 'BDR') {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdrId: context.userId
        }
      };
    }

    if (context.role === 'TEAM_LEAD' && context.territoryId) {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdr: {
            territoryId: context.territoryId
          }
        }
      };
    }

    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdr: {
            territoryId: {
              in: context.managedTerritoryIds
            }
          }
        }
      };
    }

    return baseQuery;
  }

  private static buildFinanceQuery(baseQuery: any, context: SecurityContext): any {
    if (context.role === 'BDR') {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdrId: context.userId
        }
      };
    }

    // Team leads cannot see finance data by default
    if (context.role === 'TEAM_LEAD') {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          id: 'never-match' // Effectively block access
        }
      };
    }

    if (context.role === 'MANAGER' && context.managedTerritoryIds.length > 0) {
      return {
        ...baseQuery,
        where: {
          ...baseQuery.where,
          bdr: {
            territoryId: {
              in: context.managedTerritoryIds
            }
          }
        }
      };
    }

    return baseQuery;
  }
}

// Export middleware function for easy integration
export async function withSecurity<T>(
  resource: Resource,
  action: Action,
  operation: (context: SecurityContext) => Promise<T>,
  request?: NextRequest
): Promise<T> {
  const context = await SecurityService.getSecurityContext();

  if (!context) {
    await SecurityService.logAction({
      action: action,
      resource: resource,
      success: false,
      errorMsg: 'Unauthorized access attempt'
    }, request);
    throw new Error('Unauthorized');
  }

  const hasAccess = await SecurityService.canAccessResource(context, resource, action);

  if (!hasAccess) {
    await SecurityService.logAction({
      action: action,
      resource: resource,
      success: false,
      errorMsg: 'Insufficient permissions'
    }, request);
    throw new Error('Forbidden');
  }

  try {
    const result = await operation(context);

    await SecurityService.logAction({
      action: action,
      resource: resource,
      success: true
    }, request);

    return result;
  } catch (error) {
    await SecurityService.logAction({
      action: action,
      resource: resource,
      success: false,
      errorMsg: error instanceof Error ? error.message : 'Unknown error'
    }, request);
    throw error;
  }
}

import { NextRequest } from 'next/server';
import { lookup as lookupMime } from 'mime-types';

export type AllowedUpload = {
  mime: string;
  size: number; // bytes
};

const MAX_FILE_SIZE_BYTES = 25 * 1024 * 1024; // 25MB
const ALLOWED_MIME_PREFIXES = ['image/', 'video/', 'application/pdf', 'application/vnd', 'text/plain'];

export function validateUpload({ mime, size }: AllowedUpload) {
  if (!mime || !ALLOWED_MIME_PREFIXES.some((p) => mime.startsWith(p))) {
    throw new Error('Unsupported file type');
  }
  if (size <= 0 || size > MAX_FILE_SIZE_BYTES) {
    throw new Error('File too large');
  }
}

// Minimal Vercel Blob direct upload URL creator via REST
export async function createDirectUploadUrl({ filename, mime, size }: { filename: string; mime: string; size: number }) {
  validateUpload({ mime, size });
  const token = process.env.BLOB_READ_WRITE_TOKEN;
  if (!token) throw new Error('Blob token not configured');

  const res = await fetch('https://api.vercel.com/v2/blobs/upload', {
    method: 'POST',
    headers: {
      Authorization: `Bearer ${token}`,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ filename, contentType: mime, size }),
  });
  if (!res.ok) {
    const text = await res.text();
    throw new Error(`Failed to create upload URL: ${text}`);
  }
  return (await res.json()) as { url: string; uploadUrl: string; pathname: string };
}
